[11865] = {"Им рано умирать", nil, {"Используйте охапку искусственных шкур, чтобы поймать в ловушки 8 звероловов Эрнестуэя по просьбе Зазы из лагеря Д.Э.Г.О.Ж. в Борейской тундре."}},
[11866] = {"Уши мертвых врагов", nil, {"Принесите верховному друиду Латориусу из лагеря Д.Э.Г.О.Ж. в Борейской тундре 15 ушей прихвостней Эрнестуэя и получите награду."}},
[11867] = {"Коллекция ушей", nil, {"Принесите верховному друиду Латориусу из лагеря Д.Э.Г.О.Ж. в Борейской тундре 15 ушей прихвостней Эрнестуэя и получите награду."}},
[11868] = {"Сортировщица грядет!", nil, {"По заданию Заза из лагеря Д.Э.Г.О.Ж. в Борейской тундре убейте Карину Сортировщицу."}},
[11869] = {"Судьба моллюсков", nil, {"По заданию иерофанта Сениуса из лагеря Д.Э.Г.О.Ж. в Борейской тундре убейте 10 загребущих ныряльщиков."}},
[11870] = {"Покинутый предел", nil, {"Найдите иерофанта Лиандру в Покинутом пределе."}},
[11871] = {"Пока мы на страже", nil, {"Добудьте 12 партий частей тел животных по просьбе иерофанта Лиандры из Покинутого предела в Борейской тундре."}},
[11872] = {"Гнусный специалист по моллюскам", nil, {"Убейте специалиста по моллюскам К и возвращайтесь к иерофанту Сениус в лагерь Д.Э.Г.О.Ж."}},
[11873] = {"Новости для Выкрутеня", nil, {"Вернитесь на взлетную полосу Выкрутеня и поговорите с Выкрутенем Разгоняйло."}},
[11875] = {"Обретение преимущества", nil, {"Принесите 8 горстей пыли Пустоты эмиссару Мордину в Шаттрат."}},
[11876] = {"Помоги тем, кто не может помочь себе сам", nil, {"По заданию верховного друида Латориуса в лагере Д.Э.Г.О.Ж. в Борейской тундре освободите 8 мамонтят с помощью уничтожителя ловушек Д.Э.Г.О.Ж."}},
[11877] = {"План нападения клана Ярости Солнца", nil, {"Лорд Торвос из Шаттрата хочет, чтобы вы захватили планы нападения клана Ярости Солнца."}},
[11878] = {"Ку'нок обо всем позаботится!", nil, {"Отведите мамонтенка-сироту к Ку'ноку Чудовищу в Борейской тундре."}},
[11879] = {"Кау Гроза Мамонтов", nil, {"Убейте Кау Грозу Мамонтов и добудьте боевую алебарду Кау. Доставьте ее верховному друиду Латориусу в лагерь Д.Э.Г.О.Ж. в Борейской тундре."}},
[11880] = {"Мультифазовый подход", nil, {"Предвестник Харонем из Шаттрата просит вас при помощи мультифазовых спектрографических очков 6 раз провести считывание мультифазовых данных."}},
[11881] = {"Ценный груз", nil, {"Помогите ослице Дженни добраться с места крушения к Феззиксу Штукельверту в деревню Таунка'ле, не растеряв груз."}},
[11882] = {"Игра с огнем", nil, {"Поговорите с мастером-огнеглотателем в любой из столиц Альянса."}},
[11883] = {"Танцы с огнем?", nil, {"Поговорите с мастером огненного танца в любой из столиц Орды."}},
[11884] = {"Нед, \"Повелитель люторогов\"", nil, {"По заданию Киллинджера Смотрителя Логова из лагеря Д.Э.Г.О.Ж. в Борейской тундре убейте Недара, \"Повелителя люторогов\", и его спутника, люторога по кличке \"Закуска\"."}},
[11885] = {"Кровные враги", nil, {"Найдите груды черепов в середине кругов призыва Скеттиса. С помощью 10 свитков затерянных во времени призовите каждого потомка Скеттиса, уничтожьте их и отнесите трофеи Хаззику в лагерь Черного Ветра."}},
[11886] = {"Необычная активность", nil, {"Отправляйтесь в новый лагерь культа Сумеречного Молота к югу от входа в Непроглядную Пучину на Зорамском взморье, что в Ясеневом лесу, и добудьте сумеречную корреспонденцию, затем при помощи тотема-маяка свяжитесь с проводником из Служителей Земли."}},
[11887] = {"Неприкосновенный запас", nil, {"Соберите 7 гномских ремонтных наборов с тел погибших пилотов-разведчиков со станции Выкрутеня и принесите их Феззиксу Штукельверту в деревню Таунка'ле."}},
[11888] = {"Поездка в деревню Таунка'ле", nil, {"Поговорите с ведуном земли и неба в деревне Таунка'ле."}},
[11889] = {"Смерть с небес", nil, {"По поручению сержанта Молотогора убейте вне стен Крепости Отваги 6 порабощенных Плетью изрыгателей огня с помощью укрепленной сети."}},
[11890] = {"Что там с ними?", nil, {"Исследуйте область вокруг насосной станции Выкрутеня, а затем возвращайтесь к ведуну земли и неба в деревню Таунка'ле."}},
[11891] = {"Невинный маскарад", nil, {"При помощи сферы Краба проникните в новый Сумеречный лагерь к западу от входа в Непроглядную Пучину на Зорамском взморье в Ясеневом лесу, затем вызовите проводника из Служителей Земли при помощи тотема-маяка."}},
[11892] = {"Убить Гарольда Лейна!", nil, {"По поручению верховного друида Латориуса из лагеря Д.Э.Г.О.Ж. в Борейской тундре убейте Гарольда Лейна."}},
[11893] = {"Сила стихий", nil, {"Дорейн Морозное Копыто из деревни Таунка'ле просит вас использовать тотем духа ветра, чтобы собрать энергию 10 паровых яростней."}},
[11894] = {"Заплатки", nil, {"Принесите 5 пропаренных шкур Феззиксу Штукельверту в деревню Таунка'ле."}},
[11895] = {"Одолеть бурю", nil, {"Одолейте порыв шторма, затем возвращайтесь к ведуну земли и неба в деревне Таунка'ле."}},
[11896] = {"Уязвимы для молний", nil, {"Уничтожьте 15 любых роботов, кроме механогномов, ослабленных воздействием ярости шторма, в области прудов вокруг насосной станции Выкрутеня.\n\nПосле этого вернитесь к ведуну земли и неба в деревне Таунка'ле."}},
[11897] = {"Заткнуть воронки!", nil, {"По поручению сержанта Молотогора используйте зажигательную взрывчатку, чтобы взорвать и обвалить воронки к юго-западу и северо-западу от Крепости Отваги."}},
[11898] = {"Прорыв", nil, {"Одолейте принца Валанара с его подручными, Вантрином Безжалостным и Лютионом Злобным, потом явитесь к вождю Зимней Стуже в деревне Таунка'ле."}},
[11899] = {"Души распроклятых", nil, {"Используйте ловца душ Великой Матери, чтобы захватить души 10 механогномов со станции Выкрутеня. Затем вернитесь к Великой Матери Тайге в деревне Таунка'ле."}},
[11900] = {"Что показывают приборы?", nil, {"По просьбе верховного мага Беринанда из лагеря под Маскировочным щитом соберите показания его приборов в Хладарре."}},
[11901] = {"Военные? Какие военные?", nil, {"По просьбе Джеральда Грина из Далечья отправляйтесь на север, в шахту, и выясните, что случилось с направленным туда воинским подразделением."}},
[11902] = {"Ужасное свидетельство", nil, {"Возвращайтесь в Далечье к Джеральду Грину."}},
[11903] = {"Время действовать!", nil, {"По просьбе Джеральда Грина из Далечья убейте 14 чумных падальщиков."}},
[11904] = {"Плоды трудов наших", nil, {"Отправляйтесь на шахту Далечья, добудьте ключ от повозки у капитана Якобса и используйте его для того, чтобы пустить повозку с рудой. По завершении задания вернитесь к Джеральду Грину в Далечье."}},
[11905] = {"Отсрочка неизбежного", nil, {"Проекция боевого мага Кейтлин в Нексусе просит вас использовать рядом с разломом восстановитель измерений."}},
[11906] = {"Очищение прудов", nil, {"Соберите 15 запасных деталей Выкрутеня и доставьте их Стальным Глазам в деревню Таунка'ле."}},
[11907] = {"Подручные Механозода", nil, {"Уничтожьте Шатунишку, ЕД-210, Макса Шмякса и Шлифовальщика, затем вернитесь к Ведуну земли и неба в деревню Таунка'ле."}},
[11908] = {"Справочные материалы", nil, {"По просьбе Иеремии Хоунинга вынесите книгу \"Поля, заводы и мастерские\" из горящего здания рядом с рудником Далечья."}},
[11909] = {"Убить механизатора!", nil, {"Изучите Руководство механизатора и принесите голову Механозода Великой матери Тайге в деревню Таунка'ле."}},
[11910] = {"Тайны Древних", nil, {"По просьбе верховного мага Беринарда из-под Маскировочного щита принесите 3 ледяных осколка и 3 щепки зачарованного древа, собранных с древ в Хладарре."}},
[11911] = {"Преображение", nil, {"Проекция боевого мага Кейтлин в Нексусе хочет, чтобы вы собрали 5 магических обломков кристаллических заступников."}},
[11912] = {"На подножном корму", nil, {"Соберите 10 ягод снежевики с кустов снежевики в Хладарре по просьбе библиотекаря Серры из лагеря под Маскировочным щитом."}},
[11913] = {"Надо действовать наверняка", nil, {"По просьбе Венди Даррен сожгите 8 мешков с зерном Далечья, воспользовавшись факелом Венди."}},
[11914] = {"Сохранение секретности", nil, {"По просьбе библиотекаря Серры убейте волшебных змеев и принесите за Маскировочный щит 5 субстанций маны Нексуса."}},
[11915] = {"Игра с огнем", nil, {"Поговорите с пламеглотателем-виртуозом в любой из столиц Орды."}},
[11916] = {"Воин Адского Крика", nil, {"Отыщите вождя Зимнюю Стужу в деревне Таунка'ле."}},
[11918] = {"Первое знакомство", nil, {"Убейте 10 чаровязов Хладарры по просьбе Релораза из лагеря под Маскировочным щитом."}},
[11919] = {"Охота на драконов", nil, {"По просьбе Релораза используйте копье с окровавленным наконечником, чтобы подчинить детеныша дракона Нексуса, а затем принесите Релоразу копье назад за Маскировочный щит."}},
[11920] = {"Сектанты среди нас", nil, {"Поговорите с капитаном \"Левшой\" Люггером на \"Рассекателе туч\" в Крепости Отваги."}},
[11921] = {"Опять бросаем факелы", nil, {"Выиграйте игру в бросание факелов, затем поговорите с пламеглотателем-виртуозом."}},
[11922] = {"Жонглирование факелами", nil, {"Выиграйте игру в бросание факелов, затем поговорите с пламеглотателем-виртуозом."}},
[11923] = {"Поймай факел!", nil, {"Поймайте подряд 4 факела, затем поговорите с пламеглотателем-виртуозом."}},
[11924] = {"Опять жонглирование факелами", nil, {"Поймайте 10 факелов подряд, затем поговорите с пламеглотателем-виртуозом."}},
[11925] = {"Опять жонглирование факелами", nil, {"Поймайте 10 факелов подряд, затем поговорите с пламеглотателем-виртуозом."}},
[11926] = {"Опять бросаем факелы", nil, {"Выиграйте игру в бросание факелов, затем поговорите с пламеглотателем-виртуозом."}},
[11927] = {"Встреча в трактире", nil, {"Найдите Лериссу в таверне в Крепости Отваги."}},
[11928] = {"Далечье", nil, {"Отправляйтесь в Далечье в Борейской Тундре и поговорите с Джеральдом Грином."}},
[11929] = {"Гибель деревни Таунка'ле", nil, {"Поговорите с матерью Тауранук у ворот деревни Таунка'ле."}},
[11930] = {"Через Трансборею", nil, {"Проводите эвакуированных жителей Таунка'ле через Трансборею на границу Драконьего Погоста, где их ждет Вартук Дитя Льда."}},
[11931] = {"Взломать шифр", nil, {"Добудьте Лазурный кодекс у инквизитора Калераса и 3 мерцающих руны у его чароплетов Хладарры и отнесите Релоразу за Маскировочный щит."}},
[11932] = {"Дураки и трусы", nil, {"Поговорить с Атаикой в Каскале, к северо-востоку от Крепости Отваги."}},
[11934] = {"Unlocking Your Potential [PH]", nil, {"Bring this dude some stuff."}},
[11935] = {"Похищение пламени Луносвета", nil, {"Верните пламя Луносвета хранителю мудрости Праздника."}},
[11936] = {"Наклевывается план!", nil, {"Убейте драконидов из Хладарры и используйте их застывшие топоры, чтобы разбить 5 яиц синих драконов по просьбе Релораза из лагеря под Маскировочным щитом."}},
[11938] = {"Отвлечь противника", nil, {"Подстройте 20 несчастных случаев, в которых пострадает Плеть в Храмовом городе Эн'кила, по просьбе Тассариана из Стонущего зиккурата в Борейской тундре. Используйте кости Лурида, если вам понадобится помощь."}},
[11940] = {"Охота на драконов", nil, {"По просьбе Релораза используйте копье с окровавленным наконечником, чтобы подчинить детеныша дракона Нексуса, а затем принесите ему копье за Маскировочный щит."}},
[11941] = {"Загадочный обломок", nil, {"Принесите искрометный обломок Релоразу за Маскировочный щит."}},
[11942] = {"Слова могущества", nil, {"По поручению Тассариана, который находится у Стонущего зиккурата, отправляйтесь в храмовый город Эн'кила и раздобудьте свиток верховного жреца Талет-Ха в Шпиле Боли, свиток верховного жреца Наферсета – в Шпиле Порчи и свиток верховного жреца Андората – в Шпиле Крови."}},
[11943] = {"Магическая клетка", nil, {"Добудьте тюремную обшивку у генерала Церулеана и энергетическое ядро у завоевателя Кровозмея по просьбе Релораза из лагеря под Маскировочным щитом."}},
[11944] = {"В окружении!", nil, {"По заданию капрала Венна убейте 7 защитников зиккурата."}},
[11945] = {"Подготовка к самому худшему", nil, {"По просьбе Утайка соберите в Каскале 8 ящиков припасов Каскала."}},
[11946] = {"Керистраза", nil, {"Поговорите с Керистразой, когда будете готовы к встрече с Малигосом.\n\nЕсли потеряете свою магическую клетку, поговорите с Релоразом у Маскировочного щита."}},
[11949] = {"Не сдаваться без боя!", nil, {"По просьбе Атаики убейте 12 налетчиков из племени Квалдир в Каскале."}},
[11950] = {"Мудрость Муахита", nil, {"Найдите в Каскале старейшину Муахита."}},
[11951] = {"Приманка", nil, {"Соберите в Хладарре 10 осколков кристаллизованной маны по просьбе Керистразы.\n\nЕсли потеряете свою магическую клетку, поговорите с Релоразом у Маскировочного щита."}},
[11953] = {"Ответный удар", nil, {"Убейте лейтенанта Хладноглыба, помощника Ахуна, вызвав его при помощи Камня Льда в Сумеречной пещере в северном Силитусе, затем вернитесь к старейшине Служителей Земли в столицу."}},
[11954] = {"Ответный удар", nil, {"Убейте ледяного храмовника, помощника Ахуна, вызвав его при помощи камня льда к Темному порталу на полуострове Адского Пламени, затем вернитесь к старейшине Служителей Земли в столицу."}},
[11955] = {"Ахун, Повелитель Холода", nil, {"Отправляйтесь в Узилище в резервуар Кривого Клыка, что в Зангартопи, и поговорите с Нумой Сестрой Облаков."}},
[11956] = {"Поиски талисмана", nil, {"Отправляйтесь на Стоянку Смерти и скачите на Сумраке к месту, где находится талисман Танатала. Добудьте его и принесите Тассариану в Стонущий зиккурат."}},
[11957] = {"Смерть Сарагосе!", nil, {"По просьбе Керистразы используйте средоточие магии для привлечения внимания Сарагосы, а затем сразитесь с ней."}},
[11958] = {"Чтоб ничего не пропало!", nil, {"Отберите у волчеров 6 украденных припасов Моа'ки. Верните их старейшине Ко'нани в Гавани Моа'ки."}},
[11959] = {"Убить Логуна!", nil, {"Намажьтесь кровью Логуна и возвращайтесь к старейшине Ко'нани в гавань Моа'ки."}},
[11960] = {"Планы на будущее", nil, {"Соберите 12 щенков Поляны Снегопада. Принесите их зверолову Мау'и в гавани Моа'ки."}},
[11961] = {"Духи присматривают за нами", nil, {"Найдите тело Ирука в воде, заберите тотем Иссилрука и принесите старейшине Муахиту в бухту Дыхания Ньорда."}},
[11962] = {"Последняя партия", nil, {"Доставьте ящик руды из Далечья Хильде Каменной Кузне в Крепость Отваги."}},
[11963] = {"Оружие для Далечья", nil, {"Вернитесь к Джеральду Грину в Далечье."}},
[11964] = {"Благовония для летних пламеней", nil, {"Хранитель мудрости Праздника хочет, чтобы вы отнесли благовония Лета летнему пламеню к любому поселению Альянса."}},
[11965] = {"Призыв к оружию!", nil, {"Войдите в здание ратуши в Далечье и позвоните в колокол, находящийся в этом здании."}},
[11966] = {"Благовония для праздничных сполохов", nil, {"Праздничный сказитель хочет, чтобы вы отнесли благовония Лета праздничному сполоху к любому поселению Орды."}},
[11967] = {"Сбор красных драконов", nil, {"Отведите Керистразу к Релоразу к Маскировочному щиту."}},
[11968] = {"Поворот колеса Фортуны", nil, {"По просьбе старейшины Муахита убейте Хейгарра Душегуба в бухте Дыхания Ньорда."}},
[11969] = {"Сработавший капкан", nil, {"Используйте сигнальный огонь Релораза у северо-западного входа в Нексус по просьбе Релораза."}},
[11970] = {"Хранитель мудрости праздника Середины Лета", nil, {"Поговорите с Хранителем мудрости Празднества в любой из столиц Альянса."}},
[11971] = {"Сказитель Летнего праздника", nil, {"Поговорите с праздничным сказителем в любой из столиц Орды."}},
[11972] = {"Осколки Ахуна", nil, {"Принесите ледяные осколки Луме Матери Небес."}},
[11973] = {"Пленница", nil, {"Боевой маг Кейтлин хочет, чтобы вы освободили Керистразу."}},
[11974] = {"[ph] Now, When I Grow Up...", nil, {"Отведите вашу сиротку, Саландрию, посмотреть на выступление группы L70ETC в Луносвет, на Путь Старейшин. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом."}},
[11975] = {"Когда я вырасту...", nil, {"Отведите свою подопечную, сиротку Саландрию, посмотреть на группу \"Вожди тауренов\" в Луносвет на Путь Старейшин. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом."}},
[11976] = {"Ледяные осколки", nil, {"Принесите ледяные осколки Луме Матери Небес."}},
[11977] = {"Таурен среди таунка", nil, {"Поищите эмиссара Светлое Копыто в лагере беженцев Западного Ветра."}},
[11978] = {"Добро пожаловать в Орду", nil, {"По просьбе эмиссара Светлое Копыто из лагеря беженцев Западного Ветра на Драконьем Погосте добудьте 10 ящиков с ордынским оружием."}},
[11979] = {"Таунка и таурены", nil, {"Отыщите эмиссара Светлое Копыто в лагере беженцев Западного Ветра."}},
[11980] = {"Гордость Орды", nil, {"Убейте 15 ануб'арских душителей по просьбе кровавого стража Рох'килла из лагеря беженцев Западного Ветра в Драконьем Погосте."}},
[11981] = {"Найди Куруна!", nil, {"Поговорите с Куруном, который стоит на холме, возвышающимся над Тор Моданом."}},
[11982] = {"Каменный дождь", nil, {"Курун, стоящий на холме, рядом с Тор Моданом, просит вас помешать железным дворфам, сбросив в котлован Тор Модана несколько булыжников."}},
[11983] = {"Клятва крови Орды", nil, {"Поговорите с таунка в лагере беженцев Западного Ветра и примите у пятерых из них клятву верности Орде.\n\nВерните клятву крови Орды эмиссару Светлое Копыто, когда выполните задание."}},
[11984] = {"Захлопнуть клетку", nil, {"Самир из Гранитных ключей просит вас запереть в клетку охотника оглушенного Баддом ледяного тролля."}},
[11985] = {"Битва в Проломе", nil, {"Курун, стоящий на холме, возвышающемся над Тор Моданом, просит вас убить железного тана Аргрума."}},
[11986] = {"Потрепанный дневник", nil, {"Разыщите 8 недостающих страниц, вклейте их в дневник и принесите его Тортену Глубокопу в Лагерь дружины Западного Края."}},
[11987] = {"zzOLDГадательная карта: Серебро", nil, nil},
[11988] = {"Рунический краеугольный камень", nil, {"Принесите 5 фрагментов рунического краеугольного камня Тортену Глубокопу в Лагерь дружины Западного Края."}},
[11989] = {"Перемирие?", nil, {"Дракуру из Гранитных ключей просит вас скрепить перемирие кровью, поцарапав свою руку тупым резаком, лежащим возле его клетки."}},
[11990] = {"Пузырек видений", nil, {"Дракуру из Гранитных ключей просит вас принести ему хрустальную колбу, сплетение водорослей и три листа туманолистника."}},
[11991] = {"Требуется расшифровка", nil, {"Дракуру просит вас добыть 5 застывших настоев у Драккари, обитающих среди Руин Драк'Зина, и затем выпить эликсир возле жаровни Дракуру."}},
[11992] = {"Навигационные карты (DEPRECATED)", nil, {"Раздобудьте недостающие части скадирской навигационной карты и принесите Каруку на берег Терзающего Бича."}},
[11993] = {"Рунические пророчества", nil, {"Тортен Глубокоп из Лагеря дружины Западного Края просит вас расшифровать три рунических диска."}},
[11995] = {"Безотлагательное путешествие в Покой Звезд", nil, {"Поговорите с проекцией верховного мага Модеры в Покое Звезд."}},
[11996] = {"Безотлагательное путешествие в Молот Агмара", nil, {"Поговорите с проекцией верховного мага Этаса Похитителя Солнца в Молоте Агмара."}},
[11998] = {"Подсластить пилюлю", nil, {"Принесите Бругару Гравировщику из Лагеря дружины Западного Края бочку старого доброго самогона."}},
[11999] = {"Обшарить трупы", nil, {"Найдите планы в Садах Лунного Покоя и вернитесь с ними к проекции верховного мага Этаса Похитителя Солнца в Молоте Агмара."}},
[12000] = {"Обшарить трупы", nil, {"Найдите планы в Садах Лунного Покоя и вернитесь с ними к проекции верховного мага Модеры в Покое Звезд."}},
[12001] = {"zzOLDКарты охотника: меньшие звери", nil, {"Соберите 12 рваных шкур и призовите охотника с помощью карты. Рваные шкуры можно добыть, убивая зверей 5 уровня или выше."}},
[12002] = {"Братья по оружию", nil, {"Убейте 10 рунических боевых големов и вернитесь к Бругару Гравировщику в Лагерь дружины Западного Края."}},
[12003] = {"В поисках туннелей", nil, {"Бругар Гравировщик из Лагеря дружины Западного Края просит вас осмотреть северное, восточное и южное строения в Тор Модане."}},
[12004] = {"Предотвратить союз", nil, {"Принесите проекции верховного мага Модеры в Покой Звезд останки торговца Ветром Му'фа и чешую Горамоша."}},
[12005] = {"Предотвратить союз", nil, {"Доставьте останки Му'фы Торговца Ветром и чешую Горамоша к проекции верховного мага Этаса Похитителя Солнца в Молоте Агмара."}},
[12006] = {"Месть за бесчинства!", nil, {"Убейте охотников на магов с Драконьего Погоста, ловчих из Садов Лунного Покоя, колдунов Волноловов и волшебников средоточия, общим числом 15, после чего возвращайтесь к Этениалу Тени Луны в Сады Лунного Покоя."}},
[12007] = {"Необходимые жертвы", nil, {"Дракуру просит принести ему Око пророка к жаровне в Зеб'Халаке.\n\nЧтобы эликсир Дракуру сработал на пьедестале, вам придется добыть настой Зим'бо."}},
[12008] = {"Молот Агмара", nil, {"Отправляйтесь в Молот Агмара на Драконьем Погосте и поговорите с командиром Агмаром."}},
[12009] = {"Крабовые ловушки Туа'кеа", nil, {"Соберите 8 ловушек Туа'кеа для крабов и принесите их ему в гавань Моа'ки."}},
[12010] = {"Что случилось с Орлондом?", nil, {"Найдите геодезиста Орлонда в Тор Модане."}},
[12011] = {"Признаки большой проблемы", nil, {"Сообщите рыбаку Туа'кеа о разрушенной ловушке на крабов."}},
[12013] = {"Покончить с Арканимусом!", nil, {"Уничтожьте Арканимуса и вернитесь к Этениалу Тени Луны в сады Лунного Покоя."}},
[12014] = {"Крепкий как камень?", nil, {"Верните портативный сейсмограф и сделайте последний замер колебаний в Тор Модане, после чего вернитесь к Бругару Гравировщику в Лагерь дружины Западного Края."}},
[12016] = {"Наживка", nil, {"Добудьте мясо матриарха по прозвищу \"Огромные Клешни\" и принесите его Туа'кеа в гавань Моа'ки."}},
[12017] = {"Наживка на крючке", nil, {"Убейте Ту'у'гвара, а затем вернитесь к Туа'кеа в гавань Моа'ки."}},
[12018] = {"Месть сладка", nil, {"Добудьте 2 отколотых куска горгульи, убивая горгулий-падальщиков с восточных Полей Падальщиков. Выполнив задачу, вернитесь к лорду Бевису."}},
[12019] = {"Последние ритуалы", nil, {"Отправьтесь в Храмовый город Эн'кила и отыщите сферу телепортации под парящей цитаделью Наксанар. Используйте сферу, чтобы попасть на вершину цитадели и помочь Тассариану."}},
[12020] = {"Однажды, напившись в...", nil, {"Поговорите с Бокси Болтокрутом в лагере Хмельного фестиваля."}},
[12022] = {"Пей до дна!", nil, {"Попадите кружкой с бесплатным пивом в С.С.М. 5 раз, затем поговорите с Бокси Болтокрутом."}},
[12024] = {"Перепись павших", nil, {"Обыщите скелеты на Полях Падальщиков и заберите у них расчетные ведомости Альянса."}},
[12025] = {"Алый орден среди нас", nil, {"Отыщите свидетельства связи с Алым орденом на недавно появившихся вурдалаках-падальщиках с восточных Полей Падальщиков. Когда что-нибудь найдете, вернитесь к леди Алустре."}},
[12026] = {"Потрепанный дневник", nil, {"Разыщите 8 недостающих страниц, вклейте их в дневник и верните его Ведунье Палуне в Лагерь Уанква."}},
[12027] = {"Удивительное приключение мистера Ушастика", nil, {"Помогите Эмили и мистеру Ушастику вернуться в Лагерь дружины Западного Края, после чего поговорите с оруженосцем Волтером."}},
[12028] = {"Духовное озарение", nil, {"Поговорите с Тоалу'у Мистиком после того, как пройдете обряд поиска видений."}},
[12029] = {"Пылающая Плеть", nil, {"Мак с Гранитных ключей просит вас поджечь 20 голодающих троллей Плети с помощью его грога."}},
[12030] = {"Старейшина Мана'лоа", nil, {"Поговорите со статуей старейшины Мана'лоа в деревне Инду'ле."}},
[12031] = {"Свобода перемещения", nil, {"Упокойте рыбаков, мистиков и воинов из деревни Инду'ле (общим числом 15 противников), а затем вернитесь к старейшине Мана'лоа в деревню Инду'ле."}},
[12032] = {"Разговор с бездной", nil, {"Обратитесь к Жемчужине Бездны и выполните распоряжения морской богини Оача'ноа.\n\nЕсли вы переживете все испытания, вернитесь к Тоалу'у Мистику в гавани Моа'ки."}},
[12033] = {"Послание с запада", nil, {"Прочитайте письмо от Саурфанга и уничтожьте его.\n\nВыполнив это, поговорите с посланником Торвусом."}},
[12034] = {"Победа близка…", nil, {"Поговорите с главным сержантом Юктоком в Молоте Агмара."}},
[12035] = {"Переналадка технологии", nil, {"По поручению Иеремии Хоунинга обезвредьте 5 сборщиков урожая, а затем воспользуйтесь инструментами Иеремии, чтобы перемонтировать их."}},
[12036] = {"Глубины Азжол-Неруба", nil, {"Исследуйте Провал Наржуна и возвращайтесь с данными к главному сержанту Юктоку, ожидающему у Молота Агмара."}},
[12037] = {"Спасательная операция", nil, {"Мак из Гранитных ключей просит вас отправиться в Драк'Тарон и узнать, все ли в порядке с Курцель."}},
[12038] = {"Пылающая Плеть", nil, {"Мак из Гранитных ключей просит вас поджечь 30 голодающих троллей Плети с помощью его грога."}},
[12039] = {"Черная кровь Йогг-Сарона", nil, {"Принесите Борусу Железные Клещи из Молота Агмара на Драконьем Погосте 10 образцов черной крови Йогг-Сарона."}},
[12040] = {"Общий враг в лице Артаса", nil, {"По заданию Киликса Разрушителя у Провала Наржуна на Драконьем Погосте убейте 6 ануб'арских владык подземелий."}},
[12041] = {"Потерянная империя", nil, {"Доставьте боевые планы Киликса командиру Агмару в Молот Агмара на востоке."}},
[12042] = {"Сердце древних", nil, {"Дракуру просит вас отправиться в самую верхнюю комнату Лесозаготовок Синего Неба и найти Сердце древних."}},
[12043] = {"Защита Лагеря Соплозабилось", nil, {"Убейте 12 землекопов пустошей и одного надсмотрщика пустошей на раскопках, окружающих Покой Галакронда. Вернитесь к Нарфу в Лагерь Соплозабилось, когда задание будет выполнено."}},
[12044] = {"Запасаемся", nil, {"Соберите 8 пригоршней композитной руды на раскопках, окружающих Покой Галакронда и принесите их Зинку в Лагерь Соплозабилось."}},
[12045] = {"Битый лед", nil, {"Соберите 4 горсти осколков льда от ледяных элементалей на севере и принесите их Живликс в Лагерь Соплозабилось."}},
[12046] = {"Мягкая оболочка", nil, {"Соберите 12 тонких шкур животных, убив нужное количество туннельных йормунгаров или глодателей драконьих костей близ Лагеря Соплозабилось. Добыв шкуры, вернитесь с ними к Живликс."}},
[12047] = {"Нечто тугоплавкое", nil, {"Найдите среди драконьих костей к востоку от лагеря Соплозабилось 12 расколотых костей."}},
[12048] = {"Доспехи воинов Плети", nil, {"Принесите 8 элементов доспехов воинов Плети Борусу Железные Клещи из Молота Агмара."}},
[12049] = {"Мясо с пылу с жару", nil, {"К востоку от Лагеря Соплозабилось сразитесь с огромным йормунгаром – до тех пор, пока тот не откроет рот. Затем бросьте ему в глотку мощную взрывчатку. После взрыва соберите 6 кусков обугленного мяса йормунгара с земли и принесите их Зинку."}},
[12050] = {"Лесорубы", nil, {"Используйте пульт управления Крошшером Зинка, чтобы вызвать Крошшер, когда доберетесь до гнездовья гарпий на севере. Соберите 50 связок бревен и возвращайтесь к Зинку в Лагерь Соплозабилось.\n\nЕсли потеряете пульт управления Крошшером Зинка, поговорите с Зинком в Лагере Соплозабилось, чтобы получить новый."}},
[12052] = {"Смерть гарпиям!", nil, {"Убейте госпожу Морозной выси и 15 гарпий Морозной выси в горах к северо-западу от лагеря Соплозабилось. Когда задание будет выполнено, возвращайтесь к Нарфу.\n\nЕсли вам понадобится пульт управления крошшером Зинка, обратитесь к Зинку в Лагерь Соплозабилось."}},
[12053] = {"Мощь Орды", nil, {"Установите боевой штандарт клана Песни Войны в деревне Ледяной Пыли и защитите его от нападающих.\n\nЗатем верните боевой штандарт клана Песни Войны главному сержанту Юктоку в Молот Агмара на Драконьем Погосте."}},
[12054] = {"Расшифровать записи", nil, {"Принесите пучок дыхания духа ведунье Палуне в Лагерь Уанква."}},
[12055] = {"Странное приспособление", nil, {"Отнесите странное устройство Горамоша проекции верховного мага Модеры в Покой Звезд."}},
[12056] = {"Приговорен к смерти: верховный сектант Зангус", nil, {"Убейте верховного сектанта Зангуса и отнесите его голову капитану Горту в Молот Агмара на Драконьем Погосте."}},
[12057] = {"Сшитый плотью фолиант", nil, {"Доставьте сшитый плотью фолиант капитану Горту в Молот Агмара."}},
[12058] = {"Рунические пророчества", nil, {"Ведунья Палуна из Лагеря Уанква просит вас перевести надписи на трех Рунических дисках."}},
[12059] = {"Странное приспособление", nil, {"Отнесите странное устройство Горамоша проекции верховного мага Этаса Похитителю Солнца в Молот Агмара."}},
[12060] = {"Проекты и проекции", nil, {"Подберитесь поближе и обследуйте конструкцию на вершине волнолова, плавающего в воздухе над Садами Лунного Покоя.\n\nПосле этого возвращайтесь с полученной информацией к проекции верховного мага Модеры в Покой Звезд."}},
[12061] = {"Проекты и проекции", nil, {"Подберитесь поближе и обследуйте конструкцию на вершине волнолова, плавающего в воздухе над Садами Лунного Покоя.\n\nПосле этого возвращайтесь с полученной информацией к проекции верховного мага Этаса Похитителя Солнца в Молот Агмара"}},
[12062] = {"Оскорбить Корена Худовара", nil, {"Поговорите с Кореном Худоваром."}},
[12063] = {"Сила Ледяной Пыли", nil, {"Найдите Бантока Ледяную Пыль в окрестностях деревни Ледяной Пыли."}},
[12064] = {"Ануб'арские оковы", nil, {"Принесите Бантоку Ледяной Пыли, ожидающему у деревни Ледяной Пыли на Драконьем Погосте, фрагмент ключа Анок'ры, фрагмент ключа Тивакса и фрагмент ключа Синока."}},
[12065] = {"Фокусы на взморье", nil, {"Добудьте кольцо управления средоточием силовых линий и воспользуйтесь им для получения информации о средоточии силовых линий на Сверкающем взморье.\n\nЗатем вернитесь к проекции верховного мага Модеры в Покое Звезд."}},
[12066] = {"Фокусы на взморье", nil, {"Добудьте кольцо управления средоточием силовых линий и воспользуйтесь им для получения информации о средоточии силовых линий на Сверкающем взморье.\n\nЗатем вернитесь к проекции верховного мага Этаса Похитителя Солнца в Молот Агмара."}},
[12067] = {"Письмо домой", nil, {"Доставьте письмо капитана Малин командиру Сайе Лазурной Стали в Покой Звезд."}},
[12068] = {"Голоса прошлого", nil, {"Дракуру просит вас собрать скрижали Драккари в Руинах Дракил'джин и отобрать 5 священных настоев у оракулов и заступников Драккари. Затем вам надо будет выпить эликсир Дракуру возле жаровни в руинах, чтобы призвать его дух."}},
[12069] = {"Возвращение верховного вождя", nil, {"Освободите верховного вождя Ледяную Пыль с помощью ключа от ануб'арской темницы и помогите ему одолеть короля подземелий Ануб'ет'кана.\n\nДобудьте кусок панциря Ануб'ет'кана и вернитесь с ним к командиру Агмару в Молоте Агмара в Драконьем Погосте."}},
[12070] = {"Второе дыхание", nil, {"Воспользуйтесь осколком земли, чтобы излечить великанов с Седых холмов, сражающихся в Каменистых холмах, убейте всех железных дворфов, что осмелятся встать у вас на пути, и вернитесь к Куруну на холм, возвышающийся над Тор Моданом."}},
[12071] = {"Ударим с воздуха!", nil, {"Поговорите с Вальноком Ветрояром в Молоте Агмара."}},
[12072] = {"Будь прокляты эти чумные звери!", nil, {"С помощью сигнального ружья Вальнока вызовите в деревню Ледяной Пыли кор'кронскую боевую виверну. Воспользовавшись ею, убейте 25 ануб'арских гнилостных зверей!\n\nЕсли выполните задание, возвращайтесь к Вальноку Ветрояру в Молот Агмара."}},
[12073] = {"Куй железо, пока горячо", nil, {"Убейте 10 любых железных дворфов, охраняющих Тор Модан, после чего доложите разведчику Вор'такху из Лагеря Уанква об успешном выполнении его поручения."}},
[12074] = {"Удобный союзник", nil, {"Поговорите с Куруном, который стоит на вершине холма рядом с Тор Моданом."}},
[12075] = {"Образец каменной плоти", nil, {"Возьмите образец каменной плоти с тела ледяного великана у входа в пещеру в ущелье Порочных Кристаллов, к северу от места, где стоит Зорт. Выполнив это задание, вернитесь к Зорту, ожидающему вас у центра каньона."}},
[12076] = {"Грязное дело", nil, {"Соберите два фиала едкой слюны йормунгаров с помощью скребка. Выполнив задачу, принесите сосуды Зорту в ущелье Порочных Кристаллов."}},
[12077] = {"Употреблять дважды в день", nil, {"Предложите приготовленный Зортом эликсир Ко'чару Неуязвимому."}},
[12078] = {"Червячный наездник", nil, {"Отправляйтесь в пещеры в ущелье Порочных кристаллов и заманите 3 порождения йормунгаров в крепкие ящики. Не забудьте собрать ящики, когда порождения йормунгаров окажутся в них!\n\nПринесите пойманные порождения йормунгаров Зорту в ущелье Порочных кристаллов."}},
[12079] = {"Излюбленное место червяков", nil, {"Отправляйтесь в пещеру у северной оконечности ущелья Порочных кристаллов и убейте по поручению Ко'чара Неуязвимого 8 йормунгаров-трутней Ледяного Сердца."}},
[12080] = {"Вот это червяк!", nil, {"Войдите в пещеру Ледяного Сердца и убейте Грохотунью. Не забудьте использовать защитный эликсир Зорта, чтобы спастись от едких плевков йормунгаров – если, конечно, этот эликсир у вас еще есть.\n\nВыполнив задачу, вернитесь к Ко'чару Неуязвимому в ущелье Порочных кристаллов."}},
[12081] = {"Гаврок", nil, {"Поговорите с Гавроком в Руинах Тетиса."}},
[12082] = {"Дун-да-Дун-та!", nil, {"Сопроводите Харрисона по пути из руин Дракил'джин, после чего поговорите с Харкором в лагере Харкора."}},
[12083] = {"В лесах", nil, {"Добудьте амулет управления средоточием силовых линий и используйте его для получения сведений о средоточии силовых линий в Лоталорском редколесье.\n\nПосле этого вернитесь к проекции верховного мага Модеры в Покое Звезд."}},
[12084] = {"В лесах", nil, {"Добудьте амулет управления средоточием силовых линий и воспользуйтесь им для получения сведений от средоточия силовых линий в Лоталорском редколесье.\n\nЗатем вернитесь к проекции верховного мага Этаса Похитителя Солнца в Молот Агмара."}},
[12085] = {"Письмо домой", nil, {"Отнесите письмо лейтенанта Та'зинни командиру Агмару в Молот Агмара."}},
[12086] = {"Сын Каркута", nil, {"Идите на восток к Стоянке Смерти, находящейся сразу за Уну'пе."}},
[12088] = {"Тассариан, рыцарь Смерти", nil, {"Отыщите Тассариана внутри Стонущего зиккурата, к северу от Стоянки Смерти."}},
[12089] = {"Разыскивается: магистр Кельдонус!", nil, {"Убейте магистра Кельдонуса и вернитесь к капитану Горту в Молот Агмара в Драконьем Погосте."}},
[12090] = {"Разыскивается: Гигантавр", nil, {"Убейте Гигантавра и приходите за вознаграждением к капитану Горту в Молоте Агмара на Драконьем Погосте."}},
[12091] = {"Разыскивается: Коготь Ужаса", nil, {"Убейте Когтя Ужаса и вернитесь за вознаграждением к капитану Горту в Молот Агмара на Драконьем Погосте."}},
[12092] = {"Усиление древняков", nil, {"Воспользуйтесь корой древня для усиления 3 древ Лоталора, а затем вернитесь к Сарендриане в Покой Звезд."}},
[12093] = {"Руны порабощения", nil, {"Гаврок с Руин Тетиса просит вас отключить четыре руны порабощения, убив надзирателя Дюрваля, надзирателя Коргана, надзирателя Лочли и надзирателя Брунона."}},
[12094] = {"Спящая сила", nil, {"Зарядите осколок Гаврока у первого, второго и третьего древних камней, после чего вернитесь к Гавроку в Руины Тетиса."}},
[12095] = {"В Драконью Погибель", nil, {"Поговорите с Рокханом в Драконьей Погибели в Драконьем Погосте."}},
[12096] = {"Усиление древняков", nil, {"Воспользуйтесь корой древня для усиления 3 древ Лоталора, а затем вернитесь к хранителю земли Грифу в Молот Агмара."}},
[12097] = {"Сарастра, бич Севера", nil, {"Поговорите с Рокханом, чтобы призвать ледяного змея Сарастру.\n\nУбейте змея и отнесите ледяное сердце Сарастры капитану Горту в Молот Агмара."}},
[12098] = {"Поиски в деревне Инду'ле", nil, {"Найдите командира охотников на магов в деревне Инду'ле либо на озере Инду'ле."}},
[12099] = {"Долгожданная свобода", nil, {"Воспользуйтесь Руноломом Гаврока, чтобы освободить четырех рунических великанов, после чего вернитесь к Гавроку в Руины Тетиса."}},
[12100] = {"Сдерживание гнили", nil, {"По заданию Соара Ярости Ястреба из Молота Агмара на Драконьем Погосте убейте 15 зараженных тварей и добудьте орган сопротивления гниению."}},
[12101] = {"Добрый доктор...", nil, {"Отнесите орган сопротивления гниению доктору Синтару Злобениусу в Молот Агмара."}},
[12102] = {"В поисках рубиновой сирени", nil, {"Принесите 1 цветок рубиновой сирени доктору Синтару Злобениусу в Молот Агмара на Драконьем Погосте."}},
[12104] = {"Вакцина для Соара", nil, {"Отнесите вакцину Синтара Соару Ярости Ястреба в Молот Агмара."}},
[12105] = {"Погружение во мрак", nil, {"Отнесите дневник Михаила лейтенанту Думонту в Приют Янтарной Сосны."}},
[12106] = {"Поиски в деревне Инду'ле", nil, {"Поищите командующего силами охотников на магов в деревне Инду'ле и озере Инду'ле."}},
[12107] = {"Конец цепочки", nil, {"Используйте талисман управления средоточием силовых линий, чтобы получить сведения о средоточии силовых линий под озером Инду'ле. Затем отправляйтесь на восток, к Лазуритовому святилищу драконов, расположенному почти в центре южного Драконьего Погоста, и с западного уступа понаблюдайте за тем, что там происходит.\n\nПосле этого вернитесь к проекции верховного мага Модеры в Покое Звезд."}},
[12109] = {"И снова к Гриану Камнегриву...", nil, {"Поговорите с капитаном Грианом Камнегривом в Лагере дружины Западного Края."}},
[12110] = {"Конец цепочки", nil, {"Используйте талисман управления средоточием силовых линий, чтобы получить сведения о средоточии силовых линий на дне озера Инду'ле. Затем отправляйтесь на восток, к Лазуритовому святилищу драконов, расположенному почти в центре южного Драконьего Погоста, и с западного уступа понаблюдайте за тем, что там происходит.\n\nПосле этого вернитесь к проекции верховного мага Этаса Похитителя Солнца в Молоте Агмара."}},
[12111] = {"Где бродят дикие звери", nil, {"Используйте суму с вакцинами, чтобы привить 5 снегопадных оленей и 5 полярных гризли по просьбе Соара Ярости Ястреба из Молота Агмара в Драконьем Погосте."}},
[12112] = {"Деловые отношения", nil, {"Найдите Зорта в центре ущелья порочных кристаллов, к северо-востоку от Лагеря Соплозабилось."}},
[12113] = {"Здравствуй, мяско!", nil, {"Харкор из Лагеря Харкора просит вас принести 10 кусков жилистого мяса ворга, которые можно добыть, поохотившись на хищников Ночного Воя, и 10 стейков из черпоклыка. Их можно вырезать из туш травоядных веслорогов."}},
[12114] = {"Антистрессовая терапия", nil, {"Харкор из Лагеря Харкора просит вас убить 10 заступников Драккари и 10 оракулов Драккари возле Руин Дракил'джин."}},
[12115] = {"Кольтира и язык смерти", nil, {"Отнесите сшитый плотью фолиант Кольтире Ткачу Смерти."}},
[12116] = {"Только смелым покоряются...", nil, {"Краз из Лагеря Харкора просит вас войти в Руины Дракил'джина и собрать 5 каноп Драккари."}},
[12117] = {"Путешествие в гавань Моа'ки", nil, {"Отправляйтесь в гавань Моа'ки на Драконий Погост и поговорите со старейшиной Ко'нани."}},
[12118] = {"Путешествие в гавань Моа'ки", nil, {"Отправляйтесь в гавань Моа'ки на Драконий Погост и поговорите со старейшиной Ко'нани."}},
[12119] = {"Добиться аудиенции", nil, {"Вручите свое приглашение Тариолстразу в храме Драконьего Покоя."}},
[12120] = {"Молот Драк'агуула", nil, {"Краз из Лагеря Харкора просит вас убить Драк'агуула возле Руин Дракил'джин и принести ему Дракил'джинский молот."}},
[12121] = {"Увидимся в другом мире", nil, {"Краз из Лагеря Харкора просит вас ударить Дракил'джинским молотом в гонги, окружающие Руины Дракил'джин. После этого вас, скорее всего, убьют, и тогда вы сможете войти в руины, чтобы поговорить с Ган'джо из мира мертвых."}},
[12122] = {"Добиться аудиенции", nil, {"Вручите свое приглашение в храм Драконьего Покоя Тариолстразу в храме Драконьего Покоя."}},
[12123] = {"Информация для королевы", nil, {"Вручите свое приглашение Алекстразе Хранительнице Жизни в храме Драконьего Покоя."}},
[12124] = {"Информация для королевы", nil, {"Вручите свое приглашение в храм Драконьего Покоя Алекстразе Хранительнице Жизни в храме Драконьего Покоя."}},
[12125] = {"Ради крови", nil, {"По просьбе Кольтиры Ткача Смерти воздействуйте кровавым самоцветом на спятившего селянина Инду'ле, а затем возвращайтесь к Кольтире в Молот Агмара на Драконьем Погосте с наполненным кровавым самоцветом."}},
[12126] = {"На службе нечестивости", nil, {"Используйте нечестивый самоцвет против герцога Валленхола и вернитесь с наполненным нечестивым самоцветом к Кольтире Ткачу Смерти в Молот Агмара на Драконий Погост."}},
[12127] = {"На службе у мороза", nil, {"Используйте ледяной самоцвет против загробников льда и вернитесь к Кольтире Ткачу Смерти в Молот Агмара на Драконий Погост с наполненным ледяным самоцветом."}},
[12128] = {"Как дела у Регара?", nil, {"Поговорите с Регаром Бровебоем в Лагере горняков."}},
[12129] = {"Безукоризненный замысел", nil, {"Принесите полный набор чертежей боевого голема Регару Бровебою в Лагерь горняков."}},
[12130] = {"Зачем ковать то, что можно отобрать?", nil, {"Принесите 8 частей боевого голема Регару Бровебою в Лагерь горняков."}},
[12131] = {"Нам нужна энергия", nil, {"Принесите энергобатареи Дурара и Каторна Регару Бровебою в Лагерь горняков."}},
[12132] = {"Сила разрушения", nil, {"По просьбе Кольтиры Ткача Смерти из Молота Агмара, что на Драконьем Погосте, войдите в мир теней и сразите 6 мрачных мучителей.\n\nЕсли вам нужно будет еще раз войти в мир теней, обратитесь к Кольтире."}},
[12133] = {"Разбей тыкву!", nil, {"Отдайте опаленный священный символ переодетой попечительнице сирот."}},
[12134] = {"Саша выходит на охоту", nil, {"Саша просит вас убить 12 охотников солнцестояния в Деревне Солнцестояния."}},
[12135] = {"\"И гори все огнем!\"", nil, {"Переодетая попечительница сирот просит вас помочь потушить пожар в деревне, если ее подожжет Всадник без головы. Когда пожар будет потушен, снова поговорите с попечительницей сирот."}},
[12136] = {"Переведенная книга", nil, {"Доставьте переведенный сшитый плотью фолиант капитану Горту в Молот Агмара."}},
[12137] = {"Остынь, чудо!", nil, {"Ган'джо просит вас собрать снег вечного сна из его сундука в руинах Дракил'джин.\n\nИм надо окропить древних духов Драккари и отнести крупицы духов Драккари Кразу в Лагерь Харкора."}},
[12138] = {"... а может быть, и нет", nil, {"Подзарядите вашего Боевого голема от десяти грозовых караульных и вернитесь к Регару Бровебою в Лагерь горняков."}},
[12139] = {"\"И гори все огнем!\"", nil, {"Попечительница сирот в маске просит вас помочь потушить пожар в деревне. Когда пожар потухнет, снова поговорите с попечительницей сирот в маске в городе."}},
[12140] = {"Славься, Роанук!", nil, {"Найдите Роанука Ледяную Пыль в Молоте Агмара, примите его в лоно Орды и посвятите его в вожди сил Орды.\n\nКогда ритуал будет завершен, вернитесь к командиру Агмару с клятвой крови Орды."}},
[12141] = {"Дипломатическое поручение", nil, {"Поговорите с Каруком на берегу Терзающего Бича, к юго-западу от Крепости Отваги."}},
[12142] = {"Контроль над чумой", nil, {"По заданию Дуана с Передовой 7-го легиона убейте 10 снобольдов Снежных равнин и 3 магнатавров Драконьего Погоста в лагерях магнатавров, окружающих Драконий Покой."}},
[12143] = {"Преследование в каньоне", nil, {"Дуан просит вас идти по следу снобольдов по каньону к северо-востоку от Передовой 7-го легиона и разобраться с их вожаком."}},
[12144] = {"Заразу – под контроль!", nil, {"Убейте 10 снобольдов Снежных равнин и 3 магнатавров Драконьего Погоста в лагерях магнатавров, расположенных к юго-востоку от Драконьего Покоя. Выполнив задачу, вернитесь к Контоканису в Драконьей Погибели в северо-западных Драконьих пустошах."}},
[12145] = {"Преследование в каньоне", nil, {"По поручению Контоканиса преследуйте снобольдов по каньону к югу от Драконьего Зова и разберитесь с их вожаком."}},
[12146] = {"Зловещие выводы", nil, {"Отнесите геральдический боевой рог к Драконьему Покою и найдите того, кто захотел бы оценить исходящую от него угрозу."}},
[12147] = {"Зловещие выводы", nil, {"Отнесите геральдический боевой рог к Драконьему Покою и найдите того, кто заинтересовался бы возможностью оценить потенциальную угрозу."}},
[12148] = {"Единственный в своем роде", nil, {"Найдите и убейте Расщепителя Костей в каньоне (или рядом с ним), находящемся к северо-западу от Лазуритового святилища драконов. Принесите найденное вами свидетельство Аурастразе в храм Драконьего Покоя."}},
[12149] = {"Могучие магнатавры", nil, {"Выследите и убейте Кровавого Пира, Ледоскала и Дракрова к востоку от Драконьего Покоя.\n\nКровавый Пир обитает в юго-восточном углу Драконьих Пустошей, прямо к северу от изумрудного святилища драконов. Ледоскал патрулирует северную оконечность Зеркала Рассвета. Дракров бродит у подножья утесов к западу от бронзового святилища драконов.\n\nКогда все три магнатавра будут убиты, возвращайтесь к Аурастразе в Драконий Покой."}},
[12150] = {"Мастер рун-затворник", nil, {"Отыщите Дрегмара Рунического Знака неподалеку от его пещеры, на тропе, ведущей в утесы над Зеркалом Рассвета. Бросьте ему вызов и любой ценой узнайте имя повелителя магнатавров. Добыв нужные сведения, вернитесь к Аурастразе в Драконьем Покое."}},
[12151] = {"Распоясавшийся полководец", nil, {"Отправляйтесь к кругу факелов к югу от Лазуритового святилища драконов и протрубите в геральдический боевой рог. Сразите Гром'тара Громовержца и принесите его голову Аурастразе в Драконий Покой."}},
[12152] = {"Погибель Джин'аррака", nil, {"Краз из Лагеря Харкора просит вас найти cвященное подношение Драккари в руинах Дракил'джина и осенить его пылью духов Драккари. После этого положите подношение к гонгам у руин Дракил'джина."}},
[12153] = {"Тан и его Наковальня", nil, {"Воспользовавшись боевым големом, убейте тана Гневного Топора, после чего вернитесь к Регару Бровебою в Лагерь горняков."}},
[12154] = {"Затмение", nil, {"Воспользуйтесь взрывчаткой Регара, чтобы уничтожить руническое силовое ядро в Дун Арголе. Доложите о своем успехе Регару Бровебою из Лагеря горняков."}},
[12155] = {"Разбей тыкву!", nil, {"Отдайте опаленный священный символ попечительнице сирот в маске."}},
[12157] = {"Пропавший курьер", nil, {"Поговорите с курьером Лансоном в Покое Звезд, что в Драконьем Погосте."}},
[12158] = {"Рудник Полого Камня", nil, {"Обыщите Рудник Полого Камня к северо-западу от Лагеря дружины Западного Края."}},
[12159] = {"Неупокоенные души", nil, {"Петров с Рудника Полого Камня просит вас поджечь с помощью шахтерского фонаря трупы 8 шахтеров с Рудника Полого Камня."}},
[12160] = {"Имя из прошлого", nil, {"Вернитесь к капитану Гриану Камнегриву в Лагерь дружины Западного Края."}},
[12161] = {"Рууна Слепая", nil, {"Поговорите с Рууной Слепой, которая живет в Лагере Рууны рядом с Перевалом Фордрассил."}},
[12164] = {"Час ворга", nil, {"Саша из Торговой лавки Белой Сосны посылает вас на Остров Кровавой Луны убить Селаас, Варлама, Вспоробрюха и Тень Аругала."}},
[12165] = {"Коварный план", nil, {"Принесите полный комплект чертежей боевого голема геологу Рокару в Лагерь Уанква."}},
[12166] = {"Жидкое пламя Элуны", nil, {"По просьбе стража-смотрителя Джоди Песни Луны из Покоя Звезд воспользуйтесь жидким пламенем Элуны для очищения трупов 6 гнилостных маралов и 6 бешеных гризли."}},
[12167] = {"Убить сектантов", nil, {"Соберите 5 рабочих костюмов сектантов по просьбе стража-смотрителя Джоди Песни Луны из Покоя Звезд."}},
[12168] = {"Памятный дар Зангуса", nil, {"Принесите памятный дар Зангуса стражу-смотрителю Джоди Песни Луны в Покой Звезд."}},
[12169] = {"Верховный сектант", nil, {"По заданию Стража-смотрителя Джоди Песни Луны из Покоя Звезд убейте верховного сектанта Зангуса."}},
[12170] = {"Схватка в Черноречье", nil, {"Капитан налетчиков Кронн просит вас убить 10 представителей или игроков Альянса на Лесопилке Черноречья."}},
[12171] = {"О предателях и предательстве", nil, {"Покажите депешу Альянса Палене Серебряное Облако в Покое Звезд."}},
[12172] = {"Настройка на Даларан", nil, {"По заданию проекции верховного мага Модеры в Покое Звезд настройтесь на силовые линии Нордскола."}},
[12173] = {"Настройка на Даларан", nil, {"По заданию проекции верховного мага Этаса Похитителя Солнца в Молоте Агмара настройтесь на силовые линии Нордскола."}},
[12174] = {"Главнокомандующий Халфорд Змеевержец", nil, {"Доставьте депешу Альянса главнокомандующему Халфорду Змеевержцу в крепость Стражей Зимы в восточной части Драконьего Погоста."}},
[12175] = {"Серые шкуры воргов", nil, {"Торговец шкурами Джун'ик из Крепости Завоевателей просит вас добыть 3 серые шкуры ворга с серо-дымчатых охотников, которые водятся в лесах на севере."}},
[12176] = {"Мелкая подмена", nil, {"Торговец шкурами Джун'ик просит вас принести ему 6 шкур медведей гризли."}},
[12177] = {"Уловка Джун'ика", nil, {"Принесите кусок угля и пять горстей простой муки торговцу шкурами Джун'ику в Крепость Завоевателей в Седых холмах."}},
[12178] = {"Сверток для Кренны", nil, {"Отнесите связку шкур Кренне в Крепость Завоевателей."}},
[12179] = {"Specialization 1 [PH]", nil, {"LOG: Complete this quest for Specialization 1."}},
[12180] = {"Пленные геологи", nil, {"Освободите геолога Ганна, геолога Торгана и геолога Варана, после чего вернитесь к горному пехотинцу Килиану в Лагерь горняков."}},
[12181] = {"Имя для заразы", nil, {"Покажите образец гнили Тобиасу Саркхоффу в Новом Агамонде."}},
[12182] = {"В Ядозлобь!", nil, {"Передайте образец гнили Главному распространителю чумы Миддлтону, который живет в Ядозлоби на востоке Драконьего Погоста."}},
[12183] = {"Часть маскировки", nil, {"Принесите униформу надзирателя горному пехотинцу Килиану в Лагерь горняков."}},
[12184] = {"Улыбайтесь, вас снимают!", nil, {"С помощью камеры Килиана сделайте снимки 8 железных дворфов в Дун Арголе, после чего вернитесь к горному пехотинцу Килиану в Лагерь горняков."}},
[12185] = {"Лучшая маскировка для Локена", nil, {"Замаскируйтесь под железного надзирателя рун, перехватите послание от Локена и передайте его горному пехотинцу Килиану в Лагерь горняков."}},
[12188] = {"Гниль Отрекшихся и вы: как не заразиться самим", nil, {"Принесите главному распространителю чумы Миддлтону из Ядозлоби в Драконьем Погосте 10 образцов эктоплазменного осадка."}},
[12189] = {"Меня окружают идиоты!", nil, {"Отправляйтесь в Ядозлобь в Драконьем Погосте и поговорите с главным распространителем чумы Миддлтоном."}},
[12190] = {"Поздоровайся с моим маленьким другом", nil, {"Пригмон просит вас отнести ингредиенты для Харкора в Лагерь Харкора."}},
[12191] = {"Пей до дна!", nil, {"Попадите кружкой с бесплатным пивом в С.С.М. 5 раз, затем поговорите с Биззлем Эхпрокачуном."}},
[12192] = {"Однажды, напившись в...", nil, {"Поговорите с Биззлем Эхпрокачуном в лагере Хмельного фестиваля."}},
[12195] = {"Незваный гость", nil, {"Принесите 5 рогов взрослых оленей Тормаку Бывалому из Лагеря Уанква."}},
[12196] = {"С самого начала", nil, {"Принесите 8 частей боевого голема геологу Рокару в Лагерь Уанква."}},
[12197] = {"Нам нужна энергия", nil, {"Принесите энергобатареи Дурара и Каторна Геологу Рокару в Лагерь Уанква."}},
[12198] = {"... а может быть, и нет", nil, {"Подзарядите вашего Боевого голема от десяти грозовых караульных и вернитесь к геологу Рокару в Лагерь Уанква."}},
[12199] = {"Свержение железного тана", nil, {"Воспользовавшись особой функцией вашего Боевого голема, убейте железного тана Гневного Топора, после чего вернитесь к Тормаку Бывалому в Лагерь Уанква."}},
[12200] = {"Слезы изумрудного дракона", nil, {"Принесите 8 слез изумрудного дракона главному распространителю чумы Миддлтону в Ядозлобь в Драконьем Погосте."}},
[12201] = {"Тень надзирателя", nil, {"Принесите униформу надзирателя Разведчику Вор'такху в Лагерь Уанква."}},
[12202] = {"Улыбайтесь, вас снимают!", nil, {"С помощью камеры Рокара сделайте снимки 8 железных дворфов в Дун Арголе, после чего вернитесь к Разведчик Вор'такху в Лагерь Уанква."}},
[12203] = {"Приказы Локена", nil, {"Замаскируйтесь под железного надзирателя рун, перехватите послание от Локена и передайте его разведчику Вор'такху в Лагерь Уанква."}},
[12204] = {"Во имя Локена", nil, {"Поговорите с Хью Глассом и Гавроком о Локене, после чего передайте полученные сведения разведчику Вор'такху из Лагеря Уанква."}},
[12205] = {"Разыскивается: Алый Натиск", nil, {"Убейте 20 членов Алого Натиска и доложите об этом верховному палачу Росу из Ядозлоби."}},
[12206] = {"Испорченные последние ритуалы", nil, {"По просьбе аптекаря Вики Левин из Ядозлоби испробуйте настой порчи на узнике Алого Натиска."}},
[12207] = {"Падение Фордрассила", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас отправиться к Ветвям Фордрассила, Сердцу Фордрассила или Слезам Фордрассила и принести ей 6 образцов слизи энтропических слизнюков."}},
[12208] = {"Удачи в охоте за троллями!", nil, {"Сержант Туркин отправил вас к Самиру в Гранитные ключи."}},
[12209] = {"Кража материальных ценностей", nil, {"По заданию интенданта Бартлетта из Ядозлоби похитьте 8 доспехов Алого Натиска и 8 единиц оружия Алого Натиска."}},
[12210] = {"Сезон охоты на троллей!", nil, {"Лейтенант Думонт из Приюта Янтарной Сосны послал вас поговорить с Самиром в Гранитных ключах."}},
[12211] = {"Не дадим им подняться!", nil, {"По заданию аптекаря Вики Левин из Ядозлоби воспользуйтесь ее клеткой с крысами в Новом Дольном Очаге, чтобы ободрать дочиста 15 тел, принадлежащих Алому Натиску."}},
[12212] = {"Пополнение запасов", nil, {"Старший лесник Андорхол из Приюта Янтарной Сосны просит вас убить несколько высокорогих оленей и принести ему 8 кусков сочной оленины."}},
[12213] = {"Подземная тьма", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас отыскать Сердце Фордрассила, Слезы Фордрассила и Ветви Фордрассила, углубиться под землю и воспользоваться Геомантской сферой для измерения уровня энергии."}},
[12214] = {"Новые кони", nil, {"Украдите 3 боевых коней Алого Натиска и доставьте их Ханзелу Бауэру в Ядозлобь. Передайте Ханзелу поводья из рук в руки."}},
[12215] = {"Мы или они!", nil, {"Андерхол из Приюта Янтарной Сосны просит вас найти и убить 6 серо-дымчатых охотников в Седых холмах."}},
[12216] = {"Бери самое нежное!", nil, {"Андерхол из Приюта Янтарной Сосны просит вас добыть 8 боков медведей гризли, которые водятся в Седых холмах."}},
[12217] = {"Орлиный взгляд", nil, {"Андерхол из Приюта Янтарной Сосны просит вас убить 5 императорских орлов и доложить ему о выполнении задания."}},
[12218] = {"Добрые вести не ждут на месте", nil, {"Используйте чумную бомбу распространителя гнили Отрекшихся, чтобы уничтожить 30 голодающих покойников на краю Полей Падальщиков по приказу главного распространителя чумы Миддлтона из Ядозлоби в Драконьем Погосте."}},
[12219] = {"Обломки мирового древа", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас найти Ветви Фордрассила, Сердце Фордрассила или Слезы Фордрассила и принести ей 6 образцов слизи энтропических слизнюков."}},
[12220] = {"Темное влияние", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас отыскать Сердце Фордрассила, Слезы Фордрассила и Ветви Фордрассила, углубиться под землю и воспользоваться Геомантской сферой для измерения уровня энергии."}},
[12221] = {"Гниль Отрекшихся", nil, {"Доставьте образец гнили Отрекшихся доктору Синтару Злобениусу в Молот Агмара."}},
[12222] = {"Тайны прядильщиц пламени", nil, {"Принесите 3 огнепрочных талисмана плотнику Дрейку в Приют Янтарной Сосны."}},
[12223] = {"Ослабление врага", nil, {"Убейте 12 хускерлов из клана Укротителей драконов и вернитесь к плотнику Дрейку в Приют Янтарной Сосны."}},
[12224] = {"Авангард кор'крона", nil, {"Явитесь к Саурфангу Младшему в авангард кор'крона."}},
[12225] = {"М-м-м... Янтарные желуди!", nil, {"Поговорите с искусным резчиком по дереву Андерхолом в Приюте Янтарной Сосны."}},
[12226] = {"Ух, пронесло!", nil, {"Андерхол из Приюта Янтарной Сосны просит вас принести ему 5 стеблей чернокорня."}},
[12227] = {"За дело!", nil, {"Андерхол из Приюта Янтарной Сосны просит вас взять слабительную настойку Андерхола и воспользоваться ближайшим туалетом, чтобы извлечь частично переваренные янтарные желуди."}},
[12229] = {"Возможная связь", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас принести 8 образцов крови безумных фурболгов, обитающих в Святилище Кровавого Сердца и Святилище Красного Клыка."}},
[12230] = {"Кража у осадных мастеров", nil, {"По поручению Стража смерти Молдера из Ядозлоби раздобудьте 6 осадных бомб."}},
[12231] = {"Отпрыски медвежьего бога", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас разыскать Орсонна в Святилище Красного Клыка и Кодиан в Святилище Кровавого Сердца."}},
[12232] = {"Бомбардировка баллист", nil, {"По заданию Стража смерти Молдера из Ядозлоби уничтожьте 5 баллист Нового Дольного Очага с помощью осадных бомб."}},
[12234] = {"Надо знать", nil, {"Украдите текущие приказы Алого Натиска в аббатстве, в казармах и на побережье по просьбе главы шпионов Репайн из Ядозлоби."}},
[12235] = {"Наксрамас и падение крепости Стражей Зимы", nil, {"Поговорите с командиром грифонов Уриком на грифоньей площадке в крепости Стражей Зимы."}},
[12236] = {"Урсок, медвежий бог", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас найти Тура Яролапа возле Логова Урcока и победить Урсока с его помощью. Когда Урсок падет, осыпьте его тело очищенным пеплом Фордрассила."}},
[12237] = {"Полет защитника Стражей Зимы", nil, {"Спасите 5 беспомощных селян крепости Стражей Зимы и возвращайтесь к командиру грифонов Урику в крепость Стражей Зимы."}},
[12238] = {"Очищение Драк'Тарона", nil, {"Дракуру просит вас выпить эликсир возле его жаровни в Драк'Тароне. Для использования эликсира вам понадобится пять настоев терпения."}},
[12239] = {"Шпион в Новом Дольном Очаге", nil, {"Доставьте текущие приказы Алого Натиска агенту Скулли в Новый Дольный Очаг."}},
[12240] = {"Цель и средства", nil, {"Используйте термитов семейки Левин, чтобы выманить штейгера Калейки и убить его по просьбе аптекаря Вики Левин из Ядозлоби."}},
[12241] = {"Уничтожить саженец", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас взять лиственный факел и сжечь саженец Фордрассила. Принесите пепел Фордрассила Серому Рогу."}},
[12242] = {"Семена Фордрассила", nil, {"Заклинательница ветра Серый Рог из Крепости Завоевателей просит вас отправиться в Седую Пасть и принести ей 8 семян Фордрассила."}},
[12243] = {"Огонь над водами", nil, {"По заданию аптекаря Вики Левин из Ядозлоби воспользуйтесь Пылающей водой аптекаря, чтобы поджечь паруса \"Глупости Грешника\".\n\nПока матросы будут заняты тушением пожара, спуститесь вниз и раздобудьте у капитана лоцию капитана Шели."}},
[12244] = {"Починка крошшеров", nil, {"Синипус с Лесозаготовок Синего Неба просит вас привезти ему 3 сломанных крошшера."}},
[12245] = {"Никакой пощады пленникам", nil, {"По поручению главы шпионов Репайн убейте Стража смерти Шнайдера, главного писаря Киннедия, инженера Бурка и канцлера Амай.\n\nКогда выполните это задание, сообщите об этом верховному палачу Росу в Ядозлобь."}},
[12246] = {"Возможная связь", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас принести 8 образцов безумных фурболгов, обитающих в Святилище Кровавого Сердца и Святилище Красного Клыка."}},
[12247] = {"Дети Урсока", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас разыскать Орсонна в Святилище Красного Клыка и Кодиан в Святилище Кровавого Сердца."}},
[12248] = {"Саженец Фордрассила", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас взять зеленый факел и сжечь саженец Фордрассила. Принесите ей пепел Фордрассила."}},
[12249] = {"Урсок, медвежий бог", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас найти Тура Яролапа возле Логова Урcока и победить Урсока с его помощью. Когда Урсок падет, осыпьте его тело очищенным пеплом Фордрассила."}},
[12250] = {"Семена Фордрассила", nil, {"Иерофант Тайрин из Приюта Янтарной Сосны просит вас отправиться в Седую Пасть и принести ей 8 семян Фордрассила."}},
[12251] = {"Возвращение к верховному главнокомандующему", nil, {"Поговорите с верховным главнокомандующим Халфордом Змеевержцем в крепости Стражей Зимы на Драконьем Погосте."}},
[12252] = {"Истязание истязателя", nil, {"По поручению верховного палача Роса из Ядозлоби пять раз припечатайте клеймом истязателя Альфонса, чтобы вытянуть из него нужную информацию. Получив ее, убейте Альфонса."}},
[12253] = {"Спасение с городской площади", nil, {"Спасите 6 пойманных жителей крепости Стражей Зимы по просьбе главнокомандующего Халфорда Змеевержца из крепости Стражей Зимы в Драконьем Погосте."}},
[12254] = {"Без молитвы – никак", nil, {"По заданию агента Скулли из Нового Дольного Очага раздобудьте молитвенник епископа Стрита."}},
[12255] = {"Тан Волдруна", nil, {"Одержите победу над таном Торвальдом Эрикксоном с помощью Огнедышащего и доложите об успехе плотнику Дрейку из Приюта Янтарной Сосны."}},
[12256] = {"Тайны огневязов", nil, {"Принесите 3 огнепрочных талисмана сержанту Назгриму в Крепость Завоевателей."}},
[12257] = {"Демонстрация силы", nil, {"Убейте 12 хускерлов из клана Укротителей драконов и вернитесь к сержанту Назгриму в Крепость Завоевателей."}},
[12258] = {"Судьба погибших", nil, {"Убейте 15 мстительных упырей по приказу командира Линоры Порыв Ветра в крепости Стражей Зимы в Драконьем Погосте."}},
[12259] = {"Тан Волдруна", nil, {"Одержите победу над таном Торвальдом Эрикксоном с помощью Огнедышащего и доложите сержанту Назгриму из Крепости Завоевателей."}},
[12260] = {"Полное несходство", nil, {"По заданию агента Скулли из Нового Дольного Очага похитьте образ жреца-ворона Алого Натиска. После этого верните Волшебное зеркало банши агенту Скалли."}},
[12261] = {"Отрезать пути к отступлению", nil, {"Поместите тотем уничтожения в конце тропы, ведущей из Обсидианового святилища драконов, там, где дорогу начинает заносить снегом. Защищайте его до полной зарядки, затем вернитесь к Серинару."}},
[12262] = {"Без надежды на спасение", nil, {"Убейте 6 некролитов Пылающих Глубин и 10 тлеющих скелетов за пределами Обсидианового святилища драконов, после этого вернитесь к Серинару."}},
[12263] = {"С наилучшими намерениями", nil, {"Дождитесь, пока Серинар замаскирует вас под некролита Пылающих Глубин, потом проникните в глубь Пасти Нелтариона, чтобы узнать, что там делает Культ Проклятых. Вернитесь к Серинару и расскажите о том, что они замышляют."}},
[12264] = {"Разборка с Проклятыми", nil, {"Убейте 3 некромантов Пылающих Глубин, 6 тлеющих созданий и 6 тлеющих упырей в Пасти Нелтариона, а затем возвращайтесь к Серинару."}},
[12265] = {"Осквернение осквернителей", nil, {"Найдите и уничтожьте 8 некромантских рун, выявленных на дне Пасти Нелтариона. Выполнив это, возвращайтесь к Серинару."}},
[12266] = {"Рассказ о разрушении", nil, {"Отправляйтесь обратно в Драконий Покой и расскажите Нэлис, что задание Серинара выполнено."}},
[12267] = {"Пламя Нелтариона", nil, {"Используйте пламя Нелтариона, чтобы очистить область призыва в глубине Пасти Нелтариона. После того как привлечете внимание Ротина Гниющего, убейте его, затем вернитесь к Серинару у входа в пещеру."}},
[12268] = {"Запчасти", nil, {"Трубкочист с Лесозаготовок Синего Неба просит вас собрать 4 рифленых шестеренки, 3 зубчатых колеса и 2 тугих пружины."}},
[12269] = {"Только не с нашего рудника!", nil, {"По поручению верховного лорде Леорика фон Цельдига из крепости Стражей Зимы на Драконьем Погосте добудьте 10 удостоверений шахтера Стражей Зимы."}},
[12270] = {"Искрошить Альянс", nil, {"Грекк с Лесозаготовок Синего Неба просит вас добыть для него 3 крошшера."}},
[12271] = {"Жезл принуждения", nil, {"Отнесите жезл истязателя верховному палачу Росу в Ядозлобь."}},
[12272] = {"Кровоточащая руда", nil, {"По поручению осадного инженера Полувспыха из крепости Стражей Зимы на Драконьем Погосте добудьте 10 образцов странной руды на руднике Стражей Зимы."}},
[12273] = {"Отречение", nil, {"По заданию верховного палача Роса из Ядозлоби воздействуйте жезлом принуждения на командира Джордана, главного канонира Зирхата, кузнеца Гудмана и смотрителя стойл Мерсера.\n\nКогда они подпадут под влияние жезла, убейте каждого из них, вырвав у них перед смертью признание об отречении от Алого Натиска."}},
[12274] = {"Греховное падение", nil, {"Позвоните в колокол аббатства, затем поговорите с верховным настоятелем по просьбе агента Скулли из Нового Дольного Очага."}},
[12275] = {"Демогном", nil, {"Поговорите с осадным инженером Полувспыхом в крепости Стражей Зимы в Драконьем Погосте."}},
[12276] = {"В поисках Слинкина", nil, {"Отыщите Слинкина Демогнома в руднике Стражей Зимы. Используйте самонаводящегося робота Полувспыха, если вам нужна будет помощь в поисках рудника."}},
[12277] = {"Ничего не оставлять на волю случая", nil, {"Добудьте бомбу Стражей Зимы и используйте ее, чтобы взорвать верхний и нижний стволы рудника Стражей Зимы. Выполнив задачу, вернитесь к осадному инженеру Полувспыху в крепость Стражей Зимы в Драконьем Погосте."}},
[12278] = {"Клуб \"Пиво месяца\"", nil, {"Отнесите членскую карточку \"Лучшего пивовара месяца\" Ларкину Громовару в таверну \"Горючий камень\" в Стальгорне."}},
[12279] = {"Медвежий аппетит", nil, {"Принесите 6 северных лососей Хью Глассу в Торговую лавку Красного леса."}},
[12280] = {"Починки", nil, {"Лурц с Лесозаготовок Синего Неба просит вас собрать 4 рифленых шестеренки, 3 зубчатых колеса и две тугих пружины."}},
[12281] = {"Понимание военной машины Плети", nil, {"Доставьте амуницию Полувспыха главнокомандующему Халфорду Змеевержцу в крепость Стражей Зимы."}},
[12282] = {"Отпечатки былого", nil, {"Принесите Зелигу Иллюзорному из крепости Стражей Зимы в Драконьем Погосте его гадальный шар."}},
[12283] = {"Правду не скроешь", nil, {"Добудьте дневник верховного генерала Аббендис по просьбе агента Скулли и передайте этот дневник верховному палачу Росу в Ядозлоби."}},
[12284] = {"Преследуй по пятам", nil, {"Командир Баргок приказал вам убить 15 представителей или игроков Альянса на Лесозаготовках Синего Неба."}},
[12285] = {"Без головы не возвращайся!", nil, {"По заданию верховного палача Роса из Ядозлоби убейте верховного генерала Аббендис и принесите ему ее голову."}},
[12286] = {"Кулек конфет", nil, nil},
[12287] = {"Орик Чистосердечный и Забытое взморье", nil, {"Ищите помощи у Орика Чистосердечного в Рассветном краю на Драконьем Погосте."}},
[12288] = {"В военном лазарете", nil, {"Аумана с Лесозаготовок Синего Неба просит вас перевязать медицинским жгутом 10 израненных застрельщиков."}},
[12289] = {"Нападем, пока враг слаб!", nil, {"Сержант Хартсман поручил вам убить 15 игроков или представителей Орды на Лесозаготовках Синего Неба."}},
[12290] = {"Эликсир из темноплевела", nil, {"Добудьте на Зловещей возвышенности 5 экземпляров темноплевела по просьбе Орика Чистосердечного из Рассветного края в Драконьем Погосте."}},
[12291] = {"Забытая повесть", nil, {"Выслушайте повесть о бедствиях забытого крестьянина, забытого ружейника, забытого рыцаря и забытого пехотинца по просьбе Орика Чистосердечного из Рассветного края в Драконьем Погосте."}},
[12292] = {"Помощь местных жителей", nil, {"Лейтенант Думонт из Приюта Янтарной Сосны поручил вам добраться до Торговой лавки Вечнозеленого леса и забрать кедровый сундук."}},
[12293] = {"Дело сделано", nil, {"Отнесите кедровый сундук Ивану на Заставу Тополиной рощи."}},
[12294] = {"Заключение договора", nil, {"Вернитесь к лейтенанту Думонту в Приют Янтарной Сосны."}},
[12295] = {"Урок дипломатии", nil, {"Поговорите с военным атташе Дюкалем в Среброречье."}},
[12296] = {"На грани жизни и смерти", nil, {"Реанна с Лесозаготовок Синего Неба просит вас перевязать медицинскими бинтами 10 израненных пехотинцев отряда Западного Края."}},
[12297] = {"О предателях и предательстве", nil, {"Капитан Адамс попросил вас показать депешу Альянса Гриру Рудомолоту из Крепости Западной Стражи."}},
[12298] = {"Главнокомандующий Халфорд Змеевержец", nil, {"Отвезите депешу Альянса главнокомандующему Халфорду Змеевержцу в Крепость Стражей Зимы, которая находится на востоке Драконьего Погоста."}},
[12299] = {"Северное гостеприимство", nil, {"Сергей из Среброречья просит вас убить 8 мародеров из Крепости Завоевателей."}},
[12300] = {"Испытание смелости", nil, {"Найдите часового Костехвата к северо-западу от Среброречья и пройдите испытание смелости."}},
[12301] = {"Правда вернет нам свободу", nil, {"По просьбе Орика Чистосердечного из Рассветного края, что на Драконьем Погосте, задействуйте хрустальную сферу Орика в развалинах, расположенных на Забытом взморье, для освобождения обитающих там Забытых."}},
[12302] = {"Предупреждение", nil, {"Дойдите до северо-западного края Среброречья."}},
[12303] = {"Финансовое обеспечение военных нужд", nil, {"По заданию интенданта Бартлетта из Ядозлоби добудьте 6 сундучков с забытыми сокровищами."}},
[12304] = {"Недвижимость на взморье", nil, {"По поручению наблюдателя Хансена, находящегося к северу от Забытого взморья убейте 20 призраков Забытого взморья."}},
[12305] = {"Прощальные мысли", nil, {"Вернитесь к Зелигу Иллюзорному в крепость Стражей Зимы в Драконьем Погосте."}},
[12306] = {"Клуб \"Пиво месяца\"", nil, {"Отнесите заполненную членскую карточку клуба \"Пиво месяца\" Рай'ме в Оргриммар."}},
[12307] = {"Аконитовый корень", nil, {"Катя из Среброречья просит вас выдернуть восемь аконитовых кореньев."}},
[12308] = {"Побег из Среброречья", nil, {"Откройте деревянную клетку и помогите освобожденному разведчику Альянса сбежать из Среброречья. Когда вы доберетесь до Приюта Янтарной Сосны, доложите лейтенанту Думонту обо всем, что вы видели."}},
[12309] = {"Найти Дюркона!", nil, {"Отыщите кавалериста Дюркона в склепе Стражей Зимы в Драконьем Погосте."}},
[12310] = {"Мгновенный ответ", nil, {"Лейтенант Думонт отправил вас к Заставе на опушке убить Владека и 7 охотников Среброречья."}},
[12311] = {"Подземная обитель некролорда", nil, {"По заданию кавалериста Дюркона из крепости Стражей Зимы убейте некролорда Амариона."}},
[12312] = {"Тайны Плети", nil, {"Отнесите сшитый плотью фолиант кавалеристу Дюркону, находящемуся в крепости Стражей Зимы на Драконьем Погосте."}},
[12313] = {"Unused Save Brewfest!", nil, {"Поговорите со шпионом Хмельного фестиваля в \"Угрюмом обжоре\", что в Глубинах Черной горы."}},
[12314] = {"Покончить с капитаном Зорной!", nil, {"Убейте капитана Зорну и вернитесь к барону Фриману в Бухту торговцев."}},
[12315] = {"Покончить с капитаном Брайтвотером!", nil, {"Убейте капитана Брайтвотера и доложите о своей победе генералу Хазгару из Бухты торговцев."}},
[12316] = {"Припрем их к стенке!", nil, {"Убейте 10 представителей или игроков Орды в Бухте торговцев, после чего вернитесь к лейтенанту Стюарту."}},
[12317] = {"Припрем их к стенке", nil, {"Убейте 10 представителей или игроков Альянса в Бухте торговцев, после чего вернитесь к центуриону Каггруму."}},
[12318] = {"Спасем Хмельной фестиваль!", nil, {"Поговорите со шпионом Хмельного фестиваля в трактире \"Угрюмый обжора\" в Глубинах Черной горы."}},
[12319] = {"Тайна книги", nil, {"Отнесите сшитый плотью фолиант главнокомандующему Халфорду Змеевержцу в крепость Стражей Зимы в Драконьем Погосте."}},
[12320] = {"Язык Смерти", nil, {"Отнесите сшитый плотью фолиант инквизитору Халларду в тюрьме крепости Стражей Зимы."}},
[12321] = {"Праведная проповедь", nil, {"Дождитесь, пока инквизитор Халлард закончит свою праведную проповедь, и возвращайтесь к главнокомандующему Халфорду Змеевержцу в крепость Стражей Зимы на Драконьем Погосте с добытой информацией."}},
[12323] = {"Выкурить трусов", nil, {"С помощью дымовой шашки выкурите 20 отставших работников Торговой Компании из зданий. Доложите об успешном выполнении поручения сержанту Дауни."}},
[12324] = {"Выкурить трусов", nil, {"С помощью дымовой шашки выкурите 20 отставших работников Торговой Компании из зданий. Доложите об успешном выполнении поручения Каменному стражу Тотему Ярости."}},
[12325] = {"Заброс на вражескую территорию", nil, {"Поговорите с командиром грифонов Уриком, чтобы организовать полет на заставу Торсона на Драконьем Погосте. Когда прибудете на заставу, доложитесь герцогу Августу Крушителю Врагов."}},
[12326] = {"Паровой сюрприз", nil, {"Воспользовавшись паровым танком Альянса, уничтожьте 6 чумных телег и доставьте гвардейцев 7-го легиона к усыпальнице Стражей Зимы. Если успешно выполните задание, поговорите с Амбо Кэшем внутри усыпальницы Стражей Зимы на Драконьем Погосте."}},
[12327] = {"Внетелесные ощущения", nil, {"Выпейте зелье из легчайшей пыльцы возле хрустального шара Рууны и узрите видение из прошлого."}},
[12328] = {"Просьба Рууны", nil, {"Рууна Слепая просит вас принести ей четыре горсти легчайшей пыльцы."}},
[12329] = {"Судьба и совпадения", nil, {"Поговорите с Сашей в Торговой лавке Белой Сосны."}},
[12330] = {"Слабое место Анатолия", nil, {"Саша из Торговой лавки Белой Сосны просит вас выстрелить усыпляющим дротиком в Татьяну, жительницу Деревни Солнцестояния. Принесите ее в Торговую лавку Белой Сосны."}},
