[12411] = {"Спасение сестры", nil, {"Найдите Аню в Логове Ночного Воя рядом с Деревней Солнцестояния."}},
[12412] = {"Друг моего врага", nil, {"Завоевательница Кренна из Крепости Завоевателей приказала вам убить 8 охотников Среброречья и их предводителя Владека с Заставы на опушке."}},
[12413] = {"Нападение на Среброречье", nil, {"Завоевательница Кренна из Крепости Завоевателей просит вас yбить 8 защитников Среброречья в Холмах Среброречья."}},
[12414] = {"Лошадки для лагеря", nil, {"Приведите 5 мустангов нагорья к оруженосцу Перси в Лагерь дружины Западного Края."}},
[12415] = {"Пугалка для коней", nil, {"Обратите в бегство 15 мустангов нагорья и вернитесь к Соулоку Штормовой Ярости в Лагерь Уанква."}},
[12416] = {"Горячая битва", nil, {"Помогите капитану Искандару удержать каньоны, приняв участие в смерти 12 хладных атакующих вурдалаков, 8 хладных атакующих упырей и 1 хладного атакующего поганища.\n\nКогда это будет выполнено, возвращайтесь к Церистразу."}},
[12417] = {"Погребение", nil, {"Отправляйтесь в Рубиновое святилище драконов через любой из южных или северных проходов и поищите рубиновые желуди. С помощью рубиновых желудей предайте тела павших красных драконов земле. По выполнении задания вернитесь к Церистразу."}},
[12418] = {"Через огненные поля", nil, {"Отправляйтесь в Рубиновое святилище драконов через любой из северных или южных проходов, затем убейте 6 хладных некромантов и уничтожьте источник осквернения, находящийся под святилищем. По выполнении задания вернитесь к Церистразу."}},
[12419] = {"Судьба Рубинового святилища драконов", nil, {"Принесите рубиновую брошь Далии Благословения Солнца Красу в чертог правящего Совета в Драконий Покой и расскажите ему о судьбе Далии."}},
[12420] = {"Клуб \"Пиво месяца\"", nil, {"Отнесите членскую карточку \"Лучшего пивовара месяца\" Ларкину Громовару в таверну \"Горючий камень\" в Стальгорне."}},
[12422] = {"Расчетливая доброта", nil, {"Отнесите приказ о помиловании пленному зверолову в Холмы Среброречья."}},
[12423] = {"Дневник Михаила", nil, {"Отнесите дневник Михаила Завоевательнице Кренне в Крепость Завоевателей."}},
[12424] = {"Горгонна", nil, {"Отнесите дневник Михаила Горгонне в Крепость Завоевателей."}},
[12425] = {"Рууна Слепая", nil, {"Поговорите с Рууной Слепой из Лагеря Рууны, что находится рядом с Перевалом Фордрассил."}},
[12427] = {"Бойцовская яма: медвежьи бои!", nil, {"Одержите победу над Железной Шкурой в Бойцовской яме и вернитесь к букмекеру Вел'джену за наградой."}},
[12428] = {"Бойцовская яма: битва с бешеным фурболгом", nil, {"Одержите победу над Торггом Громовым Тотемом в Бойцовской яме, после чего поговорите с букмекером Вел'дженом."}},
[12429] = {"Бойцовская яма: кровь и железо", nil, {"Одержите победу над Кроворжавом в Бойцовской яме и вернитесь к букмекеру Вел'джену за наградой."}},
[12430] = {"Бойцовская яма: смерть близка", nil, {"Одержите победу над Хоргренном Страшным Расколом в Бойцовской яме и вернитесь к букмекеру Вел'джену за наградой."}},
[12431] = {"Бойцовская яма: последняя битва", nil, {"Одержите победу над Кренной в Бойцовской яме."}},
[12432] = {"Верхом на красной ракете", nil, {"Генерал Горлок с маяка в Бухте торговцев приказал вам уничтожить грузовой корабль Альянса. \n\nГенерала Горлока можно застать только тогда, когда маяк находится под контролем Орды."}},
[12433] = {"В поисках растворителя", nil, {"Гуртор из Торговой бухты Орды просит вас принести ему канистру с элементом 115."}},
[12434] = {"Растворитель всегда пригодится!", nil, {"Требования:"}},
[12435] = {"Явиться к лорду Деврестразу", nil, {"Поговорите с лордом Деврестразом в храме Драконьего Покоя."}},
[12436] = {"Прибавка к зарплате", nil, {"Поставщик Лоркран из Крепости Завоевателей просит вас убить несколько высокорогих оленей и принести ему 5 кусков сочной оленины."}},
[12437] = {"Верхом на красной ракете", nil, {"Командир Хоузер с маяка Бухты торговцев поручил вам уничтожить грузовой корабль Орды.\n\nКомандира Хоузера можно застать только тогда, когда маяк находится под контролем Альянса."}},
[12438] = {"Разыскивается: Креуг Клятвопреступник", nil, {"Убейте Креуга Клятвопреступника и возвращайтесь за наградой к верховному лорду Леорику фон Цельдигу в крепость Стражей Зимы на Драконьем Погосте."}},
[12439] = {"Проблемы на западе", nil, {"Поговорите с Родни Веллсом в крепости Стражей Зимы, чтобы отправиться в Покой Звезд в западном Драконьем Погосте."}},
[12440] = {"В Покой Звезд!", nil, {"Поговорите с проекцией верховного мага Модеры в Покое Звезд в западной части Драконьего Погоста."}},
[12441] = {"Разыскивается: верховный шаман Кровохват", nil, {"Убейте верховного шамана Кровохвата и приходите за наградой к верховному лорду Леорику фон Цельдигу в крепость Стражей Зимы на Драконьем Погосте."}},
[12442] = {"Разыскивается: командир Алого Натиска Иустус", nil, {"Убейте командира Алого Натиска Иустуса и возвращайтесь к верховному лорду Леорику фон Цельдигу в крепости Стражей Зимы в Драконьем Погосте, чтобы получить награду."}},
[12443] = {"В поисках растворителя", nil, {"Барблфинк с Базы Альянса просит вас принести ему канистру с элементом 115."}},
[12444] = {"Резня в Черноречье", nil, {"Капитан разведчиков Картер просит вас убить 10 представителей или игроков Орды на Лесопилке Черноречья."}},
[12447] = {"Обсидиановое святилище драконов", nil, {"Отправляйтесь в Обсидиановое святилище драконов к северо-западу от Драконьего Покоя, совсем рядом с Вратами Гнева. Найдите в святилище Серинара и предложите ему свою помощь."}},
[12448] = {"Горячая битва", nil, {"Помогите капитану Дрейзену удержать каньоны, приняв участие в смерти 12 хладных атакующих вурдалаков, 8 хладных атакующих упырей и 1 хладного атакующего поганища.\n\nКогда это будет выполнено, возвращайтесь к Варгастразу."}},
[12449] = {"Погребение", nil, {"Отправляйтесь в Рубиновое святилище драконов через южный проход и поищите там рубиновые желуди. С помощью рубиновых желудей предайте тела павших красных драконов земле. По выполнении задания вернитесь к Варгастразу."}},
[12450] = {"Через огненные поля", nil, {"Отправляйтесь в Рубиновое святилище драконов через южный проход, затем убейте 6 хладных некромантов и уничтожьте источник осквернения, находящийся под святилищем. По выполнении задания вернитесь к Варгастразу."}},
[12451] = {"Вперед, в Лагерь Уанква", nil, {"Поговорите с разведчиком Вор'такхом в Лагере Уанква."}},
[12452] = {"zzOLD Судьба рубинового святилища драконов", nil, {"Принесите Церистразу рубиновую брошь Далии и расскажите ему о ее судьбе."}},
[12453] = {"Взгляд с высоты", nil, {"Заклинательница ветра Серый Рог просит вас коснуться серебряным пером шести императорских орлов в Седых холмах."}},
[12454] = {"Жизненный цикл", nil, {"По просьбе Нишеры Хранительницы Садов из изумрудного святилища драконов убейте 5 изумрудных небесных когтей."}},
[12455] = {"Унесенные ветром", nil, {"По поручению Амбо Кэша из усыпальницы Стражей Зимы соберите 8 ящиков снаряжения Стражей Зимы."}},
[12456] = {"Перо Алистоса", nil, {"Добудьте и принесите Нишере Хранительнице Садов в Изумрудное святилище драконов перо Алистоса."}},
[12457] = {"Ты и пушка", nil, {"Спасите 8 раненых солдат 7-го легиона по просьбе Амбо Кэша из усыпальницы Стражей Зимы в Драконьем Погосте."}},
[12458] = {"Семена плеточника", nil, {"Принесите 3 семени плеточника лорду Итару на вершине храма Драконьего Покоя."}},
[12459] = {"Что создает, то может и разрушать", nil, {"Уничтожьте ослабленного оживленного ледяного змея, ослабленного Турджида Зловещего и ослабленного наблюдателя Убийственного Взгляда по просьбе лорда Итара, который находится на вершине храма Драконьего Покоя."}},
[12460] = {"Прибытие в Рубиновое святилище драконов", nil, {"Явитесь к Церистразу на уступе к востоку от Рубинового святилища драконов, к западу от Драконьего Покоя."}},
[12461] = {"Прибытие в Рубиновое святилище драконов", nil, {"Явитесь к Варгастразу на хребте у Рубинового святилища драконов, к востоку от Молота Агмара."}},
[12462] = {"Кирпичик за кирпичиком", nil, {"По заданию Йорда \"Бедствия\" Снегоборода, находящегося у усыпальницы Стражей Зимы, убейте некролордов Кс'авиуса и Гора, а также 15 наксрамасских воинов Плети."}},
[12463] = {"Жаднобород должен быть найден!", nil, {"По просьбе Амбо Кэша у усыпальницы Стражей Зимы найдите Жадноборода."}},
[12464] = {"Мой старый враг", nil, {"Принесите голову верховного генерала командиру Элигору Вестник Рассвета из крепости Стражей Зимы."}},
[12465] = {"Дневник Жадноборода", nil, {"Найдите недостающие страницы дневника Жадноборода: страницу 4, страницу 5, страницу 6 и страницу 7. Отнесите их Амбо Кэшу, ожидающему рядом с усыпальницей Стражей Зимы на Драконьем Погосте."}},
[12466] = {"Погоня за Ледяной Бурей: Передовая 7-го легиона", nil, {"Явитесь к командиру легиона Тиралиону на Передовой 7-го легиона в центральном Драконьем Погосте."}},
[12467] = {"Охота на Ледяную Бурю: филактерия Тель'зана", nil, {"Добудьте с тела Ледяной Бури филактерию Тель'зана и отнесите ее главнокомандующему Халфорду Змеевержцу в крепость Стражей Зимы."}},
[12468] = {"Поручение Завоевательницы", nil, {"Поговорите с сержантом Назгримом в Крепости Завоевателей."}},
[12469] = {"Вернуть отправителю", nil, {"Ступайте на восток вдоль гряды и доставьте груз взрывчатки Зинку в Лагерь Соплозабилось в северном Драконьем Погосте."}},
[12470] = {"Тайна бесконечности", nil, {"Отправляйтесь в бронзовое святилище драконов. Когда проникнете в святилище, поставьте на землю песочные часы Вечности.\n\nЗащищайте часы, пока они идут, а потом возвращайтесь к Хроми на вершину храма Драконьего покоя и передайте ей полученную вами информацию."}},
[12471] = {"Жестокость квалдиров", nil, {"Расскажите Каруку о пленном узнике-клыкарре."}},
[12472] = {"Шаг к победе", nil, {"Отнесите филактерию Тель'зана командиру легиона Йорику в усыпальницу Стражей Зимы в Драконьем Погосте."}},
[12473] = {"Конец и начало", nil, {"Сразите Тель'зана Мраконосца и доложите об этом главнокомандующему Халфорду Змеебою из крепости Стражей Зимы на Драконьем Погосте."}},
[12474] = {"За крепость Фордрагона!", nil, {"Отправляйтесь в крепость Фордрагона в Драконьем Погосте и поговорите с верховным лордом Болваром Фордрагоном."}},
[12475] = {"Какие тайны они скрывают?", nil, {"Добудьте карту Натиска по просьбе Зелига Иллюзорного из крепости Стражей Зимы в Драконьем Погосте и вернитесь с ней к Зелигу."}},
[12476] = {"Возвращение Алого ордена?", nil, {"Убейте 20 воинов Алого Натиска в лагере Алого Натиска по приказу командира Линоры Порыв Ветра."}},
[12477] = {"Путь искупления", nil, {"Принесите копию \"Пути искупления\" главнокомандующему Халфорду Змеевержцу из крепости Стражей Зимы в Драконьем Погосте."}},
[12478] = {"Пещера Ледяной Скорби", nil, {"Используйте гадальный шар Зелига на дне пещеры Ледяной Скорби по просьбе Зелига Иллюзорного из крепости Стражей Зимы в Драконьем Погосте. Откройте тайны былого!"}},
[12479] = {"Onwards to Northrend!", nil, {"Speak to the Alliance Recruiter inside Valiance Keep in Borean Tundra or the one at Vengeance Landing in Howling Fjord."}},
[12480] = {"Onwards to Northrend!", nil, {"Speak to the Horde Recruiter inside Warsong Hold in Borean Tundra or Vengeance Landing in Howling Fjord."}},
[12481] = {"Оскорбительное поражение", nil, {"Зачитайте врайкульское оскорбление Бьорну Халгудссону из Гибльхейма, после чего приведите его прямо к Засаде Лиделла и помогите его убить."}},
[12482] = {"Атака на Ниффлвар", nil, {"Капитан следопытов Арейел просит вас отправиться в Ниффлвар и убить 5 воинов из клана Укротителей драконов, 4 руномантов из клана Укротителей драконов и 4 гончих-охотников из клана Укротителей драконов."}},
[12483] = {"Похлебка из мерцающих грибов", nil, {"Пригмон из Гранитных ключей просит вас собрать 5 мерцающих снежных грибов и 5 глаз ледяных змеев из руин Драк'Зина и 5 сладокорней из Зеб'Халака."}},
[12484] = {"Шашлык из Плети", nil, {"Пригмон из Гранитных ключей просит вас отнести мумифицированный труп тролля Маку Фирсену и бросить его в огонь."}},
[12486] = {"Быстро, на заставу Бор'горока!", nil, {"Поговорите с говорящей с духами Рычеклык на заставе Бор'горока."}},
[12487] = {"В крепости Завоевателей держи ухо востро!", nil, {"Явитесь к завоевательнице Кренне в крепости Завоевателей в Седых холмах."}},
[12488] = {"Запрос от верховного палача", nil, {"Поговорите с верховным палачом Росом в Ядозлоби, что в Драконьем Погосте."}},
[12489] = {"Добро пожаловать в Низину Шолазар", nil, {"Идите по тропе на запад, пока не дойдете до лагеря, о котором говорил Монти."}},
[12491] = {"Худое варево Худовара", nil, {"Отнесите худое варево Худовара Ипфелькоферу Железному Бочонку в лагерь Хмельного фестиваля возле Стальгорна."}},
[12492] = {"Худое варево Худовара", nil, {"Отнесите худое варево Худовара Слухарю Жмухли в лагерь Хмельного фестиваля возле Оргриммара."}},
[12493] = {"Тест PvP", nil, {"Убейте 10 противников."}},
[12495] = {"Аудиенция у королевы драконов", nil, {"Отыщите Алекстразу Хранительницу Жизни в храме Драконьего Покоя в Драконьем Погосте."}},
[12496] = {"Аудиенция у королевы драконов", nil, {"Отыщите Алекстразу Хранительницу Жизни в храме Драконьего Покоя в Драконьем Погосте."}},
[12497] = {"Галакронд и армия Плети", nil, {"Поговорите с Торастразой в храме Драконьего Покоя на Драконьем Погосте."}},
[12498] = {"На рубиновых крыльях", nil, {"Уничтожьте 30 мусорщиков Пустошей и добудьте косу Антиока. При выполнении задания возвращайтесь к Алекстразе Хранительнице Жизни в храм Драконьего Покоя."}},
[12499] = {"Возвращение в Ангратар", nil, {"Поговорите с верховным лордом Болваром Фордрагоном в крепости Фордрагона и расскажите ему о своей победе над силами Плети."}},
[12500] = {"Возвращение в Ангратар", nil, {"Поговорите с Саурфангом Младшим в отряде Кор'крона и расскажите ему о своей победе над силами Плети."}},
[12501] = {"Тролльский дозор", nil, {"Обойдите четыре заставы Серебряного Авангарда, выполните приказы капитанов и возвратитесь к командиру Кунцу на Серебряную заставу с докладом.\n\nЗаставы капитана Брендона и капитана Руперта расположены в Драк'Сотре, к юго-востоку, застава капитана Гронделя – в Драк'Агале, прямо на восток, а застава алхимика Финкльштейна – в Хеб'Валоке, к северу."}},
[12502] = {"Тролльский дозор: выше знамена!", nil, {"По поручению капитана Брендона расставьте знамена Серебряного Авангарда на пьедесталах Драккари в Драк'Сотре."}},
[12503] = {"Защита заставы", nil, {"По поручению командира Фальштава убейте 10 приспешников Плети во внутреннем дворе Серебряной заставы."}},
[12504] = {"Серебряный Авангард, уходим!", nil, {"Поговорите с 10 солдатами Серебряного Авангарда и скажите им, чтобы они немедленно возвращались к сержанту Метателю Молота к алтарю Шшератуса."}},
[12505] = {"Новый приказ для сержанта Метателя Молота", nil, {"По поручению командира Кунца доставьте приказ о передислокации сержанту Метателю Молота к алтарю Шшератуса."}},
[12506] = {"Волнения у алтаря Шшератуса", nil, {"По поручению мастера проклятий Убунго отправляйтесь на разведку в главный храм у алтаря Шшератуса."}},
[12507] = {"Странная настойка", nil, {"Отнесите странную настойку мастеру проклятий Убунго на Серебряную заставу."}},
[12508] = {"Зачистка территории", nil, {"По поручению капрала Маги у алтаря Шшератуса убейте 10 приверженцев Шшератуса – воителей Шшератуса, жрецов Шшератуса или факиров драккари."}},
[12509] = {"Тролльский дозор: сила духа", nil, {"По поручению капитана Руперта из Драк'Сотры поднимите боевой дух рекрутов Серебряного ордена."}},
[12510] = {"Совершенные элементальные флюиды", nil, {"Добудьте 3 образца совершенных элементальных флюидов для мастера проклятий Убунго с Серебряной заставы."}},
[12511] = {"У холмов есть не только глаза", nil, {"Отметьтесь у лейтенанта Думонта в Приюте Янтарной Сосны в западной части Седых холмов."}},
[12512] = {"Спасти всех", nil, {"Разыщите рыцаря Ламуфа, рыцаря Джонатана и рыцаря Жозефину. Перевяжите их раны бинтами и проводите к доктору Роджерсу в Драк'Сотру."}},
[12513] = {"Милая шляпка...", nil, {"Сам того не желая, Дон Карлос сподвиг вас сразиться с ним молодым в Старом Хилсбраде. Принесите ему шляпу Дона Карлоса в доказательство вашей победы. Он будет ждать вас в Танарисе."}},
[12514] = {"Грибная добавка", nil, {"Соберите 10 грибов-дурманов и принесите их мастеру проклятий Убунго на Серебряную заставу."}},
[12515] = {"Милая шляпка...", nil, {"Сам того не желая, Дон Карлос сподвиг вас сразиться с ним молодым в Старом Хилсбраде. Принесите ему шляпу Дона Карлоса в доказательство вашей победы. Он будет ждать вас в Танарисе."}},
[12516] = {"Хорошенького понемножку", nil, {"Убейте одурманенного пророка Шшератуса и возвращайтесь к мастеру проклятий Убунго на Серебряную заставу."}},
[12517] = {"Карты Новолуния: колода Разбойников", nil, {"Призовите предсказателя судьбы с Ярмарки Новолуния и отдайте ему колоду Разбойников."}},
[12518] = {"Карты Новолуния: колода Магов", nil, {"Призовите предсказателя судьбы с Ярмарки Новолуния и отдайте ему колоду Магов."}},
[12519] = {"Тролльский дозор: сбор медальонов", nil, {"Принесите капитану Гронделю к Колизею в Драк'Агале 7 медальонов Драккари, снятых с убитых троллей Драккари."}},
[12520] = {"Охота на люторогов: проверка", nil, {"Убейте 15 груборогих люторогов и вернитесь в лагерь Эрнестуэя к Хемингу Эрнестуэю."}},
[12521] = {"Куда запропастился Хеминг Эрнестуэй?", nil, {"Поговорите с верховным магом Пентарусом, когда соберетесь отправиться в Низину Шолазар. По прибытии поговорите с Монти Мушкетоном в диких мангровых зарослях Низины Шолазар."}},
[12522] = {"Нужен двигатель – найдем двигатель!", nil, {"Веслекс Вездеключ из лагеря Эрнестуэя просит вас принести ему мотор для ветролета."}},
[12523] = {"Есть детали? – Тащи сюда!", nil, {"Принесите 7 запчастей Торговой Компании Веслексу Вездеключу в лагерь Эрнестуэя."}},
[12524] = {"Проблемы Торговой Компании", nil, {"Дебаар из лагеря Эрнестуэя хочет, чтобы вы убили 15 членов Торговой Компании. Вы можете убить бузотеров или землечерпов Компании."}},
[12525] = {"Сотри ухмылку с его лица", nil, {"Убейте штейгера Хитрохмыла и его телохранителя Мясного Пирога, а затем вернитесь к Дебаар в лагерь Эрнестуэя."}},
[12526] = {"Охота на люторогов: погоня", nil, {"Поговорите с Оракулом Су-рамом у Скелета Нозронна."}},
[12527] = {"Прожорливые твари", nil, {"Принесите 5 кристаллов василиска алхимику Финкльштейну в святилище в Хеб'Валоке."}},
[12528] = {"Подыграть несложно", nil, {"Найдите верховного шамана Ракжака на холме Бешеного Сердца и скажите, что вы – его новый \"раб\"."}},
[12529] = {"Раб охотника на обезьян", nil, {"Убейте 8 покорных барабанчей и 6 лютых барабанчей по просьбе верховного шамана Ракжака, живущего на холме Бешеного Сердца.\n\nЕсли вы потеряете кандалы Горегека, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12530] = {"Вопли барабанчат", nil, {"Горегек дал вам прут для барабанчат: если потыкать их этим прутом, то на их крики из укрытия появится предводительница стаи барабанчей. Убейте предводительницу стаи и расскажите об этом верховному шаману Ракжаку на холме Бешеного Сердца.\n\nЕсли вы потеряете кандалы Горегека, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12531] = {"Подземная угроза", nil, {"Разыщите Серфекса под песками южной реки и принесите его коготь верховному шаману Ракжаку на холм Бешеного Сердца.\n\nЕсли вы потеряете кусок мела Дажика, поговорите со Старейшиной Харкеком на холме Бешеного Сердца."}},
[12532] = {"Побег из курятника", nil, {"Поймайте 12 сбежавших куриц для старейшины Харкека.\n\nЕсли вы потеряете кусок мела Дажика, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12533] = {"Ученик охотника на ос", nil, {"Убейте 6 ос Сапфирного Улья и 9 рабочих Сапфирного улья для шамана Ракжака, живущего на холме Бешеного Сердца.\n\nЕсли вы потеряете кусок мела Дажика, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12534] = {"Матка Сапфирового Улья", nil, {"Принесите жало матки Сапфирного Улья верховному шаману Ракжаку, обитающему на холме Бешеного Сердца.\n\nЕсли вы потеряете кусок мела Дажика, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12535] = {"Кристаллы силы", nil, {"Верховный шаман Ракжак с холма Бешеного Сердца хочет, чтобы вы принесли ему 8 кристаллов Небесного Пути."}},
[12536] = {"Трудная поездка", nil, {"Оседлайте пойманного кроколиска и отправляйтесь к убежищу Шепота Тумана; затем обратитесь к Зепику за дальнейшими указаниями.\n\nЕсли вы потеряете охотничий рог Зепика, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12537] = {"Молния все-таки бьет в то же место", nil, {"Зепик хочет, чтобы вы разрушили кристаллы Небесного Пути перед алтарем Шепота Тумана.\n\nЕсли вы потеряете охотничий рог Зепика, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12538] = {"Шептуны не станут слушать", nil, {"Зепик хочет, чтобы вы убили 12 горлоков Шепота Тумана.\n\nЕсли вы потеряете охотничий рог Зепика, поговорите со старейшиной Харкеком на холме Бешеного Сердца."}},
[12539] = {"Вместе весело шагать", nil, {"Найдите дорогу к холму Бешеного Сердца и поговорите с верховным шаманом Ракжаком."}},
[12540] = {"Исполнение приказа", nil, {"Найдите раненого оракула Гласа Дождя по просьбе верховного шамана Ракжака с холма Бешеного Сердца."}},
[12541] = {"Тролльский дозор: в подмастерья к алхимику", nil, {"Помогите алхимику Финкльштейну в Хеб'Валоке в приготовлении сыворотки правды.\n\nКогда вы будете готовы приступить к работе, скажите об этом алхимику и выполняйте все его указания."}},
[12542] = {"Зов Ордена", nil, {"Явитесь к рыцарю Валусу в Опоре Света в Драконьем Погосте."}},
[12543] = {"Подношение Су-раму", nil, {"Принесите оракулу Су-раму, живущему возле Скелета Нозронна, 5 целых зубов кобры."}},
[12544] = {"Скелет Нозронна", nil, {"Используйте фимиам Су-рама возле скелета Нозронна, а затем вернитесь к Хемингу Эрнестуэю в его лагерь и расскажите о том, что узнали."}},
[12545] = {"Очищение Джинта'калара", nil, {"По заданию рыцаря Валуса из Опоры Света на Драконьем Погосте убейте 15 джинта'каларских воинов Плети."}},
[12546] = {"Отмщение", nil, {"Отправляйтесь к Застывшей лавине в Низине Шолазар и используйте руну \"Омега\", чтобы привести в действие Этимидиана. Используйте его, чтобы уничтожить 200 прислужников Плети, Битиуса Ваятеля Плоти, Ургрета из Тысячи Могил и Градохлада.\n\nВыполнив задание, поговорите с Аватарой Фрейи в Низине Шолазар."}},
[12547] = {"Руна пробуждения", nil, {"Найдите руну \"Омега\" на Террасе Создателя и принесите ее к Этимидиану."}},
[12548] = {"Этимидиан", nil, {"Войдите в Связующую спираль на южной границе Низины Шолазар и разыщите оружие, известное как Этимидиан."}},
[12549] = {"Охота на терропардов: стать хищником", nil, {"Убейте 15 терропардов и возвращайтесь в лагерь Эрнестуэя к Баку Кантвеллу."}},
[12550] = {"Охота на терропардов: по следу зверя", nil, {"Бак Кантвелл из лагеря Эрнестуэя хочет, чтобы вы выявили 3 следа Шанго среди следов терропардов у озера Горьких Волн."}},
[12551] = {"Охота на кроколисков: испытание", nil, {"Убейте 15 мангровых кроколисков и вернитесь к Дебаар в лагерь Эрнестуэя."}},
[12552] = {"Смерть некромагам!", nil, {"По поручению сержанта Осколка Луны в Драк'Сотре убейте 8 некромагов Хат'ар."}},
[12553] = {"Прядильные органы", nil, {"Принесите специалисту Зубоколесу в Драк'Сотру 5 целых прядильных органов."}},
[12554] = {"Малас Осквернитель", nil, {"Принесите сержанту Осколку Луны в Драк'Сотру голову Осквернителя."}},
[12555] = {"Оплетающие сети", nil, {"По поручению специалиста Зубоколеса из Драк'Сотры сбейте 5 сеятелей чумы с помощью катапульты-сетемета."}},
[12556] = {"Охота на люторогов: смертельный удар", nil, {"Принесите рог Фаранна Хемингу Эрнестуэю в его лагерь."}},
[12557] = {"Лабораторная работа", nil, {"Отыщите в лаборатории Финкльштейна грязную болотную личинку, иссохшее крыло нетопыря, янтарный желудь и замерзшую змеиную слизь."}},
[12558] = {"Охота на терропардов: прыжок", nil, {"Принесите шкуру Шанго Баку Кантвеллу в лагерь Эрнестуэя."}},
[12559] = {"Включение Связующей спирали: Обитель Творцов", nil, {"Аватара Фрейи с водопадов Гласа Дождя хочет, чтобы вы отправились в Обитель Творцов и воспользовались активационным переключателем \"гамма\"."}},
[12560] = {"Охота на кроколисков: план", nil, {"Принесите 5 листьев песочного папоротника Дебаар в лагерь Эрнестуэя."}},
[12561] = {"Вопрос доверия", nil, {"Завоюйте доверие Фрейи: уничтожьте 6 чумных трупов и 10 острокостных опустошителей в Затерянных землях."}},
[12562] = {"Элементали не для Драккари", nil, {"По заданию младшего лейтенанта Джакса из Серебряной заставы убейте 10 водопрядов Драккари."}},
[12563] = {"Тролльский дозор", nil, {"Обойдите четыре заставы Серебряного Авангарда, выполните приказы капитанов и возвратитесь к командиру Кунцу на Серебряную заставу с докладом.\n\nЗаставы капитана Брендона и капитана Руперта расположены в Драк'Сотре, застава капитана Гронделя – в Драк'Агале, а застава алхимика Финкльштейна – в Хеб'Валоке."}},
[12564] = {"Тролльский дозор: обезболивающее снадобье", nil, {"Соберите 5 распустившихся водяных маков на полях Драк'Сотры для капитана Брендона из Драк'Сотры."}},
[12565] = {"Благословение Зим'Абвы", nil, {"По поручению мастера проклятий Убунго принесите 10 подношений Драккари к изваянию Зим'Абвы."}},
[12566] = {"Помощь Лагерю Заиндевевшего Копыта", nil, {"Поговорите с вождем Прахтотемом в Лагере Заиндевевшего Копыта."}},
[12567] = {"Благословение Зим'Абвы", nil, {"Требования:"}},
[12568] = {"Тролльский дозор: смертельная усталость", nil, {"По поручению капитана Руперта полейте 5 павших пехотинцев Серебряного Авангарда в Драк'Сотре маслом для сожжения."}},
[12569] = {"Охота на кроколисков: засада", nil, {"Принесите челюсть Прогрызателя Дебаар в лагерь Эрнестуэя."}},
[12570] = {"Счастливое непонимание", nil, {"Отведите раненого оракула в Прибежище Гласа Дождя и поговорите с верховным оракулом Су-сэем."}},
[12571] = {"Большая Скверная Змея должна уйти", nil, {"Верховный оракул Су-сэй из Прибежища Гласа Дождя просит вас убить Ядоклыка и 10 императорских кобр.\n\nЕсли потеряете мешок с жуками Лафу, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12572] = {"Боги любят блестящие штучки", nil, {"Соберите 6 драгоценных блестяшек, которые выкопает Лафу, и принесите их верховному оракулу Су-сэю в прибежище Гласа Дождя.\n\nЕсли потеряете мешок с жуками Лафу, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12573] = {"Заключение мира", nil, {"Верховный оракул Су-сэй из Прибежища Гласа Дождя хочет, чтобы вы пошли к шаману Векжику и предложили ему мир. Вернитесь к Су-сэю с ответом.\n\nЕсли потеряете мешок с жуками Лафу, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12574] = {"Так скоро?", nil, {"Отправляйтесь в убежище Шепота Тумана и поговорите с Призывателем туманов Су-ганом.\n\nЕсли вы потеряете любимый кристалл Джалута, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12575] = {"Потерянное сокровище Шепота Тумана", nil, {"Убейте полководца Тартека и добудьте сокровище Шепота Тумана по просьбе призывателя туманов Су-гана.\n\nЕсли вы потеряете любимый кристалл Джалута, поговорите с верховным оракулом Су-сэем в Прибежище Гласа Дождя."}},
[12576] = {"Тяжелая рука", nil, {"Убейте 8 копьеносцев племени Бешеного Сердца и 6 падальщиков племени Бешеного Сердца по просьбе Призывателя туманов Су-гана из убежища Шепота Тумана.\n\nЕсли вы потеряете любимый кристалл Джалута, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12577] = {"Пора домой!", nil, {"Возвращайтесь в прибежище Гласа Дождя."}},
[12578] = {"Разгневанный горлок", nil, {"Отправляйтесь в деревню Мохобродов и поговорите с Мульдом.\n\nЕсли вы потеряете резиновый мячик Мульда, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12579] = {"Жизненная Сила для мохобродов", nil, {"Мульд хочет, чтобы вы собрали 10 осколков Жизненной Силы, но при этом держались подальше от пещеры внутри самой колонны.\n\nЕсли вы потеряете резиновый мячик Мульда, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12580] = {"Спаситель мохобродов", nil, {"Мульд хочет, чтобы вы спасли 6 мохобродов-жертв из деревни Мохобродов, хотя некоторые из них, возможно, уже мертвы.\n\nЕсли вы потеряете резиновый мячик Мульда, поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя."}},
[12581] = {"Бремя героя", nil, {"Убейте Артруиса Бессердечного.\n\nМульд не сможет отправиться с вами."}},
[12582] = {"Защитник племени Бешеного Сердца", nil, nil},
[12583] = {"Разбившийся сеятель", nil, {"Соберите детали сеятеля чумы для специалиста Зубоколеса в Драк'Сотре."}},
[12584] = {"Зло в чистом виде", nil, {"По указанию капитана Руперта соберите 10 кусков саронита и отнесите их Эйтриггу на Серебряную заставу."}},
[12585] = {"Тролльский дозор: животворящее тепло", nil, {"Принесите капитану Гронделю в Драк'Агал 20 кусков древесины высохшего колючника."}},
[12587] = {"Тролльский дозор", nil, {"Обойдите четыре заставы Серебряного Авангарда, выполните приказы капитанов и возвратитесь к командиру Кунцу на Серебряную заставу с докладом.\n\nЗаставы капитана Брендона и капитана Руперта расположены в Драк'Сотре, к юго-востоку, застава капитана Гронделя – в Драк'Агале, прямо на восток, а застава алхимика Финкльштейна – в Хеб'Валоке, к северу."}},
[12588] = {"Тролльский дозор: будем копать", nil, {"По поручению капитана Брана из Драк'Сотры возьмите стальной заступ, раскопайте курганы древних троллей неподалеку и отыщите реликвию Драккари."}},
[12589] = {"Ничего опасного", nil, {"Выстрелите из винтовки РЖР в яблоко на голове Везучего Вильгельма, а потом поговорите с Дростаном."}},
[12591] = {"Тролльский дозор: подрывные работы", nil, {"По поручению капитана Руперта из Драк'Сотры взорвите 5 нерубианских кратеров с помощью фугасных гранат."}},
[12592] = {"Вызов великого охотника", nil, {"Убейте 60 животных, помогая Хемингу и его друзьям на охоте. Когда покончите с этим, возвращайтесь к Дростану в лагерь Эрнестуэя, чтобы сравнить ваши результаты."}},
[12593] = {"На службе Короля-лича", nil, {"Доложите о своем прибытии инструктору Разувию из Сердца Акеруса."}},
[12594] = {"Тролльский дозор: зачистка местности", nil, {"По поручению капитана Гронделя убейте 7 мшистых буянов в Драк'Агале."}},
[12595] = {"В поисках крупной дичи", nil, {"Поговорите с Дорианом Грозой Драконов в его лагере в Низине Шолазар."}},
[12596] = {"Па'трулль", nil, {"Обойдите четыре заставы Серебряного Авангарда, выполните приказы капитанов и возвратитесь к командиру Кунцу на Серебряную заставу с докладом.\n\nЗаставы капитана Брендона и капитана Руперта расположены в Драк'Сотре, к юго-востоку, застава капитана Гронделя – в Драк'Агале, прямо на восток, а застава алхимика Финкльштейна – в Хеб'Валоке, к северу."}},
[12597] = {"Обезболивающее снадобье", nil, {"Соберите 5 распустившихся водяных маков на полях Драк'Сотры для капитана Брендона из Драк'Сотры."}},
[12598] = {"Подрывные работы", nil, {"По заданию капитана Руперта из Драк'Сотры обрушьте 5 нерубских тоннелей с помощью фугасных гранат."}},
[12599] = {"Животворящее тепло", nil, {"Принесите капитану Гронделю в Драк'Агал 20 кусков древесины высохшего колючника."}},
[12601] = {"В подмастерья к алхимику", nil, {"Помогите алхимику Финкльштейну в Хеб'валоке в приготовлении сыворотки правды.\n\nКогда вы будете готовы приступить к работе, скажите об этом алхимику и выполняйте все его указания."}},
[12602] = {"В подмастерья к алхимику", nil, {"Помогите алхимику Финкльштейну в Хеб'Валоке в приготовлении сыворотки правды.\n\nКогда вы будете готовы приступить к работе, скажите об этом алхимику и выполняйте все его указания."}},
[12603] = {"Время точить когти", nil, {"Убейте 6 доисторических драконов, а затем поговорите с Дорианом Грозой Драконов в форпосте Дориана."}},
[12605] = {"Подготовка ловушки", nil, {"Принесите Дориану Грозе Драконов 6 доисторических дракончиков в форпост Дориана."}},
[12606] = {"В коконах", nil, {"Вскройте нерубские коконы в Драк'Сотре и вернитесь к капитану Руперту в Драк'Сотру, освободив 3 взятых в плен пехотинцев."}},
[12607] = {"Поимка мамонта", nil, {"Приведите Любопытсону в форпост Дориана твердобивневого мамонта."}},
[12608] = {"Вторжение сектантов", nil, {"Поговорите с Аватарой Фрейи у водопадов Гласа Дождя."}},
[12609] = {"Пополнение запасов", nil, {"Соберите 7 унций свежей паучьей лимфы от пауков-капканщиков в Зул'Драке для капитана Арната из Хеб'Валока."}},
[12610] = {"Подрезанные крылышки", nil, {"Соберите 7 безукоризненных крыльев нетопырей в Зул'Драке для капитана Арната из Хеб'Валока."}},
[12611] = {"Семикратное воздаяние", nil, {"Аватара Фрейи хочет, чтобы вы победили Талграна Сеятеля Гнили при помощи Оберега Фрейи, отразив его проклятия и обратив их против него."}},
[12612] = {"Разрушенная колонна", nil, {"Отправляйтесь к колонне Жизненной Силы и попытайтесь понять, как она была разрушена."}},
[12613] = {"Включение Связующей спирали: Дозор Творцов", nil, {"Аватара Фрейи хочет, чтобы вы направились к Дозору Творцов на востоке Низины Шолазар и там задействовали активационный переключатель \"тета\"."}},
[12614] = {"Материнский гнев", nil, {"Убейте Праматерь Сливину и возвращайтесь к Дориану Грозе Драконов в форпост Дориана."}},
[12615] = {"Благословение Зим'Торги", nil, {"По поручению знахаря Кхуфу принесите 10 подношений Драккари к изваянию Зим'Торги."}},
[12616] = {"Тайная комната", nil, {"Посланник Серебряного Рассвета просит вас обыскать комнату Тенриса Мутная Кровь, которая находится среди комнат слуг в Каражане."}},
[12617] = {"Уничтожение захватчиков", nil, {"Аватара Фрейи, стоящая у водопадов Гласа Дождя, попросила вас отправиться в лагерь Руки Смерти на северо-западе и убить 8 секретных агентов сектантов."}},
[12618] = {"Благословение Зим'Торги", nil, {"Требования:"}},
[12619] = {"Украшенный рунический меч", nil, {"Обыщите оружейные стойки в Черном оплоте, найдите затупившийся в боях меч и перекуйте его в рунический меч в рунной кузне.\n\nПринесите готовый клинок инструктору Разувию в Черный оплот."}},
[12620] = {"Гнев Хранительницы Жизни", nil, {"Аватара Фрейи у водопадов Гласа Дождя хочет, чтобы вы протрубили в Рог Фрейи на вершине Мерцающей колонны."}},
[12621] = {"Договор Фрейи", nil, {"Выслушайте то, что вам расскажет Аватара Фрейи."}},
[12622] = {"Вожди у Джин'Алаи", nil, {"По поручению охотника за скальпами Ауне из Зим'торги заберите сокровища у Кутубе'сы, Гаванила и Чуло Безумного."}},
[12623] = {"К знахарю", nil, {"Поговорите со знахарем Кхуфу в Зим'Торге."}},
[12624] = {"Оно может быть где угодно!", nil, {"Ищите золотое обручальное кольцо у всех обитателей Низины Шолазар."}},
[12625] = {"Власть над Акерусом", nil, {"Найдите главного кузнеца крепости Корвия на войсковом этаже Черного оплота."}},
[12627] = {"Прорыв в Джин'Алаи", nil, {"По поручению знахаря Кхуфу из Зим'Торги опрокиньте Синий котел, Зеленый котел, Фиолетовый котел и Красный котел."}},
[12628] = {"Разговор с Хар'коа", nil, {"Поговорите с Хар'коа у алтаря Хар'коа."}},
[12629] = {"Можно убежать, но нельзя скрыться", nil, {"По указанию Стефана с Черной заставы отправляйтесь за Реликварий Агонии и соберите 5 клубков прогнивших внутренностей поганища с тлетворных поганищ и 5 сгустков вязкой слюны вурдалака."}},
[12630] = {"Насса надо как следует пнуть", nil, {"С помощью Насса соберите образцы волос 10 иссохших троллей для Стефана с Черной заставы.\n\nЕсли вы потеряете Насса, возвращайтесь к Стефану."}},
[12631] = {"Приглашение", nil, {"Отнесите колье нежизни Стефану на Черную заставу."}},
[12632] = {"Первым делом – мои дети", nil, {"Используйте удивительные свойства уса Хар'коа для того, чтобы освободить и воскресить ее детей. Когда все будет сделано, вернитесь к Хар'коа. Она ждет вас у алтаря."}},
[12633] = {"Зов тьмы", nil, {"Отнесите змеевидное колье Стефану на Черную заставу."}},
[12634] = {"Кому ликер, кому лимонад", nil, {"Принесите Мрачнобуху Громовару в лагерь Эрнестуэя 1 апельсин, 2 грозди бананов и 1 папайю."}},
[12635] = {"Реликвии богини", nil, {"По поручению То'кини из Зим'Торги соберите 10 хар'коанских реликвий."}},
[12636] = {"Око Акеруса", nil, {"Предстаньте перед Королем-личом в Черном оплоте."}},
[12637] = {"Счастливое избавление", nil, {"Отдайте колье нежизни Датуре Кровавой Розе."}},
[12638] = {"На волосок от смерти", nil, {"По предложению Стефана с Черной заставы отнесите змеевидное колье Датуре Кровавой Розе."}},
[12639] = {"Застывшая земля", nil, {"Принесите укротителю стихий Дагоде в Зим'Торге 7 порций субстанции ледяной земли."}},
[12640] = {"Закрытие порталов", nil, {"По поручению охотника за скальпами Ауне из Зим'торги закройте 7 порталов элементалей."}},
[12641] = {"Смерть подбирается с высоты", nil, {"Король-лич приказал вам заглянуть в Око Акеруса, чтобы осмотреть Крепость Алого ордена, Кузню Нового Авалона, Ратушу Нового Авалона и Часовню Багрового Пламени.\n\nДля этого воспользуйтесь механизмом управления Оком Акеруса."}},
[12642] = {"Дух Рунока", nil, {"Поговорите с духом Рунока у алтаря Рунока."}},
[12643] = {"Серебряная отделка", nil, {"По указанию Стефана с Черной заставы отправляйтесь за Реликварий Агонии и соберите 5 клубков прогнивших внутренностей поганища с тлетворных поганищ и 5 сгустков вязкой слюны вурдалака."}},
[12644] = {"Выпивка – дело тонкое…", nil, {"Завершите процесс дистилляции и принесите пунш \"Зов джунглей\" Мрачнобуха Громовара в лагерь Эрнестуэя."}},
[12645] = {"Дегустация", nil, {"Предложите Хемингу Эрнестуэю, Гадриусу Гарлоу и Тамаре Шестеркрут попробовать пунш \"Зов джунглей\", а затем вернитесь к Мрачнобуху Громовару в лагерь Эрнестуэя."}},
[12646] = {"Пророк мой – враг мой", nil, {"Принесите духу Рунока настой бога белых медведей."}},
[12647] = {"Конец мучениям", nil, {"Используйте фимиам мучителя, чтобы пробудить тело Рунока. Сила, которой вас наделил дух, поможет вам убить Рунока.\n\nЕсли по каким-то причинам аура амулета будет рассеяна, возвращайтесь к духу Рунока, чтобы возобновить эффект."}},
[12648] = {"Маскировка", nil, {"Используйте заколдованное колье, чтобы замаскироваться под воина Плети.\n\nПребывая в личине воина Плети, купите для Стефана с Черной заставы горькую плазму у Гнилехряща.\n\nЕсли вы потеряете свое заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12649] = {"Переодевание", nil, {"Используйте Заколдованное колье, чтобы замаскироваться под воина Плети.\n\nПребывая в личине воина Плети, купите горькую плазму у Гнилехряща.\n\nЕсли вы потеряете свое заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12650] = {"Разграбленное святилище", nil, {"Принесите летописцу То'кини в Зим'Торгу 7 артефактов Рунока."}},
[12651] = {"Лагерь у озера", nil, {"Поговорите с Тамарой Шестеркрут в лагере у озера."}},
[12652] = {"Преющие вурдалаки проголодались", nil, {"По поручению Гнилехряща из Реликвария Агонии возьмите чашу мозгов и кишок и накормите 10 преющих вурдалаков."}},
[12653] = {"Назад к Хар'коа", nil, {"Поговорите с Хар'коа у алтаря Хар'коа."}},
[12654] = {"Охотник на полставки", nil, {"Доставьте останки Острокола Тамаре Шестеркрут в лагерь у озера."}},
[12655] = {"Благословение Зим'Рука", nil, {"По поручению знахаря Кхуфу принесите 10 подношений Драккари к изваянию Зим'Рука."}},
[12656] = {"Благословение Зим'Рука", nil, {"Требования:"}},
[12657] = {"Мощь Плети", nil, {"Король-лич приказал вам передать его распоряжения верховному лорду Дариону Могрейну в Черный оплот.\n\nЧтобы попасть в Зал Власти, воспользуйтесь мерцающим телепортом позади Короля-лича."}},
[12658] = {"Моя ручная птица Рух", nil, {"Принесите Любопытсону в форпост Дориана 7 яиц руха."}},
[12659] = {"Скальпы!", nil, {"По поручению Ауне из Зим'торги возьмите нож Ауне и снимите скальпы с 10 хеб'драккарских троллей.\n\nНе забывайте, сначала троллей следует обыскать."}},
[12660] = {"Орудия разрушения", nil, {"По поручению Аватары Фрейи отправляйтесь в лагерь Руки Смерти на северо-запад и уничтожьте 4 единицы нестабильной взрывчатки."}},
[12661] = {"На разведку в Волтар", nil, {"По поручению Стефана используйте заколдованное колье, чтобы пробраться в Волтар. Там вы должны делать все, о чем вас попросит предводитель сил Плети.\n\nЕсли вы потеряете Заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12662] = {"Смерть Хеб'Джина", nil, {"По заданию укротителя стихий Дагоды из Зим'Торги убейте Хеб'Джина."}},
[12663] = {"Воссоединение", nil, {"По заданию Дракуру из Волтара поговорите с Кровомесом и отправляйтесь в Зул'Драк."}},
[12664] = {"Темный горизонт", nil, {"По заданию Дракуру из Волтара поговорите с Кровомесом и отправляйтесь в Зул'Драк."}},
[12665] = {"Дурное предчувствие", nil, {"Узнайте, что случилось с Кетц'лун. Хар'коа даст вам в помощь одного из своих детей, чтобы незаметно пробраться к алтарю Кетц'лун.\n\nКогда все выясните, возвращайтесь обратно к Хар'коа – она ждет вас у своего алтаря."}},
[12666] = {"Путешествие в Нижний мир", nil, {"Соберите 8 священных украшений и принесите их Хар'коа к алтарю Хар'коа."}},
[12667] = {"Поиски богини крылатых змеев", nil, {"Разыщите дух Кетц'лун у алтаря Кетц'лун."}},
[12668] = {"Задел для мести", nil, {"Убейте 12 троллей из числа служителей Кетц'лун или помешанных на змеях берсерков рядом с Темными купелями.\n\nПотом вернитесь к Кетц'лун, которая будет ждать вас у алтаря."}},
[12669] = {"Пока все идет на удивление паршиво", nil, {"По поручению Стефана с Черной заставы вернитесь к Дракуру и выполняйте его приказы.\n\nКогда вернетесь в Реликварий Боли, вылейте растворитель в 5 котлов Плети.\n\nЕсли вы потеряете свое заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12670] = {"Жатва Алых", nil, {"Доложите о своем прибытии в Разлом Смерти принцу Валанару."}},
[12671] = {"Разведывательный полет", nil, {"Помогите пилоту Вику совершить разведывательный полет."}},
[12672] = {"Подготовка к мести", nil, {"Соберите 10 потусторонних энергетических фрагментов и вернитесь к алтарю Кетц'лун."}},
[12673] = {"Сбор кристаллов", nil, {"Командир Дракуру в Волтарасе хочет, чтобы вы взяли скипетр внушения и с его помощью заставили гнилостных упырей собрать 7 кристаллов гнили."}},
[12674] = {"Вся ярость преисподней", nil, {"По поручению духа Кетц'Лун воздействуйте проклинаающим жезлом Кетц'лун на верховного жреца Му'фуну, верховную жрицу Туа-Туа и верховного жреца Хавинни. После этого вы должны их убить."}},
[12675] = {"И последнее...", nil, {"Отнесите Хар'коа потусторонние энергетические фрагменты и попросите ее зарядить их частичкой ее духа. Вы найдете ее у алтаря Хар'коа."}},
[12676] = {"Диверсия", nil, {"По поручению Стефана с Черной заставы продолжайте выполнять поручения Дракуру.\n\nКроме того, активируйте заряды взрывчатки, чтобы уничтожить 5 повозок Плети в Реликварии Боли.\n\nЕсли вы потеряете свое заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12677] = {"Опасные материалы", nil, {"По поручению Стефана с Черной заставы вернитесь в Волтар и продолжайте выполнять поручения Дракуру. Также вам нужно украсть 5 добытых кристаллов гнили.\n\nЕсли вы потеряете свое заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12678] = {"Когда Хаос правит бал, Страдание заказывает музыку", nil, {"Принц Валанар из Разлома Смерти приказал вам убить 10 крестоносцев Алого ордена или 10 крестьян из Алого ордена и 10 граждан Тихоземья."}},
[12679] = {"Сегодня мы ужинаем в Тихоземье", nil, {"Оритос Затмитель Небес из Разлома Смерти просит вас принести ему 15 саронитовых стрел."}},
[12680] = {"Великий угонщик", nil, {"Саланар Всадник предложил вам украсть лошадь из Тихоземских стойл и привести ее в Разлом Смерти."}},
[12681] = {"Агент по реагентам", nil, {"Принесите в форпост Дориана 5 изогнутых когтей руха и отдайте Колвину Норрингтону."}},
[12683] = {"Жгучее желание помочь", nil, {"Колвин Норрингтон из форпоста Дориана хочет, чтобы вы принесли 5 образцов слюны гидры и убили 5 гидр Горьких Волн."}},
[12684] = {"Кровь мертвого бога", nil, {"Соберите 7 колб крови Мам'тота и принесите их Хар'коа к алтарю Хар'коа."}},
[12685] = {"Что посеешь, то и пожнешь", nil, {"Убейте истощенного пророка Кетц'лун и доложите об исполнении знахарю Кхуфу из Зим'Торги."}},
[12686] = {"Терпение лопнуло", nil, {"По поручению Дракуру из Волтара отправляйтесь в Реликварий Боли и примените скипетр Дозволенности к одному из слуг Дракуру. Потом с помощью этого слуги убейте Дармука."}},
[12687] = {"Путь в Долину Теней", nil, {"Саланар Всадник из Разлома Смерти предложил вам убить темного всадника Акеруса и забрать его скакуна.\n\nЕсли вам удастся совершить это, вернитесь в Разлом Смерти на коне и вызовите Саланара."}},
[12688] = {"Проектирование аварии", nil, {"Помогите инженеру Хелис сбежать из Раскопа Хитрохмыла, а затем поговорите с Хемингом Эрнестуэем в лагере Эрнестуэя."}},
[12689] = {"Длань Оракулов", nil, nil},
[12690] = {"Нам это на руку", nil, {"По поручению Дракуру из Волтара с помощью скипетра властности получите контроль над раздувшимися поганищами. С их помощью у Хладного разлома убейте 60 дробителей черепов Драккари, чтобы выманить 3 вождей племени.\n\nВожди появятся, когда будет убито достаточно дробителей черепов."}},
[12691] = {"Старинный сундук", nil, {"Добудьте у стражей Шолазара огромный каменный ключ и старинный откройте сундук, который вы нашли в Дозоре Творцов."}},
[12692] = {"Возвращение охотника на личей", nil, {"Вернитесь на холм Бешеного Сердца и поговорите со старейшиной Харкеком."}},
[12695] = {"Возвращение сухой шкуры", nil, {"Вернитесь в прибежище Гласа Дождя и поговорите с верховным оракулом Су-сэем."}},
[12696] = {"Воздушная разведка", nil, {"Профессор Кальверт хочет, чтобы вы полетели в лагерь у озера и там поговорили с пилотом Виком."}},
[12697] = {"Готик Жнец", nil, {"Поговорите с Готиком Жнецом в Разломе Смерти."}},
[12698] = {"Подарок с подвохом", nil, {"Готик Жнец из Разлома Смерти передал вам Дар Жнеца, с помощью которого вам надо обратить 5 шахтеров из Алого ордена в вурдалаков. Выполнив это поручение, верните прибор Жнецу."}},
[12699] = {"Затруднительное положение", nil, {"Поищите ключи Вика в Слиянии рек."}},
[12700] = {"Момент для атаки", nil, {"Поговорите с принцем Валанаром в Разломе Смерти."}},
[12701] = {"Побоище у Заставы Света", nil, {"Принц Валанар из Разлома Смерти приказал вам убить 100 защитников из Алого ордена."}},
[12702] = {"Куриная вечеринка", nil, {"Поймайте 12 сбежавших кур для старейшины Харкека с холма Бешеного Сердца."}},
[12703] = {"Буйство Картака", nil, {"Принесите чистый образчик Крови Оракула к алтарю Картака, чтобы призвать Картака. Используя его силу, уничтожьте 50 горлоков из племени Заиндевелых по просьбе шамана Джакжека в форте Картака."}},
[12704] = {"Умиротворение Великого камня дождя", nil, {"По просьбе верховного оракула Су-сэя из прибежища Гласа Дождя выберите себе спутника и с его помощью выкопайте 6 сверкающих сокровищ.\n\nОбязательно поговорите с верховным оракулом Су-сэем в прибежище Гласа Дождя, чтобы заручиться поддержкой кто-то из ваших прежних спутников."}},
[12705] = {"Воля Титанов", nil, {"Добудьте зараженный кристалл у одного из волчеров, что водятся рядом с Заиндевевшей гаванью и принесите его к великому камню молний, чтобы вызвать Со-холу. С помощью Со-холу убейте 50 находящихся поблизости волчеров Бешеного Сердца, после чего вернитесь к призывателю Су-мету в Заиндевелую гавань."}},
[12706] = {"Победа у Разлома Смерти", nil, {"Доставьте доклад принца Валанара верховному лорду Дариону Могрейну в Акерус: Черный оплот."}},
[12707] = {"Яростное правосудие", nil, {"По поручению Ауне из Зим'торги используйте медальон Мам'тота, чтобы успокоить одного из разъяренных мамонтов и забраться к нему на спину. Затем затопчите насмерть 12 послушников Мам'тота."}},
[12708] = {"Заколдованные воины Тики", nil, {"По заданию укротителя стихий Дагоды из Зим'Торги уничтожьте 12 заколдованных воинов Тики."}},
[12709] = {"Проклятые тайники", nil, {"Принесите летописцу То'кини в Зим'Торгу 12 трофеев из крепости Зол'Маза."}},
[12710] = {"Открытая тайна", nil, {"Телепортируйтесь в верхний зал Дракуру в Волтаре."}},
[12711] = {"Забытые письма", nil, nil},
[12712] = {"Ключ полководца Зол'Маза", nil, {"Раздобудьте ключ полководца Зол'Маза для Хар'коа из Зим'Торги."}},
[12713] = {"Предательство", nil, {"По поручению Стефана с Черной заставы убейте Дракуру.\n\nЕсли вы потеряете заколдованное колье, возвращайтесь к Стефану на Черную заставу, и он даст вам другое."}},
[12714] = {"Воля Короля-лича", nil, {"Возвращайтесь в Разлом Смерти и обратитесь к принцу Валанару."}},
[12715] = {"Склеп Воспоминаний", nil, {"Найдите принца Келесета в Склепе Воспоминаний."}},
[12716] = {"Просьба распространителя чумы", nil, {"Нот Чумной попросил вас принести ему пустой котел, железную цепь и 10 черепов рыцарей Света."}},
[12717] = {"Особое пойло Нота", nil, {"Положите груду рыцарских черепов в чумной котел."}},
[12718] = {"Черепа для пойла", nil, {"Требования:"}},
[12719] = {"Некуда бежать и негде спрятаться", nil, {"Принц Келесет из Склепа Воспоминаний приказал вам убить мэра Квимби и принести учетную книгу Нового Авалона."}},
[12720] = {"Как завоевывать друзей и оказывать влияние на врагов", nil, {"Принц Келесет из Склепа Воспоминаний приказал вам узнать всю правду про \"Багровый Рассвет.\"\n\nДостаньте доводы Келесета из узорной шкатулки и \"разговорите\" с их помощью рыцарей Алого ордена."}},
[12721] = {"Буйство", nil, {"По поручению Хар'коа из Зим'Торги освободите Акали и сообщите об этом знахарю Кхуфу в Зим'Торге."}},
[12722] = {"Овцы на бойне", nil, {"Барон Ривендер из Склепа Воспоминаний приказал вам убить 10 солдат Алого ордена и 15 жителей Нового Авалона."}},
[12723] = {"За Алым фронтом", nil, {"Найдите Орбаза Кровопорча в Таверне Алого ордена в Новом Авалоне."}},
[12724] = {"Путь истинного рыцаря", nil, {"Орбаз Кровопорч из Таверны Алого ордена в Новом Авалоне приказал вам украсть расписание патрулей Нового Авалона из Крепости Алого ордена."}},
[12725] = {"Братья по смерти", nil, {"Найдите Кольтиру Ткача Смерти в Крепости Алого ордена."}},
[12726] = {"Песнь ветра и воды", nil, {"Су-ни хочет, чтобы вы использовали Барабаны Шторма на уступе Ваятеля Бурь и помогли Хайфуну поглотить 3 загробников бури и 3 призраков дождя."}},
[12727] = {"Кровавый побег", nil, {"Убейте верховного инквизитора Валрота и отнесите его голову Тассариану в таверну Алого ордена в Новом Авалоне."}},
[12728] = {"Наблюдения за расселиной: пещера Зимних Плавников", nil, {"По просьбе библиотекаря Гаррена с Янтарной гряды воспользуйтесь тайнометром у пещер Зимних Плавников."}},
[12729] = {"Боги говорили с нами...", nil, {"Принесите безупречное сердце стража и субстанцию ветра Хар'коа в Зим'Торгу."}},
[12730] = {"Вызов в Зол'Хебе", nil, {"По заданию Хар'коа из Зим'Торги убейте пророка Акали."}},
[12732] = {"Сила озаренной крови", nil, {"Принесите фиал озаренной крови матриарха Реджеку на холм Бешеного Сердца."}},
[12733] = {"Вызов смерти", nil, {"Олрун Зовущая Битву приказала вам выиграть 5 дуэлей с другими рыцарями смерти.\n\nВыберите себе противников среди посвященных рыцарей смерти или других игроков."}},
[12734] = {"Реджек: первая кровь", nil, {"Омойте меч Реджека в крови осы Сапфирного улья, лютого барабанча и трех воинов или оракулов Шепота Тумана. Вернитесь на холм Бешеного Сердца к Реджеку."}},
[12735] = {"Очищающая песня", nil, {"Су-ни из прибежища Гласа Дождя хочет, чтобы вы прозвонили в колокола Очищения возле озера Горьких Волн, у реки Ледяных Оков и у Слияния рек, а затем убили злых духов, отравляющих эти воды."}},
[12736] = {"Кристальное сияние разума", nil, {"Су-ни из прибежища Гласа Дождя хочет, чтобы вы сыграли на диджериду созерцания на вершине Мерцающей колонны, Колонны Солнечного Благословения, Колонны Мохосвета и Колонны Небесного Пути."}},
[12737] = {"Песнь Плодородия", nil, {"Оракул Су-ни из прибежища Гласа Дождя хочет, чтобы вы отправились на уступ Ваятеля Бурь, что к северо-западу от Обители Творцов, и сыграли на Роге Плодородия возле 8 кучек влажной плодородной грязи."}},
[12738] = {"Мольбы об отмщении", nil, {"Найдите командора Чумопала в Часовне Багрового Пламени."}},
[12739] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Малара Храброга, узника из Серебряного Рассвета, заключенного в темнице."}},
[12740] = {"Парашюты для Серебряного Авангарда", nil, {"По поручению командира Фальштава на Серебряной заставе передайте парашюты 10 серебряным рыцарям или щитоносцам Серебряного Авангарда во внутреннем дворе."}},
[12741] = {"Сила Бури", nil, {"Соберите 3 субстанции грозы и 3 субстанции бури и отнесите их в Святилище бурь. Вернитесь на холм Бешеного Сердца к Реджеку и принесите ему истинную силу бури."}},
[12742] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Эллен Станбридж, узницу из Серебряного Рассвета, заключенную в темнице."}},
[12743] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Язмину Окенторн, узника из Серебряного Рассвета, заключенного в темнице."}},
[12746] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Валока Праведного, узника из Серебряного Рассвета, заключенного в темнице."}},
[12747] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить леди Эонис, узника из Серебряного Рассвета, заключенного в темнице."}},
[12748] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Кага Сталезуба, узника из Серебряного Рассвета, заключенного в темнице."}},
[12749] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Игги Темноклыка, узника из Серебряного Рассвета, заключенного в темнице."}},
[12750] = {"Приятный сюрприз", nil, {"Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Антуана Брэка, узника из Серебряного Рассвета, заключенного в темнице."}},
[12751] = {"Возвращение домой", nil, {"Возвращайтесь к Тассариану в Таверну Алого ордена, что в Новом Авалоне, и сообщите ему о вашей победе."}},
[12752] = {"Отчаянный поиск", nil, {"Поговорите с верховным аптекарем Гнилессом в новом аптекарском лагере на Террасе Света в Шаттрате."}},
[12753] = {"Вынужденный союз", nil, {"Поговорите с епископом Лазарил в новом лагере Альянса на Террасе Света в Шаттрате."}},
[12754] = {"Засада в укреплении", nil, {"Орбаз Кровопорч из Таверны Алого ордена в Новом Авалоне приказал вам убить курьера Алого ордена и забрать его пожитки и депешу."}},
[12755] = {"Встреча с судьбой", nil, {"Орбаз Кровопорч приказал вам доставить депешу курьера Алого ордена верховному генералу Аббендис в Королевскую гавань."}},
[12756] = {"Алый Натиск", nil, {"Отнесите Путь Искупления Орбазу Кровопорчу в Таверну Алого ордена в Новом Авалоне."}},
[12757] = {"Теплый прием для армий Алого ордена", nil, {"Отнесите Путь Искупления верховному лорду Дариону Могрейну в Акерус: Черный оплот."}},
[12758] = {"Шлем героя", nil, {"Заполучите взрывчатку у любого члена Торговой Компании возле Раскопа Хитрохмыла и используйте ее, чтобы заполучить голову наблюдателя бурь. Принесите голову на холм Бешеного Сердца и отдайте Реджеку."}},
[12759] = {"Орудия войны", nil, {"Раздобудьте несколько ловушек Зепика из форта Картака. Используя ловушки, убейте 50 горлоков из племени Заиндевелых, а затем вернитесь к шаману Якжеку в форт Картака."}},
[12760] = {"Тайная сила племени Бешеного Сердца", nil, {"Воспользовавшись силой секретного напитка племени Бешеного Сердца, перебейте 30 горлоков из племени Заиндевелых у форта Картака."}},
[12761] = {"Власть над кристаллами", nil, {"Добудьте кристаллы в Заиндевелой гавани и используйте их, чтобы убить 50 волчеров Бешеного Сердца, находящихся поблизости. Выполнив задание, возвращайтесь к призывателю молний Су-мету в Заиндевелую гавань."}},
[12762] = {"Могущество Великих", nil, {"Зарядите отполированный дремлющий кристалл возле колонны Жизненной Силы. Затем отправляйтесь в Заиндевелую гавань и используйте силу кристалла, чтобы убить 30 волчеров из стаи Бешеного Сердца, находящихся поблизости, и поговорите с Призывателем молний Су-метом в Заиндевелой гавани."}},
[12763] = {"Смена приоритетов", nil, {"Разведчик Вор'такх просит вас отправиться в Зул'Драк и разыскать сержанта Рианну в Разломе Света.\n\nВам укажет дорогу Макки Зимний Ветер, находящаяся в лагере Уанква."}},
[12766] = {"Разговор с послом", nil, {"Поговорите с Лориэль Верный Клинок в храме Драконьего Покоя."}},
[12767] = {"Разговор с послом", nil, {"Поговорите с Голлуком Каменным Кулаком в храме Драконьего Покоя."}},
[12768] = {"Распорядитель храма Драконьего Покоя", nil, {"Поговорите с Тариолстразом в храме Драконьего Покоя."}},
[12769] = {"Распорядитель храма Драконьего Покоя", nil, {"Поговорите с Тариолстразом в храме Драконьего Покоя."}},
[12770] = {"Перераспределение", nil, {"Гриан Камнегрив отправил вас к сержанту Рианне в Разлом Света, находящийся в Зул'Драке.\n\nОбратитесь к Самуэлю Клирбуку, и он доставит вас на место."}},
[12772] = {"Вынужденный союз", nil, {"Поговорите с епископом Лазарил в новом лагере Альянса на Террасе Света в Шаттрате."}},
[12773] = {"Дарнас", nil, {"Епископ Лазарил просит вас отнести заряженный Светом артефакт Тиранде Шелест Ветра в Дарнас."}},
[12774] = {"Штормград", nil, {"Епископ Лазарил просит вас отнести заряженный Светом артефакт королю Вариану Ринну в Штормград."}},
[12775] = {"Вынужденный союз", nil, {"Поговорите с епископом Лазарил в новом лагере Альянса на Террасе Света в Шаттрате."}},
[12776] = {"Экзодар", nil, {"Епископ Лазарил просит вас отнести заряженный Светом артефакт Велену в Экзодар."}},
[12777] = {"Вынужденный союз", nil, {"Поговорите с епископом Лазарил в новом лагере Альянса на Террасе Света в Шаттрате."}},
[12778] = {"Конец Алого ордена", nil, {"Найдите Короля-лича в Разломе Смерти."}},
[12779] = {"Конец всему...", nil, {"Король-лич приказал вам покончить с Алым орденом.\n\nВозьмите рог ледяного наследия и призовите ледяного покорителя, с помощью которого вам надо будет убить 150 солдат из Алого ордена и уничтожить 10 баллист Алого ордена. Постарайтесь не попасть под обстрел и вовремя уходите с линии огня!"}},
[12781] = {"Добро пожаловать!", nil, {"Отнесите подарочный сертификат Черного оплота в Черный оплот и отдайте его Банши Сиокси."}},
[12782] = {"Отчаянный поиск", nil, {"Поговорите с верховным аптекарем Гнилессом в новом аптекарском лагере на Террасе Света в Шаттрате."}},
[12783] = {"Отчаянный поиск", nil, {"Поговорите с верховным аптекарем Гнилессом в новом аптекарском лагере на Террасе Света в Шаттрате."}},
[12784] = {"Отчаянный поиск", nil, {"Поговорите с верховным аптекарем Гнилессом в новом аптекарском лагере на Террасе Света в Шаттрате."}},
[12785] = {"Оргриммар", nil, {"Верховный аптекарь Гнилесс просит вас отнести разрываемый на части мозг аптекарю Карлову в Оргриммаре."}},
[12786] = {"Громовой Утес", nil, {"Верховный аптекарь Гнилесс просит вас отнести разрываемый на части мозг аптекарю Чейни в Громовой Утес."}},
[12787] = {"Подгород", nil, {"Верховный аптекарь Гнилесс просит вас отнести разрываемый на части мозг опытному аптекарю Фаранеллу в Подгород."}},
[12788] = {"Луносвет", nil, {"Верховный аптекарь Гнилесс просит вас отнести разрываемый на части мозг аптекарю Тепеш в Луносвет."}},
[12789] = {"В Разлом!", nil, {"По распоряжению рыцаря Валуса из Опоры Света доложите о своем прибытии сержанту Рианне из Разлома Света в Зул'Драке."}},
[12790] = {"Как уйти и вернуться: магический путь", nil, {"Воспользуйтесь кристаллом телепортации на Аметистовую заставу у Даларана и кристаллом телепортации в Даларан по просьбе верховного мага Селиндры из Даларана."}},
[12791] = {"Магическое королевство Даларан", nil, {"Отнесите кирин-торский перстень верховному магу Селиндре к Аметистовым Вратам Даларана."}},
[12792] = {"Первым делом", nil, {"По поручению командира Кунца помогите сержанту Рианне в Разломе Света."}},
[12793] = {"Дым на горизонте", nil, {"По поручению Кхуфу из Зим'Торги переговорите с сержантом Рианной в Разломе Света.\n\nМаака обеспечит вам безопасное путешествие."}},
[12794] = {"Магическое королевство Даларан", nil, {"Отнесите кирин-торский перстень верховному магу Селиндре к Аметистовым Вратам Даларана."}},
[12795] = {"На помощь заставе", nil, {"По поручению Датуры Кровавой Розы поговорите с командиром Фальштавом на Серебряной заставе."}},
[12796] = {"Магическое королевство Даларан", nil, {"Отнесите кирин-торский перстень верховному магу Селиндре к Аметистовым Вратам Даларана."}},
[12797] = {"Назад по Связующей спирали", nil, {"Принесите Аватаре Фрейи руну \"Омега\". Вы найдете Аватару в Низине Шолазар у водопадов Гласа Дождя."}},
[12798] = {"Карты Новолуния: колода Мечей", nil, {"Призовите предсказателя судьбы с Ярмарки Новолуния и отдайте ему колоду Мечей."}},
[12799] = {"Сбор эктоплазмы", nil, {"Соберите 5 колб древней эктоплазмы от заблудших духов Драккари на озерах Жа'Джина для капитана Арната из Хеб'Валока."}},
[12800] = {"Приказ Короля-лича", nil, {"Найдите Командира Плети Таланора у лесопилки Бровача."}},
[12801] = {"Свет зари", nil, {"Разбейте войска Серебряного Рассвета и найдите Свет зари.\n\nЕсли вам удастся выжить, доложите о своей победе верховному лорду Дариону Могрейну."}},
[12802] = {"Мое сердце – в твоих руках", nil, {"Дракуру просит вас принести Сердце древних на его пьедестал в Перевале Драк'атала.\n\nНо сначала вам надо будет собрать 5 настоев безнадеги – иначе эликсир не сработает."}},
[12803] = {"Сила природы", nil, {"Отправляйтесь на Увядшую поляну, расположенную между водопадами Гласа Дождя и Затерянными землями и найдите Аватару Фрейи."}},
[12804] = {"Стейк, достойный охотника", nil, {"Принесите Коргу Тесаку в лагерь Эрнестуэя 5 стейков из длинношеего травояда."}},
[12805] = {"Сохранение жизненной силы", nil, {"По просьбе Фрейи отправляйтесь к Одинокому осколку на юго-восток и примените самоцвет жизненной силы к 8 уничтоженным элементалям жизненной силы."}},
[12806] = {"На Уступ Смерти со всех ног!", nil, {"Отправляйтесь к лорду-командиру Арету на Уступ Смерти, что в Ледяной Короне."}},
[12807] = {"Немного истории...", nil, {"Выслушайте рассказ лорда-командира Арета на Уступе Смерти."}},
[12808] = {"Вынужденный союз", nil, {"Поговорите с епископом Лазарил в новом лагере Альянса на Террасе Света в Шаттрате."}},
[12809] = {"Стальгорн", nil, {"Епископ Лазарил просит вас отнести заряженный Светом артефакт главному механику Меггакруту в Стальгорн."}},
[12810] = {"Кровавая вода", nil, {"Соберите кровь 10 прожорливых акул в бурдюк для крови и принесите его лорду-командиру Арету на Уступ Смерти."}},
[12811] = {"Отчаянный поиск", nil, {"Поговорите с верховным аптекарем Гнилессом в новом аптекарском лагере на Террасе Света в Шаттрате."}},
[12812] = {"Оргриммар", nil, {"Верховный аптекарь Гнилесс просит вас отнести разрываемый на части мозг аптекарю Карлову в Оргриммар."}},
[12813] = {"Из праха восстаньте!", nil, {"Возьмите настойку Темной Целительницы, которую дала вам Сетаал Темная Целительница на Уступе Смерти, и окропите тела любых 10 бойцов Алого Натиска, которых найдете."}},
[12814] = {"Потребность в грифоне", nil, {"Отведите грифона Алого Натиска к Узо Призывателю Смерти на Уступ Смерти."}},
[12815] = {"Закрытое воздушное пространство", nil, {"Убейте 10 всадников на грифонах из Алого Натиска по распоряжению Узо Призывателя Смерти с Уступа Смерти."}},
[12816] = {"Нападение Плети на Луносвет", nil, {"Соберите три тусклых Некротических камня Плети, которые носит при себе нежить, откружившая Луносвет, и исследуйте светящиеся рунические круги вблизи лагеря Плети."}},
[12817] = {"Нападение Плети на Экзодар", nil, {"Соберите три тусклых некротических камня созданий Плети, бродящих вокруг Экзодара, и изучите светящиеся рунические круги вокруг лагеря, после чего вернитесь к лейтенанту Крегору."}},
[12818] = {"Сбор деталей", nil, {"Принесите Глуму Свечекруту в К-3 10 деталей из обугленных обломков."}},
[12819] = {"Плевое дело", nil, {"По поручению Глума Свечекрута из К-3 принесите инструменты Свечекрута с минного поля."}},
[12820] = {"Осторожное прикосновение", nil, {"Рикет из К-3 просит вас воспользоваться усовершенствованными противопехотными минами на Побоище Гарма, чтобы оттеснить 12 противников: захватчиков из Гарма или последователей с Холмов Снежной Слепоты."}},
[12821] = {"Танго в тюремном блоке", nil, {"По поручению Рикет из К-3 разыщите батарею для телепортера и с ее помощью активируйте площадку телепортера у Подъема Гарма."}},
[12822] = {"Средство для устрашения", nil, {"По заданию Рикет из К-3 убейте 6 дозорных Гарма и 8 приверженцев с Холмов Снежной Слепоты."}},
[12823] = {"Безупречный план", nil, {"Джино у Подъема Гарма просит вас положить надежно перевязанный сверток со взрывчаткой у алтаря Хладонутра, убить Тормара Хладонутра и выбраться из пещеры до того, как она обрушится."}},
[12824] = {"Выдающийся подрывник", nil, {"Возвращайтесь к Рикет в К-3."}},
[12826] = {"Почти безопасны", nil, {"Отнесите усовершенствованные противопехотные мины Глума Рикет в К-3."}},
[12827] = {"Пополнение запасов провизии", nil, {"По заданию Рикет из К-3 стащите 16 сухих пайков гноллов из ближайшего лагеря свирепых гноллов холмов."}},
[12828] = {"Полет вдохновения", nil, {"По просьбе Рикет из К-3 возьмите УРВР, убейте мамонта и принесите в К-3 8 больших кусков мяса мамонта."}},
[12829] = {"Перед тем, как входить", nil, {"По заданию Торы Громыхалы у пещеры Хрустальной Паутины убейте 12 пауков в этой пещере."}},
[12830] = {"Изъятие руды", nil, {"По просьбе Тор Громыхалы у пещеры Хрустальной Паутины соберите 5 образцов нечистой саронитовой руды."}},
[12831] = {"Ядовитый укус", nil, {"Принесите раненому гоблину-рудокопу в пещере Хрустальной Паутины ядовитую железу ледяного ползуна."}},
[12832] = {"Побег", nil, {"Проводите раненого гоблина-рудокопа из пещеры Хрустальной Паутины в К-3, а потом поговорите с Рикет."}},
[12833] = {"Излишки", nil, {"Рикет из К-3 просит вас воспользоваться усовершенствованными противопехотными минами на Побоище Гарма, чтобы оттеснить захватчиков из Гарма и последователей с Холмов Снежной Слепоты."}},
[12836] = {"Выражение благодарности", nil, {"По заданию Рикет из К-3 убейте Кривошерста."}},
[12838] = {"Сбор информации", nil, {"Соберите 5 донесений разведки Алого Натиска по заданию Аурока Мрачного Проклятия с Уступа Смерти."}},
[12839] = {"Грандиозные планы верховного адмирала", nil, {"Отнесите письмо верховного адмирала лорду-командиру Аретy на Уступ Смерти."}},
[12840] = {"Совершенно секретно", nil, {"Выбейте информацию из капитанов Вельсингтона и Хартфорда, убейте обоих и возвращайтесь к лорду-командиру Арету на Уступ Смерти."}},
[12841] = {"Сделка с Каргой", nil, {"По просьбе Лок'лиры Карги принесите ей в Заброшенный рудник руны Ирквинн, отобрав их у надзирательницы Сиры."}},
[12842] = {"Ковка рун: подготовка к битве", nil, {"Инструктор Разувий из Черного оплота приказал вам выгравировать любую руну на вашем руническом клинке души.\n\nВыбранную гравировку можно заменить в любой момент, воспользовавшись ближайшей кузней рун."}},
[12843] = {"Они забрали наших мужчин!", nil, {"По просьбе Гретхен Искрошип отправляйтесь в деревню Сифрельдар и спасите 5 плененных гоблинов."}},
[12844] = {"Вернуть оборудование", nil, {"По просьбе Гретхен Искрошип принесите ей 8 предметов из снаряжения К-3, которые вы найдете в деревне Сифрельдар к северу от K-3."}},
[12846] = {"Найти всех до единого...", nil, {"Разыщите вход в Заброшенный рудник на севере деревни Сифрельдар и попробуйте выяснить, где может быть Зив Искрошип."}},
[12847] = {"Второй шанс", nil, {"Убейте архиепископа Ландгрена и откройте Врата Арета рядом с трупом. Поговорите с лордом-командиром Аретом, когда тот закончит допрашивать дух архиепископа в Багровом соборе."}},
[12848] = {"Вечный голод", nil, {"Инструктор Разувий из Черного оплота приказал вам освободить недостойного ученика и хладнокровно убить его!\n\nВыжить должен только один!"}},
[12849] = {"Сила крови, льда и нечестивости", nil, nil},
[12850] = {"Командир сил Плети Таланор", nil, {"Найдите командира сил Плети Таланора на балконе первого этажа Черного оплота."}},
[12851] = {"Медвежья хватка", nil, {"По заданию Бриджаны из деревни Бруннхильдар оседлайте Ледоклыка и застрелите 7 снежных воргов и 15 снежных великанов в долине Древней Зимы."}},
[12852] = {"Поиски адмирала", nil, {"Убейте верховного адмирала Вествинда, а потом возвращайтесь к лорду-командиру Арету на Уступ Смерти."}},
[12853] = {"Поторопить события", nil, {"Поговорите с Глумом Свечекрутом в лаборатории К-3 в Грозовой Гряде."}},
[12854] = {"По следам Бранна", nil, {"Заберите завернутую в мешковину записку из лагеря Бранна и возвращайтесь к археологу Андорину в Ледяную крепость."}},
[12855] = {"Найти преступника", nil, {"Используйте ошейник северной гончей в Заброшенном лагере, чтобы выследить тех, кто забрал вещи Бранна. Когда вы их разыщете, включите переговорное устройство и свяжитесь с теми, с кем получится связаться."}},
[12856] = {"Холодные сердца", nil, {"По заданию Бриджаны летите в Дун Ниффелем, освободите 3 пленных протодраконов, спасите 9 пленных бруннхильдарок и привезите их обратно, к востоку от деревни Бруннхильдар."}},
[12857] = {"Плавник Ярогрива", nil, {"Принесите плавник Ярогрива вождю Яростного Когтя в Разлом Света в Зул'Драке."}},
[12858] = {"Кусочки головоломки", nil, {"Соберите 6 фрагментов диска изобретателя и сложите из них диск изобретателя."}},
[12859] = {"Негасимый огонь", nil, {"Используйте огнетушитель Яростного Когтя, чтобы потушить 15 пожаров в логове Яростного Когтя по просьбе старого шамана Моки в Разломе Света в Зул'Драке."}},
[12860] = {"Сбор информации", nil, {"Воспользуйтесь диском изобретателя, чтобы считать 7 фрагментов секретных данных из банков данных."}},
[12861] = {"Плохие тролли", nil, {"По заданию вождя Яростного Когтя из Разлома Света в Зул'Драке отберите у троллей Дракуру необходимое количество \"Отмычек\" Дракуру и освободите 8 пойманных волчеров из стаи Яростного Когтя."}},
[12862] = {"Когда остается единственный выход...", nil, {"Воспользуйтесь принадлежащим Рикет пропеллерным доставочным аппаратом Д16, чтобы продвинуться вглубь Грозовой Гряды."}},
[12863] = {"Большое спасибо", nil, {"Отнесите ящик с медикаментами Глорталу Жесткобороду из Ледяной крепости."}},
[12864] = {"Пропавшие разведчики", nil, {"Обыщите долину Дыхания Бора и водопад Бора и попробуйте разыскать пропавших разведчиков из клана Зиморожденных."}},
[12865] = {"Верные друзья", nil, {"По поручению Фьюрлина Ледоброва из Ледяной крепости добудьте 8 кусков свежего мяса люторога и покормите грозовестников, обитающих прямо над городом."}},
[12866] = {"Сопротивление агрессорам", nil, {"По заданию Глортала Жесткоборода из Ледяной крепости убейте 8 крикуний и 8 ведьм из стаи Ледяного Пера."}},
[12867] = {"Похитители птенцов", nil, {"Принесите Фьюрлину Ледоброву из Ледяной крепости 15 яиц грозовестников."}},
[12868] = {"Источник всех бед", nil, {"По просьбе Глортала Жесткоборода убейте Сирану Ледяной Вопль."}},
[12869] = {"Все слишком далеко зашло", nil, {"По поручению Фьюрлина Ледоброва убейте с помощью грозовестника 16 змеев Грозовой Гряды в Подножии."}},
[12870] = {"Древние реликвии", nil, {"Принесите Рорку Остробороду в Ледяную крепость 10 реликвий Ульдуара."}},
[12871] = {"Помощь Лиги исследователей", nil, {"Отнесите оболочку Норганнона Лагнусу в Ледяную крепость."}},
[12872] = {"Оболочка Норганнона", nil, {"Заберите оболочку Норганнона у архивариуса Мехатона."}},
[12873] = {"Король Зиморожденных", nil, {"Поговорите с Йоргом Грозовым Сердцем в Ледяной крепости."}},
[12874] = {"Рвение Зиморожденного", nil, {"По указанию Йорга Грозового Сердца из Ледяной Крепости поговорите с Фьюрлином Ледобровом, который организует вам поездку и назначит для вас испытание."}},
[12875] = {"Опытный проводник", nil, {"Разыщите Дрома Ледохвата."}},
[12876] = {"Нежеланные гости", nil, {"По просьбе Фьюрлина Ледоброва убейте 10 погромщиков из клана Закаленных Бурей."}},
[12877] = {"Одинокий часовой", nil, {"Заберите у наблюдателя железных дворфов перстень-печатку Ледохвата и покажите его Кретею в Подножии."}},
[12878] = {"Спрятанная реликвия", nil, {"Найдите ядро Норганнона, руководствуясь информацией, которую дал Кретей в Подножии."}},
[12879] = {"Ярость короля Зиморожденных", nil, {"Отнесите ядро Норганнона Кретею в Подножие."}},
[12880] = {"Главный исследователь", nil, {"Отнесите ядро Норганнона Бранну Бронзобороду к Машине Творцов."}},
[12882] = {"Древние реликвии", nil, {"Отнесите 10 реликвий Ульдуара Олуту Пивобрюху на место крушения Гром'арша."}},
[12883] = {"Приказы Дракуру", nil, {"Отнесите приказы Дракуру главе рыцарей Лантинге в Разлом Света в Зул'Драке."}},
[12884] = {"Черная застава", nil, {"Отнесите приказы Дракуру Стефану Ваду на Черную Заставу в Зул'Драке."}},
[12885] = {"Изгнанники Ульдуара", nil, {"Поговорите с Глыбоскалом Камнетесом в Приюте Глыбоскала."}},
[12886] = {"Драконобойца", nil, {"Воспользуйтесь хильдским гарпуном, чтобы победить 10 наездниц на драконе Круга хильд в Храме Штормов."}},
[12887] = {"Непростая работа", nil, {"Уничтожьте Глаз и переговорите с бароном Раздором в Мрачном Своде."}},
[12888] = {"УБЕР-И", nil, {"Принесите в Библиотеку Изобретателя карту доступа к УБЕР-И, 4 пригоршни кобальтовых болтов и 5 перегруженных конденсаторов и попробуйте починить УБЕР-И."}},
[12889] = {"Панель управления прототипом", nil, {"Попробуйте разобраться с панелью управления прототипом в Библиотеке Изобретателя."}},
[12891] = {"Воплощение идеи", nil, {"Принесите скипетр сектанта, крюк поганища, веревку упыря и 5 эссенций Плети барону Раздору в Мрачный Свод."}},
[12892] = {"Непростая работа", nil, {"Уничтожьте Глаз и переговорите с бароном Раздором в Мрачном Своде."}},
[12893] = {"Освобождение разума", nil, {"Подействуйте скипетром повеления на тела Злоба, леди Найтсвуд и Прыгуна, а потом возвращайтесь к барону Раздору в Мрачный Свод."}},
[12894] = {"Передовой лагерь рыцарей", nil, {"Разыщите рыцаря Маккеллара в Передовом лагере рыцарей в Зул'Драке."}},
[12895] = {"Пропавший Бронзобород", nil, {"Заберите завернутую в мешковину записку из лагеря Бранна и возвращайтесь к Боктару Кровавому Безумию на место крушения Гром'арша."}},
[12896] = {"Последний аргумент", nil, {"Убейте генерала Погибель Света и доложите об исполнении Тассариану на борту \"Усмирителя небес\"."}},
[12897] = {"Последний аргумент", nil, {"Убейте генерала Погибель Света и доложите об исполнении Кольтире Ткачу Смерти на борту \"Молота Оргрима\"."}},
[12898] = {"Мрачный Свод", nil, {"Поговорите с бароном Раздором в Мрачном Своде."}},
[12899] = {"Мрачный Свод", nil, {"Поговорите с бароном Раздором в Мрачном Своде."}},
[12900] = {"Изготовление сбруи", nil, {"По заданию Астрид Бьорнриттар из деревни Бруннхильдар раздобудьте 3 шкуры ледогривых йети."}},
[12901] = {"Что-то из ничего", nil, {"Принесите 10 обломков металлолома Плети инженеру Риду в Передовой лагерь рыцарей в Зул'Драке."}},
[12902] = {"Кто за этим стоит?", nil, {"Обыщите логово Яростного Когтя и попытайтесь узнать, кто был зачинщиком атаки на волчеров Яростного Когтя."}},
[12903] = {"Для того и нужны друзья...", nil, {"Разыщите рыцаря Даргата, Герка и Бурра по просьбе рыцаря Маккеллара из Передового лагеря рыцарей."}},
[12904] = {"Орудие мести", nil, {"Убейте 15 варгулов по просьбе Герка из Тупика Трыма в Зул'Драке."}},
[12905] = {"Милдред Жестокая", nil, {"Поговорите с Милдред Жестокой в Заброшенном руднике."}},
[12906] = {"Дисциплинирование", nil, {"По заданию Милдред Жестокой из Заброшенного рудника накажите 6 обессиленных врайкулов с помощью вразумляющего жезла."}},
[12907] = {"Нужны показательные примеры", nil, {"По заданию Милдред Жестокой из Заброшенного рудника убейте Гархала."}},
[12908] = {"Наглая пленница", nil, {"Отнесите ключ Милдред Лок'лире Карге в Заброшенный рудник."}},
[12909] = {"Острый нюх", nil, {"Поговорите с Калииси у подножья горы, на востоке от места крушения Гром'арша."}},
[12910] = {"Найти преступника", nil, {"Оседлайте Ледокуса и поезжайте на юг, взяв след по записке Бранна. Когда вам удастся заполучить переговорное устройство, свяжитесь с его помощью с Бранном Бронзобородом."}},
[12911] = {"Kill Credit Test", nil, {"/wave at Kill Credit Test"}},
[12912] = {"Приближение великой бури", nil, {"Придумайте способ, как освободить из клетки Гимера, короля штормовых великанов. Поговорите с инженером Ридом в Передовом лагере рыцарей."}},
[12913] = {"Говори по-орочьи!", nil, {"Отправляйтесь на место крушения Гром'арша и узнайте, не сможет ли Мотеха Дитя Ветра вам помочь."}},
[12914] = {"Спасение Гимера", nil, {"Принесите 6 субстанций банши и 6 комьев диатомовой земли инженеру Риду в Передовой лагерь рыцарей в Зул'Драке."}},
[12915] = {"Возобновление старых связей", nil, {"По заданию Торима из Храма Штормов убейте Фьорна и 5 железных великанов из клана Закаленных Бурей у Наковальни Фьорна к востоку от Дун Ниффелема. Чтобы вызвать на подмогу воинов земли, воспользуйтесь оберегом земли Торима. Чтобы его задействовать, нужен гранитный валун из Наковальни Фьорна."}},
[12916] = {"Наша единственная надежда", nil, {"Поговорите с Гимером в Тупике Трыма в Зул'Драке после того, как освободите его из клетки Плети с помощью огромного сефориевого заряда."}},
[12917] = {"Поймай голос ветра!..", nil, {"Поймайте 5 голосов ветра, Мотеха Дитя Ветра на месте крушения Гром'арша ждет, чтобы вы их принесли."}},
[12918] = {"Огранка камней", nil, {"Принесите два огромных цитрина, два темных нефрита и два сумрачных кристалла Унгуло в Валгард."}},
[12919] = {"Месть короля Бурь", nil, {"Когда будете готовы к битве, скажите об этом Гимеру.\n\nСидя на голове у Гимера, уничтожьте 100 воинов Плети и убейте Алгара Избранного, Трыма и принца Навариуса. В случае успешного исхода доложите об этом серебряному рыцарю Маккеллару из передового лагеря рыцарей."}},
[12920] = {"Вдогонку за Бранном", nil, {"Используйте переговорное устройство Бранна, чтобы связаться с Бранном Бронзобородом. Потом доложите о результатах Боктару Кровавому Безумию, который ждет вас на месте крушения Гром'арша.\n\nЕсли вы потеряете переговорное устройство Бранна, вам поможет Мотеха Дитя Ветра на месте крушения Гром'арша."}},
[12921] = {"Смена декораций", nil, {"Поговорите с Лок'лирой Каргой в деревне Бруннхильдар."}},
[12922] = {"Очищающее пламя", nil, {"Соберите 10 гневных искр с пылающих загробников на Промерзшем озере и с их помощью разожгите огонь в Наковальне Фьорна."}},
[12924] = {"Возобновление союза", nil, {"Отправляйтесь в Дун Ниффелем и попросите короля Йоккума разрешить воссоздать доспех Торима. Выполнив поручение Йоккума, поговорите с Ньормельдом в Дун Ниффелеме."}},
[12925] = {"Верность традициям", nil, {"По заданию Тиры Квиншал из деревни Бруннхильдар отправляйтесь в Валькирион и добудьте у валькирионских претенденток флаконы масла льда. Используйте их, чтобы уничтожить 30 зачумленных яиц протодраконов."}},
[12926] = {"Кусочки головоломки", nil, {"Воспользуйтесь переговорным устройством Бранна, чтобы связаться с Бранном Бронзобородом и выяснить, что делать дальше."}},
[12927] = {"Сбор информации", nil, {"Воспользуйтесь диском изобретателя, чтобы считать 7 фрагментов секретных данных из банков данных."}},
[12928] = {"Оболочка Норганнона", nil, {"Заберите оболочку Норганнона у архивариуса Мехатона."}},
[12929] = {"Земельники Ульдуара", nil, {"Поговорите с Глыбоскалом Камнетесом в Приюте Глыбоскала."}},
[12930] = {"Горсть редкой земли", nil, {"С помощью шахтерской кирки земельника добудьте 7 комков зачарованной земли и принесите 5 отрезов ледяной ткани Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12931] = {"Контратака", nil, {"По заданию Глыбоскала Камнетеса убейте 10 мародеров или разорителей из клана Закаленных Бурей и возвращайтесь к нему в Приют Глыбоскала."}},
[12932] = {"Амфитеатр Страданий: Иггдрас!", nil, {"Выйдите на арену в Амфитеатре Страданий и победите Иггдраса! Потом вернитесь к троллю-прислужнику Водину и получите награду."}},
[12933] = {"Амфитеатр Страданий: магнатавр!", nil, {"Выйдите на арену в Амфитеатре Страданий и победите Смрадоборода! Потом вернитесь к троллю-прислужнику Водину и получите награду."}},
[12934] = {"Амфитеатр Страданий: существо из другого мира!", nil, {"Выйдите на арену в Амфитеатре Страданий и победите повелителя элементалей! Потом вернитесь к троллю-прислужнику Водину и получите награду."}},
[12935] = {"Амфитеатр Страданий: Клыкаррмагеддон!", nil, {"Выйдите на арену в Амфитеатре Страданий и победите Ориноко Клыколома! Потом вернитесь к троллю-прислужнику Водину и получите награду."}},
[12936] = {"Амфитеатр Страданий: Коррак Кровопуск!", nil, {"Выйдите на арену в Амфитеатре Страданий и победите Коррака Кровопуска! Потом вернитесь к троллю-прислужнику Водину и получите награду."}},
[12937] = {"Исцеление раненых", nil, {"Используйте земляные примочки, чтобы вылечить 8 раненых земельников-защитников, потом возвращайтесь к Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12938] = {"Герцог", nil, {"Поговорите с герцогом Ланкралом в Мрачном Своде."}},
[12939] = {"Вызов чести", nil, {"Возьмите флаг вызова, вызовите на поединок и победите 6 мьординских бойцов по просьбе герцога Ланкрала из Мрачного Свода."}},
