[12951] = {"Известия для барона", nil, {"Поговорите с бароном Раздором в Мрачном Своде."}},
[12952] = {"Огранка камней", nil, {"Принесите два огромных цитрина, два темных нефрита и два сумрачных кристалла Картеру Тиффенсу в Лагерь Возмездия."}},
[12953] = {"Гори, Валькирион, гори", nil, {"С помощью валькирионских гарпунных пушек подожгите 6 валькирионских стогов сена."}},
[12954] = {"Амфитеатр Страданий: Иггдрас!", nil, {"Выйдите на арену в Амфитеатре Страданий и победите Иггдраса! Потом вернитесь к троллю-прислужнику Водину и получите награду."}},
[12955] = {"Унижение соперников", nil, {"Вызовите на дуэль и победите Сигрид Дитя Льда, Ефрема Верного, Ону'Зуна и Тинки Свистопляс по поручению Прыгуна из Мрачного Свода."}},
[12956] = {"Лучик надежды", nil, {"Отнесите очищенный светящийся металл Ториму в Храм Штормов."}},
[12957] = {"Рабы Закаленных Бурей", nil, {"По заданию Глыбоскала Камнетеса из Приюта Глыбоскала освободите 6 пленных механогномов и убейте 3 надсмотрщиков из клана Закаленных Бурей."}},
[12958] = {"Заказ от торговой компании: амулет из кровавого нефрита", nil, {"Создайте амулет из кровавого нефрита, объединив амулет врайкулов, темный нефрит и кровавый камень, после чего принесите его Тимоти Джонсу в Даларан. Амулет врайкулов можно добыть с любого врайкула в Нордсколе."}},
[12959] = {"Заказ от торговой компании: блестящая статуэтка из бивня", nil, {"Создайте блестящую статуэтку из бивня, объединив кальцедоний, сумрачный кристалл и северный бивень, и принесите ее Тимоти Джонсу в Даларан.\n\nДля того чтобы раздобыть северный бивень, вам нужно будет убить любого черпорога или мамонта в Нордсколе."}},
[12960] = {"Заказ от торговой компании: брошь Беспощадного солнца", nil, {"Создайте брошь Беспощадного солнца из броши железных дворфов, огромного цитрина и солнечного хрусталя и принесите ее Тимоти Джонсу в Даларан.\n\nБрошь железных дворфов можно добыть у любого железного дворфа в Нордсколе."}},
[12961] = {"Заказ от торговой компании: изысканная костяная статуэтка", nil, {"Создайте изысканную костяную статуэтку из кости протодракона, солнечного хрусталя и темного нефрита и принесите ее Тимоти Джонсу в Даларан. Кость протодракона можно добыть с любого протодракона в Нордсколе."}},
[12962] = {"Заказ от торговой компании: Реликвия из сияющей брони", nil, {"Создайте реликвию Сияющей брони из осколка элементальной брони, кровавого камня и огромного цитрина и принесите ее Тимоти Джонсу в Даларан. Осколок элементальной брони можно добыть у любого элементаля-загробника в Нордсколе."}},
[12963] = {"Заказ от торговой компании: реликвия Восходящего Солнца", nil, {"Создайте реликвию Восходящего Солнца из реликвии Плети, солнечного хрусталя и сумрачного кристалла и принесите ее Тимоти Джонсу в Даларан. Реликвию Плети можно добыть у любого последователя Плети в Нордсколе."}},
[12964] = {"Темная руда", nil, {"Принесите 5 образцов руды Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12965] = {"Дары Локена", nil, {"Уничтожьте Неистовство Локена, Мощь Локена и Благосклонность Локена, а затем вернитесь к Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12966] = {"Мимо него не пройдешь", nil, {"По поручению короля Йоккума из Дун Ниффелема разыщите Ньормельда у Наковальни Фьорна."}},
[12967] = {"Сражение со стихиями", nil, {"По поручению Ньормельда отправляйтесь к наковальне Фьорна вместе с Храппи и помогите ему убить 10 пылающих загробников."}},
[12968] = {"Безрассудство Юльды", nil, {"По заданию Ивы Мстительной из деревни Бруннхильдар убейте Юльду Опустошительницу в Валькирионе."}},
[12969] = {"Это твой гоблин?", nil, {"Бросьте вызов Агнете Тирсдоттар, чтобы спасти Зива Искрошипа. Добившись успеха, возвращайтесь к Лок'лире Карге из деревни Бруннхильдар."}},
[12970] = {"Круг хильд", nil, {"Выслушайте предложение Лок'лиры Карги."}},
[12971] = {"Вызов принят", nil, {"Сразите 6 дуэлянтов-победителей по заданию Лок'лиры Карги из деревни Бруннхильдар."}},
[12972] = {"Тебе понадобится медведь", nil, {"Поговорите с Бриджаной неподалеку от деревни Бруннхильдар."}},
[12973] = {"Братья Бронзобороды", nil, {"Прикрывайте Бранна и ветролет, пока не доберетесь до Ледяной крепости."}},
[12974] = {"Призыв бойцовского клуба", nil, {"Разыщите Гаргтокa в Амфитеатре Страданий в Зул'Драке."}},
[12975] = {"Мемориал", nil, {"По заданию короля Йоккума из Дун Ниффелема соберите 8 фрагментов рога в Громовержье."}},
[12976] = {"Памятник павшим", nil, {"По поручению короля Йоккума отнесите фрагменты рога Ходира Ньормельду в Дун Ниффелем."}},
[12977] = {"Зов Ходира", nil, {"Убейте 5 ниффелемских прародителей и 5 неупокоенных дворфов из клана Зиморожденных в Громовержье. Протрубите над их телами в рог, затем вернитесь к рогу Ходира в Дун Ниффелем."}},
[12978] = {"Навстречу буре", nil, {"По заданию Брюора Железоруба в Приюте Глыбоскала убейте 10 Закаленных Бурей из Нидавелира. Вы можете убивать воителей клана Закаленных Бурей, волхвов клана Закаленных Бурей или боевых големов клана Закаленных Бурей."}},
[12979] = {"Доспехи тьмы", nil, {"Принесите 4 образца темной пластины для доспехов Брюору Железорубу в Приют Глыбоскала."}},
[12980] = {"Секрет доспехов", nil, {"Отнесите темную пластину для доспехов в Мастерскую Мимира и попросите рабочего Тока осмотреть ее. Потом возвращайтесь к Брюору Железорубу в Приют Глыбоскала."}},
[12981] = {"Жар и холод", nil, {"Идите к Промерзшему озеру, убейте хрупких загробников, обитающих в тех краях, и с помощью субстанции Льда остудите дымящиеся осколки у наковальни Фьорна. Потом соберите 6 кусков застывшего железа и вернитесь к наковальне Фьорна в Дун Ниффелем."}},
[12982] = {"Пленники из ордена Черного Клинка", nil, {"Освободите 8 пленных рыцарей Черного Клинка по просьбе Вэлена Разоблаченного из Мрачного Свода. Пленников держат в клетках в районе Йотунхейма."}},
[12983] = {"Последняя из рода", nil, {"Спасите медведицу-матриарха в пещере зимней спячки по просьбе Астрид Бьорнриттар из деревни Бруннхильдар."}},
[12984] = {"Валдуран Дитя Бури", nil, {"Протрубите в боевой рог Глыбоскала, когда разыщете Валдурана Дитя Бури. Одержите победу над ним и вернитесь к Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12985] = {"Ковка шлема", nil, {"По заданию Ньормельда в Дун Ниффелеме возьмите кирку с алмазным наконечником, вырвите с ее помощью глаза у мертвых железных великанов у Промерзшего озера и принесите ему 8 глаз Закаленных Бурей."}},
[12986] = {"Судьба титанов", nil, {"По заданию Кретея из Подножия посетите Храм Изобретений, Храм Зимы, Храм Жизни и Храм Порядка. Используйте банк данных для изучения каждого из них."}},
[12987] = {"Шлем Ходира", nil, {"Летите к острию ледяного шипа в северной части Дун Ниффелема и прочитайте народу Ньормельда текст со скрижалей Утверждения."}},
[12988] = {"Штормовые кузни", nil, {"Разрушьте северную штормовую кузню, центральную штормовую кузню и южную штормовую кузню с помощью бомб Глыбоскала. Потом возвращайтесь к Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12989] = {"Нашествие тьмы", nil, {"По заданию Астрид Бьорнриттар убейте 8 прожорливых йормунгаров в пещере зимней спячки на востоке от деревни Бруннхильдар."}},
[12991] = {"Удар по больному месту", nil, {"Убейте 10 изобретателей из клана Закаленных Бурей и возвращайтесь к Брюору Железорубу в Приют Глыбоскала."}},
[12992] = {"Истребление врайкулов", nil, {"Убейте 15 врайкулов в районе Йотунхейма по заданию Злоба из Мрачного Свода."}},
[12993] = {"Колоссальная опасность", nil, {"Разыщите документы с наступательными и оборонительными характеристиками колосса и принесите их Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[12994] = {"Контрразведчик", nil, {"Убейте 3 Секретных агентов клана Закаленных Бурей в долине Древних Зим.\n\nС помощью клыка астрального ворга призовите призрачного ледяного ворга, который поможет вам обнаружить скрывающихся врагов. По выполнении задания вернитесь к повелительнице логова ледяных воргов в Дун Ниффелем."}},
[12995] = {"Наша метка", nil, {"По заданию барона Раздора из Мрачного Свода воткните 15 знамен Черного Клинка рядом с телами мертвых врайкулов в районе Йотунхейма."}},
[12996] = {"Тренировочный заезд", nil, {"Воспользуйтесь поводьями матриарха ледочревов, чтобы сразиться с Киргарааком, как вам велела Астрид Бьорнриттар из деревни Бруннхильдар."}},
[12997] = {"В Яму!", nil, {"Отправляйтесь в Яму Клыка, воспользуйтесь поводьями матриарха ледочревов и победите 6 боевых медведей Круга хильд по заданию Астрид Бьорнриттар из деревни Бруннхильдар."}},
[12998] = {"Сердце бури", nil, {"По поручению Глыбоскала Камнетеса из Приюта Глыбоскала отправляйтесь в Ульдис и сделайте все возможное, чтобы надзиратель Нарвир не сумел заполучить Сердце бури."}},
[12999] = {"Костяная ведьма", nil, {"Поговорите с костяной ведьмой в пещере в деревне Ньорндар."}},
[13000] = {"Чрезвычайные меры", nil, {"Отправляйтесь в Логово Гиморака на востоке и добудьте 6 шкур воргов по просьбе кровостража Лорги."}},
[13001] = {"Копье Ходира", nil, {"Добудьте 3 осколка Вечного льда в пещере зимней спячки и 3 шкуры спокойных мамонтов в долине Древних Зим и отнесите все сказителю Рандвиру в Дун Ниффелем."}},
[13002] = {"Огранка камней", nil, {"Принесите два огромных цитрина, два темных нефрита и два сумрачных кристалла Геба'ли в Крепость Песни Войны."}},
[13003] = {"Как убить дракона", nil, {"С помощью копья Ходира загарпуньте дикого змея в долине Древних Зим и сразитесь с ним не на жизнь, а на смерть."}},
[13004] = {"Огранка камней", nil, {"Принесите два огромных цитрина, два темных нефрита и два сумрачных кристалла Алестосу в Крепость Отваги."}},
[13005] = {"Клятва детей земли", nil, {"Используйте рог Вершин, чтобы призвать земельников на битву с железными дворфами и убить 20 железных дворфов и 7 железных часовых по просьбе Торима с Террасы Творцов."}},
[13006] = {"Полировка кислотой", nil, {"Идите в пещеру зимней спячки, что к западу от Дун Ниффелема, и соберите 5 сгустков вязкого масла злобных масленышей. Потом возвращайтесь в Дун Ниффелем и натрите маслом шлем Ходира."}},
[13007] = {"Железный колосс", nil, {"Уничтожьте железного колосса с помощью прирученного йормунгара, а потом возвращайтесь к Глыбоскалу Камнетесу в Приют Глыбоскала."}},
[13008] = {"Тактика Плети", nil, {"Освободите 8 опутанных рыцарей по заданию командира рыцарей Энтари из Оплота Серебряного Авангарда в Ледяной Короне."}},
[13009] = {"Новое начало", nil, {"Отнесите перекованные доспехи Торима в Храм Штормов по просьбе Ньормельда."}},
[13010] = {"Кролмир, Молот Бурь", nil, {"Поговорите с королем Йоккумом в Дун Ниффелеме и попробуйте выяснить, что ему известно о Кролмире, принадлежавшем Ториму."}},
[13011] = {"Усмирение Йормуттар", nil, {"По приказу короля Йоккума из Дун Ниффелема убейте Йоркуттар в пещере зимней спячки."}},
