[13518] = {"Последняя группа беженцев", nil, {"Найдите всех выживших беженцев из Аубердина."}},
[13519] = {"Культ Сумеречного Молота", nil, {"Убейте верховного сектанта Азеринна и 10 фанатиков культа Сумеречного Молота в лагере Сумеречного Молота."}},
[13520] = {"Дар морей", nil, {"Соберите 16 затвердевших мышц моллюска."}},
[13521] = {"Жужжалка 413", nil, {"Соберите 4 куска мяса зараженного прибрежного краба и отнесите их к Жужжалке 413 для анализа."}},
[13522] = {"Угроза из воды", nil, {"Убейте 8 коварных потоков."}},
