[13527] = {"Никакого вкуса", nil, {"Принесите образец разложившейся медвежьей плоти на восток к Жужжалке 413."}},
[13528] = {"Жужжалка 723", nil, {"Соберите 6 образцов внутренностей зараженных колючешерстных медведей и принесите их к Жужжалке 723 для анализа."}},
[13529] = {"Источник разложения", nil, {"Убейте Зенна Грязное Копыто и 8 коварных греллей в Скалистой пещере."}},
[13537] = {"Любовь к окуням", nil, {"С помощью навыка рыбной ловли поймайте 4 морских окуня Темных берегов."}},
[13538] = {"Южный саботаж", nil, {"Уничтожьте башню Темного Взора, башню Огненного Дозора или башню Клинка Зимы, а затем вернитесь к офицеру-стратегу Абрамису на Озере Ледяных Оков."}},
[13539] = {"Свержение башен", nil, {"Уничтожьте башню Темного Взора, башню Огненного Дозора или башню Клинка Зимы, затем вернитесь к офицеру-стратегу Килрату."}},
[13542] = {"Против ветра", nil, {"Воспользуйтесь лунным колодцем в руинах Аубердина, чтобы уничтожить 8 наручей бесноватого циклона."}},
[13543] = {"Три молота", nil, {"Убейте подругу облаков Буйногривую, повелителя ветров Цу-Цу и повелителя небес Браакса."}},
[13544] = {"Благословение медведя", nil, {"Старейшина Бролг из логова Чернолесья попросил вас принести ему пучок благословенных трав."}},
[13545] = {"Очищение оскверненных", nil, {"Старейшина Бролг просит вас воспользоваться пучком благословенных трав, чтобы очистить 7 фурболгов Чернолесья, после чего убейте духов осквернения, сводивших их с ума."}},
[13546] = {"Осквернитель", nil, {"Старейшина Бролг из логова Чернолесья просит вас убить Шаракса Осквернителя."}},
[13547] = {"Отделение душ", nil, {"Отделите души Тандриса Ветропряда, Элиссы Звездный Ветер, Тальдана и Кайлаис Лунное Перо."}},
[13548] = {"Кулек конфет", nil, nil},
[13549] = {"Хвост трубой", nil, {"По просьбе летописца То'кини из Зим'Торги усыпляйте из воздушного ружья То'кини северных леопардов и ледолапых медведей. Возвращайтесь к нему, поймав по три самки каждого вида."}},
[13554] = {"Излечение во тьме", nil, {"Добудьте 6 образцов зараженной лимфы существ из Скалистой пещеры."}},
[13556] = {"Яйца для Дубра'джина", nil, {"Соберите 12 яиц ящера Гундрака по заданию Га'ваны из Дубра'джина."}},
[13557] = {"Неожиданная удача", nil, {"Освободите 8 незараженных колючешерстных медведей или незараженных сумеречников из клеток в Скалистой пещере."}},
[13558] = {"Призывая гром", nil, {"Воспользуйтесь ритуальной сферой Эфериона, чтобы добыть сущность Эфериона."}},
[13559] = {"Подношение Ходиру", nil, {"Требования:"}},
[13560] = {"Океан не столь глубок", nil, {"Горбольд Сталерук из Лор'данела просит вас поговорить с Гари и получить у него дальнейшие указания."}},
[13561] = {"Утешение для высокорожденного", nil, {"Убейте 6 проклятых высокорожденных и 6 терзаемых высокорожденных."}},
[13562] = {"Последние огни Башал'Арана", nil, {"Погасите последний огонь Башал'Арана."}},
[13563] = {"Вечная любовь", nil, {"Освободите душу Анайи Вестницы Зари и добудьте ее подвеску."}},
[13564] = {"Пропавший спутник", nil, {"Найдите Лютого Когтя."}},
[13565] = {"Уничтожить дважды", nil, {"Воспользуйтесь окаменелым корнем, чтобы поднять из небытия 6 иссхоших энтов, и убейте предводительницу наг Леди Жаниру."}},
[13566] = {"Ингредиенты для ритуала", nil, {"Добудьте 3 уса лунопарда, 3 клочка шерсти пятнистой оленихи и 3 клочка меха колючешерстного медведя."}},
[13567] = {"Дух оленя", nil, {"Хранитель Каритий велел вам получить благословение великого духа оленя."}},
[13568] = {"Дух лунопарда", nil, {"Хранитель Каритий велел вам получить благословение великого духа лунопарда."}},
[13569] = {"Ритуальное грозовое облако", nil, {"Вдохните эссенцию перед хранителем Каритием и примите благословение одного из великих духов: оленя, колючешерстного медведя или лунопарда.\n\nУчтите, что вы можете получить благословение только одного духа."}},
[13570] = {"Воспоминания об Аубердине", nil, nil},
[13571] = {"Бюро находок Флетчера", nil, nil},
[13572] = {"Жаровни Нефритового Пламени", nil, {"Грен Деримех из логова Чернолесья просит вас уничтожить 8 жаровен Нефритового Пламени."}},
[13573] = {"Возвращение Малфуриона", nil, {"Найдите Малфуриона Ярость Бури в Оке Урагана, что к юго-востоку от лагеря беженцев на Темных берегах."}},
[13575] = {"Эта земля в их крови", nil, {"Поговорите со старейшиной Карелапом в Лагере Чернолесья к юго-западу от Ока урагана."}},
[13576] = {"Взаимная помощь", nil, {"Воспользуйтесь успокаивающим тотемом, чтобы поглотить останки 8 вольных духов огня."}},
[13577] = {"Последний совух", nil, {"Поговорите с Арумом в Осененной луной берлоге к северу от Ока Урагана."}},
[13578] = {"Прощание с Аруном", nil, {"Добудьте 8 перьев убитых совухов."}},
[13579] = {"Защитник Амет'Арана", nil, {"Поговорите с Селенном на руинах Амет'Арана."}},
[13580] = {"Успокоение стихий", nil, {"Поместите заряженный успокаивающий тотем на вершину скалы рядом с Искристым берегом и защищайте его, пока ритуал не будет завершен."}},
[13581] = {"Дар Чернолесья", nil, {"Вернитесь к Малфуриону Ярость Бури в Око Урагана."}},
[13582] = {"Огонь Элуны", nil, {"Добудьте факел Элуны у Хору Хранителя пламени."}},
[13583] = {"Клятва совуха", nil, {"Вернитесь к Малфуриону Ярость Бури в Око Урагана."}},
[13584] = {"Колыбельная для Земли", nil, {"Убейте 8 разъяренных элементалей земли."}},
[13585] = {"Клятва защитника", nil, {"Поговорите с Малфурионом Яростью Бури в Оке Урагана, что на Темных берегах."}},
[13586] = {"Изумрудный сон", nil, {"Пройдите сквозь портал кошмаров в Пещеру землекрушителя и поговорите с Тессерой."}},
[13587] = {"Ходячий кошмар", nil, {"Добудьте изумрудный свиток у стража кошмара в Пещере землекрушителя."}},
[13588] = {"Око бурь", nil, {"Оседлайте Тессеру и убейте 12 наездников из культа Сумеречного Молота, а также уничтожьте сумеречный портал."}},
[13589] = {"Захватчики из племени Пронзающего Копья", nil, {"Разыщите лейтенанта Морру Звездный Ветер на руинах Матистры."}},
[13590] = {"Передовая", nil, {"Доложите Охотнице Сандрии Закат Луны, которую можно найти в долине Пронзающего Копья."}},
[13591] = {"Странные совпадения", nil, {"Принесите Послание Адского Крика стрелку Глинде Нал'Шие в Лор'данел."}},
[13592] = {"Теория и практика", nil, {"Убейте 10 обращенных героев по заданию сэра Маркуса Барлоу из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13593] = {"Искатель славы из Штормграда", nil, {"Если вы хотите стать искателем славы Штормграда, поговорите еще раз с маршалом Якобом Алерием в Павильоне Серебряного союза."}},
[13594] = {"Не забывайте про Орду", nil, {"Убейте 10 ордынцев в Убежище Батрана."}},
[13595] = {"Ни пряди волос", nil, {"Бросьте бутыль буйного огня на тело Батрана и сожгите его."}},
[13596] = {"Планы Сумеречного Молота", nil, {"Добудьте 6 планов Сумеречного Молота из их лагеря."}},
[13597] = {"Дух колючешерстного медведя", nil, {"Хранитель Каритий велел вам получить благословение великого духа колючешерстного медведя."}},
[13598] = {"Горькое лекарство", nil, {"Разыщите 6 дымных поганок."}},
[13599] = {"Возвращение Лютого Когтя", nil, {"Вернитесь к Волкору и Лютому Когтю в Лор'данел."}},
[13600] = {"Достойное оружие", nil, {"Принесите меч Драк'Мара маршалу Якобу Алерию в Павильон Серебряного союза на Ристалище Серебряного турнира."}},
[13601] = {"На помощь беженцам", nil, {"Часовой Лендра из Лор'данела просит вас поговорить с жрицей Алинией в Лагере беженцев."}},
[13602] = {"Наги Зорамского взморья", nil, {"Убейте 15 наг из клана Зловещего Хвоста на Зорамском взморье."}},
[13603] = {"Клинок, достойный чемпиона", nil, {"Раздобудьте меч Ясеневого озера и принесите его маршалу Якобу Алерию в Павильон Серебряного союза на Ристалище Серебряного турнира."}},
[13604] = {"Диск доступа к Архиву", nil, {"Отнесите диск доступа к Архиву к панели управления Архивом в Ульдуаре."}},
[13605] = {"Последний выживший", nil, {"Сопроводите археолога Холли в безопасное место."}},
[13606] = {"Печать Фрейи", nil, {"Добудьте печать Фрейи по заданию геолога Дорена.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13607] = {"Священный планетарий", nil, {"Найдите Священный планетарий по заданию геолога Дорена из Архива в Ульдуаре."}},
[13609] = {"Печать Ходира", nil, {"Добудьте печать Ходира по заданию геолога Дорена.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13610] = {"Печать Торима", nil, {"Добудьте печать Торима по заданию геолога Дорена.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13611] = {"Печать Мимирона", nil, {"Добудьте печать Мимирона по заданию геолога Дорена.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13612] = {"Защита лагеря Мор'Шан", nil, {"Кадрак с заставы Мор'шан поручил вам убить 5 застрельщиков Ясеневого леса и 5 стрелков Ясеневого леса."}},
[13613] = {"Спасение павших", nil, {"Исцелите врачебным зельем 5 раненых защитников заставы Мор'шан."}},
[13614] = {"Алгалон", nil, {"Принесите печати хранителей к панели управления Архивом в Ульдуаре."}},
[13615] = {"Пустой колчан", nil, {"Труун с заставы Мор'шан поручил вам собрать 10 исправных стрел вокруг Зеркала Небес."}},
[13616] = {"Клинок Зимы", nil, {"Маршал Якоб Алерий из Павильона Серебряного союза на Ристалище Серебряного турнира просит вас принести Клинок Зимы."}},
[13617] = {"К западу от Пучины", nil, {"Поговорите с Таленом в лагере у Непроглядной Пучины в Ясеневом лесу."}},
[13618] = {"Найти Гората!", nil, {"Кадрак с заставы Мор'шан попросил вас обойти Зеркало Небес в поисках Гората."}},
[13619] = {"Последний отчет", nil, {"Горат попросил вас отнести пузырек с его кровью Кадраку на заставу Мор'шан."}},
[13620] = {"Наконец-то, к Дине!", nil, {"Кадрак просит вас отнести пузырек с кровью Гората Дине на заставу Мор'шан."}},
[13621] = {"Месть Гората", nil, {"Окропите тело Гората его густой кровью и убейте капитана Элендилада."}},
[13622] = {"Древняя история", nil, {"Принесите расколотый Вал'анир к панели управления в Архив Ульдуара."}},
[13623] = {"Посылка для Орендила", nil, {"Доставьте мешочек с Батрановым волосом Орендилу Широколисту на заставу Мейстры, что в Ясеневом лесу."}},
[13624] = {"Отряд под твоим командованием", nil, {"Часовой Ойнея отдает под ваше командование отряд стражников заставы Мейстры, чтобы вы убили 3 крушителей из Ясеневого леса и 10 захватчиков из Орды."}},
[13625] = {"Большая тренировка", nil, {"Отработайте прием \"Выпад\" 5 раз на манекене для ближнего боя, прием \"Удар по щиту\" 2 раза на мишени для дальнего боя и прием \"Рывок\" 2 раза на манекене для стремительной атаки, затем вернитесь к разведчице Шалиндрии на Ристалище Серебряного турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в Павильоне Серебряного союза."}},
[13626] = {"Последние почести", nil, {"Добудьте священный молот Фиро и молитвенник Очистителя."}},
[13627] = {"Откуда дровишки?", nil, {"Принесите 12 грубо обтесанных досок Беззлу Рвизасову на Ристалище Серебряного турнира."}},
[13628] = {"Где бревна?", nil, {"Труун поручил вам оседлать Жестокого Бивня и отправиться в рабочий лагерь Песни Войны.\n\nЧтобы получить поводья для управления Жестоким Бивнем, поговорите с Кадраком."}},
[13630] = {"Главная причина", nil, {"Отключите синий, зеленый и красный лунные камни."}},
[13631] = {"Все хорошо, что хорошо кончается", nil, {"Доставьте код отклика \"Альфа\" Ронину в Даларан."}},
[13632] = {"Служба в арьергарде", nil, {"Убейте 10 фурболгов на руинах Ордил'Арана."}},
[13633] = {"Черный рыцарь из Западного Края?", nil, {"Узнайте правду о Черном рыцаре и поделитесь информацией с рыцарем Райдаллой на Ристалище Серебряного турнира."}},
[13634] = {"Черный рыцарь из Серебряного бора?", nil, {"Узнайте правду о Черном рыцаре и поделитесь информацией с рыцарем Райдаллой на Ристалище Серебряного турнира."}},
[13635] = {"Отчет Южных врат", nil, {"Доставьте отчет пилота Большенога капитану Ругельфассу в Долину Королей."}},
[13636] = {"Заказы для Грозовой Вершины", nil, {"Доставьте заказы для Грозовой Вершины горному пехотинцу Грозовой Вершине на станцию Альгаз."}},
[13637] = {"Доставка барана", nil, nil},
[13638] = {"Плененный горный пехотинец", nil, {"Капитан Ругельфасс в Телсамаре просит вас отыскать горного пехотинца Булыжного Карьера."}},
[13639] = {"Снабжение экспедиции", nil, {"Найдите Хальдара, Мирана и Сайана."}},
[13640] = {"Руководящий состав", nil, {"Заставьте работать несобранных батраков и соберите 5 вязанок свежесрубленного леса."}},
[13641] = {"Кристалл провидца", nil, {"Принесите кристалл провидца рыцарю Райдалле в Серебряный павильон на Ристалище Серебряного турнира."}},
[13642] = {"Свет Элуны", nil, {"Создайте Омытое светом зелье."}},
[13643] = {"История погибших", nil, {"Воспользуйтесь кристаллом провидца перед могилами сэра Венделла Балфура, Лориена Блеска Солнца и Коналла Железной Хватки, узнайте причину их гибели и доложите рыцарю Райдалле из Серебряного павильона."}},
[13644] = {"Тот, кто был воскрешен", nil, {"Убейте предвестника Афотика."}},
[13645] = {"К Раэне Волкопас", nil, {"Отнесите светящийся самоцвет Раэне Волкопас в Астранаар, что в Ясеневом лесу."}},
[13646] = {"Связь с Астранааром", nil, {"Поговорите с часовым Луарой в Астранааре, что в Ясеневом лесу."}},
[13647] = {"Присоединение к охоте", nil, {"Поговорите с Мареком Железносердом в Приюте Странников."}},
[13648] = {"РАЗЫСКИВАЕТСЯ: шпион Черного Железа", nil, {"Убейте Горика Пивохлеба."}},
[13649] = {"Веселая проверка Джастина", nil, {"Попадите кружкой с бесплатным пивом в С.С.М. 5 раз, затем поговорите с Бокси Болтокрутом."}},
[13650] = {"Руки прочь от имущества!", nil, {"Проверьте состояние следующих артефактов: опрокинутого великана, нарядной женщины и разбитой скрижали."}},
[13651] = {"Надо немного смазки", nil, {"Горка из рабочего лагеря Песни Войны просит вас собрать 5 порций слизи в Ясеневом лесу."}},
[13652] = {"Защита Каменной Плотины", nil, {"Найдите главного инженера Плотиннера VII на Каменной Плотине."}},
[13653] = {"Кризис на заставе", nil, {"Горка просит вас сопроводить ее к заставе Мор'шан и отчитаться перед Кадраком.\n\nОкликните ее, когда вы будете готовы отправиться в путь."}},
[13654] = {"Странный оруженосец", nil, {"Добудьте большую бедренную кость дровосека-скелета и оглушите Малорика. Обыщите его и доложите обо всем рыцарю Райдалле из Серебряного павильона на Ристалище Серебряного турнира."}},
[13655] = {"Документ Лиги исследователей (2 из 6)", nil, {"Верните документ Лиги исследователей Торрену Квадратной Челюсти в Телсамар."}},
[13656] = {"Документ Лиги исследователей (1 из 6)", nil, {"Верните документ Лиги исследователей Торрену Квадратной Челюсти в Телсамар."}},
[13657] = {"Документ Лиги исследователей (3 из 6)", nil, {"Верните документ Лиги исследователей Торрену Квадратной Челюсти в Телсамар."}},
[13658] = {"Документ Лиги исследователей (4 из 6)", nil, {"Верните документ Лиги исследователей Торрену Квадратной Челюсти в Телсамар."}},
[13659] = {"Документ Лиги исследователей (6 из 6)", nil, {"Верните документ Лиги исследователей Торрену Квадратной Челюсти в Телсамар."}},
[13660] = {"Документ Лиги исследователей (5 из 6)", nil, {"Верните документ Лиги исследователей Торрену Квадратной Челюсти в Телсамар."}},
[13661] = {"Сердечная признательность", nil, nil},
[13662] = {"Завоевать благосклонность", nil, {"Требования:"}},
[13663] = {"Приказы для Черного рыцаря", nil, {"Воспользуйтесь волшебной уздечкой, чтобы подчинить грифона Черного рыцаря и попасть к дому его хозяина. Как только вы попадете на место, найдите украденное приглашение на турнир и приказы для Черного рыцаря, после чего отнесите их рыцарю Райдалле в Серебряный павильон на Ристалище Серебряного Турнира."}},
[13664] = {"Падение Черного рыцаря", nil, {"Одержите победу над Черным рыцарем, после чего поговорите с рыцарем Райдаллой в Серебряном павильоне на Ристалище Серебряного турнира."}},
[13665] = {"Великая битва", nil, {"Принесите три знака искателя славы капитану Джозефу Холли в шатер Альянса на Ристалище Серебряного турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в Павильоне Серебряного союза."}},
[13666] = {"Клинок, достойный чемпиона", nil, {"Раздобудьте меч Ясеневого озера и принесите его чародею Таэлису в шатер Альянса на Ристалище Серебряного турнира."}},
[13667] = {"Серебряный Турнир", nil, {"Чтобы принять участие в турнире, поговорите с чародеем Таэлисом в шатре Альянса на Ристалище Серебряного турнира, он внесет ваше имя в списки."}},
[13668] = {"Серебряный Турнир", nil, {"Чтобы принять участие в турнире, поговорите с магистром Эдиеном Солнечной Низиной в шатре Орды на Ристалище Серебряного турнира, он внесет ваше имя в списки."}},
[13669] = {"Достойное оружие", nil, {"Принесите меч Драк'Мара чародею Таэлису в Павильон Серебряного союза на Ристалище Серебряного турнира."}},
[13670] = {"Клинок Зимы", nil, {"Чародей Таэлис из Павильона Серебряного союза на Ристалище Серебряного турнира просит вас принести Клинок Зимы."}},
[13671] = {"Тренировка на поле боя", nil, {"Убейте 8 воинов Плети в Ледяной Короне по заданию Аварета Молниеносного Удара из Павильона Серебряного союза на Ристалище Серебряного турнира. Вы можете найти их в любой области Ледяной Короны, за исключением зоны бомбардировки."}},
[13672] = {"Подготовка к испытанию", nil, {"Чтобы продемонстрировать свою готовность пройти испытание претендентов, покажите 15 печатей претендента чародею Таэлису из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13673] = {"Клинок, достойный чемпиона", nil, {"Раздобудьте меч Ясеневого озера и принесите его магистру Эдиену Солнечной Низине в Павильон Похитителей Солнца на Ристалище Серебряного турнира."}},
[13674] = {"Достойное оружие", nil, {"Принесите меч Драк'Мара магистру Эдиену Солнечной Низине в Павильон Похитителей Солнца на Ристалище Серебряного турнира."}},
[13675] = {"Клинок Зимы", nil, {"Магистр Эдиен Солнечная Низина из Павильона Похитителей Солнца на Ристалище Серебряного турнира просит вас принести Клинок Зимы."}},
[13676] = {"Тренировка на поле боя", nil, {"Убейте 8 воинов Плети в Ледяной Короне по заданию Амариэля Верного Солнцу из Павильона Похитителей Солнца на Ристалище Серебряного турнира. Вы можете найти их в любой области Ледяной Короны, за исключением зоны бомбардировки."}},
[13677] = {"Большая тренировка", nil, {"Отработайте прием \"Выпад\" 5 раз на манекене для ближнего боя, прием \"Удар по щиту\" 2 раза на мишени для дальнего боя и прием \"Рывок\" 2 раза на манекене для стремительной атаки, затем вернитесь к Галатии Сияющий Рассвет в шатер Орды на Ристалище Серебряного турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в Павильоне Похитителей Солнца."}},
[13678] = {"Подготовка к испытанию", nil, {"Чтобы продемонстрировать свою готовность пройти испытание претендентов, отдайте 15 печатей претендента магистру Эдиену Солнечной Низине из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[13679] = {"Испытание претендента", nil, {"Поговорите с оруженосцем Давидом, чтобы вызвать на поединок вашего противника. Как только вы победите искателя славы Серебряного Авангарда, вернитесь к чародею Таэлису в шатер Альянса на Ристалище Серебряного Турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в Павильоне Серебряного союза."}},
[13680] = {"Испытание претендента", nil, {"Поговорите с оруженосцем Давидом, чтобы вызвать на поединок вашего противника. Как только вы победите искателя славы Серебряного Авангарда, вернитесь к магистру Эдиену Солнечной Низине в Павильон Похитителей Солнца на Ристалище Серебряного Турнира."}},
[13681] = {"Обломки стен Ульдуара", nil, {"Принесите 15 небольших камней Боззлу Рвизасову на Ристалище Серебряного турнира."}},
[13682] = {"Атака с воздуха", nil, {"Эдрик Чистый из Серебряного павильона на Ристалище Серебряного турнира просит вас уничтожить Ледяную Пасть и 3 бомбистов-сектантов."}},
[13683] = {"Прервать ритуалы", nil, {"Уничтожьте 8 ритуальных самоцветов."}},
[13684] = {"Искатель славы из Штормграда", nil, {"Если вы хотите стать искателем славы Штормграда, поговорите с маршалом Якобом Алерием на Ристалище Серебряного турнира."}},
[13690] = {"Искатель славы из Экзодара", nil, {"Если вы хотите стать искателем славы Экзодара, поговорите с Колосусом на Ристалище Серебряного турнира."}},
[13691] = {"Искатель славы из Оргриммара", nil, {"Если вы хотите стать искателем славы Оргриммара, поговорите с Мокрой Дробителем Черепов на Ристалище Серебряного турнира."}},
[13692] = {"Меч из моря", nil, {"Старина Барло из Леса Хрустальной Песни просит вас принести ему Меч Рыцарства."}},
[13693] = {"Искатель славы из Сен'джина", nil, {"Если вы хотите стать искателем славы Сен'джина, поговорите с Зул'тором в Павильоне Похитителей Солнца на Ристалище Серебряного Турнира."}},
[13694] = {"Искатель славы из Громового Утеса", nil, {"Если вы хотите стать искателем славы Громового Утеса, поговорите с Руноком Буйногривым на Ристалище Серебряного турнира."}},
[13695] = {"Искатель славы из Подгорода", nil, {"Если вы хотите стать искателем славы Подгорода, поговорите со стражем смерти Визери на Ристалище Серебряного турнира."}},
[13696] = {"Искатель славы из Луносвета", nil, {"Если вы хотите стать искателем славы Луносвета, поговорите с Эрессеей Певицей Рассвета на Ристалище Серебряного турнира."}},
[13697] = {"Путь искателя славы", nil, {"Принесите 25 печатей искателя славы Мокре Дробителю Черепов в Павильон Похитителей Солнца на Ристалище Серебряного Турнира."}},
[13698] = {"Взрывчатые материалы на борту", nil, {"Уничтожьте 8 бочонков со взрывчаткой с помощью крошшера клана Песни Войны."}},
[13699] = {"Испытание искателя славы", nil, {"Поговорите с оруженосцем Дэнни, чтобы вызвать на поединок вашего противника. Как только вы победите чемпиона Серебряного Авангарда, вернитесь к маршалу Якобу Алерию на Ристалище Серебряного Турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в Павильоне Серебряного союза."}},
[13702] = {"Рождение героя", nil, {"Поговорите с вершительницей правосудия Мариэль Чистосердечной на Ристалище Серебряного турнира."}},
[13703] = {"Искатель славы из Стальгорна", nil, {"Если вы хотите стать искателем славы Стальгорна, поговорите еще раз с Ланой Твердомолот в Павильоне Серебряного Союза."}},
[13704] = {"Искатель славы из Гномрегана", nil, {"Если вы хотите стать искателем славы Гномрегана, поговорите еще раз с Амброзом Искрокрутом в Павильоне Серебряного Союза."}},
[13705] = {"Искатель славы из Экзодара", nil, {"Если вы хотите стать искателем славы Экзодара, поговорите еще раз с Колосусом в Павильоне Серебряного Союза."}},
[13706] = {"Искатель славы из Дарнаса", nil, {"Если вы хотите стать искателем славы Дарнаса, поговорите еще раз с Джейлин Закатной Песней."}},
[13707] = {"Искатель славы из Оргриммара", nil, {"Если вы хотите стать искателем славы Оргриммара, поговорите еще раз с Мокрой Дробителем Черепов в Павильоне Похитителей Солнца."}},
[13708] = {"Искатель славы из Сен'джина", nil, {"Если вы хотите стать искателем славы Сен'джина, поговорите еще раз с Зул'тором в Павильоне Похитителей Солнца."}},
[13709] = {"Искатель славы из Громового Утеса", nil, {"Если вы хотите стать искателем славы Громового Утеса, поговорите еще раз с Руноком Буйногривым в Павильоне Похитителей Солнца."}},
[13710] = {"Искатель славы из Подгорода", nil, {"Если вы хотите стать искателем славы Подгорода, поговорите еще раз со стражем смерти Визери в Павильоне Похитителей Солнца."}},
[13711] = {"Искатель славы из Луносвета", nil, {"Если вы хотите стать искателем славы Луносвета, поговорите еще раз с Эрессеей Певицей Рассвета в Павильоне Похитителей Солнца."}},
[13712] = {"Путь к спасению", nil, {"Поговорите с Кадраком на заставе Мор'шан и сопроводите его к Заставе Расщепленного Дерева."}},
[13713] = {"Испытание искателя славы", nil, {"Поговорите с оруженосцем Дэнни, чтобы вызвать на поединок вашего противника. Как только вы победите чемпиона Серебряного Авангарда, вернитесь к Лане Твердомолот в шатер Альянса на Ристалище Серебряного Турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в Павильоне Серебряного союза."}},
[13714] = {"Путь искателя славы", nil, {"Принесите 25 печатей искателя славы Лане Твердомолот в Павильон Серебряного союза на Ристалище Серебряного Турнира."}},
[13715] = {"Путь искателя славы", nil, {"Принесите 25 печатей искателя славы Амброзу Искрокруту в шатер Альянса на Ристалище Серебряного Турнира."}},
[13716] = {"Путь искателя славы", nil, {"Принесите 25 печатей искателя славы Колосусу в шатер Альянса на Ристалище Серебряного Турнира."}},
[13717] = {"Путь искателя славы", nil, {"Принесите 25 печатей искателя славы Джейлин Закатной Песне в шатер Альянса на Ристалище Серебряного Турнира."}},
