[13798] = {"Огненный ливень", nil, {"Бросайте проклятую руду с башен Заставы Расщепленного Дерева, чтобы обозначить цели Драаке. Убейте таким образом 30 бойцов ночных эльфов и 5 гневных древней.\n\nЕсли вам надо заново принять облик беса, обратитесь к Дурану."}},
[13799] = {"Where Are My Keys?", nil, {"Dr. Fingers wants you to kill his evil clone, Evil Fingers, and retrieve his keys."}},
[13801] = {"Голоса мертвых эльфов", nil, {"Уничтожьте 15 любых призраков ночных эльфов в Обители Дор'Данил."}},
[13802] = {"The Goblin with Two Brains", nil, {"Bring a Slobberfang Murloc Brain Stem to Professor Flozz along the coast."}},
[13803] = {"Кровь слабых", nil, {"Кадрак просит вас принести окровавленный топор Дурану на Заставу Расщепленного Дерева."}},
[13805] = {"Пронзи их сердце!", nil, {"Дуран хочет, чтобы вы окропили Сердце Леса оскверненной калдорайской кровью."}},
[13808] = {"Миссия немыслима", nil, {"Кадрак просит вас распылить секретный порошок вызова над тлеющей жаровне в Сатирнааре."}},
[13809] = {"Атака с воздуха", nil, {"Эдрик Чистый из Серебряного павильона на Ристалище Серебряного турнира просит вас уничтожить Ледяную Пасть и 3 бомбистов-сектантов."}},
[13810] = {"Битва на вражеской территории", nil, {"Убейте 15 членов Культа Проклятых в Ледяной Короне по заданию Селлиана Первого Луча из Серебряного павильона на Ристалище Серебряного турнира. Вы можете найти их в любой области Ледяной Короны."}},
[13811] = {"На равных с чемпионами", nil, {"Принесите 4 знака чемпиона Луури в павильон Серебряного союза на Ристалище Серебряного турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в павильоне Похитителей Солнца."}},
[13812] = {"Атака с воздуха", nil, {"Крок Гроза Плети с Ристалища Серебряного турнира просит вас уничтожить Ледяную Пасть и 3 бомбистов-сектантов."}},
[13813] = {"Битва на вражеской территории", nil, {"Убейте 15 членов Культа Проклятых в Ледяной Короне по заданию Зор'бе Кровопускателя с Ристалища Серебряного турнира. Вы можете действовать в любой области Ледяной Короны."}},
[13814] = {"На равных с чемпионами", nil, {"Принесите 4 знака чемпиона Иллирии Приходу Тьмы на Ристалище Серебряного турнира.\n\nЕсли вы потеряете копье, возьмите другое с любой оружейной стойки рядом со стойлами или в павильоне Похитителей Солнца."}},
[13815] = {"Лесоруб", nil, {"Крокк хочет, чтобы вы отправились на лесозаготовки Песни Войны и срубили там несколько дубов Ясеневого леса топором Расщепленного Дерева.\n\nЕсли вы вдруг потеряете секретный порошок вызова, поговорите с Кадраком на Заставе Расщепленного Дерева."}},
[13816] = {"Священный планетарий (героич.)", nil, {"Найдите Священный планетарий по заданию геолога Лорен из Архива в Ульдуаре."}},
[13817] = {"Диск доступа к Архиву (героич.)", nil, {"Отнесите диск доступа к Архиву к панели управления Архивом в Ульдуаре."}},
[13818] = {"Алгалон (героич.)", nil, {"Принесите печати хранителей к панели управления Архивом в Ульдуаре."}},
[13819] = {"Все хорошо, что хорошо кончается (героич.)", nil, {"Доставьте код отклика \"Альфа\" Ронину в Даларан."}},
[13820] = {"Семья Рвизасовов", nil, {"Поговорите с Беззлом Рвизасовом на Ристалище Серебряного турнира."}},
[13821] = {"Печать Фрейи (героич.)", nil, {"Добудьте печать Фрейи по заданию геолога Лорен.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13822] = {"Печать Ходира (героич.)", nil, {"Добудьте печать Ходира по заданию геолога Лорен.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13823] = {"Печать Торима (героич.)", nil, {"Добудьте печать Торима по заданию геолога Лорен.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13824] = {"Печать Мимирона (героич.)", nil, {"Добудьте печать Мимирона по заданию геолога Лорен.\n\nЗа дополнительными сведениями обратитесь к панели управления Архивом."}},
[13825] = {"Мидии с сюрпризом", nil, nil},
[13828] = {"Мастер ближнего боя", nil, {"Вооружитесь копьем и оседлайте кель'дорайского скакуна – он ждет вас снаружи павильона. Поговорите с Джераном Локвудом и отработайте прием \"Выпад\" 5 раз на манекене для ближнего боя. После этого вернитесь к чародею Таэлису в Павильон Серебряного союза на Ристалище Серебряного турнира."}},
[13829] = {"Мастер ближнего боя", nil, {"Вооружитесь копьем и оседлайте крылобега Похитителей Солнца – он ждет вас снаружи павильона. Поговорите с Джераном Локвудом и отработайте прием \"Выпад\" 5 раз на манекене для ближнего боя. После этого вернитесь к магистру Эдиену Солнечной Низине в шатер Орды на Ристалище Серебряного турнира."}},
[13830] = {"Рыба-призрак", nil, {"Раскройте тайну рыбы-призрака по просьбе Марсии Чейз из Даларана."}},
[13831] = {"Странное предписание", nil, {"Доставьте записи аптекаря Тарнариуну Исцелителю Деревьев в Лор'данел."}},
[13832] = {"Звезда сточных вод", nil, {"Принесите Марсии Чейз в Даларан ржавое украшение."}},
[13833] = {"Густая кровь", nil, {"Принесите Марсии Чейз в Даларан 5 ужрей-кровопийц."}},
[13834] = {"Опасный деликатес", nil, {"Принесите Марсии Чейз в Даларан 10 террорелей."}},
[13835] = {"Мастер дальнего боя", nil, {"Вооружитесь копьем и оседлайте кель'дорайского скакуна – он ждет вас снаружи павильона. Поговорите с Валисом Ловцом Ветра и отработайте \"Удар по щиту\" 2 раза на мишени для дальнего боя. После этого вернитесь к разведчице Шалиндрии на Ристалище Серебряного турнира."}},
[13836] = {"Обезручен!", nil, {"Принесите откушенную руку Олисарре Доброй в Даларан."}},
[13837] = {"Мастер стремительной атаки", nil, {"Вооружитесь копьем и оседлайте кель'дорайского скакуна – он ждет вас снаружи павильона. Поговорите с Раганом Сталебрюхом и отработайте \"Рывок\" 2 раза на манекене для стремительной атаки. После этого вернитесь к Аварету Молниеносному Удару в шатер Альянса на Ристалище Серебряного турнира."}},
[13838] = {"Мастер дальнего боя", nil, {"Вооружитесь копьем и оседлайте крылобега Похитителей Солнца – он ждет вас снаружи павильона. Поговорите с Валисом Ловцом Ветра и 2 раза отработайте \"Удар по щиту\" на мишени для дальнего боя. После этого вернитесь к Галатии Сияющий Рассвет в шатер Орды на Ристалище Серебряного турнира."}},
[13839] = {"Мастер стремительной атаки", nil, {"Вооружитесь копьем и оседлайте крылобега Похитителей Солнца – он ждет вас снаружи павильона. Поговорите с Раганом Сталебрюхом и отработайте прием \"Рывок\" 2 раза на манекене для стремительной атаки. После этого вернитесь к Амариэлю Верному Солнцу в шатер Орды на Ристалище Серебряного турнира."}},
[13840] = {"ПОВТОРНОЕ ИСПОЛЬЗОВАНИЕ", nil, {"Найдите цыплячий корм и вернитесь к загадочной курице. Когда найдете курицу, покажите ей, как вы рады ее видеть (эмоция \"/рад\"), а потом накормите специальным цыплячьим кормом."}},
[13841] = {"Искренние извинения", nil, {"Драака просит вас передать Гаррошу Адскому Крику объяснительную записку."}},
[13842] = {"Искупление", nil, {"Поговорите с Дураном в шахте Расщепленного Дерева."}},
[13843] = {"Комплект для сборки Хламобота", nil, {"Изучите данные панели управления прототипом в библиотеке Изобретателя."}},
[13844] = {"Сокровища Алталакса", nil, {"Найдите фолиант Нарассина среди руин Алталакса и убейте предводителя Отрекшихся Тигана Холловея."}},
[13845] = {"Запечатанный фиал с ядом", nil, {"Принесите запечатанный фиал с ядом алхимику Синесре в Стоки Даларана."}},
[13846] = {"Вклад в общее дело", nil, {"Поговорите с оруженосцем Арти и пожертвуйте 10 золотых на нужды Серебряного Авангарда."}},
[13847] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию капитана Джозефа Холли из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13848] = {"Плохие новости", nil, {"Кадрак просит вас поговорить с командиром Мрачный Клык на Зорамском взморье."}},
[13849] = {"Астранаар в огне!", nil, {"Потушите 15 пожаров."}},
[13850] = {"Иммунитет к яду", nil, {"Мор'век из Кратера Ун'Горо утверждает, что вам необходимо 20 раз отравиться кровью ядошкурого равазавра, чтобы выработать иммунитет к яду. Чтобы кровь равазавра попала на вашу кожу, нападите на него."}},
[13851] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Клары Взубногой из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13852] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Фликля Гайковерта из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13853] = {"Возвращение огня", nil, {"Подстрелите 15 геллионов Адского Крика и их ветрокрылов-наблюдателей с помощью метателя из Астранаара."}},
[13854] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Рании из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13855] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Айре Устремленной к Звездам из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[13856] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Моры Сестры Воргов из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[13857] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Гаджу из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[13858] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Анки Точеное Копыто из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[13859] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию Аниры Турон из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[13860] = {"У вражеских врат", nil, {"Убейте 15 костяных пехотинцев, 10 костяных разведчиков и 3 костяных лейтенантов по заданию дрессировщика Дреча из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[13861] = {"Битва у Цитадели", nil, {"По заданию Эдрика Чистого из Серебряного павильона на Ристалище Серебряного турнира убейте 3 костяных командиров."}},
[13862] = {"Битва у Цитадели", nil, {"По заданию Эдрика Чистого из Серебряного павильона на Ристалище Серебряного турнира убейте 3 костяных командиров."}},
[13863] = {"Битва у Цитадели", nil, {"По заданию Крока Грозы Плети с Ристалища Серебряного турнира убейте 3 костяных командиров."}},
[13864] = {"Битва у Цитадели", nil, {"По заданию Крока Грозы Плети с Ристалища Серебряного турнира убейте 3 костяных командиров."}},
[13865] = {"Мокрая работенка", nil, {"Крокк просит вас убить Доринара, Арминона, Эндолара и 12 разведчиков Ясеневого леса.\n\nЕсли вы вдруг потеряете секретный порошок вызова, поговорите с Кадраком на Заставе Расщепленного Дерева."}},
[13866] = {"К заставе!", nil, {"Поговорите с Кулгом Кровобрызгом на границе Ясеневого леса и Азшары, и он оправит вас на заставу Мор'шан. По прибытии немедленно разыщите Кадрака."}},
[13867] = {"Устранение угрозы фурболгов", nil, {"Убейте 15 фурболгов в деревне Колючего Меха."}},
[13868] = {"Искажающее влияние?", nil, {"Отнесите оберег троллей Раэне Волкопас в Астранаар, что в Ясеневом лесу."}},
[13869] = {"Вернуть останки", nil, {"Раздобудьте 10 штабелей древесины клана Песни Войны."}},
[13870] = {"Как нельзя лучше", nil, {"Крокк просит вас проникнуть на лесопилку Песни Войны и поговорить с надзирателем Гортаком."}},
[13871] = {"Безопасность превыше всего!", nil, {"Надзиратель Гортак просит вас принести ему голову калдорайской убийцы."}},
[13872] = {"Мудрость воргена", nil, {"Отнесите оберег троллей пилигриму Гефесту в Астранаар, что в Ясеневом лесу."}},
[13873] = {"Последняя просьба Шилы", nil, {"Страж Менерин просит вас отнести запечатанное послание Шилы стражу Гуртару."}},
[13874] = {"Жизнь без оберега", nil, {"Найдите 8 оберегов троллей."}},
[13875] = {"Просьба Гуртара", nil, {"Страж Гуртар просит вас отдать венок из кровавых чашечек Драаке с Заставы Расщепленного Дерева."}},
[13876] = {"Он зашел слишком далеко", nil, {"Найдите коллекцию Жилотяга."}},
[13877] = {"По течению", nil, {"Поговорите с Сабиной на южной границе лавового потока Громовой вершины, что в Ясеневом лесу."}},
[13878] = {"Сквозь огонь и воду", nil, {"Снимите цепи с волков в горящих стойлах."}},
[13879] = {"Громовая вершина", nil, {"Бройк поручил вам найти Стиквада у Громовой вершины."}},
[13880] = {"Горячая лава", nil, {"С помощью дара земли заполните 8 лавовых разломов."}},
[13881] = {"Одержимые", nil, {"Убейте 6 одержимых колючешерстных медведей и обследуйте озеро, из которого они пили."}},
[13882] = {"Семена жизни", nil, {"Соберите Семя Земли, Семя Моря и Семя Неба."}},
[13883] = {"Обломки кораблей", nil, {"Соберите 10 кусков затонувшего металлолома."}},
[13884] = {"Потушить огонь", nil, {"Убейте 10 лавовых яростеней. Обратитесь к Арктану, если вам потребуется еще один морозный волноход."}},
[13885] = {"В защиту Темных берегов", nil, {"Поговорите с Орсеем возле Рощи Древних и оседлайте гиппогрифа. Пролетая над рощей, защитите 8 серых колючешерстных медведей, 8 белохвостых оленей и лунопардов-матриархов и патриархов."}},
[13886] = {"Вихрь", nil, {"Убейте лорда Магматара с помощью Кружащегося вихря."}},
[13887] = {"Яйца ядошкурого равазавра", nil, {"Принесите Мор'веку 4 яйца ядошкурого равазавра. Он будет ждать вас на юго-востоке Топей."}},
[13888] = {"Вихрь", nil, {"Убейте лорда Магматара с помощью Кружащегося вихря."}},
[13889] = {"Бедный, голодный равазаврик", nil, {"Ваш равазаврик хочет съесть 15 кусков свежего мяса динозавра."}},
[13890] = {"Да будет вечное пламя", nil, {"Командир Мрачный Клык из Зорам'гара поручил вам залить жир гидры-тайнохлыста в огонь на маяке."}},
[13891] = {"Поглотитель Темных берегов", nil, {"Воспользуйтесь саженцем жизненоса на берегу обмелевшего озера к югу от Рощи Древних, чтобы уничтожить Всепожирающий артефакт и его хранителя."}},
[13892] = {"Не оставляй следов", nil, {"Проникните к Мечу Властителя, воспользовавшись магией статуэтки пантеры, и проследите за служителями культа Сумеречного Молота."}},
[13893] = {"Соггот и Кронн", nil, {"Вернитесь к Лариену в Рощу Древних и передайте ему полученную вами информацию."}},
[13895] = {"Дремлющие древни", nil, {"Найдите Ароса на северо-востоке от Рощи Древних на Темных берегах."}},
[13896] = {"Раскопанное знание", nil, {"Поговорите с археологом Гроффом на Раскопках Сомнамбулера."}},
[13897] = {"Битва за Темные берега", nil, {"Убейте аватара Соггота у Меча Властителя."}},
[13898] = {"Волны против нас", nil, {"Убейте 8 мирмидонов клана Темной Чешуи."}},
[13899] = {"Владыка Черной Чешуи", nil, {"Убейте Полководца Гневного Гребня в Назж'вел."}},
[13900] = {"Дар Азшаре", nil, {"Не дайте жрицам клана Темной Чешуи передать Рог Древних Азшаре над Назж'вел."}},
[13901] = {"Глубокое отчаяние", nil, {"Убейте 6 жриц прилива из Непроглядной Пучины."}},
[13902] = {"В наступление!", nil, {"Поговорите с Балреном-медведем в Роще Древних на Темных берегах."}},
[13903] = {"Обед из улья Гориши", nil, {"Ваш равазаврик проголодался. Принесите ему 10 кусков мяса силитида."}},
[13904] = {"Всмятку, в мешочек или сырые?", nil, {"Ваш равазаврик хочет, чтобы вы принесли ему 12 яиц силитида."}},
[13905] = {"Перья опаляющего руха", nil, {"Ваш равазаврик хочет, чтобы вы принесли ему 5 перьев опаляющего руха."}},
[13906] = {"Они так быстро растут", nil, {"Вернитесь к Мор'веку на юго-восток Топей в Кратер Ун'Горо со своим равазавриком, а также принесите 20 молочных зубов равазавра, 20 отрезов рунической ткани, 20 кусков грубой кожи и 80 золотых монет."}},
[13907] = {"Зачистка руин", nil, {"Убейте 10 оракулов племени Серой Мглы или беженцев из племени Серой Мглы возле Раскопок Сомнамбулера."}},
[13909] = {"Обломки для бездомных", nil, {"Соберите 5 подлежащих восстановлению частей строений Серой Мглы на берегу и в воде возле Раскопок Сомнамбулера."}},
[13910] = {"Новый дом", nil, {"Постройте лачугу для мурлоков в специальном месте на западе от раскопок рассеянного геолога."}},
[13911] = {"Рассеянный геолог", nil, {"Покажите геологу Сомнамбулеру, где лежит его журнал, после чего поговорите с археологом Фердом на раскопках."}},
[13912] = {"Тайны болота", nil, {"Найдите покрытый грязью древний диск среди ближайших руин."}},
[13913] = {"Они забрали наших гномов!", nil, {"Спасите Буга \"Моднявого\" и Минкс из фургона Бесконечной Агонии, принадлежащего мастеру пыток Тундраку."}},
[13914] = {"Перья опаляющего руха", nil, {"Ваш равазаврик хочет, чтобы вы принесли ему 5 перьев опаляющего руха."}},
[13915] = {"Бедный, голодный равазаврик", nil, {"Ваш равазаврик хочет съесть 15 кусков свежего мяса динозавра."}},
[13916] = {"Всмятку, в мешочек или сырые?", nil, {"Ваш равазаврик хочет, чтобы вы принесли ему 12 яиц силитида."}},
[13917] = {"Обед из улья Гориши", nil, {"Ваш равазаврик проголодался. Принесите ему 10 кусков мяса силитида."}},
[13918] = {"Терминал титанов", nil, {"Используйте обнаружитель затерянных артефактов на пляже возле Раскопок Сомнамбулера, чтобы найти 5 деталей артефакта. Соберите их вместе, чтобы получить древнее устройство с прорезью."}},
[13919] = {"Путешествие к Лунному колодцу", nil, {"Очистите Слезу Элуны в Лунном колодце чистоты, что в Ясеневом лесу."}},
[13920] = {"Прежде, чем уйти...", nil, {"Принесите 5 стейков из оленины командиру Мрачному Клыку в форт Зорам'гар."}},
[13921] = {"Тот, кто будет прощен", nil, {"Поговорите с Аврием Дурным Толком у башни в Ночных лесах, что в Ясеневом лесу."}},
[13922] = {"В оскверненных руках", nil, {"Добудьте 5 лунных камней Упавшего Неба."}},
[13923] = {"На заставу Адского Крика", nil, {"Поговорите с капитаном Гоггатом в дозоре Адского Крика."}},
[13924] = {"Все в порядке", nil, {"Отнесите освященные лунные камни Пельтуру Белой Луне в Астранаар, что в Ясеневом лесу."}},
[13925] = {"Подстелить соломку", nil, {"Испытайте саженец жизненоса на любом здоровом седом колючешерстном медведе, белохвостом олене, лунопарде-матриархе или патриархе."}},
[13926] = {"Ру, оракул-сиротка", nil, {"Поговорите с попечительницей сирот Арией; после этого воспользуйтесь детской свистулькой оракула, чтобы позвать сиротку, о котором вы будете заботиться во время Детской недели."}},
[13927] = {"Кекек, волчер-сиротка", nil, {"Поговорите с попечительницей сирот Арией, после этого воспользуйтесь детской свистулькой волчера, чтобы позвать сиротку, о котором вы будете заботиться во время Детской недели."}},
[13928] = {"Тела павших", nil, {"Соберите 10 трупов смеющихся сестер Халаннии."}},
[13929] = {"Самое большое дерево!", nil, {"Отведите вашего подопечного Ру к Седой Пасти. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13930] = {"Дом людей-медведей", nil, {"Отведите вашего подопечного Кекека к Седой Пасти. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13931] = {"Новый подарок к новому году", nil, {"Отоварьте свой купон на праздничную кружку у Бликса Чиништукса в лагере Хмельного фестиваля."}},
[13932] = {"Новый подарок к новому году", nil, {"Отоварьте свой купон на праздничную кружку у Бликса Чиништукса в лагере Хмельного фестиваля."}},
[13933] = {"Святилище бронзовых драконов", nil, {"Отведите вашего подопечного Ру к Бронзовому святилищу драконов. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13934] = {"Святилище бронзовых драконов", nil, {"Отведите вашего подопечного Кекека к Бронзовому святилищу драконов. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13935] = {"Защитить древо!", nil, {"Поговорите с Халаннией, чтобы призвать тень Тенумбры, и воспользуйтесь ее силой, чтобы уничтожить 50 врагов из клана Песни Войны в приюте в Ночных Лесах."}},
[13936] = {"Болван Шкрып", nil, {"Капитан Гоггат просит вас поговорить с Шкрыпом в Дозоре Адского Крика."}},
[13937] = {"Поездка в \"Чудо чудное\".", nil, {"Купите бумажный дирижаблик в магазине \"Чудо чудное\" и бросьте его вашему подопечному Ру."}},
[13938] = {"Заглянем в \"Чудо чудное\".", nil, {"Купите бумажный дирижаблик в магазине \"Чудо чудное\" и бросьте его вашему подопечному Кекеку."}},
[13940] = {"Пробуждение ото Сна", nil, {"Поговорите с Малфурионом Яростю Бури в Оке Урагана."}},
[13942] = {"Бомба на взводе", nil, {"Шкрып просит вас собрать 10 образцов омытой лунным светом глины."}},
[13943] = {"Превентивный удар", nil, {"Капитан Гоггат хочет, чтобы вы убили 10 застрельщиков Астранаара и 3 офицеров Астранаара."}},
[13944] = {"Малый рост, короткий запал", nil, {"Взорвите сломанный фургон в Дозоре Адского Крика с помощью импровизированной взрывчатки."}},
[13945] = {"Опасность обители", nil, {"Убейте 20 фурболгов Кривой Сосны внутри и вокруг обители Бен'этиль."}},
[13946] = {"Возмездие природы", nil, {"Используйте семена гневоплода, чтобы убить 12 греллей в Пещере Бесов."}},
[13947] = {"Адстранаар!", nil, {"Капитан Гоггат просит вас убить 20 часовых Астранаара и 10 метателей из Астранаара с помощью импровизированной взрывчатки Шкрыпа."}},
[13948] = {"В дозор", nil, {"Проникните к Мечу Властителя, воспользовавшись магией статуэтки пантеры, и проследите за предводителями культа Сумеречного Молота."}},
[13949] = {"Эскорт караванов до Перекрестка", nil, {"Поговорите с Халгой Кровавый Глаз на Дальней Заставе о сопровождении каравана Перекрестка до фермы Гроль'дома."}},
[13950] = {"Друзья детства!", nil, {"Отведите вашего подопечного Ру к Приюту Зимних Плавников. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13951] = {"Друзья детства!", nil, {"Отведите вашего подопечного Кекека на Поляну Снегопада. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13952] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу человека на кладбище Штормграда.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[13953] = {"Наги среди нас", nil, {"Вернитесь к Балрену-медведю в Рощу Древних в Темные берега."}},
[13954] = {"Королева драконов", nil, {"Отведите вашего подопечного Ру познакомиться с Алекстразой Связующей Жизнь, которая обитает на вершине храма Драконьего Покоя. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13955] = {"Королева драконов", nil, {"Отведите вашего подопечного Кекека познакомиться с Алекстразой Хранительницей Жизни, на вершину храма Драконьего Покоя. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13956] = {"Встреча с Великим", nil, {"Отведите вашего подопечного Ру к Этимидиану на Террасу Создателя. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13957] = {"Великий Хеминг Эрнестуэй", nil, {"Отведите вашего подопечного Кекека познакомиться с Хемингом Эрнестуэем в Лагерь Эрнестуэя. Не забудьте позвать сиротку, если его не окажется рядом, когда вы прибудете на место."}},
[13958] = {"Состояние критическое!", nil, {"Гоггат поручил вам отнести боевой перевязочный набор Таггу в приют в Ночных Лесах."}},
[13959] = {"Возвращение в приют", nil, {"Отведите вашего подопечного обратно в приют Даларана, отдав детскую свистульку оракула попечительнице сирот Арии."}},
[13960] = {"Возвращение в приют", nil, {"Отведите вашего подопечного обратно в приют Даларана, отдав детскую свистульку волчера попечительнице сирот Арии."}},
[13961] = {"Допросить их", nil, {"Используйте сеть Гроль'дома для поимки погромщика из племени Иглогривых и притащите его Тогрику на ферму Гроль'дома."}},
[13962] = {"Безвыходное положение", nil, {"Тагг из приюта в Ночных Лесах просит вас убить хранителя Ордануса."}},
[13963] = {"Всеми правдами и неправдами", nil, {"Допросите пленника из племени Иглогривых. Если он без сознания, Тогрик может привести его в чувство."}},
[13964] = {"К башне Звездной Пыли", nil, {"Поговорите с часовой Веленой Звездный Всполох в Башне Звездной Пыли."}},
[13965] = {"Весточка от Эдьюнов", nil, {"Поговорите с Бенджари Эдьюном в доме Эдьюнов, что в Ясеневом лесу."}},
[13966] = {"Подарок к Зимнему Покрову", nil, nil},
[13967] = {"Проредить... стадо?", nil, {"Сенани Громовое Сердце в приюте Серебряного Ветра просит вас принести Таркану 15 ушей фурболгов – тотемистов из племени Гиблой Чащи и ведмедей из племени Гиблой Чащи."}},
[13968] = {"Уничтожение Мучеклыка", nil, {"Уничтожьте Мучеклыка, предводителя Иглогривых."}},
[13969] = {"Пропавший кодо Гроль'дома", nil, {"Найдите пропавшего кодо Гроль'дома где-то на Тернистом холме."}},
[13970] = {"Животноводство", nil, {"Соберите 5 мешков украденного зерна в лагерях Иглогривых на Тернистом холме."}},
[13971] = {"Возвращение кодо", nil, {"Поговорите с Краналом Фиссом с фермы Гроль'дома, чтобы быть уверенным, что пропавший кодо благополучно прибыл."}},
[13973] = {"Ополчение Гроль'дома", nil, {"Убейте 6 погромщиков из племени Иглогривых."}},
[13974] = {"Маленькая посылка Шкрыпа", nil, {"Шкрып попросил вас отнести маленькую посылку Флузу в приют Серебряного Ветра."}},
[13975] = {"Доставка каравана в Перекресток", nil, {"Поговорите с Рокко Хлыстоногом с фермы Гроль'дома и проводите его караван до Перекрестка."}},
[13976] = {"Три друга леса", nil, {"Поговорите с Больюном в его лагере, что в Ясеневом лесу."}},
[13977] = {"Массовое производство", nil, {"Флуз просит вас отнести порошок \"Пиф-паф!\" Шкрыпа Джинксу в Производственный цех."}},
[13979] = {"Гоблинские эксперты", nil, {"Добудьте голову Бдыжня."}},
[13980] = {"Они где-то рядом!", nil, {"Джинкс из Производственного цеха просит вас уничтожить 12 убийц из Ясеневого леса."}},
[13981] = {"Они готовят бомбу!", nil, {"Доложите о существовании бомбы охотнице Джалин в Башне Звездной Пыли, что в Ясеневом лесу."}},
[13982] = {"В цепях", nil, {"Добудьте сердце Каливы."}},
[13983] = {"Гвозди для собственного гроба", nil, {"Джинкс просит вас собрать 3 бронзовых шестеренки, 3 куска медной обшивки и 5 зажимных болта."}},
[13985] = {"Очищение святилища", nil, {"Убейте 12 демонов в святилище Огненной Расщелины."}},
[13986] = {"Раненый товарищ", nil, {"Отнесите побитый фамильный щит Апони Светлой Гриве на Вершину Старейшин Громового Утеса."}},
[13987] = {"Последний рубеж", nil, {"Убейте 30 вторгшихся демонов."}},
[13988] = {"Растущая проблема", nil, {"Возьмите орла Глаза Тонги на Громовую Вершину на юго-западе от Кабестана."}},
[13989] = {"Король Гиблой Чащи", nil, {"Поместите знамя Кровавого Клыка на тотемном кургане Гиблой Чащи и не позвольте фурболгам уничтожить его, пока не появится вождь. Одолейте вождя Мургута."}},
[13991] = {"Похищенное серебро", nil, {"Поговорите с Газрогом у входа в Кабестан."}},
[13992] = {"Небольшая диверсия", nil, {"Убейте 8 кентавров из племени Колкар."}},
[13995] = {"Гора короля кентавров", nil, {"Защитите Горгала Злошрама, когда он нападет на кентавров. Помогите ему убить Курака."}},
[13997] = {"If You're Reading This While I'm In Combat...", nil, {"ELM Daze Target wants you to kill 5 ELM Attackers for Dan and Great Justice!"}},
[13998] = {"Веруем в грибы", nil, {"Посадите грибные культуры возле Застывшего оазиса и понаблюдайте за результатом."}},
[13999] = {"Кто кого дурит?", nil, {"Доставьте изыскания Хелбрима Сашие в Кабестан в Северных Степях."}},
[14001] = {"Гоблинские спасательные шлюпки", nil, {"Спасите 6 уцелевших гоблинов."}},
[14003] = {"Починка самофланжа", nil, {"Отнесите сломанный самофланж к Браку Трубореву в лагере."}},
[14004] = {"Вернуться к создателю самофланжа", nil, {"Отнесите переконструированные обломки самофланжа обратно Пароклапану в Лагерь Котельника. Чтобы попасть к нему, поговорите с Венаки."}},
[14005] = {"Месть Элуны", nil, {"Используйте данные Тариндреллой силы, чтобы пробраться к источнику порчи и уничтожить его."}},
[14006] = {"Руководство по эксплуатации", nil, {"Соберите руководство по эксплуатации самофланжа."}},
[14007] = {"\"Верный выстрел\"", nil, {"Поговорите с Баммом Взрыбоммом, обучитесь приему \"Верный выстрел\" и поразите тренировочный манекен 3 раза."}},
[14008] = {"Чародейские стрелы", nil, {"Поговорите с Физзом Лихтером и обучитесь заклинанию \"Чародейские стрелы\", после чего дважды опробуйте его на тренировочном манекене.\n\nПомните, что \"Чародейские стрелы\" можно использовать только с некоторой вероятностью после применения других атакующих заклинаний. Для того чтобы активировать \"Чародейские стрелы\", вначале используйте на тренировочном манекене заклинание \"Огненный шар\"."}},
[14009] = {"Быстрое исцеление", nil, {"Поговорите с сестрой Горазлатой и обучитесь заклинанию \"Быстрое исцеление\", после чего трижды используйте его на раненом сотруднике."}},
[14010] = {"Потрошение", nil, {"Поговорите с Крадли Дротик и обучитесь приему \"Потрошение\", после чего трижды опробуйте его на тренировочном манекене.\n\nПомните, что данный прием является продолжением серии, поэтому для начала потребуется применить против манекена умение \"Коварный удар\"."}},
[14011] = {"\"Стихийный удар\"", nil, {"Поговорите с Макссом Оползнем и обучитесь приему \"Стихийный удар\", после чего дважды опробуйте его на тренировочном манекене."}},
[14012] = {"Жертвенный огонь", nil, {"Поговорите с Ехидом Опальчиком и обучитесь заклинанию \"Жертвенный огонь\", после чего трижды опробуйте его на тренировочном манекене."}},
[14013] = {"Рывок", nil, {"Поговорите с Воином-матиком NX-01 и обучитесь приему \"Рывок\", после чего опробуйте его на тренировочном манекене."}},
[14014] = {"Верните наши вещи!", nil, {"Добудьте 8 ящиков инструментов."}},
[14016] = {"Проклятие Черного рыцаря", nil, {"Рыцарь Райдалла из Серебряного павильона на Ристалище Серебряного Турнира просит вас осмотреть могилу Черного рыцаря."}},
[14017] = {"Судьба Черного рыцаря", nil, {"Перехватите приказы доктора Колера на Заставе Вестника Смерти, а затем вернитесь к рыцарю Райдалле в Серебряный павильон на Ристалище Серебряного Турнира."}},
[14019] = {"Связка спелых бананчиков", nil, {"Накормите 10 обезьян с гранатой, бросив им бананы с селитрой."}},
[14021] = {"Глубинная суть проблемы", nil, {"Проведите испуганного шахтера по Каджа'митовой пещере."}},
[14022] = {"Пиршество странников", nil, {"Поговорите с Грегори Табором – он стоит рядом с вратами Штормграда."}},
[14023] = {"Пряная хлебная запеканка", nil, {"Приготовьте 5 порций пряной хлебной запеканки и отнесите ее Джасперу Муру, который стоит возле праздничных столов у ворот Штормграда."}},
[14024] = {"Тыквенный пирог", nil, {"Испеките 5 кусков тыквенного пирога и принесите их Эдварду Уинслоу, который стоит возле праздничных столов Стальгорна."}},
[14028] = {"Клюквенный соус", nil, {"Приготовьте 5 порций клюквенного соуса и отнесите ее Джасперу Муру, который стоит возле праздничных столов у ворот Штормграда."}},
[14030] = {"Голодающие Дарнаса", nil, {"Приготовьте 5 порций хлебной запеканки и отнесите ее Исааку Аллертону, который стоит у входа в Дарнас."}},
[14031] = {"Фотоохота", nil, {"Фотокамерой ТКК с ультравспышкой сделайте снимки алтаря пигмеев и наскальных рисунков в трех пещерах."}},
[14033] = {"Засахаренный батат", nil, {"Приготовьте 5 порций засахаренного батата и отнесите их Грегори Табору, который стоит у ворот Штормграда."}},
[14034] = {"Припугнуть Фута", nil, {"Добудьте чертеж корабля у главного инженера Фута в таверне \"Сломанный киль\", что в Кабестане."}},
[14035] = {"Запеченная индейка", nil, {"Приготовьте 5 запеченных индеек и отнесите их Исааку Аллертону к вратам Дарнаса."}},
[14036] = {"Пиршество странников", nil, {"Поговорите с Майлсом Стендишом во внутреннем дворе Подгорода."}},
[14037] = {"Пряная хлебная запеканка", nil, {"Приготовьте 5 порций пряной хлебной запеканки и отнесите ее Вильяму Маллинсу, который стоит возле праздничных столов во внутреннем дворе Подгорода."}},
[14038] = {"Кто не с нами, тот против нас", nil, {"Прикрепите магнитную мину в самой тонкой части корпуса \"Непобедимого\"."}},
[14039] = {"Дом Калдорай", nil, {"Отнесите наполненный сосуд Тиранде Шелест Ветра в Дарнас."}},
[14040] = {"Тыквенный пирог", nil, {"Испеките 5 кусков тыквенного пирога и принесите их Фрэнсису Итону, который стоит у праздничных столов возле Оргриммара."}},
[14041] = {"Клюквенный соус", nil, {"Приготовьте пять порций клюквенного соуса и принесите их Докину Дальнему Лугу, который ждет у подъемника на западе Громового Утеса."}},
[14042] = {"Оружие и контрабанда", nil, {"Уничтожьте 5 Тераморских схронов боеприпасов на берегу."}},
[14043] = {"Засахаренный батат", nil, {"Приготовьте 5 порций засахаренного батата и принесите их Фрэнсису Итону, который стоит у праздничных столов возле Оргриммара."}},
[14044] = {"Нехватка продовольствия в Подгороде", nil, {"Приготовьте 5 порций пряной хлебной запеканки и 5 порций клюквенного соуса, и отнесите их Майлсу Стендишу к входу в Подгород."}},
[14045] = {"Найти барона Дольноберега", nil, {"Найдите упомянутого пирата барона Дольноберега и поговорите с ним."}},
[14046] = {"Потребности истинного барона", nil, {"Если вы доверяете барону Дольноберегу, поговорите с ним и освободите его от наручников."}},
[14047] = {"Запеченная индейка", nil, {"Приготовьте 5 запеченных индеек и принесите их Фрэнсису Итону к вратам Оргриммара."}},
[14048] = {"Слишком мало индеек", nil, {"Принесите 20 запеченных индеек Кэтрин Железный Чан к вратам Стальгорна."}},
[14049] = {"Самая необычная карта", nil, {"Возьмите татуированную голову у входа в лагерь Альянса."}},
[14050] = {"Богатство Газлоу", nil, {"Газлоу из Кабестана хочет, чтобы вы отправились в скалы над городом и выкопали его сокровище."}},
[14051] = {"Не забыть про запеканку!", nil, {"Принесите 20 порций пряной хлебной запеканки Кэтрин Железный Чан к праздничным столам у ворот Стальгорна."}},
[14052] = {"Встреча с Тони", nil, {"Поговорите с Тони Два Клыка на Острове Битв, что в Северных Степях."}},
[14053] = {"У нас опять кончился соус?", nil, {"Принесите 20 порций клюквенного соуса Эллен Мур к праздничным столам возле Штормграда."}},
[14054] = {"Проще пареной тыквы", nil, {"Принесите 20 кусков тыквенного пирога Мэри Аллертон к праздничным столам возле Дарнаса."}},
[14055] = {"Батат – значит, батат!", nil, {"Принесите 20 порций засахаренного батата Джасперу Муру к праздничным столам возле Штормграда."}},
[14056] = {"Там мрачный Гломп над златом чахнет", nil, {"Убейте Гломпа и добудьте сокровища Гломпа."}},
[14057] = {"Оружие. Нам нужно оружие", nil, {"Соберите 6 тяжелых дворфийских винтовок."}},
[14058] = {"Батат – значит, батат!", nil, {"Принесите 20 порций засахаренного батата Вильяму Маллинсу к праздничным столам над Подгородом."}},
[14059] = {"У нас опять кончился соус?", nil, {"Принесите 20 порций клюквенного соуса Роберте Картер, которая стоит возле праздничных столов во внутреннем дворе Подгорода."}},
[14060] = {"Проще пареной тыквы", nil, {"Принесите 20 кусков тыквенного пирога Мархаре Голдвит к праздничным столам возле Громового Утеса."}},
[14061] = {"Слишком мало индеек", nil, {"Принесите 20 запеченных индеек Ондани Большой Мельнице к вратам Оргриммара."}},
[14062] = {"Не забыть про запеканку!", nil, {"Принесите 20 порций пряной хлебной запеканки Ондани Большой Мельнице к вратам Оргриммара."}},
[14063] = {"Мятеж!", nil, {"Используйте пиратский сигнальный рог, чтобы призвать Тони Два Клыка и его команду на \"Кинжал Девы\". Помогите ему справиться с Алисией Катберт."}},
[14064] = {"Богатый пир", nil, {"Съешьте по 5 порций каждого из праздничных блюд с пиршественного стола, пока не ощутите дух пиршества."}},
[14065] = {"Богатый пир", nil, {"Съешьте по 5 порций каждого из праздничных блюд с пиршественного стола, пока не ощутите дух пиршества."}},
[14066] = {"Осмотр места преступления", nil, {"Найдите разбитую повозку каравана возле дороги между Кабестаном и Перекрестком и поищите вокруг улики."}},
[14067] = {"Похищенное серебро", nil, {"Убейте 8 яркочешуйчатых опустошителей, 4 яркочешуйчатых смертехвата и найдите украденное серебро."}},
[14068] = {"Охота на яфферов", nil, {"Используйте \"Ловуфку для яфферов\" Кала'мы для поимки 5 маленьких яфферов."}},
[14069] = {"Хороших работников трудно найти", nil, {"Усмирите 8 взбунтовавшихся троллей."}},
[14070] = {"Как вразумить должников", nil, {"Избейте Бруно Негорючего, Френки Скрипетельку, Джека Молотка и Судзи Магика."}},
[14071] = {"Кружим по кварталу", nil, {"Проедьтесь на хотроде и подвезите друзей – Туза, Гоббера и Иззи."}},
[14072] = {"Против Верога", nil, {"Найдите 4 документа оперативной информации кентавров в Застывшем Оазисе."}},
[14073] = {"Врата Смерти получает подкрепление", nil, {"Отправляйтесь с докладом к Регтару Вратам Смерти на его заставу к западу от Перекрестка, что в Северных Степях."}},
[14074] = {"Лапами кверху", nil, {"Верните 10 украденных окорочков долгонога Нараси Снежной Заре из Павильона Серебряного союза на Ристалище Серебряного турнира."}},
[14075] = {"Беспорядки в шахтах", nil, {"Убейте 6 роющих червей."}},
[14076] = {"Завтрак для чемпионов", nil, {"Найдите 4 яйца йормунгаров и принесите их Савинии Певице Преданий на Ристалище Серебряного турнира."}},
[14077] = {"Милосердие Света", nil, {"Воспользуйтесь Молитвенником исповедницы для проведения последнего обряда над телами 8 убитых туаликов, после чего вернитесь к Нараси Снежной Заре в шатер Альянса на Ристалище Серебряного турнира."}},
[14078] = {"Тотальная эвакуация", nil, {"Найдите лейтенанта Вальдена в северо-западном углу Торговой площади."}},
[14079] = {"Уроки верховой езды в Элвиннском лесу", nil, {"Верните это рекламное сообщение Рандалу Охотнику на лесопилку Восточной долины в Элвиннском лесу."}},
[14080] = {"Остановить нападение", nil, {"Нараси Снежная Заря из Павильона Серебряного союза на Ристалище Серебряного турнира просит вас убить 10 квалдиров."}},
[14081] = {"Уроки верховой езды в Лесах Вечной Песни", nil, {"Верните это рекламное сообщение Пераскамину в стойла Турона в Луносвете."}},
[14082] = {"Уроки верховой езды в Экзодаре", nil, {"Верните это рекламное сообщение Аалун, которая находится неподалеку от Экзодара на острове Лазурной Дымки."}},
[14083] = {"Уроки верховой езды в Дун Мороге", nil, {"Верните это рекламное сообщение Ультаму Громовому Рогу на ферму Янтарленов в Дун Мороге."}},
[14084] = {"Уроки верховой езды в Дун Мороге", nil, {"Верните это рекламное сообщение Бинджи Перосвисту в поселок Сталежара в Дун Мороге."}},
[14085] = {"Уроки верховой езды в Дарнасе", nil, {"Верните это рекламное сообщение Джартсаму в анклав Кенария в Дарнасе."}},
[14086] = {"Уроки верховой езды в Оргриммаре", nil, {"Верните это рекламное сообщение Кильдару на Аллею Чести в Оргриммаре."}},
[14087] = {"Уроки верховой езды в Мулгоре", nil, {"Верните это рекламное сообщение Кару Певцу Бури в деревню Кровавого Копыта в Мулгоре."}},
[14088] = {"Уроки верховой езды в Дуротаре", nil, {"Верните это рекламное сообщение Ксар'Ти в деревню Сен'джин в Дуротаре."}},
[14089] = {"Уроки верховой езды в Тирисфальских лесах", nil, {"Верните это рекламное сообщение Вельме Варнам в Брилл в Тирисфальских лесах."}},
[14090] = {"Гормоку нужны снобольды", nil, {"Воспользуйтесь утяжеленной сетью, чтобы поймать 8 последователей с Холмов Снежной Слепоты, и приведите их Савинии Певице Преданий в Павильон Серебряного союза на Ристалище Серебряного турнира."}},
[14091] = {"Дурной знак", nil, {"Вернитесь к принцу Лиаму Седогриву на Торговую площадь."}},
[14092] = {"Завтрак для чемпионов", nil, {"Принесите 4 яйца йормунгаров Тайлосу Вестнику Зари в шатер Орды на Ристалище Серебряного турнира."}},
[14093] = {"И разверзся ад", nil, {"Принц Лиам Седогрив приказал вам убить 6 озлобленных воргенов."}},
[14094] = {"Спасение припасов", nil, {"Спасите 4 ящика с припасами."}},
[14095] = {"Опознание останков", nil, {"Отнесите зуб кракена Крестоносцу Серебристый Рассвет в Серебряный павильон."}},
[14096] = {"На этот раз ты нарвался, Кул", nil, {"Спасите Кула Безрассудного и 4 пленных претендентов с Заставы Вестника Смерти и вернитесь к Нараси Снежной Заре в шатер Альянса на Ристалище Серебряного турнира."}},
[14098] = {"Эвакуация Торговой площади", nil, {"Помогите эвакуировать людей из 3 домов."}},
[14099] = {"Королевский приказ", nil, {"Поговорите с Гвен Армстед в северо-восточной части Военного квартала."}},
[14100] = {"Реликвия Служителей Земли", nil, {"Принесите четыре тотема Готуру Четыре Ветра в Оргриммар."}},
[14101] = {"Дроттинн Хротгар", nil, {"Верховный крестоносец Аделард из Серебряного павильона на Ристалище Серебряного турнира просит вас одолеть Дроттинна Хротгара."}},
[14102] = {"Призыватель туманов Йингвар", nil, {"Верховный крестоносец Аделард из Серебряного павильона на Ристалище Серебряного турнира просит вас одолеть Призывателя туманов Йингвара."}},
[14103] = {"Титановый порошок", nil, {"Требования:"}},
[14104] = {"Орнолф Меченый", nil, {"Верховный крестоносец Аделард из Серебряного павильона на Ристалище Серебряного турнира просит вас одолеть Орнолфа Меченого."}},
[14105] = {"Вестник смерти Карос", nil, {"Верховный крестоносец Аделард из Серебряного павильона на Ристалище Серебряного турнира просит вас одолеть вестника смерти Кароса."}},
[14106] = {"Na Kada's Quest Test", nil, {"Требования:"}},
[14107] = {"Судьба Павших", nil, {"Крестоносец Серебристый Рассвет из Серебряного павильона просит вас даровать покой 6 духам павших героев."}},
[14108] = {"Изгнать Кракена!", nil, {"Крестоносец Серебристый Рассвет просит вас оседлать серебряного гиппогрифа, бросить 8 пылающих копий в кракена Северного моря и поразить 6 квалдиров-пробудителей глубин.\n\nСеребряного гиппогрифа можно найти возле палатки Серебряного Авангарда на Ристалище Серебряного Турнира."}},
[14109] = {"Новый наряд", nil, {"Купите блестящие украшения у Гэппи Серебряного Зуба, новый стильный костюмчик у Забо и модные темные очки у Миссы Фарцовщицы в переулке Дельцов."}},
[14110] = {"Новый наряд", nil, {"Купите блестящие украшения у Гэппи Серебряного Зуба, новый стильный костюмчик у Забо и модные темные очки у Миссы Фарцовщицы в переулке Дельцов."}},
[14111] = {"Реликвия Служителей Земли", nil, {"Принесите четыре тотема Предсказателю Эанну в Стальгорн."}},
[14112] = {"Кстати, чем питаются йети?", nil, {"Савиния Певица Преданий из Павильона Серебряного союза на Ристалище Серебряного турнира просит вас принести ей 3 куска мяса акулы Северного моря."}},
[14113] = {"Банкет", nil, {"Наденьте свой потрясный прикид для вечеринки (темные очки, новый стильный костюмчик и блестящие украшения) и отправляйтесь развлекать 10 кутил из Кезана, собравшихся у бассейна в штаб-квартире ТКК."}},
[14115] = {"Пираты-наемники", nil, {"Убейте 12 пиратов-наемников, чтобы спасти свою карьеру."}},
[14116] = {"Незваный гость", nil, {"Поговорите с торговым принцем Галливиксом на верхнем этаже штаб-квартиры ТКК на Кезане."}},
[14117] = {"Глаза Ясеневого леса", nil, {"Убейте 8 разведчиков с заставы Талрендис."}},
[14118] = {"Оленина для солдат", nil, {"Соберите 15 больших кусков оленины, убив ослабших мохоногих маралов."}},
[14120] = {"Миллиард деревянных?!", nil, {"Поговорите с Хамми Кофельнагель в штаб-квартире ТКК на Кезане."}},
[14121] = {"Грабь награбленное", nil, {"Садитесь в хотрод, задавите 12 наемных грабителей и заберите у каждого украденные им вещи."}},
[14122] = {"Ограбление банка", nil, {"Взломайте хранилище Первого банка Кезана и заберите свои личные сбережения."}},
[14123] = {"Антикварные ценности", nil, {"Раздобудьте \"Ястреба Малди\", \"Гоблину Лизу\" и \"Платиновую бомбу\"."}},
[14124] = {"Каджа'митовые месторождения", nil, {"Соберите 12 кусков каджа'мита."}},
[14125] = {"Страховочный план", nil, {"Устройте перегрузку барахлящего генератора, включите прохудившуюся печку, уроните сигару на легковоспламеняющуюся кровать и запустите газобота, чтобы поджечь штаб-квартиру ТКК!"}},
[14126] = {"Сбережения", nil, {"Отдайте ваши сбережения торговому принцу Галливиксу на борту его яхты на Кезане."}},
[14127] = {"Возвращение высокорожденных?", nil, {"Доставьте приказы разведчика Аг'тору Кровопалу к задним вратам Оргриммара."}},
[14128] = {"Возвращение высокорожденных?", nil, {"Доставьте приказы разведчика Малинее Опустошительнице Небес на оргриммарскую пересадочную станцию ракетной дороги."}},
[14129] = {"Сбежавшие крошшеры!", nil, {"Атакуйте один из сбежавших крошшеров возле задних врат Оргриммара и заберитесь внутрь, когда он будет значительно поврежден."}},
[14130] = {"Друзья бывают разных цветов", nil, {"Найдите Эргла в руинах храма Арккоран."}},
[14131] = {"Собери мне чуть-чуть", nil, {"Соберите 10 бобов кауфи с растений в руинах храма Арккоран."}},
[14132] = {"Это просто невежливо!", nil, {"Убейте 10 макринни в руинах храма Арккоран."}},
[14134] = {"Древесина для подрядчика", nil, {"Воспользуйтесь перепрограммированным крошшером, чтобы собрать 6 штабелей азшарской древесины. Если вы потеряете свой крошшер, можно поймать и перепрограммировать другой."}},
[14135] = {"На деревья", nil, {"Воспользуйтесь циркулярной пилой крошшера, чтобы спилить азшарские молодые деревца и убейте 9 снайперов с заставы Талрендис, когда они появятся."}},
[14136] = {"Спасательная операция на море", nil, {"Гирана Окровавленная из Павильона Похитителей Солнца на Ристалище Серебряного турнира просит вас убить 8 квалдиров-берсерков и 3 квалдиров-гарпунеров, напавших на \"Огненный Ястреб\"."}},
[14138] = {"Бизнес – прежде всего", nil, {"Передайте поощрение Хамми штейгеру Промокаху в штаб-квартире ТКК на Кезане."}},
[14140] = {"Остановить нападение", nil, {"Гирана Окровавленная с Ристалища Серебряного турнира просит вас убить 10 квалдиров."}},
[14141] = {"Гормоку нужны снобольды", nil, {"Воспользуйтесь утяжеленной сетью, чтобы поймать 8 последователей с Холмов Снежной Слепоты, и приведите их Тайлосу Вестнику Зари в шатер Орды на Ристалище Серебряного турнира."}},
[14142] = {"На этот раз ты нарвался, Кул", nil, {"Спасите Кула Безрассудного и 4 пленных претендентов с Заставы Вестника Смерти и вернитесь к Гиране Окровавленной на Ристалище Серебряного турнира."}},
[14143] = {"Лапами кверху", nil, {"Верните 10 украденных окорочков долгонога Гиране Окровавленной из Павильона Похитителей Солнца на Ристалище Серебряного турнира."}},
[14144] = {"Милосердие Света", nil, {"Воспользуйтесь Молитвенником исповедницы для проведения последнего обряда над телами 8 убитых туаликов, после чего вернитесь к Гиране Окровавленной в шатер Орды на Ристалище Серебряного турнира."}},
[14145] = {"Кстати, чем питаются йети?", nil, {"Тайлос Вестник Зари из Павильона Похитителей Солнца на Ристалище Серебряного турнира просит вас принести ему 3 куска мяса акулы Северного моря."}},
[14146] = {"Защитить врата!", nil, {"Подрядчик Жаднюк просит вас поспешить к задним вратам Оргриммара и убить 20 ночных эльфов-налетчиков."}},
[14151] = {"Багровый рубин", nil, {"Линзи Черноболт просит вас успешно трансмутировать пять эпических драгоценных камней с помощью алхимии. Можно трансмутировать аметрин, око Зула, страхолит, царский янтарь и величественный циркон."}},
[14152] = {"Спасательная операция на море", nil, {"Отправляйтесь в Северное море и убейте 8 врайкулов-берсерков и 3 врайкулов-гарпунеров, напавших на \"Гребень Волны\"."}},
[14153] = {"Банкет", nil, {"Наденьте свой потрясный прикид для вечеринки (темные очки, новый стильный костюмчик и блестящие украшения) и отправляйтесь развлекать 10 кутил из Кезана, собравшихся у бассейна в штаб-квартире ТКК."}},
[14154] = {"На расстоянии укуса", nil, {"В течение 2 минут сдерживайте натиск воргенов на крыше тюрьмы Гилнеаса."}},
[14155] = {"Арборицид", nil, {"Воспользуйтесь крошшером, чтобы уничтожить древня с заставы Талрендис. Если потеряете крошшер, можете воспользоваться запасным, его можно взять у задних врат Оргриммара."}},
[14157] = {"Старые распри", nil, {"Поговорите с капитаном Бродериком в Каменной тюрьме, что в Гилнеасе."}},
[14159] = {"Арсенал мятежного лорда", nil, {"Поговорите с Джосайей Авери в Военном квартале Гилнеаса."}},
[14160] = {"Список заслуг", nil, {"Доставьте Список заслуг Тимоти Джонсу в Даларан."}},
[14161] = {"Удар по василискам", nil, {"Убейте 10 серокаменных василисков в карьере у подножья горы."}},
[14162] = {"Отчитаться Хорзаку", nil, {"Найдите Хорзака Кругоглода на оргриммарской пересадочной станции ракетной дороги в Азшаре."}},
[14163] = {"Призыв к оружию: Остров Завоеваний", nil, {"Победите на поле боя Острова Завоеваний и вернитесь к бригадному генералу Альянса в любую из столиц Альянса, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14164] = {"Призыв к оружию: Остров Завоеваний", nil, {"Принесите почетные знаки каждого поля боя ордынскому вестнику войны в любую столицу Орды, на Озеро Ледяных Оков, в Даларан или Шаттрат."}},
[14165] = {"Хладный камень", nil, {"Найдите в карьере у подножья горы окаменевшего горняка и принесите его Хорзаку Кругоглоду на оргриммарскую пересадочную станцию ракетной дороги."}},
[14166] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому даларанскому духу на кладбище Даларана.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14167] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу дворфа на кладбище Стальгорна.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14168] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу гнома на кладбище Стальгорна.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14169] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу дренея на кладбище Экзодара.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14170] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу ночного эльфа на кладбище Дарнаса.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14171] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу эльфийки крови на кладбище Луносвета.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14172] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу Алдора на кладбище Шаттрата.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14173] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу Провидца на кладбище Шаттрата.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14174] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу Отрекшегося на кладбище Подгорода.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14175] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу орка на кладбище Оргриммара.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14176] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу таурена на кладбище Громового Утеса.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14177] = {"Благодарный покойник", nil, {"Принесите поминальный хлеб веселому духу тролля на кладбище Оргриммара.\n\nЧтобы увидеть дух, используйте оранжевые бархатцы."}},
[14178] = {"Призыв к оружию: Низина Арати", nil, {"Победите на поле боя Низины Арати и вернитесь к бригадному генералу Альянса в любую из столиц Альянса, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14179] = {"Призыв к оружию: Око Бури", nil, {"Победите на поле боя Ока Бури и вернитесь к бригадному генералу Альянса в любую из столиц Альянса, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14180] = {"Призыв к оружию: Ущелье Песни Войны", nil, {"Победите на поле боя Ущелья Песни Войны и вернитесь к бригадному генералу Альянса в любую из столиц Альянса, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14181] = {"Призыв к оружию: Низина Арати", nil, {"Победите на поле боя Низины Арати и вернитесь к ордынскому вестнику войны в любую из столиц Орды, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14182] = {"Призыв к оружию: Око Бури", nil, {"Победите на поле боя Ока Бури и вернитесь к ордынскому вестнику войны в любую из столиц Орды, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14183] = {"Призыв к оружию: Ущелье Песни Войны", nil, {"Победите на поле боя Ущелья Песни Войны и вернитесь к ордынскому вестнику войны в любую из столиц Орды, на Озеро Ледяных Оков, в Даларан или в Шаттрат."}},
[14184] = {"Мое время прошло…", nil, {"Выполняя последнюю волю Фуриена в Тефрис-Аране, садитесь на его дракондора, отправляйтесь на Пост Фуриена и расскажите его сестре Серелии о смерти брата."}},
[14188] = {"Месть за Фуриена", nil, {"По просьбе Серелии поговорите с Сорремом на Посту Фуриена."}},
[14189] = {"Расшифровка", nil, {"По поручению Соррема разыщите древние таблички.\n\nПобеседуйте с Наримаром на Посту Фуриена, чтобы отправиться туда, где Фуриен занимался своими исследованиями."}},
[14190] = {"Идеальная призма", nil, {"Снимите хрустальную подвеску с саботажника с заставы Талрендис в карьере у подножья горы."}},
[14191] = {"По следам Фуриена", nil, {"Следуя указаниям древних табличек в святилище Элуны, обойдите три места в Саргероне, где расположены самые примечательные руины."}},
[14192] = {"Призмолом", nil, {"Отнесите хрустальную подвеску в оружейный шкаф в штабе карьера у подножья горы."}},
[14193] = {"Пара дынь решит любую проблему", nil, {"Соберите 8 плодов дыни."}},
[14194] = {"Превоплощение", nil, {"Воспользуйтесь превоплотителем, чтобы оживить окаменевших горняков в карьере у подножья горы."}},
[14195] = {"Многое проясняется", nil, {"Воспользовавшись подсказкой, полученной из записей Фуриена, разыщите разрушенные чертоги Элуны на севере Саргерона."}},
[14196] = {"Поджигатель", nil, {"Создайте бушующий вихрь, убив 7 адептов Элуны рядом с огненным штормом.\n\nЕсли вы потеряете свой самоцвет вихря, возьмите еще один у древнего рунического камня вихря."}},
[14197] = {"Выполнить план", nil, {"Соберите 20 слитков железа в карьере у подножья горы в Азшаре."}},
[14198] = {"Всадник бушующего вихря", nil, {"Используйте силу самоцвета бушующего вихря на руинах Высоты Найджела. Призвав огненный шторм, убейте 40 защитников Высоты Найджела и сожгите 15 торговцев Высоты Найджела.\n\nЕсли вы потеряете свой самоцвет бушующего вихря, возьмите еще один у древнего рунического камня вихря."}},
[14201] = {"Тысяча историй на песке", nil, {"Соберите 5 эльфийских артефактов в руинах вокруг озера Меннар в Азшаре."}},
[14202] = {"Исследование озера", nil, {"Проводите гоблина-геодезиста к трем устройствам исследовательского оборудования, установленного вокруг озера Меннар, и защищайте его, пока он снимает показания. Другого геодезиста всегда можно вызвать при помощи маяка геодезиста."}},
[14204] = {"Из тени", nil, {"Убейте 6 скрытней из стаи Кровавых Клыков."}},
[14209] = {"Субстанция в двигателе", nil, {"Осмотрите бульдозер Дубника и соберите образцы неизвестной субстанции, которые засорили двигатель."}},
[14212] = {"Готовность к жертве", nil, {"Присоединитесь к лорду Дарию Кроули и убейте 30 ловцов из стаи Кровавого Клыка."}},
[14213] = {"Десять фунтов плоти", nil, {"Соберите 10 кусков плоти сатиров Саргерона и положите их на жаровню Элуны."}},
[14214] = {"Послание Седогриву", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14215] = {"Воспоминания мертвых", nil, {"Отнесите перстень Калифы на берег озера Меннар. Прикоснитесь к духу Калифы, чтобы пережить ее воспоминания. Поговорите с другими духами, пока не узнаете секреты озера Меннар."}},
[14216] = {"Загадка песчаникового камня", nil, {"Достаньте песчаниковый камень со дна озера Меннар."}},
[14217] = {"Жертвоприношение сатиров", nil, {"Соберите 10 кусков плоти сатиров Саргерона и положите их на жаровню Элуны."}},
[14218] = {"Кровь и пепел", nil, {"Убейте 80 ловцов из стаи Кровавого Клыка с помощью пушек повстанцев."}},
[14219] = {"Оружие поверженного врага", nil, {"Соберите 20 сабель сатиров – возьмите с оружейных стоек или отберите у сатиров, обитающих в этих краях, – и сложите их у подножия статуи прислужницы Элуны."}},
[14220] = {"Конец всего", nil, nil},
[14221] = {"Отступать, но не сдаваться", nil, {"Поговорите с лордом Дарием Краули в Соборе Рассвета."}},
[14222] = {"Последний рубеж", nil, {"Убейте 8 негодующих ловцов."}},
[14223] = {"Спокойный сон капитана", nil, {"Убейте Кохора по просьбе Соррема с Поста Фуриена."}},
[14225] = {"Ты поймешь, когда увидишь", nil, {"Найдите следы древнего ритуала призыва в крепости Громового Топора по просьбе Соррема с Поста Фуриена."}},
[14226] = {"Проблема под ногами", nil, {"Воспользуйтесь личным чародейским помощником, чтобы обратить в насекомых 12 демонов, а затем прихлопнуть их."}},
[14227] = {"Голова на голове", nil, {"Соберите 100 отбеленных черепов в Крепости Громового Топора, затем принесите их в круг призыва Пылающего Клинка, вызовите и убейте Назаргена."}},
[14230] = {"Физический труд", nil, {"Верните Справочник изменника из лагеря Легаш."}},
[14231] = {"Useless quest record", nil, {"Требования:"}},
[14232] = {"Надрать уши", nil, {"Принесите 25 ушей орков Пылающего Клинка Соррему на Пост Фуриена."}},
[14233] = {"Орки умеют писать?", nil, {"Отнесите полевой дневник Хамми Кофельнагель на Берег Кораблекрушения на Затерянных островах."}},
[14234] = {"Враг моего врага", nil, {"Доставьте полевой дневник Аггре в главный лагерь Орды на Затерянных островах."}},
[14235] = {"Гибельная долина", nil, {"Найдите Килага Жутеклыка в Гибельной долине на Затерянных островах."}},
[14236] = {"Газонокосилка", nil, {"С помощью газонокосилки в вашем гоблинском все-в-одном-чудо-поясе выкосите 100 растений в Гибельной долине."}},
[14237] = {"Движение вперед", nil, {"Найдите Килага Жутеклыка и его разведчиков на Лесном наблюдательном пункте на Затерянных островах."}},
[14238] = {"Инфракрасный = Инфрамертвый", nil, {"Убейте 10 убийц из ШРУ.\n\nЕсли с вашим орком-разведчиком что-то случится, вернитесь к Килагу – возможно, он выдаст вам другого напарника."}},
[14239] = {"Не иди к Свету!", nil, nil},
[14240] = {"К утесам", nil, {"Поговорите с разведчиком Браксом, который находится на Затерянных Островах, на тропе, ведущей к северу от Дикой лощины."}},
[14241] = {"К гиролету!", nil, {"Добудьте ключи от гиролета."}},
[14242] = {"Драгоценный груз", nil, {"Обнаружьте местонахождение драгоценного груза в трюме корабля Альянса \"Караул Возмездия\" у побережья Затерянных островов."}},
[14243] = {"Возмездие вождя", nil, {"С помощью вихря стихий убейте 50 матросов Альянса.\n\nКогда это будет сделано, вас перенесут к Лесному наблюдательному пункту."}},
[14244] = {"Вперед и вверх!", nil, {"Постарайтесь не погибнуть во время перелета через Зловещий пролив на ракете, запущенной из ракетной пращи."}},
[14245] = {"Городок-из-табакерки", nil, {"Заберитесь на гору взрывчатки и нажмите на поршень Городка-из-табакерки."}},
[14246] = {"Спасение перепелятников", nil, {"Разложите 3 яйца перепелятников по гнездам и защищайте яйца от хищников, пока птицы их не увидят и не заберут."}},
[14247] = {"Упрямые земли", nil, {"По поручению Тунка из Обители Тунка отыщите необычный фрагмент ландшафта."}},
[14248] = {"Требуется помощь!", nil, {"Поговорите со штейгером Промокахом у входа в Каджа'митовую пещеру на Затерянных островах."}},
[14249] = {"Приодеть Вилла", nil, {"Добудьте 80 нетронутых перьев громоголова у гиппогрифов возле руин Нордрессы."}},
[14250] = {"Возобновляемый ресурс", nil, {"Воспользуйтесь чародейским зарядом, чтобы получить 5 порций оживленного базальта от Бальбоа в руинах Нордрессы."}},
[14251] = {"Новое начало", nil, {"Соберите 15 жал скорпиона."}},
[14252] = {"Много золота за плохие хвосты", nil, {"Соберите 15 хвостов старых василисков."}},
[14253] = {"Оперите меня!", nil, {"Соберите 25 перьев жуткого перепелятника."}},
[14254] = {"Тяжелый металл", nil, {"Соберите 7 образцов элемента 116 и отнесите их Бизби из каравана Бизби."}},
[14255] = {"Этель-Ретор", nil, {"По поручению Серелии с Поста Фуриена отправляйтесь к Корре в Этель-Ретор."}},
[14256] = {"Угроза с моря", nil, {"Поговорите с Коррой и она вам покажет, чем опасны наги."}},
[14257] = {"Охота на нагов", nil, {"Соберите 10 костей наг, 30 образцов чешуи колдуний и 4 гребня воинов из клана Скользящего Плавника."}},
[14258] = {"Обстрел", nil, {"Принесите 5 снарядов для гоблинской мортиры из секретной лаборатории в Азшаре."}},
[14260] = {"Под маскировкой", nil, {"Используйте оберег Скользящего Плавника."}},
[14261] = {"Ледяной холод", nil, {"Воспользуйтесь Криохладом 16, чтобы убить 8 агатовых драконов."}},
[14262] = {"Выпотрошить рыбку", nil, {"Убейте 6 сынов штормов из клана Бича Злобы и 6 зовущих море из клана Бича Злобы."}},
[14263] = {"Расход тимьяна", nil, {"Соберите 8 пучков тимьяна живого гнева возле руин Нордрессы."}},
[14264] = {"Морской бой", nil, {"Убейте 5 слуг Нептулона. (Для того чтобы с ними сразиться, вы должны быть в обличье наги.)\n\nЕсли вы потеряете трезубец клана Скользящего Плавника, возвращайтесь к Валишж.\n\nЕсли вы потеряете оберег клана Скользящего Плавника, возвращайтесь к Корре в Этель-Ретор."}},
[14265] = {"Твой наставник", nil, {"Поговорите с сержантом Клизом в Военном квартале."}},
[14266] = {"Рывок", nil, {"Поговорите с сержантом Клизом и выучите способность \"Рывок\". Затем используйте ее на воргене из стаи Кровавых Клыков."}},
[14267] = {"Исследование святилища наг", nil, {"Исследуйте святилище наг для Торга Двудроба."}},
[14268] = {"Глубокая тайна", nil, {"По поручению жрицы Валишж разыщите древнюю гравировку Нептулона."}},
[14269] = {"Кто-то ищет тебя", nil, {"Поговорите с Лорен Укрывательницей в Военном квартале."}},
[14270] = {"Осколок краеугольного камня", nil, {"Найдите поблизости осколок краеугольного камня."}},
[14271] = {"Доложиться Двудробу", nil, {"Вернитесь к Торгу Двудробу на оргриммарскую южную конечную станцию ракетной дороги в Азшаре."}},
[14272] = {"Потрошение", nil, {"Поговорите с Лорен Укрывательницей и выучите способность \"Потрошение\". Затем убейте с помощью этой способности воргена из стаи Кровавых Клыков."}},
[14273] = {"Странные знакомства", nil, {"Поговорите с Витом Дарквокером в Военном квартале."}},
[14274] = {"Жертвенный огонь", nil, {"Поговорите с Витом Дарквокером и выучите заклинание \"Жертвенный огонь\". Потренируйтесь применять это заклинание на воргенах из стаи Кровавых Клыков."}},
[14275] = {"Кто-то следит за тобой", nil, {"Поговорите с ловчим Блейком в Военном квартале."}},
[14276] = {"Верный выстрел", nil, {"Поговорите с ловчим Блейком и обучитесь способности \"Верный выстрел\". Затем убейте с ее помощью двух воргенов из стаи Кровавых Клыков."}},
[14277] = {"Зов магии", nil, {"Поговорите с Мириам Пробудительницей Чар в Военном квартале."}},
[14278] = {"В поисках сестры", nil, {"Поговорите с сестрой Альмирой в Военном квартале."}},
[14279] = {"Быстрое исцеление", nil, {"Поговорите с сестрой Альмирой и выучите заклинание \"Быстрое исцеление\". Примените его дважды к раненому стражу."}},
[14280] = {"Ветра шепчут твое имя", nil, {"Поговорите с Целестиной Урожайной в Военном квартале."}},
[14281] = {"Чародейские стрелы", nil, {"Поговорите с Мириам Пробудительницей Чар и выучите заклинание \"Чародейские стрелы\". Убейте с его помощью воргена из стаи Кровавых Клыков."}},
[14282] = {"Раскрытая тайна", nil, {"Отнесите древнюю гравировку Нептулона кенарийской исследовательнице Корре."}},
[14283] = {"Омоложение", nil, {"Поговорите с Целестиной Урожайной и выучите заклинание \"Омоложение\". Воспользуйтесь им, чтобы помочь раненому стражу."}},
[14284] = {"Месть загробника", nil, {"Сядьте верхом на лорда Гидрониса и сразитесь со жрицей Валишж."}},
[14285] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14286] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14287] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14288] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14289] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14290] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14291] = {"Спасение в единстве", nil, {"Поговорите с королем Генном Седогривом в Военном квартале."}},
[14292] = {"Враг наших врагов…", nil, {"По поручению кенарийской исследовательницы Корры побеседуйте с Лордом Гидронисом."}},
[14293] = {"Спасти Креннана Аранаса", nil, {"Спасите Креннана Аранаса."}},
[14294] = {"Время перегруппироваться", nil, {"Поговорите с королем Генном Седогривом на площади Седогрива в Гилнеасе."}},
[14295] = {"Сестры моря", nil, {"Убейте леди Силистру и леди Вестру, уничтожив их камни силы."}},
[14296] = {"Смотри, куда идешь", nil, {"Получите 6 энергетических зарядов, прикасаясь к силовым проводам при подъеме к Волшебному замку."}},
[14297] = {"За освобождение", nil, {"Воспользуйтесь железнокованными ключами сектантов и драконов, чтобы освободить 4 гоблинов-рабочих на Черной гряде."}},
[14299] = {"Пристанище Ксилема", nil, {"Воспользуйтесь порталом в башню верховного мага Ксилема и найдите в башне Джоанну."}},
[14300] = {"Испытание огнем", nil, {"Пройдите испытание огнем, находясь среди огненных рун и не загоревшись в течение 10 последовательных вспышек."}},
[14301] = {"Возвращение с докладом", nil, {"Возвращайтесь в Этель-Ретор к кенарийской исследовательнице Корре."}},
[14302] = {"Рапорт начальству", nil, {"Отнесите отчет Корры Карнуму в рощу Карнума."}},
[14303] = {"Возвращение к Аггре", nil, {"Сообщите Аггре о том, что произошло. Она находится на Затерянных островах, в главном лагере Орды."}},
[14304] = {"Кровь на анализ", nil, {"Принесите 10 напившихся крови пиявок."}},
[14305] = {"Время пожинать", nil, {"Соберите 10 образцов дикого алоэ."}},
[14306] = {"Прополка", nil, {"Уничтожьте 7 выкорчеванных плеточников."}},
[14307] = {"Враждебный ветер", nil, {"Уничтожьте 5 вихревых штормоходов."}},
[14308] = {"Когда наука атакует", nil, {"Воспользуйтесь огнеустранителем Х-21, чтобы потушить 8 очагов пожара и спасти 6 студентов-исследователей из пекла в секретной лаборатории.\n\nЕсли вы потеряете ваш огнеустранитель, возьмите еще один у ассистентки Грили на южной конечной станции ракетной дороги."}},
[14309] = {"Успокоительное для кодо", nil, {"Напоите 5 разъяренных кодо успокаивающим настоем."}},
[14310] = {"Ошибка сегментации: дамп памяти сохранен", nil, {"Активируйте панель управления реактором и сопроводите азшаритовое ядро на южную конечную станцию ракетной дороги. Используйте огнеустранитель Х-21, когда ядро будет перегреваться."}},
[14311] = {"Чудо своими руками", nil, {"Посадите семена Кенария в 5 местах на плодородной земле в Кенарийских Зарослях."}},
[14312] = {"Позвольте представить", nil, {"Гаррен Темный Ветер на Просеке Карнума хочет, чтобы вы поговорили с ханом Ле'прахом."}},
[14313] = {"Снова среди людей", nil, {"Поговорите с Креннаном Аранасом в Темной Гавани."}},
[14314] = {"Побег из плена", nil, {"Уничтожьте стражей племени Маграм в деревне Маграм."}},
[14316] = {"Имущество кентавров", nil, {"Отыщите 7 ящиков с припасами Кенария в землях Маграм."}},
[14318] = {"Сложные переговоры", nil, {"Хан Ле'прах хочет, чтобы вы вонзили Копье хана Колкар в камень Отцов и защищали его от клана Маграм, пока не появится вождь Камма.\n\nПоговорите с ханом Ле'прахом, когда будете готовы отправиться на поле боя."}},
[14320] = {"В поисках ингредиентов", nil, {"Найдите ящик с эссенцией мандрагоры."}},
[14321] = {"Вторжение", nil, {"Поговорите с Гвен Армстед в Темной Гавани."}},
[14322] = {"Плохая наука! Плохая!", nil, {"Убейте 8 заряженных электричеством гиппогрифов."}},
[14323] = {"Абсорбент", nil, {"Соберите 20 капель кипящей воды из Коварного источника восточнее руин храма Арккоран."}},
[14324] = {"Горячая вода", nil, {"Получите магическую сферу кипящей воды от повелителя кипящей воды у башни Эльдары."}},
[14325] = {"Работа за еду", nil, {"Соберите 5 боков василиска."}},
[14326] = {"Встретимся наверху", nil, {"Проберитесь на верхнюю палубу \"Караула Возмездия\", который стоит у Затерянных островов, и поговорите с Траллом."}},
[14327] = {"Я даю слово", nil, {"Хан Камма хочет, чтобы вы поговорили с ханом Ле'прахом."}},
[14328] = {"Три принца", nil, {"Расправьтесь с Рохо, Шериком и Джорретом."}},
[14329] = {"Не так быстро!", nil, {"Хан Шодо хочет, чтобы вы убили 10 извергов из племени Мародин в Долине Копий.\n\nВы должны обыскать их тела в поисках подсказок о местонахождении Керры."}},
[14330] = {"За закрытыми дверьми", nil, {"Найдите Керру в стенах Марадона."}},
[14332] = {"Время перемен", nil, {"Керра хочет, чтобы вы поговорили с ханом Ле'прахом."}},
[14333] = {"Пока ты здесь", nil, {"Обыщите Долину Копий и найдите 6 оскверненных реликвий."}},
[14334] = {"Брюхажира", nil, {"Убейте Брюхажиру по заданию Май'Лахии."}},
[14335] = {"Дело нужное", nil, {"Убейте 5 крабов с мертвой хваткой и 5 жестколапых клешневиков."}},
[14336] = {"Убивай или будешь убит", nil, {"Поговорите с принцем Лиамом Седогривом. Вы найдете его рядом с Темной Гаванью."}},
[14337] = {"Деревня Ночных Охотников", nil, {"Поговорите с Май'Лахией по просьбе Карнума Ткача Болот."}},
[14338] = {"Застава Скитающихся Духов", nil, {"По поручению Карнума Ткача Болот поговорите с Маурином Костоломом."}},
[14339] = {"Тара для перевозки", nil, {"Соберите 3 полых рога умирающих кодо и отнесите их Маурину Костолому."}},
[14340] = {"Встречают по одежке", nil, {"Наденьте вашу новую шляпу волшебника и поговорите с проекцией верховного мага Ксилема. Его можно найти к северо-востоку от северной пересадочной станции ракетной дороги."}},
[14341] = {"Снаряжение Смида", nil, {"По поручению Натаки на Заставе Скитающихся Духов отнесите Смиду его снаряжение."}},
[14342] = {"Вторжение демонов", nil, {"По поручению Фелгура Бью-Двоих уничтожьте 10 младших инферналов."}},
[14343] = {"Зелье Маурина", nil, {"По поручению Маурина отнесите зелье Маурина Карнуму в Рощу Карнума."}},
[14344] = {"Уничтожение Джукгара", nil, {"Используйте источник мощи преисподней, чтобы взять под контроль лорда Азретока у алтаря Джукгара и использовать его силы для убийства Джугкара Грим'рода."}},
[14345] = {"Изматывающая учеба", nil, {"Поговорите с Соратой Жонглирующим Огнем на северной пересадочной станции ракетной дороги."}},
[14346] = {"Очищение нашей расселины", nil, {"По поручению Карнума Ткача Болот убейте 10 захватчиков и 10 сирен из клана Скользящего Плавника, а также трех морских великанов при помощи зелья Маурина."}},
[14347] = {"Держать строй!", nil, {"Убейте 10 отрекшихся-захватчиков."}},
[14348] = {"Одному не справиться", nil, {"Воспользуйтесь черными бочонками с порохом, чтобы взорвать 4 кошмарных поганища."}},
[14350] = {"Курьер из Багрового Легиона", nil, {"Возвращайтесь в Восточные Чумные земли и выследите курьера из Багрового Легиона. Убейте его и заберите приказ верховного рыцаря.\n\nЗа дальнейшими указаниями обращайтесь к Натаносу Гнилостню."}},
[14352] = {"Нечестивый союз", nil, {"Отнесите маленький свиток Брагору Кровопалу в Подгород."}},
[14354] = {"Дары Элуны", nil, {"Отправьтесь к реликвиям Элуны в Саргероне."}},
[14355] = {"В монастырь Алого ордена", nil, {"Убейте верховного инквизитора Вайтмейн, командира Алого ордена Могрейна, Ирода из Алого ордена и псаря Локси, после этого вернитесь с докладом к Брагору Кровопалу в Подгород."}},
[14356] = {"Силы разрушения...", nil, {"Принесите книги \"Заклинания Тьмы\" и \"Заклятия Пустоты\" Брагору Кровопалу в Подгород."}},
[14357] = {"Оружие поверженного врага", nil, {"Соберите 20 сабель сатиров – возьмите с оружейных стоек или отберите у сатиров, обитающих в этих краях."}},
[14358] = {"Десять фунтов плоти", nil, {"Добудьте 10 кусков плоти различных сатиров в Саргероне."}},
[14359] = {"Благословение свыше", nil, {"Добудьте 10 кусков плоти различных сатиров в Саргероне и возложите на жаровню Элуны."}},
[14360] = {"Пара дынь решит любую проблему", nil, {"Соберите 8 плодов дыни и выжмите их сок в кубок Элуны в Саргероне."}},
[14361] = {"Спокойный сон капитана", nil, {"Ликвидируйте Кохора."}},
[14362] = {"Надрать уши", nil, {"Добудьте 25 ушей орков Пылающего Клинка."}},
[14363] = {"Ты поймешь, когда увидишь", nil, {"Найдите следы древнего ритуала призыва в крепости Громового Топора."}},
[14364] = {"Голова на голове", nil, {"Соберите 100 отбеленных черепов в Крепости Громового Топора, затем принесите их в круг призыва Пылающего Клинка, вызовите и убейте Назаргена."}},
[14365] = {"Этель-Ретор", nil, {"Капрал Мелкис хочет, чтобы вы поговорили с Коррой у Этель-Ретора."}},
[14366] = {"Удержание позиций", nil, {"Поговорите с Гвен Армстед в Темной Гавани."}},
[14367] = {"Подвал Фермы Аллена", nil, {"Поговорите с лордом Годфри в подвале Фермы Аллена, что в Гилнеасе."}},
[14368] = {"Спасите детей!", nil, {"Спасите Синтию, Эшли и Джеймса, которые остались где-то на ферме Хэммондов."}},
[14369] = {"Звериная сила", nil, {"Убейте 8 рядовых или матросов Отрекшихся."}},
[14370] = {"Таинственный азшарит", nil, {"Соберите 5 образцов азшарита в ближайших скалах."}},
[14371] = {"Гигантская закуска", nil, {"Соберите 8 изуродованных туш скальных мокрокрылов."}},
[14372] = {"Лагерь Таргада", nil, {"Карнум Ткач Болот хочет, чтобы вы поговорили с Таргадом."}},
[14373] = {"Вторжение демонов", nil, {"Уничтожьте 10 младших инферналов."}},
[14374] = {"Порталы Легиона", nil, {"Закройте 6 порталов на поле Маннорок."}},
[14375] = {"Последняя капля человечности", nil, nil},
[14376] = {"Уничтожение Джукгара", nil, {"Используйте источник мощи преисподней, чтобы взять под контроль лорда Азретока у алтаря Джукгара и использовать его силы для убийства Джугкара Грим'рода."}},
[14377] = {"Дружба с великанами", nil, {"Поговорите с Гормунганом у южной конечной станции ракетной дороги и узнайте секрет азшарита."}},
[14378] = {"Охота на Брендола", nil, {"Отыщите сумку Брендола."}},
[14379] = {"Удар по лобстудару!", nil, {"Достаньте 7 сочных хвостов лобстудара."}},
[14380] = {"Впечатляющее открытие", nil, {"Отнесите странное зелье Карнуму на Просеку Карнума."}},
[14381] = {"Очищение нашей расселины", nil, {"Убейте 10 захватчиков и 10 сирен из клана Скользящего Плавника, а также трех морских великанов при помощи зелья Маурина."}},
[14382] = {"Двое у моря", nil, {"Воспользуйтесь катапультой Отрекшихся, чтобы попасть на корабли и убить капитанов – Морриса и Ансона."}},
[14383] = {"Ужасные механики Опустошенного берега", nil, {"Убейте Бингема Прибамбаса на Опустошенном берегу, а вместе с ним 4 гномов с сетеметами и 6 гномов с шокерами."}},
[14384] = {"Изменение маршрута!", nil, {"Офицер Дженки хочет, чтобы вы, используя ее грифона, добрались до Высоты Найджела и поговорили с капралом Мелкисом."}},
[14385] = {"Первый эксперимент с азшаритом", nil, {"Заберите гигантскую порцию слабительного из башни гномов на Опустошенном берегу. Попытайтесь накормить слабительным великана Гормунгана."}},
[14386] = {"Вожак стаи", nil, {"Выследите темного следопыта Тиалу и убейте ее с помощью мастифов."}},
[14387] = {"Дела на местности", nil, {"Капитан Пентигаст хочет, чтобы вы поговорили с Валлариэлем в Высоте Найджела."}},
[14388] = {"Второй эксперимент с азшаритом", nil, {"Сообщите ассистентке Грили, что вы готовы быть уменьшенным до размера крысы. Затем оседлайте крысу ракетной дороги и воспользуйтесь ею, чтобы напугать великана Гормунгана."}},
[14389] = {"Разве не очевидно?", nil, {"Воспользуйтесь порталом за спиной Джоанны в башне Ксилема, чтобы найти Анару целительницу душ и, возможно, Азурегоса."}},
[14390] = {"Когда легко – скучно", nil, {"Убедите Азурегоса прекратить отшельничество и встретиться с Калесгосом."}},
