[24438] = {"Исход", nil, {"Сядьте в дилижанс рядом с Поместьем Седогрива."}},
[24439] = {"Захват Азшары", nil, {"Убейте командира Джарродена на заставе Талрендис и заберите его голову."}},
[24440] = {"Очищение колодца Заиндевевшего Копыта", nil, {"Очистите колодец Заиндевевшего Копыта при помощи тотема очищения Заиндевевшего Копыта."}},
[24441] = {"Тотем Громового Рога", nil, {"Принесите 6 когтей охотников прерий и 6 когтей пум плоскогорья."}},
[24442] = {"Планы нападения квалдиров", nil, {"Добудьте Сердце тумана и принесите его вместе с планами нападения квалдиров Вершительнице правосудия Мариэль Чистосердечной на ристалище Серебряного Турнира."}},
[24443] = {"Плененный охотник", nil, {"Найдите у свинобразов из племени Дыбогривов грубый ключ и отоприте клетку, в которой держат Хону Охотницу на Оленей."}},
[24444] = {"Плененный шаман", nil, {"Найдите у свинобразов из племени Дыбогривов грубый ключ и отоприте клетку, в которой держат Мииру Громовое Копыто."}},
[24445] = {"Плененный друид", nil, {"Найдите Кешу Древесное Копыто."}},
[24446] = {"Плененный служитель солнца", nil, {"Найдите у свинобразов из племени Дыбогривов грубый ключ и отоприте клетку, в которой держат Боу Рассветного Рога."}},
[24448] = {"Перевод на поле боя", nil, {"Поговорите с капитаном Торком на передовой заставе в руинах Эльдарата."}},
[24449] = {"Увольнение на берег", nil, {"Доставьте сердце Арккорока дядюшке Бедламу в Гавань Трюмных Вод."}},
[24451] = {"Встреча с чародеем", nil, {"Возьмите книгу \"Древние клинки драконьей ковки\" у чародея Тибалина."}},
[24452] = {"Поиски прибыли", nil, {"Воспользуйтесь генератором поля невидимости, чтобы проникнуть в руины Эльдарата и обнаружить сердце Арккорока."}},
[24453] = {"Рядовой разговор", nil, {"Поговорите с рядовым Ворчестером на оргриммарской пересадочной станции ракетной дороги."}},
[24454] = {"Возвращение к Каладису Сияющему Копью.", nil, {"Отнесите книгу \"Древние клинки драконьей ковки\" Каладису Сияющему Копью в Покой Кель'Делара."}},
[24455] = {"Быстрая передислокация", nil, {"Явитесь к капитану Десото в Гавань Трюмных Вод для передислокации."}},
[24456] = {"Очищение колодца Громового Рога", nil, {"Очистите колодец Громового Рога при помощи тотема очищения Громового Рога."}},
[24457] = {"Обряд прозрения", nil, {"Поговорите с Зарельманом Две Луны в деревне Кровавого Копыта."}},
[24458] = {"Привет, оружие", nil, {"Доставьте боевое азшаритовое ядро Блинику Шипоплаву в Гавань Трюмных Вод. Поговорите с Фризом Штопором, чтобы он доставил вас туда."}},
[24459] = {"Морин Ловец Облаков", nil, {"Найдите Морина Ловца Облаков восточнее деревни Кровавого Копыта."}},
[24460] = {"Беспорядок", nil, {"Paul's Test Questgiver wants you to kill zombies."}},
[24461] = {"Перековать меч", nil, {"Каладис Сияющее Копье из Покоя Кель'Делара поручил вам найти пять заряженных саронитовых слитков и с помощью молота начальника кузни создать Перекованный Кель'Делар из потертой рукояти."}},
[24463] = {"Проникновение в Ясеневый лес", nil, {"Поговорите с Кулгом Кровобрызгом в Ясеневом лесу."}},
[24467] = {"Затемнение", nil, {"Убейте сумеречного лорда Катрану и Злобария на Черной гряде."}},
[24468] = {"Застрявшие в болоте", nil, {"Спасите 5 уцелевших в катастрофе в Хейлвудской трясине."}},
[24469] = {"Держать строй!", nil, {"Убейте 6 захватчиков из племени Каменной Челюсти."}},
[24470] = {"Задай им жару", nil, {"Убейте 3 недотеп из племени Каменной Челюсти."}},
[24471] = {"Помощь раненым", nil, {"Возьмите набор первой помощи Стена и перевяжите 4 раненых горных пехотинцев Холодной долины."}},
[24472] = {"Обманный трюк", nil, {"Убейте 4 огров-прислужников и украдите знамя Корота из его логова."}},
[24473] = {"Запертые в Старой Наковальне", nil, {"Поговорите с Йоной Железняк в Старой Наковальне."}},
[24474] = {"Первым делом, первым делом... пиво!", nil, {"Раздобудьте бочонок \"Молота бури\" крепкого, бочонок тераморского светлого эля и бочонок \"Гноменберга\"."}},
[24475] = {"Все остальное", nil, {"Раздобудьте 3 кабаньих окорока и 4 рваных волчьих шкуры."}},
[24476] = {"Закалить клинок", nil, {"Закалите перекованный Кель'Делар в горниле душ."}},
[24477] = {"Артефакты дворфов", nil, {"Соберите 5 забытых дворфийских артефактов."}},
[24478] = {"Испытание холодом", nil, {"Пройдите испытание холодом, собрав 20 порций субстанции Льда и избежав удара обжигающего холода."}},
[24479] = {"Испытание сумраком", nil, {"Пройдите испытание сумраком, заманив в руны тени 20 плачущих душ и не дав им коснуться вас."}},
[24480] = {"Залы Отражений", nil, {"Отнесите Кель'Делар в Покои Клинка, что в Залах Отражений."}},
[24483] = {"Грозовой Перевал", nil, {"Поговорите с Гвен Армстед в Грозовом Перевале."}},
[24484] = {"Контроль над паразитами", nil, {"Убейте 6 быстролапов Злобной Породы в Грозовом Перевале."}},
[24486] = {"Куй железо, пока горячо", nil, {"Раздобудьте 5 бесценных артефактов племени Каменной Челюсти у падальщиков из племени Каменной Челюсти."}},
[24487] = {"Белобороду нужна твоя помощь", nil, {"Отправляйтесь в лагерь Грелина Белоборода в Холодной долине."}},
[24488] = {"Замена запчастей", nil, {"Соберите 6 запчастей."}},
[24489] = {"В окружении троллей", nil, {"Встаньте рядом с ворожеей Мерзлогривов в каждом из лагерей Мерзлогривов и послушайте, что она скажет."}},
[24490] = {"Путешествие в Стальгорн", nil, {"Доложитесь Ручнику Ржавозубчику в Холодной долине."}},
[24491] = {"За этим гирокоптером!", nil, {"Поговорите с Мило Прищеменем рядом со Старой Наковальней."}},
[24492] = {"Пакуйте вещи", nil, {"Раздобудьте бутыль пива Холодной долины, рваный плащ из волчьей шкуры и остатки кабанины."}},
[24493] = {"Не забудьте про нас", nil, {"Доложитесь Тареку Чернокамню в Караносе."}},
[24494] = {"Наделенная силой руна", nil, {"Прочитайте наделенную силой руну, а затем поговорите с Тео Молотоштормом в Старой Наковальне."}},
[24495] = {"Тени прошлого", nil, {"Найдите 6 старых страниц с записями."}},
[24496] = {"Чародейская руна", nil, {"Прочитайте чародейскую руну, а затем поговорите с Тигли Веселинг в Холодной долине."}},
[24497] = {"Снова десант", nil, {"Обеспечьте безопасность полета в Храбростан с жрецами-десантниками в восточной части Гавани Трюмных Вод."}},
[24498] = {"Путь в Цитадель", nil, {"Освободите 15 порабощенных солдат Альянса и убейте начальника кузни Гархлада."}},
[24499] = {"Тени замученных душ", nil, {"Чтобы обеспечить безопасный проход в Яму Сарона, убейте Броньяма и Пожирателя Душ."}},
[24500] = {"Гнев Короля-лича", nil, {"Найдите леди Джайну Праудмур и выберитесь из Залов Отражений."}},
[24501] = {"Пра-проблемы", nil, {"Убейте Ригну в ее логове."}},
[24502] = {"Вынужденная неучтивость", nil, {"Заберитесь в \"Буканьер Трюмных Вод\", а затем метните футбомбные мячи в 8 \"Акул Хитрой Шестеренки\"."}},
[24503] = {"Гол!", nil, {"Забейте победный гол, запустив футбомбный мяч между двумя дымоходами за воротами соперников."}},
[24504] = {"Освобождение Верхнего пути", nil, {"Убейте 3 разведчиков Верхнего пути."}},
[24505] = {"Оборона Охотничьего холма", nil, {"Поговорите с Онатай на Охотничьем холме в Южных Степях."}},
[24506] = {"В ледяную цитадель", nil, {"Войдите в Кузню Душ со стороны Цитадели Ледяной Короны и найдите леди Сильвану Ветрокрылую."}},
[24507] = {"Путь в Цитадель", nil, {"Освободите 15 порабощенных солдат Орды и убейте начальника кузни Гархлада."}},
[24510] = {"В ледяную цитадель", nil, {"Войдите в Кузню Душ со стороны Цитадели Ледяной Короны и найдите леди Джайну Праудмур."}},
[24511] = {"Тени замученных душ", nil, {"Чтобы обеспечить безопасный проход в Яму Сарона, убейте Броньяма и Пожирателя Душ."}},
[24512] = {"Спасение пленников", nil, {"Возьмите 5 ящиков конфискованного оружия с Заставы Чести и вооружите им 5 узников Когтистых гор. Используйте веревки, чтобы быстро пробраться в лагерь Альянса и быстро исчезнуть оттуда."}},
[24513] = {"Око за око", nil, {"Убейте капитана Пика и принесите Олтаргу его глаза."}},
[24514] = {"Шкуры ящеров", nil, {"Добудьте 8 шкур страхокусов, убивая луговых страхокусов и зубастых страхокусов."}},
[24515] = {"Сигнал бедствия", nil, {"Отправляйтесь в Лагерь Уна'фе в Буйных зарослях в Южных Степях и поговорите с Макабой Плоским Копытом."}},
[24517] = {"Посеять среди них страх", nil, {"Убейте 8 пехотинцев Заставы Чести и 1 офицера Заставы Чести."}},
[24518] = {"Нижний путь", nil, {"Доставьте проекты дорог Килроку Кровавой Дубине на Охотничий холм в Южных Степях."}},
[24519] = {"Нейтрализация топографа", nil, {"Найдите и убейте топографа Генри Зьюкса."}},
[24520] = {"Добрые вести для Хамми", nil, {"Поговорите с Хамми Кофельнагель в штаб-квартире ТКК на Кезане."}},
[24522] = {"Путешествие к Солнечному Колодцу", nil, {"Поговорите с Халдароном Светлым Крылом у входа на плато Солнечного Колодца на острове Кель'Данас."}},
[24523] = {"Тотем Буйногривых", nil, {"Соберите 4 зуба могучего волка прерий."}},
[24524] = {"Очищение колодца Буйногривых", nil, {"Очистите колодец Буйногривых при помощи тотема очищения колодца Буйногривых."}},
[24525] = {"Преследование противника", nil, {"Убейте 6 зубастых падальщиков."}},
[24526] = {"Пополнение книги заклинаний", nil, {"Обучитесь заклинанию \"Чародейские стрелы\" у Тигли Веселинг. Дважды успешно испытайте действие этого заклинания на тренировочных манекенах рядом с входом в Старую Наковальню."}},
[24527] = {"Твой путь начинается здесь", nil, {"Обучитесь \"Стихийному удару\" у Тео Молотошторма, затем 2 раза испытайте этот прием на тренировочном манекене рядом с входом в Старую Наковальню."}},
[24528] = {"Сила света", nil, {"Обучитесь заклинаниям \"Печать праведности\" и \"Правосудие\" у Бромоса Груммнера. Наложив на себя \"Печать праведности\", сотворите заклинание \"Правосудие\" и испытайте на одном из тренировочных манекенов у входа в Старую Наковальню."}},
[24529] = {"Хуже худшего", nil, {"Спасите 5 пленных беженцев из Таурахо и убейте 8 терноплетов или бритвостражей из племени Дыбогривов."}},
[24530] = {"О, насыщенная жизнь охотника", nil, {"Обучитесь Верному выстрелу у Торгаса Мрачнона, затем пять раз сделайте Верный выстрел по тренировочным манекенам рядом с входом в Старую Наковальню."}},
[24531] = {"Подготовка к битве", nil, {"Обучитесь \"Рывку\" у Трана Кормана, затем испытайте этот прием на одном из тренировочных манекенов рядом с входом в Старую Наковальню."}},
[24532] = {"Потрошение противника", nil, {"Обучитесь \"Потрошению\" у Сольма Харгрина, затем 3 раза испытайте этот прием на тренировочных манекенах рядом с входом в Старую Наковальню."}},
[24533] = {"Слова могущества", nil, {"Обучитесь заклинанию \"Быстрое исцеление\" у Бранстока Халдера, затем 5 раз испытайте его на раненых ополченцах в Старой Наковальне."}},
[24534] = {"Понятным им языком", nil, {"Протрубите в рог на боевой площадке Дыбогривов и победите Саблеморда в одиночном бою."}},
[24535] = {"Талориен Искатель Рассвета", nil, {"Наведайтесь к останкам Талориена Искателя Рассвета и получите его благословение на владение Кель'Деларом."}},
[24536] = {"Дышать нечем!", nil, {"С помощью анализатора воздуха Гвоздезуба изучите состав воздуха вокруг шести надушенных стражников, а затем вернитесь к детективу с результатами."}},
[24539] = {"Когда деревья атакуют", nil, {"Спасите 5 беженцев из Таурахо, убив хваткие лианы, которые опутывают их."}},
[24540] = {"Танец войны", nil, {"Победите Орно Зловещего Тотема на тропе Каменного Когтя."}},
[24541] = {"Позаимствовать духи", nil, {"Замаскировавшись под посыльного, отправляйтесь к воротам Оргриммара, возле которых находятся товары Королевской химической компании. Добудьте ящик Королевской компании и вернитесь к детективу Крямсу Гвоздезубу в Оргриммар."}},
[24542] = {"Необычные цветы", nil, {"Соберите 10 необычных цветочных бутонов в Буйных зарослях."}},
[24543] = {"Разделенная семья", nil, {"Найдите Кирджа Жесткого Рога в Лагере вендетты в Южных Степях."}},
[24545] = {"Святость и скверна", nil, {"Поместите Отмщение Света, 25 кусков древнейшего саронита, разъедающую кровь Гниломорда и разъедающую кровь Тухлопуза в кузню рун Верховного лорда Могрейна в Цитадели Ледяной Короны.\n\nРазъедающую кровь Гниломорда и Тухлопуза можно получить в рейде для 25 игроков в Цитадели Ледяной Короны."}},
[24546] = {"Отступать некуда", nil, {"Захватите нейтральный холм в Кровавых полях и защитите его от атак Альянса."}},
[24547] = {"Пиршество душ", nil, {"Верховный лорд Дарион Могрейн поручил вам убить 50 слуг Короля-лича в Цитадели Ледяной Короны с помощью Грани Тьмы. Души будут засчитаны в рейде на 10 или 25 игроков."}},
[24548] = {"Расколотый трон", nil, {"Верховный лорд Могрейн поручил вам собрать 50 осколков Льда Тьмы.\n\nОсколки Льда Тьмы можно получить только в рейде 25 игроков."}},
[24549] = {"Темная Скорбь...", nil, {"Верховный лорд Могрейн просит вас принести ему Грань Тьмы."}},
[24550] = {"Путешествие в Громовой Утес", nil, {"Поговорите с Бейном Кровавым Копытом в Громовом Утесе."}},
[24551] = {"Встреча с новым начальником", nil, {"Убейте разведчиков Северной Стражи и соберите 10 Тераморских медалей за заслуги."}},
[24552] = {"Гордость льва", nil, {"Убейте равнинных хищников и равнинных хозяев стаи общим числом 5 особей."}},
[24553] = {"Очищение Кель'Делара", nil, {"Окуните закаленный Кель'Делар в Солнечный Колодец. Когда вы будете готовы войти на плато Солнечного Колодца, обратитесь к хранителю Солнечного Колодца."}},
[24554] = {"Потертая рукоять", nil, {"Принесите потертую рукоять Миралию Блеску Солнца в Покой Кель'Делара, что к югу от ристалища Серебряного Турнира."}},
[24555] = {"То, что знают драконы", nil, {"Поговорите с Красом на вершине Храма Драконьего Покоя."}},
[24556] = {"Подходящая маскировка", nil, {"Позаимствуйте у Шанди Шикблеска гербовую накидку."}},
[24557] = {"Планы Серебряного союза", nil, {"Перехватите приказы Серебряного союза."}},
[24558] = {"Возвращение к Миралию Блеску Солнца", nil, {"Принесите книгу \"Древние клинки драконьей ковки\" Миралию Блеску Солнца в Покой Кель'Делара."}},
[24559] = {"Перековать меч", nil, {"Возьмите 5 слитков зараженного саронита и молот начальника кузни, чтобы соединить фрагменты Кель'Делара и создать перекованный Кель'Делар."}},
[24560] = {"Закалить клинок", nil, {"Закалите перекованный Кель'Делар в горниле душ."}},
[24561] = {"Залы Отражений", nil, {"Отнесите Кель'Делар в Покои Клинка, что в Залах Отражений."}},
[24562] = {"Путешествие к Солнечному Колодцу", nil, {"Поговорите с Халдароном Светлым Крылом у входа на плато Солнечного Колодца на острове Кель'Данас."}},
[24563] = {"Талориен Искатель Рассвета", nil, {"Наведайтесь к останкам Талориена Искателя Рассвета и получите его благословение на владение Кель'Деларом."}},
[24564] = {"Очищение Кель'Делара", nil, {"Окуните закаленный Кель'Делар в Солнечный Колодец. Когда вы будете готовы войти на плато Солнечного Колодца, обратитесь к хранителю Солнечного Колодца."}},
[24565] = {"Биологическая угроза", nil, {"Убейте 5 загадочных страхокусов и 5 загадочных равнинных долгоногов."}},
[24567] = {"Перепроверка", nil, {"Поговорите с тренером Проверяком на площадке Каджаро на Кезане."}},
[24568] = {"На стены!", nil, {"Поговорите с Винноа Сосновый Бор в Кровавых полях, чтобы защитить врата Мулгора от атаки."}},
[24569] = {"Прорыв блокады", nil, {"Уничтожьте 4 осадные машины Северной Стражи у ворот Мулгора."}},
[24570] = {"Ловля змей", nil, {"Поймайте 5 ослабших гадюк."}},
[24571] = {"Семена хлещущей лозы", nil, {"Соберите 4 скорченных семечка хлещущей лозы."}},
[24572] = {"Возвращение Таурахо", nil, {"Убейте 8 мародеров Таурахо на руинах Таурахо."}},
[24573] = {"Последние почести", nil, {"Примените погребальный тотем к телам Крульмоо, Дранха, Йонада и Омусы на руинах Таурахо."}},
[24575] = {"День освобождения", nil, {"Освободите 5 порабощенных селян в Шахте Тлеющего Угля."}},
[24576] = {"Разговор по душам...", nil, {"Отправляйтесь к башне дирижаблей, что в центре Оргриммара, и допросите Снивела Ржавстера. Постарайтесь получить хоть какие-нибудь доказательства."}},
[24577] = {"Визит в Крепость Отчаяния", nil, {"Поговорите с полководцем Кровавым Эфесом, затем отправляйтесь в Крепость Отчаяния и сообщите Гар'дулу, что Кровавый Эфес скоро будет."}},
[24578] = {"Черная дубрава", nil, {"Осмотрите мельницу Брэдшоу рядом с Грозовым Перевалом."}},
[24579] = {"Сартарион должен умереть!", nil, {"Убейте Сартариона.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24580] = {"Ануб'Рекан должен умереть!", nil, {"Убейте Ануб'Рекана.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24581] = {"Нот Чумной должен умереть!", nil, {"Убейте Нота Чумного.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24582] = {"Инструктор Разувий должен умереть!", nil, {"Убейте Инструктора Разувия.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24583] = {"Лоскутик должен умереть!", nil, {"Убейте Лоскутика.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24584] = {"Малигос должен умереть!", nil, {"Убейте Малигоса.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24585] = {"Огненный Левиафан должен умереть!", nil, {"Убейте Огненного Левиафана.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24586] = {"Острокрылая должна умереть!", nil, {"Убейте Острокрылую.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24587] = {"Повелитель горнов Игнис должен умереть!", nil, {"Убейте Повелителя горнов Игниса.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24588] = {"Разрушитель XT-002 должен умереть!", nil, {"Убейте Разрушителя XT-002.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24589] = {"Лорд Джараксус должен умереть!", nil, {"Убейте лорда Джараксуса.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24590] = {"Лорд Ребрад должен умереть!", nil, {"Убейте Лорда Ребрада.\n\nЭто задание можно выполнить на любом уровне сложности и любым составом рейда."}},
[24591] = {"Отстранение Гар'дула", nil, {"Отправляйтесь на вершину северной башни Крепости Отчаяния и помогите полководцу Кровавому Эфесу \"освободить\" Гар'дула от командования."}},
[24592] = {"Предательство в Пределе бури", nil, {"Добравшись до Предела бури, выпейте зелье невидимости Креннана и убейте барона Эшбери и лорда Вальдена."}},
[24593] = {"Не человек, но и не зверь", nil, {"Испейте воды из Источника Спокойствия, Источника Равновесия и Источника Ярости."}},
[24594] = {"Очищение Кель'Делара", nil, {"Окуните закаленный Кель'Делар в Солнечный Колодец. Когда вы будете готовы войти на плато Солнечного Колодца, обратитесь к хранителю Солнечного Колодца."}},
[24595] = {"Очищение Кель'Делара", nil, {"Окуните закаленный Кель'Делар в Солнечный Колодец. Когда вы будете готовы войти на плато Солнечного Колодца, обратитесь к хранителю Солнечного Колодца."}},
[24597] = {"Подарок для короля Штормграда", nil, {"Подарите чудесный браслет-оберег королю Штормграда Вариану Ринну."}},
[24598] = {"Очищение Кель'Делара", nil, {"Окуните закаленный Кель'Делар в Солнечный Колодец. Когда вы будете готовы войти на плато Солнечного Колодца, обратитесь к хранителю Солнечного Колодца."}},
[24602] = {"Покой для усопших", nil, {"Найдите 5 пропавших памятных вещей."}},
[24603] = {"Война Манкрика", nil, {"Убейте 13 свинобразов из племени Иглогривых."}},
[24604] = {"Забота о Манкрике", nil, {"Отнесите подарок Махки Манкрику к югу от Кремневого лагеря."}},
[24606] = {"Кровь Степей", nil, {"Соберите у свинобразов из племени Иглогривых 6 безделушек из кровавых осколков."}},
[24607] = {"Возрождение Черного Копья", nil, {"Встретьтесь с Нортетом на тренировочной площадке Черного Копья."}},
[24608] = {"Кривозуб", nil, {"Убейте Кривозуба."}},
[24609] = {"Подарок для правителя Стальгорна", nil, {"Подарите чудесный браслет-оберег Мурадину Бронзобороду в Стальгорне."}},
[24610] = {"Подарок для верховной жрицы Элуны", nil, {"Подарите чудесный браслет-оберег Тиранде Шелест Ветра из Дарнаса."}},
[24611] = {"Подарок для Пророка", nil, {"Подарите чудесный браслет-оберег пророку Велену из Экзодара."}},
[24612] = {"Подарок для Вождя", nil, {"Подарите чудесный браслет-оберег вождю в Оргриммаре."}},
[24613] = {"Подарок для королевы банши", nil, {"Подарите чудесный браслет-оберег леди Сильване Ветрокрылой из Подгорода."}},
[24614] = {"Подарок для верховного вождя", nil, {"Подарите чудесный браслет-оберег Бейну Кровавому Копыту из Громового Утеса."}},
[24615] = {"Подарок для правящего лорда Кель'Таласа", nil, {"Подарите чудесный браслет-оберег Лор'темару Терону из Луносвета."}},
[24616] = {"Сбросить хвост", nil, {"Воспользуйтесь талисманом Белрисы, чтобы победить темного разведчика."}},
[24617] = {"Тал'дорен, дом среди глуши", nil, {"Поговорите с Дарием Краули в Тал'дорене, самом сердце Черной дубравы."}},
[24618] = {"Захватить Боевой Шрам", nil, {"Отправляйтесь в Боевой Шрам. Найдите флаг и убейте 10 передовых бойцов Форта Триумфа.\n\nЗахват флага даст преимущество в бою всем игрокам Орды в Боевом Шраме."}},
[24619] = {"Конечности есть конечности", nil, {"Примените костяную пилу Колдера к телам в Боевом Шраме и соберите 15 отрезанных конечностей."}},
[24620] = {"Сердце есть сердце", nil, {"Убейте самца тяжелоногого кодо в Боевом Шраме и возьмите его огромное сердце."}},
[24621] = {"Мозги есть мозги", nil, {"Убейте капитана форта Триумфа на дальней стороне Боевого Шрама и возьмите свежий мозг."}},
[24622] = {"Лучший спутник тролля", nil, {"Поговорите с Киджарой на Берегу Кровавого Когтя."}},
[24623] = {"Спасение детенышей", nil, {"Спасите 12 потерянных кровопалых детенышей с помощью свистка Кровопалых."}},
[24624] = {"Жест милосердия", nil, {"Убейте 8 оскверненных кровопалых ящеров."}},
[24625] = {"Спутник морской ведьмы", nil, {"Убейте Наж'тесса и заберите его сферу."}},
[24626] = {"Молодой и горячий", nil, {"Поймайте Стремительного Когтя и приведите его в стойла возле крепости Черного Копья."}},
[24627] = {"Слишком близко", nil, {"Убейте 6 стенающих банши."}},
[24628] = {"Приготовления", nil, {"Принесите Вассандре 6 лунных листьев."}},
[24629] = {"Легкий аромат духов", nil, {"Опрыскайте десять прохожих из флакончика духов королевской компании с распылителем."}},
[24631] = {"Небесные всадники", nil, {"Используйте крюк Томусы, чтобы цеплять боевых грифонов форта Триумфа. Сбросьте и убейте 5 наемников Громового Молота."}},
[24632] = {"Возмездие таурена", nil, {"Найдите Ганна Каменное Копье в Острие копья в Южных Степях."}},
[24633] = {"Просьба Махки", nil, {"Поговорите с Манкриком."}},
[24634] = {"Битва умов", nil, {"Убейте разведчика Степей и заберите инструкции ШРУ."}},
[24635] = {"Легкий аромат одеколона", nil, {"Опрыскайте десять прохожих из флакончика одеколона королевской компании с распылителем."}},
[24636] = {"Вкус конфет", nil, {"Раздайте десяти прохожим кусочки королевского шоколада."}},
[24637] = {"Ответственный за штурм Таурахо", nil, {"Поговорите с Картогом, потом заманите генерала Готорна в ловушку и убейте его."}},
[24639] = {"Азы мастерства: поражение цели", nil, {"Уничтожьте 6 манекенов-тики."}},
[24640] = {"Искусство воина", nil, {"Поговорите с Нортетом и потренируйтесь в освоении приема \"Рывок\". Отработайте \"Рывок\" на манекенах-тики."}},
[24641] = {"Практическое занятие", nil, {"Добудьте 6 шкур буйногривой рыси."}},
[24642] = {"Яма испытаний", nil, {"Поговорите с тюремщиком Черного Копья, а затем спуститесь в яму испытаний и убейте пленного разведчика из клана Злобной Чешуи."}},
[24643] = {"Превосходя ожидания", nil, {"Поговорите с Вол'джином в крепости Черного Копья."}},
[24645] = {"Конец Королевской компании", nil, {"Убейте 5 головорезов химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами за вратами Янтарной мельницы, в Серебряном бору. После этого вернитесь к детективу в Оргриммар."}},
[24646] = {"Отвоевать свое", nil, {"Протрубите в талдоренский рог у охотничьего домика на краю леса и заберите оттуда таинственный артефакт."}},
[24650] = {"Конец Королевской компании", nil, {"Убейте 5 окропителей химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами в Зимних ключах, к востоку от Круговзора. После этого вернитесь к детективу в Оргриммар."}},
[24652] = {"Конец Королевской компании", nil, {"Убейте 5 распылителей химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами в Опустевших лесах Хрустальной Песни, что к северо-западу от Зеркала Сумерек. После этого вернитесь к детективу в Оргриммар."}},
[24653] = {"Эхо Агамаггана", nil, {"Соберите 3 отполированных кабаньих черепа."}},
[24654] = {"Проблема с жучками", nil, {"Возьмите ведро пылающей смолы и вылейте смолу на жилища силитидов. Убейте 10 членистоногов силитида, когда они появятся."}},
[24655] = {"Дышать нечем!", nil, {"Изучите состав воздуха вокруг шести надушенных стражников при помощи анализатора воздуха Гвоздезуба, а затем вернитесь к детективу с результатами."}},
[24656] = {"Позаимствовать духи", nil, {"Замаскировавшись под посыльного, отправляйтесь к воротам Штормграда, возле которых находятся товары Королевской химической компании. Добудьте ящик Королевской компании и вернитесь к инспектору Кромсу Гвоздезубу в Штормград."}},
[24657] = {"Разговор по душам...", nil, {"Отправляйтесь в порт Штормграда и допросите Снивела Ржавстера. Постарайтесь получить хоть какие-нибудь доказательства."}},
[24658] = {"Конец Королевской компании", nil, {"Убейте 5 служащих химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами к северо-востоку от Гарнизона у Западного ручья, что за пределами Элвинна. После этого вернитесь к инспектору в Штормград."}},
[24661] = {"REUSE ME", nil, {"Убейте 5 служащих химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами к западу от врат Оргриммара в Дуротаре. После этого вернитесь к детективу Крямсу Гвоздезубу в Оргриммар."}},
[24664] = {"Конец Королевской компании", nil, {"Убейте 5 окропителей химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами в Зимних ключах, к востоку от Круговзора. После этого вернитесь к инспектору в Штормград."}},
[24666] = {"Конец Королевской компании", nil, {"Убейте 5 распылителей химической компании и воспользуйтесь кориевой бомбой Гвоздезуба, чтобы уничтожить повозку с химикатами в Опустевших лесах Хрустальной Песни, что к северо-западу от Зеркала Сумерек. После этого вернитесь к инспектору в Штормград."}},
[24667] = {"Кремневый лагерь", nil, {"Отнесите полевой справочник Крогола по силитидам Тауне Небесной Охотнице в Кремневый лагерь в Южных Степях."}},
[24671] = {"Куриный переполох", nil, {"Прикрепите фейерверки с дистанционным управлением к 10 клушам."}},
[24672] = {"Вперед и вверх", nil, {"Найдите Креннана Аранаса по дороге в Предел бури."}},
[24673] = {"Возвращение в Грозовой Перевал", nil, {"Поговорите с Гвен Армстед в Грозовом Перевале."}},
[24674] = {"Рабы не мы", nil, {"Убейте надсмотрщика Бульогга в Шахте Тлеющего Угля."}},
[24675] = {"Последняя трапеза", nil, {"Добудьте 10 кусков вырезки оленины."}},
[24676] = {"Полное уничтожение", nil, {"Убейте 4 отрекшихся-пехотинца, палача Корнелла и Валнова Безумного в Деревне Тлеющего Угля."}},
[24677] = {"Атака с фланга", nil, {"Поговорите с лордом Хьювелом, чтобы он отправил вас на Пост у стойл, где вас ждет Лорна Краули."}},
[24678] = {"По колено в нечисти", nil, {"Воспользуйтесь полусгоревшим факелом, чтобы добраться до другого конца туннеля под Гилнеасом."}},
[24679] = {"Благословение патриарха", nil, {"Положите благословенные подношения на могилу Адерика."}},
[24680] = {"Килевая гавань", nil, {"Поговорите с лордом Дарием Краули в Килевой гавани Гилнеаса."}},
[24681] = {"И у нас есть союзники", nil, {"Воспользуйтесь эльфийским метателем глеф, чтобы убить 40 орков-налетчиков, 8 всадников Волчьей Пасти и уничтожить 4 орочьих боевых машины."}},
[24682] = {"Яма Сарона", nil, {"Встретьтесь с леди Сильваной Ветрокрылой на входе в Яму Сарона."}},
[24683] = {"Яма Сарона", nil, {"Встретьтесь с леди Джайной Праудмур в Яме Сарона."}},
[24684] = {"Визил в курятнике", nil, {"Отправляйтесь в крепость Бейл'дан и убейте 10 дворфов Бейл'дана (ружейников, солдат и офицеров). Затем найдите Визила Темнилу, который должен быть внутри крепости."}},
[24685] = {"Дворфийская крепость", nil, {"Найдите планы бейл'данской крепости."}},
[24686] = {"Несомые волей волн", nil, {"Выловите семена кровоцветов с помощью сети Гарла. Необходимо 4 штуки."}},
[24687] = {"Букет смерти", nil, {"Убейте 6 стаек молодых кровоцветов."}},
[24688] = {"Зен'Алири", nil, {"Вернитесь к Зен'Алири на заставу Маршалла."}},
[24689] = {"По течению на север", nil, {"Свяжитесь с Итисом Лунным Стражем в Тревожных садах."}},
[24690] = {"Яропламень", nil, {"Убейте Яропламеня."}},
[24691] = {"Специфические лакомства", nil, {"Раздобудьте 96 ног силитидов."}},
[24692] = {"Молодой колосс", nil, {"Убейте молодого колосса из улья Гориши."}},
[24693] = {"Мшистая горка", nil, {"Поговорите с Гремиксом на Мшистой горке."}},
[24694] = {"Терраса Создателя", nil, {"Поговорите с исследователем Ун'Горо, чтобы отправиться на Террасу Создателя, после чего отыщите Набию."}},
[24695] = {"С высоты их мудрости", nil, {"Потяните за рычаги, что находятся за Набией, и выполните ее задания."}},
[24697] = {"Как вернуть мясу свежесть", nil, {"Заманите ближайших динозавров в ямы вокруг заставы Маршалла, бросив им ошметки гниющего мяса."}},
[24698] = {"Археологическая авантюра", nil, {"Отправляйтесь к Искру Пустокопу на Горячие источники Голакка."}},
[24699] = {"Гормашш Клейкий", nil, {"Убейте Гормашша и добудьте невероятно липкую смолу."}},
[24700] = {"Нелегко собрать", nil, {"Соберите 4 цветка смолоцвета."}},
[24701] = {"Мусор Маршалла", nil, {"Раздобудьте 10 упаковок выброшенных припасов."}},
[24702] = {"Здесь покоится Даданга", nil, {"Добудьте 10 ростков кровоцветов."}},
