[25176] = {"Воспользоваться случаем", nil, {"Добудьте 6 кул-тирасских сокровищ."}},
[25177] = {"Буря на взморье", nil, {"Убейте 11 пенящихся морских элементалей."}},
[25178] = {"В поисках разбитых кораблей", nil, {"Раздобудьте 4 набора гномских инструментов."}},
[25179] = {"Снижение потерь", nil, {"Спасите 4 раненых рубак Колючего Холма."}},
[25182] = {"Убить всех!", nil, {"Убейте 10 шахтеров в шахтерском городке Дранфикса в Южных Степях."}},
[25183] = {"…и закопать остальных!", nil, {"Заложите 6 частей взрывчатки в шахтерском городке Дранфикса."}},
[25184] = {"Головокружительная поездка на тачке", nil, {"Заберитесь в вагонетку поезжайте на ней до конца маршрута, а потом поговорите с ассистенткой Грили."}},
[25185] = {"Дело сделано", nil, {"Вернитесь к генералу Двукоссе в Обход Двукоссы в Южных Степях."}},
[25186] = {"Безрезультатное сопротивление", nil, {"Убейте Кону Шаг Грома на границе между Когтистыми Горами и Южными Степями."}},
[25187] = {"Жертвы наводнения", nil, {"Разыщите выживших после разлива реки Строптивой с помощью подзорной трубы Тонка."}},
