[25657] = {"Сундук Костеглода", nil, {"Бадд хочет, чтобы вы выстрелили минированной приманкой из ржавого гарпунного ружья на Проклятом рифе."}},
[25658] = {"На века", nil, {"Приведите в действие 6 щитов Нар'шолы.\n\nЭто задание из цикла \"Видения воительницы Леди Наз'жар\". Чтобы вернуться к видению, в нужном месте воспользуйтесь клинком воительницы Леди Наз'жар."}},
[25659] = {"Такое не забывается", nil, {"Убейте Варкула Безжалостного.\n\nЭто задание из цикла \"Видения воительницы Леди Наз'жары\". Чтобы вернуться к видению, в нужном месте воспользуйтесь клинком воительницы Леди Наз'жар."}},
[25660] = {"Неупокоенный", nil, {"Обратитесь к Аджамон Призывательнице Духов в Укрепление Южных морей в Тысяче Игл."}},
[25661] = {"С миру по нитке...", nil, {"Раздобудьте сердце злой Долли, легкие морского волка Файярго и печень Лилли Сухопутной Крысы."}},
[25662] = {"Освободить наших сестер", nil, {"Добудьте 5 ключей от клетки Орды и освободите 5 пленных охотниц."}},
[25663] = {"Подношение для Авианы", nil, {"Используйте священный нектар на Погребальном круге Авианы."}},
[25664] = {"На честном слове и на одном крыле", nil, {"Используйте манок огромной птицы у одного из гнезд Блейта на Краю света. Убейте Блейта и заберите одно из древних перьев."}},
[25665] = {"Потусторонний зов", nil, {"Вернитесь к Чолуне в святилище Авианы на горе Хиджал."}},
[25666] = {"Грязная работенка", nil, {"Раздобудьте 5 грязных внутренностей морских червей, охотясь на гигантских морских червей возле Раскопок Харрисона."}},
[25667] = {"Истребление вендиго", nil, {"Убейте 10 вендиго в Серой берлоге. Молодые вендиго тоже считаются."}},
[25668] = {"Украденные припасы", nil, {"Найдите 6 украденных ящиков с припасами в Серой берлоге."}},
[25669] = {"Неспокойная лесопилка", nil, {"Убейте полководца Роктрога, возьмите ключ от клетки охотницы Иллионы и освободите ее."}},
[25670] = {"Гм-гм", nil, {"Прикрепите цепь Бадда к ржавому гарпунному ружью на Проклятом рифе. Выстрелите, когда Костеглод окажется в зоне поражения."}},
[25671] = {"Проредить Орду", nil, {"Убейте 20 любых ордынских солдат в Когтистых горах."}},
[25672] = {"Анатомический театр", nil, {"Соберите 10 трупов животных."}},
[25673] = {"Просто попроси Алису", nil, {"Найдите машину войны БА-64, Механизированный огонь, Механизированный лед и Механизированный воздух."}},
[25674] = {"Безосновательная гордость", nil, {"Убейте душителя из клана Молота Ужаса в Форте Молота Ужаса."}},
[25675] = {"Бой с ограми", nil, {"Убейте 6 извергов и 5 чернокнижников из клана Молота Ужаса."}},
[25676] = {"Удар по карману", nil, {"Поговорите с Рофилием Дейном на Рудниках Гарнизона в Выжженных землях."}},
[25677] = {"Не нашим – не вашим!", nil, {"Полейте концентрированным растворителем 6 груд руды на Рудниках Гарнизона."}},
[25678] = {"Выбери свою судьбу", nil, {"Убейте 8 рудокопов, 8 инженеров и 3 штейгеров из Крепости Стражей Пустоты."}},
[25679] = {"В гору!", nil, {"Поговорите с Так'арили в глубине Рудников Гарнизона в Выжженных землях."}},
[25680] = {"Это не мы!", nil, {"Найдите и убейте то, что напугало рабочих на Рудниках Гарнизона."}},
[25681] = {"Кое-кого просто нельзя не убить", nil, {"Убейте главного геолога Хондо. Чтобы пробраться к нему, используйте неприметную коробку."}},
[25682] = {"Задание выполнено", nil, {"Отнесите отчет из Рудников Гарнизона Окрилле в Форт Молота Ужаса, что в Выжженных землях."}},
[25683] = {"Горн Молота Ужаса", nil, {"Поговорите с волшебницей крови Линорой в Горне Молота Ужаса в Выжженных землях."}},
[25684] = {"Настроиться на кровавый камень", nil, {"Научитесь использовать телепортирующие кровавые камни в Горне Молота Ужаса."}},
[25685] = {"Первый шаг", nil, {"Добудьте 9 флаконов крови хохочущей гиены и 9 флаконов крови краснокаменного василиска."}},
[25686] = {"Ритуал крови", nil, {"Поговорите с волшебницей крови Линорой, чтобы она начала ритуал."}},
[25687] = {"По кусочкам", nil, {"Найдите голову, туловище и ноги Лорама."}},
[25688] = {"Алтарь Бурь", nil, {"Отнесите тело Лорама на Алтарь Бурь и защищайте тело, пока не завершится ритуал."}},
[25689] = {"Времени нет", nil, {"Отнесите диссонирующую руну дозорному Ваззику к Темному порталу в Выжженных землях."}},
[25690] = {"Волшебнику крови тоже хочется кушать", nil, {"Отправляйтесь в Выжженные земли и раздобудьте 5 кусков мяса пеплогривых кабанов."}},
[25691] = {"Обугленный гранит у Темного портала", nil, {"Соберите 24 обугленные гранитные крошки."}},
[25692] = {"Поганая кровь демонов", nil, {"Добудьте 7 бутылок жуткой крови демонов."}},
[25693] = {"Дальнейшая подготовка", nil, {"Отнесите посылку Ваззика Лораму Многоногу в Горн Молота Ужаса в Выжженных землях."}},
[25694] = {"Защита тылов", nil, {"Убейте Джаррока Рваное Крыло, Шахандану и Гумгаза."}},
[25695] = {"Осторожность не помешает", nil, {"Изучите планы Альянса у Темного портала и постарайтесь, чтобы вас не поймали за этим занятием."}},
[25696] = {"Экспедиция Солнечного Паруса", nil, {"Поговорите с Роханом Солнечным Парусом в экспедиции Солнечного Паруса в Выжженных землях."}},
[25697] = {"Амулет Аллистария", nil, {"Найдите амулет Аллистария в Змеиных Кольцах в Выжженных землях."}},
[25698] = {"Амулет Севины", nil, {"Отнимите амулет Севины у Солюна, который живет близ Берега Обломков в Выжженных землях."}},
[25699] = {"Амулет Грола", nil, {"Найдите амулет Грола на Берегу Обломков в Выжженных землях."}},
[25700] = {"Лорам Многоног ждет", nil, {"Поговорите с волшебницей крови Линорой, когда будете готовы принять участие в ее ритуале."}},
[25701] = {"Демон, имя тебе Рах'лих", nil, {"Поговорите с привратником смерти к северу от Утеса Осквернителя. Когда вы окажетесь на утесе, уничтожьте тело Разелиха, а потом захватите его душу при помощи каменного ножа опечатывания."}},
[25702] = {"Наги пришли… деревню сожгли…", nil, {"По поручению Солюна поговорите с Нептусем, вождем мурлоков из стаи Каменистого Озера."}},
[25703] = {"Их надо остановить", nil, {"Убейте 10 варваров и 10 чародеек из клана Кровавого Прибоя."}},
[25704] = {"Безумный волхв", nil, {"Извлеките душу из тела безумного волхва Тирта."}},
[25705] = {"Ложные идолы", nil, {"Разбейте 8 идолов Азш'ира на Красных берегах."}},
[25706] = {"Месть Нептуся", nil, {"Убейте 8 послушников, 8 ревнителей, 4 прохвостов и 4 идолопоклонников из клана Кровавого Прибоя."}},
[25707] = {"Будущее Каменистого Озера", nil, {"Оттащите брошенный груз Кровавого Прибоя к Зловещему морю, подальше от Красных берегов, и выпустите головастиков в воду."}},
[25708] = {"Души погибших товарищей", nil, {"Убейте 13 утонувших жителей Гилнеаса на Берегу Обломков, чтобы их души упокоились в мире."}},
[25709] = {"Отстрел темнохвостых", nil, {"Используя взрывные стрелы Енохи, убейте 20 темнохвостых любителей костей на Берегу Обломков."}},
[25710] = {"Мелкие распри", nil, {"Убейте разведчика Окрил'лона на подступах к Крепости Стражей Пустоты в Выжженных землях."}},
[25711] = {"Уничтожить Окрил'лон", nil, {"Убейте 9 пехотинцев Окрил'лона в лагере поддержки Гарнизона."}},
[25712] = {"Крепость Стражей Пустоты", nil, {"Убейте капитана Метлека в лагере поддержки Гарнизона."}},
[25713] = {"Кто же не любит хорошего взрыва?", nil, {"С помощью ручной гранаты Ракитни взорвите 8 ящиков с оружием Окрил'лона в лагере поддержки Гарнизона.\n\nБросайте гранаты издалека, чтобы не подорваться самому."}},
[25714] = {"Дозорный Махар Ба", nil, {"Отправляйтесь к дозорному Махару Ба в Башню Магов в Крепости Стражей Пустоты."}},
[25715] = {"Обломки кораблекрушения", nil, {"Осмотрите обломки кораблекрушения на Берегу Обломков в Выжженных землях."}},
[25716] = {"Сектанты у нас на пороге", nil, {"Раздобудьте 6 средоточий чар Приверженцев Тени у воинственных чародеев и оккультистов из культа Приверженцев Тени в Выжженных землях."}},
[25717] = {"Демоны и друид", nil, {"Убейте 5 стражников ужаса – разрушителей, 5 повелителей ужаса – осквернителей, 5 оскверненных темностволых древней и 3 скверноспорых болотников в Проклятом лесу."}},
[25718] = {"Что делать дальше", nil, {"Поговорите с Элайджей Рассветным Полетом в экспедиции Солнечного Паруса в Выжженных землях."}},
[25719] = {"Демонический резервуар", nil, {"Убейте нечистых черных медведей, нечистых ночных ловцов и нечистых крикунов в Проклятом лесу, а потом составьте из их органов демонический резервуар."}},
[25720] = {"Падение Марла Червоточного", nil, {"Убейте Марла Червоточного у дерева Малдраз в Выжженных землях. Для того чтобы вывести Марла из транса, используйте демонический резервуар."}},
[25721] = {"Борьба с наводнением", nil, {"Убейте 10 элементалей потока."}},
[25722] = {"Сухой остаток", nil, {"Соберите 8 образцов принесенных наводнением наносов."}},
[25723] = {"Не повезло жесткозубам", nil, {"Добудьте 8 кусков мяса жесткозуба, убивая вытесненных жесткозубов."}},
[25724] = {"Агрессия Мерзлогривов", nil, {"Убейте 5 ясновидцев из племени Мерзлогривов и 7 снегобродов из племени Мерзлогривов на Мерцающем хребте."}},
[25725] = {"Целебная клюква", nil, {"Соберите 15 пригоршней клюквы."}},
[25726] = {"Нивелирная работа", nil, {"Добудьте глухой нивелир из илистого слизнюка."}},
[25727] = {"Друнгельд Исподлоб", nil, {"Убейте Друнгельда Исподлоба и заберите его бороду."}},
[25728] = {"Дикая охота Господина П.", nil, {"Убейте 20 гоблинов-техников и 5 гоблинских жнецов."}},
[25729] = {"Предатель Геренцо", nil, {"Убейте Геренцо Терминатрикса."}},
[25730] = {"БЕРЕГИСЬ РИФОГРЫЗА!", nil, {"Убейте Рифогрыза и возьмите в качестве доказательства огромный зуб Рифогрыза."}},
[25731] = {"Птица в руке", nil, {"Погасите сигнальный огонь гарпий, чтобы привлечь внимание Марион Червивое Крыло. Сражайтесь с ней, пока Тизали не схватит ее, а затем допросите предводительницу гарпий."}},
[25732] = {"Игра в кости", nil, {"Бадд просит вас забрать крушитель олова у короля Гурбоггла на шельфе Гурбоггла."}},
[25733] = {"Вон из моей пещеры!", nil, {"Убейте 7 пещерных ловцов и 7 ловцов-кровопийц."}},
[25734] = {"Дно пещеры Тельгена", nil, {"Заберите сейсмические записи Тельгена из пещеры Тельгена."}},
[25735] = {"Огневитовая руда", nil, {"Добудьте 8 кусков огневитовой руды."}},
[25736] = {"Центр приливной волны", nil, {"Отнесите центр приливной волны Форбе Крепкой Стамеске."}},
[25737] = {"Радушный прием", nil, {"Обратитесь к королю Гурбогглу."}},
[25739] = {"Не Орда, так…", nil, {"Убейте королеву Силит."}},
[25740] = {"Разведка боем", nil, {"Отыщите Тизали у входа в Гнездо Сетрии на горе Хиджал."}},
[25741] = {"Подготовка на будущее", nil, {"Соберите 10 ядовитых желез моховых пауков."}},
[25743] = {"Вот ведь незадача", nil, {"Воспользуйтесь крушителем олова, чтобы разбить оловянного пророка."}},
[25744] = {"Переговоры", nil, {"Поговорите с Краззлом Шустрецом на Утесе Треснувшего Копыта в Тысяче Игл."}},
[25745] = {"Переговоры", nil, {"Поговорите с Ризнеком на Утесе Треснувшего Копыта в Тысяче Игл."}},
[25746] = {"Род Сетрии", nil, {"Убейте 12 приспешников Сетрии: сумеречных драконидов и/или сумеречных драконидов-бронников."}},
[25747] = {"Взгляд в будущее", nil, {"Найдите адмирала Дворека под южной террасой в Руинах Вайш'ира и поговорите с ним.\n\nВоспользуйтесь морским коньком Валорен Говорящей с волнами, чтобы быстро добраться до форпоста."}},
[25748] = {"Ясные цели", nil, {"Убейте 10 наг на нижнем уровне под юго-западной террасой."}},
[25749] = {"Не так уж безоружны", nil, {"Принесите 8 связок арбалетных болтов дозорным Альянса, расставленным на уступах под южной террасой."}},
[25751] = {"Внезапное озарение", nil, {"Соберите 8 раздувшихся шишек водорослей, висящих под юго-западной террасой."}},
[25752] = {"Медлить нельзя", nil, {"Поговорите с инженером Мегагексом, стоящим у здания, чтобы присоединиться к атаке, а затем убейте 100 солдат Азш'ира, 20 водных визгуний Азш'ира и 12 археев Азш'ира с помощью импровизированной взрывчатки."}},
[25753] = {"Повержены, но не забыты", nil, {"Спасите 6 раненых добровольцев."}},
[25754] = {"Подсчет", nil, {"Отправляйтесь на разведку в северо-западную часть круглой террасы и в туннель на севере. Они расположены на террасе Нар'шолы на среднем уровне руин."}},
[25755] = {"Видения прошлого: битва на гряде Бейл'аран", nil, {"Отправляйтесь на место гибели квалдирского генерала и с помощью сломанного клинка Воительницы призовите видение из прошлого, чтобы узнать продолжение истории о Воительнице."}},
[25756] = {"Хватай Зерина!", nil, {"Раздобудьте предложение Орды."}},
[25757] = {"Хватай Углеборода!", nil, {"Раздобудьте предложение Альянса."}},
[25758] = {"Изъян в броне", nil, {"Соберите 8 сумеречных пластин для доспехов."}},
[25760] = {"Видения прошлого: вторжение в Вайш'ир", nil, {"Воспользуйтесь клинком воительницы Леди Наз'жар на поле битвы возле юго-восточной окраины руин."}},
[25761] = {"Сорвать доспехи", nil, {"Воспользуйтесь кинжалом Тизали, чтобы разбудить и убить 3 сумеречных берсерков. Кинжал можно применять многократно, чтобы полностью сорвать доспех с берсерка."}},
[25762] = {"Древняя жаровня", nil, {"Разожгите древнюю жаровню и раздобудьте священное пламя."}},
[25763] = {"Кодекс теней", nil, {"Поведайте о своей находке Тизали Вороне в Гнезде Сетрии на горе Хиджал."}},
[25764] = {"Охота за яйцами", nil, {"Отключайте генераторы покрова теней до тех пор, пока не обнаружите яйцо Авианы. Затем охраняйте его до того момента, как друиды-вороны заберут яйцо в безопасное место."}},
[25765] = {"Скажешь, что ты от Коко", nil, {"Доложитесь командиру армии Валену в лагере экспедиции Северной Стражи, что в Когтистых горах."}},
[25766] = {"Тайное наследие", nil, {"Используйте тюрьму высокорожденных, чтобы упокоить 5 духов высокорожденных."}},
[25767] = {"В поисках воспоминаний", nil, {"Соберите 8 реликвий Эльдре'тара."}},
[25768] = {"Сбрасывай бомбу: пост на Мутном озере!", nil, {"Доложитесь командиру разведчиков Барусу на посту на Туманном озере в Когтистых горах."}},
[25769] = {"Вызов к лорду Болотной Пахоте", nil, {"Поговорите с лордом Болотной Пахотой в крепости Ветрорезов в Когтистых горах."}},
[25770] = {"Кати бочку!", nil, {"Доставьте эль горного пехотинца Форбе Крепкой Стамеске на привал Крепкой Стамески."}},
[25771] = {"Дренейский хлам", nil, {"Отнесите несовершенный фрагмент дренетиста Кум'ише Коллекционеру в Выжженных землях."}},
[25772] = {"Для коллекции Кум'иши", nil, {"Отнесите безупречную сферу из дренетиста Кум'ише Коллекционеру в Выжженных землях."}},
[25773] = {"Природное явление", nil, {"Отнесите осколок элементаля посланнику Служителей Земли в Стальгорн, Штормград, Экзодар или Дарнас."}},
[25774] = {"Золото дураков", nil, {"Соберите 8 глыб колчедановой руды с Тысячи Игл."}},
[25775] = {"Золото дураков", nil, {"Соберите 8 глыб колчедановой руды с Тысячи Игл."}},
