[26184] = {"Мечта Червоточного", nil, {"Убейте 5 стражников ужаса-разрушителей, 5 повелителей ужаса-осквернителей, 5 оскверненных темностволых древней и 3 скверноспорых болотников в Проклятом лесу."}},
[26185] = {"Спросить совета у Круга Кенария", nil, {"Поговорите с кенарийской наблюдательницей Шайаной в Сурвиче в Выжженных землях."}},
[26186] = {"Демонический резервуар", nil, {"Убейте нечистых черных медведей, нечистых ночных ловцов и нечистых крикунов в Проклятом лесу, а потом составьте из их органов демонический резервуар."}},
[26187] = {"Падение Марла Червоточного", nil, {"Убейте Марла Червоточного у дерева Малдраз в Выжженных землях. Для того чтобы вывести Марла из транса, используйте демонический резервуар."}},
[26188] = {"Маззранач", nil, {"Принесите бедренную кость пумы плоскогорья Мауру Заклинателю Дождя в деревню Кровавого Копыта."}},
[26189] = {"Угроза Злого Клыка", nil, {"Убейте Горфакса Злого Клыка и 16 орков из клана Драконьей Пасти в Лагере Злого Клыка."}},
[26190] = {"Рыбацкая услада", nil, {"Раздобудьте 5 гигантских зубаток. Принесите рыбу Робби Флею в Штормграде."}},
[26191] = {"Плод стараний", nil, nil},
[26192] = {"Сиротки тоже любят печенье!", nil, {"Раздобудьте 4 мешка сахарной пудры и отнесите Робби Флею в Штормград."}},
[26193] = {"Оборона Провала", nil, {"Обратитесь к Эрунаку в Темной пещере, чтобы отправиться в битву за Обрыв над бездной."}},
[26194] = {"Оборона Провала", nil, {"Обратитесь к Эрунаку Говорящему с Камнем в Зловещем гроте, чтобы вступить в битву за Обрыв над бездной."}},
[26195] = {"Кто сторожит Стража Природы", nil, {"Поговорите с Ферилоном Дитя Листвы в Роще Стража Природы, что в Болотине."}},
[26196] = {"Длиннокос Мрачный", nil, {"Отнести полевой набор Ферилона Длиннокосу Мрачному в Дун Модре."}},
[26197] = {"Будущее Гномрегана", nil, {"Встретьтесь с Бипси Хладолей в Новом Городе Механиков."}},
[26198] = {"Искусство мага", nil, {"Поговорите с Бипси Хладолей, чтобы изучить заклинание \"Чародейские стрелы\". Когда заклинание будет изучено, дважды опробуйте его на тренировочном манекене в Новом Городе Механиков."}},
[26199] = {"Будущее Гномрегана", nil, {"Поговорите с \"Доком\" Вертушкой в Новом Городе Механиков."}},
[26200] = {"Искусство жреца", nil, {"Поговорите с \"Доком\" Вертушкой, чтобы изучить заклинание \"Быстрое исцеление\". Потренируйтесь применять эту технику 5 раз на раненых пехотинцах в Новом Городе Механиков."}},
[26201] = {"Власть чернокнижника", nil, {"Поговорите с Аламаром Мрачнном и 5 раз потренируйтесь применять заклинание \"Жертвенный огонь\" на тренировочном манекене в Новом Городе Механиков."}},
[26202] = {"Будущее Гномрегана", nil, {"Встретьтесь с Аламаром Мрачнном в Новом Городе Механиков."}},
[26203] = {"Будущее Гномрегана", nil, {"Поговорите с сержантом-инструктором Парогоном в Новом Городе Механиков."}},
[26204] = {"Искусство воина", nil, {"Поговорите с сержантом-инструктором Парогоном, чтобы обучиться атаке. Найдите тренировочный манекен в Новом Городе Механиков и потренируйтесь на нем."}},
[26205] = {"Работа для робота", nil, {"Воспользуйтесь многофункциональным роботом GS-9x, чтобы очистить 5 ядовитых гейзеров на зараженном аэродроме. Когда подберетесь достаточно близко к ядовитой луже, робот автоматически начнет очищение."}},
[26206] = {"Будущее Гномрегана", nil, {"Поговорите с Келси Стализвон в Новом Городе Механиков."}},
[26207] = {"Искусство разбойника", nil, {"Поговорите с Келси Стализвон, чтобы обучиться способности \"Потрошение\", а затем найдите в Новом Городе Механиков тренировочный манекен и попрактикуйтесь в использовании техники 3 раза."}},
[26208] = {"Битва продолжается", nil, {"Посмотрите на голографическую панель перед главным механиком Меггакрутом, чтобы узнать об операции \"Гномреган\"."}},
[26209] = {"На меня повесили убийство", nil, {"Поговорите с несколькими бездомными людьми, населяющими владение Янсена, чтобы найти какие-нибудь указания на то, кто убил Хмуробровов."}},
[26210] = {"Гаммерита!", nil, {"Убейте Гаммериту и верните Катуму Удильщику из деревни Сломанного Клыка его лучшую блесну.\n\nКатум сказал, что Гаммерита ползает по берегу среди остальных черепах."}},
[26211] = {"Хрустогрызы, чувак!", nil, {"Убейте 10 морских хрустогрызов по поручению Катума Удильщика из деревни Сломанного Клыка во Внутренних землях."}},
[26212] = {"Лярд потерял свой обед", nil, {"Найдите обед Лярда из деревни Сломанного Клыка во Внутренних землях.\n\nЛярд сказал, что потерял свой обед на острове к северу, где могут встретиться тролли из племени Порочной Ветви."}},
[26213] = {"По горячему следу: клан Речной лапы", nil, {"Найдите улику против гноллов из стаи Речной Лапы."}},
[26214] = {"По горячему следу: мурлоки", nil, {"Добудьте улику против мурлоков."}},
[26215] = {"Встреча с Переобутым Лу", nil, {"Поговорите с Переобутым Лу на тыквенной ферме Хмуроброва в Западном крае."}},
[26219] = {"Замыкая круг", nil, {"C \"Крепости Волдрина\" поднимитесь на борт \"Хвата Х2\" и окажите помощь в операции в Темной пещере."}},
[26220] = {"С беконом все потянет", nil, {"Заруби взрослых кабанов к югу от Оргриммара и принеси мне 6 туш. Я буду ждать тебя тут, в Волоке."}},
[26221] = {"Замыкая круг", nil, {"Со \"Стигийской Награды\" поднимитесь на борт \"Верна\" и окажите помощь в операции в Зловещем гроте."}},
[26222] = {"Добыча запчастей", nil, {"Принесите 7 запчастей инженеру Искролязгу в Новый Город Механиков."}},
[26223] = {"Охота на бродяг", nil, {"Убейте 8 среброгривых бродяг."}},
[26224] = {"Охота на дикарей", nil, {"Убейте 10 диких совух."}},
[26225] = {"Портвейн \"Пупелливербос\"", nil, {"Соберите 12 бутылок портвейна \"Пупелливербос\"."}},
[26226] = {"Раки под креольским соусом", nil, {"Поймайте 10 ильных раков в Оргриммаре. Принесите добычу Мароггу."}},
[26227] = {"Осторожно: злые фрукты!", nil, {"Сорвите 8 колючих груш. Доставьте их Мароггу в Оргриммар."}},
[26228] = {"Живи и радуйся", nil, {"Используйте старый дом Переобутого Лу у заднего входа в рудник Янго в Западном крае."}},
[26229] = {"\"Свечка МОЯ!\"", nil, {"Убейте 12 кобольдов-землекопов."}},
[26230] = {"Пир во время чумы", nil, {"Добудьте 6 хвостов койотов и 5 горстей свежей грязи."}},
[26232] = {"Прощальные мысли Лу", nil, {"Подслушайте лиходеев, спрятавшись за домом на тыквенной ферме Хмуроброва в Западном крае."}},
[26233] = {"Укради у своих", nil, {"Раздобудьте 6 бочек риса Черного Копья. Принесите их Мароггу в Оргриммар."}},
[26234] = {"Укради у своих", nil, {"Достаньте 6 бочек кезанского риса и принесите их Мароггу в Оргриммар."}},
[26235] = {"Вор тоже хочет кушать", nil, {"Раздобудьте 3 пайка ордынских пехотинцев. Отнесите их Мароггу в Оргриммар."}},
[26236] = {"Обыск у Сальденов", nil, {"Поговорите с фермером Сальденом на ферме Сальдена в Западном крае."}},
[26237] = {"Тяжелые времена", nil, {"Убейте 10 дозорных полей."}},
[26238] = {"Дикие дворфы", nil, {"Убейте 10 воинов или шаманов клана Громового Молота."}},
[26240] = {"Бей в барабаны", nil, {"Испробуйте действие боевых барабанов на 6 воинах племени Сломанного Клыка."}},
[26241] = {"Похлебка Западного края", nil, {"Добудьте 6 плодов окры, 6 боков кровоклыка и 6 кусков жилистого мяса шкуродера."}},
[26243] = {"Столп мира", nil, {"Спросите Маруута Камневяза о столпе мира."}},
[26244] = {"Земля забирает все", nil, {"Убейте 5 глубинных элементалей."}},
[26245] = {"Крушение воздушного судна", nil, {"Обыщите место крушения \"Неистовства Бури\" в Подземье."}},
[26246] = {"Судовой журнал капитана", nil, {"Отнесите судовой журнал капитана Марууту Камневязу в Храм Земли, что в Подземье."}},
[26247] = {"Дипломатия прежде всего", nil, {"Поговорите с буревестницей Милрой на боевом корабле Альянса."}},
[26248] = {"Все наши друзья мертвы", nil, {"Воспользуйтесь тотемом духов у 6 тел убитых членов команды."}},
[26249] = {"Каюта адмирала", nil, {"Поищите улики в кабине адмирала на боевом корабле Альянса."}},
[26250] = {"А впрочем... взять одного пленного", nil, {"Победите Мор'норока Ненавистного на боевом корабле Альянса в Подземье."}},
[26251] = {"Пленных не брать", nil, {"Убейте 6 сумеречных саботажников."}},
[26252] = {"Сердце дозорного", nil, {"Принесите сердце дозорного полей фермеру Сальдену на ферму Сальдена."}},
[26254] = {"Растянуть связки", nil, {"Садитесь на Клюва Бури и проследите за допросом Мор'норокка."}},
[26255] = {"Вернуться в Храм Земли", nil, {"Поговорите с Маруутом Камневязом в Храме Земли в Подземье."}},
[26256] = {"Пустить кровь кроводелам", nil, {"Заберите приказы Сумеречного Молота у одного из Сумеречных кроводелов в Кратере Смертокрыла."}},
[26257] = {"Он живой!", nil, {"Примените сердце дозорного полей к перегруженному уборочному голему и затем используйте перегруженный уборочный голем для уничтожения 25 заряженных уборочных големов."}},
[26258] = {"Кратер Смертокрыла", nil, {"Отправляйтесь в Кратер Смертокрыла в Подземье."}},
[26259] = {"Кровь Хранителя Земли", nil, {"Добудьте 5 образцов крови Нелтариона из живой крови в Кратере Смертокрыла."}},
[26260] = {"Записи начальника кузни", nil, {"Найдите записи начальника кузни в Кратере Смертокрыла в Подземье."}},
[26261] = {"Допрос рабов", nil, {"Найдите ключ поработителя из Сумеречного Молота и освободите 6 порабощенных шахтеров."}},
[26263] = {"Торнар Громовой Рокот", nil, {"Убейте Торнара Громового Рокота."}},
[26264] = {"Что остается потом", nil, {"Разыщите на зараженном аэродроме токсических слякочей и соберите 6 единиц спасенного имущества."}},
[26265] = {"Уборка грязи", nil, {"Уничтожьте 6 оживших загрязнений на зараженном аэродроме."}},
[26266] = {"Надежда народа", nil, {"Отнесите похлебку Западного края Надежде Сальден на Сторожевой холм."}},
[26267] = {"Большая уборка на Осклизлой скале", nil, {"Убейте 10 нефритовых слизнюков."}},
[26268] = {"Припасы с Осклизлой скалы", nil, {"Соберите 5 покрытых слизью ящиков с припасами."}},
[26269] = {"Зеленые холмы Тернистой долины", nil, {"Найдите страницу 14 \"Зеленых холмов Тернистой долины\"."}},
[26270] = {"Прими нашу благодарность", nil, {"Поговорите с Сальмой Сальден на ферме Сальдена в Западном крае."}},
[26271] = {"Накормить голодных и отчаявшихся", nil, {"Накормите похлебкой Западного края 20 бездомных в Сторожевом холме."}},
[26272] = {"Возрождение Черного Копья", nil, {"Найдите Волдреку на тренировочной площадке Черного Копья."}},
[26273] = {"Азы мастерства: поражение цели", nil, {"Уничтожьте 6 манекенов-тики."}},
[26274] = {"Искусство чернокнижника", nil, {"Поговорите с Волдрекой и потренируйтесь в освоении заклинания \"Жертвенный огонь\". Затем 5 раз испытайте \"Жертвенный огонь\" на манекенах-тики."}},
[26275] = {"Практическое занятие", nil, {"Добудьте 6 шкур буйногривой рыси."}},
[26276] = {"Яма испытаний", nil, {"Поговорите с тюремщиком Черного Копья, а затем спуститесь в яму испытаний и убейте пленного разведчика из клана Злобной Чешуи."}},
[26277] = {"Превосходя ожидания", nil, {"Поговорите с Вол'джином в крепости Черного Копья."}},
[26278] = {"Защита Гром'гола: угроза ящеров", nil, {"Уничтожьте 12 яиц кнутохвоста."}},
[26279] = {"Защита Гром'гола: нападение на огров", nil, {"Уничтожьте 4 громил и 4 знахарей из клана Мош'огг."}},
[26280] = {"Защита Гром'гола: угроза мурлоков", nil, {"Убейте 15 черножабрых мурлоков."}},
[26281] = {"Защита Гром'гола: троллье воровство", nil, {"Добудьте ящики с пайками Гром'гола на руинах Бал'лал."}},
[26282] = {"Милость Акил'дара", nil, {"Требования:"}},
[26283] = {"Жуть-муть", nil, {"Возьмите образец Вязкой Мути."}},
[26284] = {"Пропавшие без вести", nil, {"Освободите 6 захваченных подрывников из клеток вокруг форта Мерзлогривов."}},
[26285] = {"Верните мне мой порох!", nil, {"Отправляйтесь в форт Мерзлогривов и отберите 7 украденных бочонков с порохом у костегрызов или мародеров из племени Каменной Челюсти."}},
[26286] = {"На защите Западного края", nil, {"Добудьте гнолльские приказы атаковать."}},
[26287] = {"Дружина Западного края", nil, {"Убейте 12 любых гноллов из стаи Речной лапы, напавших на Сторожевой холм."}},
[26288] = {"Джанго Крапчатый", nil, {"Убейте 5 мистиков и 5 надсмотрщиков из стаи Речной Лапы и Джанго Крапчатого."}},
[26289] = {"Найти агента Кеарнен", nil, {"Найдите агента Кеарнен."}},
[26290] = {"Тайны башни", nil, {"Войдите в башню Мортвейка и найдите Хеликса Отломчикса."}},
[26291] = {"Большой переполох в Луноречье.", nil, {"Доложите о том, что вам удалось обнаружить, маршалу Гриану Камнегриву на Сторожевом холме в Западном крае."}},
[26292] = {"В Луноречье!", nil, {"Доложитесь капитану Алперту в Луноречье в Западном крае."}},
[26293] = {"Боевые машины", nil, {"Поговорите со штейгером Глиббсом у задних врат Оргриммара в Оргриммаре."}},
[26294] = {"Оружие массовой дисфункции", nil, {"Осматривайте \"Разрушители Адского Крика\", пока не найдете машину с дефектом, а затем проучите главного инженера."}},
[26295] = {"Пропаганда", nil, {"Добудьте информационный буклет, тайный дневник, выпуск луноречского вестника и таинственное воззвание."}},
[26296] = {"Сбор доказательств", nil, {"Добудьте 6 красных бандан."}},
[26297] = {"Заря нового дня", nil, {"Отправляйтесь на условленое место в Луноречье и добудьте необходимую информацию."}},
[26298] = {"Поиски Йеннику", nil, {"Добудьте 6 клыков троллей из племени Кровавого Скальпа в руинах Бал'лал."}},
[26299] = {"Охота за головами", nil, {"Раздобудьте 25 сушеных голов, которые носят при себе тролли из племени Кровавого Скальпа, живущие в руинах Зуулдая."}},
[26300] = {"Знания Неззилока", nil, {"Отрубите голову Неззилоку Зловещему в Зул'Кунде."}},
[26301] = {"Разговор с Неззилоком", nil, {"Добудьте трофей из Балиа'ма и Зиата'джай в руинах Дробителей Черепов в Северной Тернистой долине."}},
[26302] = {"Судьба Йеннику", nil, {"Поговорите с Кин'вилаем в Бамбале."}},
[26303] = {"Око разума", nil, {"Отнимите Око разума у Май'Зота."}},
[26304] = {"Ночь в джунглях", nil, {"Потушите 8 жаровен в Зул'Мамве, что в Северной Тернистой долине."}},
[26305] = {"Спасение Йеннику", nil, {"Отнесите Нимбойе наполненный самоцвет Души."}},
[26306] = {"Начало расправы", nil, {"Убейте 5 знахарей и 5 охотников за головами из племени Порочной Ветви в Джинта'Алоре."}},
[26307] = {"Смерть Порочной Ветви!", nil, {"Убейте 5 берсерков и 5 темных чародеев из племени Порочной Ветви в Джинта'Алоре."}},
[26308] = {"Победа наша!", nil, {"Убейте 5 кровопийц и 5 пожирателей душ из племени Порочной Ветви в Джинта'Алоре."}},
[26309] = {"Темные сосуды", nil, {"Соберите 5 Сосудов с порченой кровью в Джинта'Алоре."}},
[26310] = {"Быстролапые паучата", nil, {"Соберите 8 быстролапых паучков в Джинта'Алоре."}},
[26311] = {"Неизведанные воды", nil, {"Найдите Эрезегора Огнеклыка в таверне \"Хвост виверны\", которая расположена на Аллее Чести в Оргриммаре. Возьмите у него карту побережья Сумеречного нагорья."}},
[26312] = {"Неравный бой", nil, {"Помогите Тону Зимнему Утесу, буревестнице Милре и Харготу Бледному Пламени победить их противников, чтобы они смогли перевести дух."}},
[26313] = {"Суть наших проблем", nil, {"Соберите 6 сердец яростных грохотунов."}},
