[26568] = {"Не моя война", nil, {"Вернитесь к полковнику Тротману."}},
[26569] = {"Геодезическое снаряжение", nil, {"Раздобудьте 5 подзорных труб Черной горы."}},
[26570] = {"Армия Ренда", nil, {"Убейте 25 любых орков из клана Черной горы на лесопилке Альтера или в лагере Ренда."}},
[26571] = {"Орудия войны", nil, {"Верните лук Кишана и нож выживания Кишана."}},
[26572] = {"Золотой улов", nil, {"Возьмите разделочный нож Разгара, чтобы отрезать хвост рокочущей ящерицы на Громовом хребте к югу от Оргриммара. Когда раздобудете хвост, оглушите им золотую бородавчатку. Возвращайтесь с уловом к Разгару на Аллею Чести."}},
[26573] = {"Всем сердцем с нами", nil, {"Отыщите красную головную повязку Кишана и нефритовый амулет Кишана."}},
[26574] = {"Убежище Аделлы", nil, {"Доберитесь до Аделлы в Тень Ветвей в Фераласе."}},
[26575] = {"Последняя капля", nil, {"Убейте Горгонита."}},
[26576] = {"Твердая рука", nil, {"Поговорите с Терратом Непоколебимым у Белого гнездовья."}},
[26577] = {"Тернистый путь", nil, {"Поговорите с Горсиком Беспокойным в Багровом зале."}},
[26578] = {"Гиблогрибы", nil, {"Уничтожьте 10 гиблогрибов."}},
[26579] = {"Проявить слабость", nil, {"Убейте 8 грибных чудищ."}},
[26580] = {"Знакомые незваные гости", nil, {"Поговорите с \"незваными смертными\" в Багровом зале."}},
[26581] = {"Ветер в голове", nil, {"Возьмите ветробеса Лорвариуса, отправляйтесь к облакам красного тумана, которые находятся у воды в Багровом зале, и возвращайтесь с образцом."}},
[26582] = {"Неестественные причины", nil, {"Поговорите с Горсиком Беспокойным в Багровом зале."}},
[26583] = {"Гнев грибоманта", nil, {"Поговорите с целительницей земли Норсалой, а затем убейте грибоманта Глопа."}},
[26584] = {"Встряхнуть и смешать", nil, {"Убейте 8 скалолазов из Верлока в нижней части Верлока."}},
[26585] = {"Борьба с порчей", nil, {"Добудьте 8 мешочков верлокской чудоросли."}},
[26586] = {"Поиски компании \"Браво\"", nil, {"Поищите на лесопилке Альтера кого-нибудь из команды \"Браво\"."}},
[26587] = {"Побег – штука сложная", nil, {"Найдите ключ от клетки Месснера."}},
[26588] = {"Строптивый улов", nil, {"Поймайте гигантскую разъяренную щуку в реке Строптивой. Выйти к реке можно через западные ворота Оргриммара. Возвращайтесь с уловом к Разгару на Аллею Чести."}},
[26589] = {"В дебрях Фераласа", nil, {"Отправляйтесь в Фералас и поговорите с Кону Руническим Тотемом в лагере Атайа."}},
[26590] = {"Святая вода ясности", nil, {"Добудьте святую воду ясности из родника Нек'Мани, расположенного на мысе Тернистой долины."}},
[26591] = {"Медобслуживание на передовой", nil, {"Окажите первую помощь 10 раненым земельникам."}},
[26592] = {"Дифракционный хромоскоп", nil, {"Раздобудьте рубин Кровавого Паруса и сапфир Кровавого Паруса."}},
[26593] = {"Напугать Трусишку", nil, {"Раздобудьте потроха 5 старых горилл Мглистой долины."}},
[26594] = {"Возвращение к Маккинли", nil, {"Отнесите плату Трусишки \"Морскому волку\" Маккинли."}},
[26595] = {"Долг платежом красен", nil, {"Добудьте деревянную ногу Моури, гаечный ключ Фитцтютеля и драгоценное зеркало Эфрама."}},
[26596] = {"Зов Калимдора", nil, {"Отправляйтесь на корабле из Пиратской Бухты в Кабестан, где вам следует поговорить с Газлоу."}},
[26597] = {"Лихорадка Тернистой долины", nil, {"Найдите знахаря Унбагву и попросите его вызвать Мокка Дикаря. Принесите сердце Мокка Фину Горемыку."}},
[26598] = {"Сердце Мокка", nil, {"врем текст 02 – протокол"}},
[26599] = {"Сундук капитана", nil, {"Заберите сундук Смоттса у Горлаша на мысе Тернистой долины."}},
[26600] = {"Великанское пиршество", nil, {"Купите ящик третьесортного мяса у Келси Янса и ящик портвейна \"Пупелливербос\" у Алкача Виски."}},
[26601] = {"Мок'раш Колун", nil, {"Убейте Мок'раша на острове Жанейро."}},
[26602] = {"Блюдо подается ящиками", nil, {"Отнесите гигантский ящик еды для великанов в спасательную шлюпку капитана Смоттса, затем приманите с ее помощью Неголаша и убейте его."}},
[26603] = {"Послание в бутылке", nil, {"Найдите автора записки на острове Жагуаро в районе мыса Тернистой долины."}},
[26604] = {"Защита Ее Королевского величества Пубы", nil, {"Убейте 5 жагуаров."}},
[26605] = {"Загребущие лапки", nil, {"Заберите у обезьян острова Жагуаро тиару, скипетр, туфли и дневник Пубы."}},
[26606] = {"Гибель Муклы", nil, {"Убейте короля Муклу и заберите ключ от кандалов."}},
[26607] = {"Они первые пролили кровь", nil, {"Отнесите Джону Джею Кишану его имущество. Джон находится в таверне Приозерья, в Красногорье."}},
[26608] = {"Сорванные переговоры", nil, {"Вернитесь к адмиралу Каменному Кулаку, который находится в море рядом с портом Драконьей Пасти на Сумеречном нагорье."}},
[26609] = {"Пираты Кровавого Паруса", nil, {"Первый помощник Кразз попросил вас осмотреть залив на севере от Пиратской Бухты."}},
[26610] = {"Вероломство пиратов Кровавого Паруса", nil, {"Доложите о своей находке первому помощнику Краззу в Пиратской Бухте."}},
[26611] = {"Предупредить барона", nil, {"Сообщите барону Ревилгазу о готовящемся нападении пиратов Кровавого Паруса."}},
[26612] = {"На разведку", nil, {"Убейте 8 морских волков из шайки Кровавого Паруса и 8 старших волхвов из шайки Кровавого Паруса и раздобудьте секретные карты и приказы пиратов Кровавого Паруса."}},
[26613] = {"Понюшка табака", nil, {"Диг из Пиратской Бухты хочет, чтобы вы принесли ему 15 понюшек табаку."}},
[26614] = {"Смотри в оба", nil, {"Отнимите глаз Диззи у пиратов Кровавого Паруса."}},
[26616] = {"Ни конца ни края", nil, {"Отправьтесь на лодке Кишана в Лагерь Безмолвия в Красногорье."}},
[26617] = {"Старый пират", nil, {"Покажите истертую монету командиру флота Морскому Рогу в Пиратской Бухте."}},
[26618] = {"Волки идут по пятам", nil, {"Убейте 12 лютых волков."}},
[26619] = {"Значит, ты за революцию?", nil, {"Проникните в порт Драконьей Пасти и поговорите с Зелой."}},
[26620] = {"Пряные котлеты из волчатины", nil, {"Добудьте 5 стейков из волчьей грудинки, убивая лютых волков."}},
[26621] = {"Восстание", nil, {"Вооружите 3 рабочих или торговцев из клана Драконьей Пасти, а затем убейте 10 головорезов из клана Драконьей Пасти и 4 чернокнижника из клана Драконьей Пасти."}},
[26622] = {"Смерть Мор'гору!", nil, {"Убейте вождя Мор'гора в порту Драконьей Пасти. Вы можете попросить торговцев и рабочих Драконьей Пасти помочь вам в бою."}},
[26623] = {"Темные пирожки с крабами", nil, {"Добудьте 6 кусочков темного паучьего мяса ядовитых кругопрядов."}},
[26624] = {"Связи с Кровавым Парусом", nil, {"Поговорите с Йенси Гриллсеном у водопада к северу от Пиратской Бухты."}},
[26625] = {"Троггзор Терранатор", nil, {"Добудьте дубинку Терранатора."}},
[26627] = {"Отшельник", nil, {"Разыщите обитателей башни в Приюте Бродяги."}},
[26628] = {"Смерть со дна морского", nil, {"Защитите Мураша О'Брина во время атаки."}},
[26629] = {"Вопрос лояльности", nil, {"Добудьте голову командира флота Морского Рога."}},
[26630] = {"Вполне похоже на таурена-пирата", nil, {"Раздобудьте коровью голову и огромную пиратскую шляпу, а затем отнесите их командиру флота Морскому Рогу."}},
[26631] = {"Пират-новичок", nil, {"Покажите свои бумаги капитану Кильвателю."}},
[26632] = {"Сопровождение", nil, {"Доставьте катапульту земельников в целости и сохранности в Шлаковый зал."}},
[26633] = {"Обязанности уборщика", nil, {"Возьмите на палубе \"Буруна\" швабру юнги и мойте пол в течение 2 минут."}},
[26634] = {"Бич пиратов", nil, {"Раздобудьте 5 бушелей лаймов."}},
[26635] = {"Ныряние за ядрами", nil, {"Добудьте 6 пушечных ядер Кровавого Паруса из-под \"Буруна\"."}},
[26636] = {"Полевой набор компании \"Браво\": Маскировка", nil, {"Соберите 5 охапок листьев и 5 кучек лисьих экскрементов."}},
[26637] = {"Полевой набор компании \"Браво\": Хлороформ", nil, {"Соберите 8 гланд грязекопов."}},
[26638] = {"Охота на охотников", nil, {"Убейте 8 охотников из клана Черной горы."}},
[26639] = {"Информатор Сухопек", nil, {"Найдите Сухопека."}},
[26640] = {"Немыслимые зверства", nil, {"Отнесите донесение Сухопека Джону Джею Кишану в Лагерь Безмолвия в Красногорье."}},
[26641] = {"Без яиц яичницу не сделаешь…", nil, {"Соберите 5 яиц порождения Шадры в Агол'вате."}},
[26642] = {"Во спасение Степей.", nil, {"Отправьтесь с докладом к Каргалу Боевому Шраму на Дальнюю заставу, что в Северных Степях."}},
[26643] = {"На голодном пайке", nil, {"Уничтожьте 6 оплетенных жертв в Ползучих руинах."}},
[26644] = {"Новые горизонты", nil, {"Отправляйтесь к командиру флота Фираллону на \"Кровавую завесу\", швартующуюся у мыса Тернистой долины."}},
[26645] = {"Ночной Дозор", nil, {"Убейте 8 гниющих ужасов возле фермы Слепой Мэри, что к юго-востоку от Темнолесья."}},
[26646] = {"Военнопленные", nil, {"Освободите военнопленных, запертых в клетке в долине Ренда."}},
[26647] = {"Пушка-старушка", nil, {"Научитесь стрелять из пушки и 5 раз попадите ядром в изображение Веселого Тимми."}},
[26648] = {"Наши смертельные враги", nil, {"Убейте 15 ниндзя на острове Жагуаро."}},
[26649] = {"Черное золото", nil, {"Сойдите с \"Кровавой завесы\" на берег, поговорите с гребцом из шайки Кровавого Паруса, а потом из пушки уничтожьте 75 нефтяников Торговой Компании."}},
[26650] = {"Госпожа Удача", nil, {"Отправляйтесь в капитанскую каюту \"Госпожи Удачи\", стоящей у мыса Тернистой долины."}},
[26651] = {"Чтобы выиграть войну, нужно ею стать", nil, {"Воспользуйтесь полевым набором команды \"Браво\", чтобы заложить сефориевый заряд в башню и в склад снаряжения Черной горы."}},
[26652] = {"Нить из призрачных волос", nil, {"Отнесите Слепой Мэри призрачный гребень."}},
[26653] = {"Припасы из Темнолесья", nil, {"Поговорите с мадам Евой в Темнолесье."}},
[26654] = {"Гребень для Евы", nil, {"Верните гребень для призрачных волос мадам Еве в Темнолесье."}},
[26655] = {"Доставка ниток", nil, {"Принесите Аберкромби нить из призрачных волос. Его можно найти в башне в Приюте Бродяги."}},
[26656] = {"Только. Не. Останавливайся.", nil, {"Доставьте как минимум 5 опаловых стражей из Белого гнездовья на восток, к каменному кругу возле Трона Теразан."}},
[26657] = {"Жесткое приземление", nil, {"Убейте 6 каменных драконов."}},
[26658] = {"Хрупкие ценности", nil, {"Найдите каменную киянку."}},
[26659] = {"Резонирующий удар", nil, {"Победите Эозеру."}},
[26660] = {"Сок зомби", nil, {"Поговорите с хозяином таверны Смиттсом."}},
[26661] = {"Сбор цветков гнили", nil, {"Соберите 5 цветков гнили на Безмятежном кладбище."}},
[26662] = {"Крушащая волна", nil, {"Поговорите с долговязым Джоном Медяком на \"Госпоже Удаче\", громилой Шоном Стукером на \"Кровавой завесе\" и Плаксой Мэри Смиттс на \"Буруне\"."}},
[26663] = {"Саботаж", nil, {"Уничтожьте бочонок грога, бочонок пороха и ящик пушечных ядер."}},
[26664] = {"Обвинение в мятеже", nil, {"Обвините 9 корсаров из шайки Кровавого Паруса в мятеже и убейте их."}},
[26665] = {"Зов пиратов", nil, {"Расскажите обо всем барону Ревилгазу в Пиратской Бухте у мыса Тернистой долины."}},
[26666] = {"Легенда о Сталване", nil, {"Найдите клерка Далтри в ратуше Темнолесья."}},
[26667] = {"Украденные письма", nil, {"Соберите пачку изрезанных писем в Светлой роще."}},
[26668] = {"Мгновенный взрыв", nil, {"Взорвите долину Ренда."}},
[26669] = {"В темном углу", nil, {"Обыщите Гниющий сад и найдите вырванную страницу дневника."}},
[26670] = {"Погибель Роланда", nil, {"Обыщите Погибель Роланда и найдите оставшиеся страницы дневника Сталвана."}},
[26671] = {"Судьба Сталвана Мистмантла", nil, {"Отнесите легенду о Сталване Тобиасу Мистмантлу."}},
[26672] = {"Узнать правду", nil, {"Отнесите фамильное кольцо Мистмантла мадам Еве."}},
[26674] = {"Месть Мистмантла", nil, {"Воспользуйтесь фамильным кольцом Мистмантла в поместье Мистмантла, к востоку от города."}},
[26675] = {"Цена доверия", nil, nil},
[26676] = {"Доставка сока", nil, {"Отнесите сок зомби в хижину Аберкромби, что в Приюте Бродяги."}},
[26677] = {"Чучело вурдалака", nil, {"Достаньте 7 ребер вурдалаков у зловонных трупов в поместье Мистмантла."}},
[26678] = {"Крепленый ром", nil, {"Разыщите бочонок крепленого рома барона Ревилгаза в Пиратской Бухте."}},
[26679] = {"Возвращение к Ревилгазу", nil, {"Доставьте крепленый ром барону Ревилгазу в Пиратскую Бухту."}},
[26680] = {"Нападение огров", nil, {"Верните ящик Аберкромби владельцу."}},
[26681] = {"Письмо для мэра", nil, {"Отнесите письмо Аберкромби мэру Темнолесья, Элло Чернодреву."}},
[26682] = {"Неуклюжая угроза", nil, {"Уничтожьте 7 сухолистых ползунов или сухолистых яростеней в топи к северу от деревни Гиблотопь."}},
[26683] = {"Взгляни на звезды", nil, {"Найдите Мэри и уговорите ее пожертвовать зеркало для проекта Виктора."}},
[26684] = {"Невменяемый вурдалак", nil, {"Заберите зеркало Слепой Мэри у невменяемого вурдалака."}},
[26685] = {"Отличное стекло", nil, {"Убейте огра Ззарк'Вула, живущего в самом южном кургане Сумеречного леса и возьмите его монокль."}},
[26686] = {"Ходячие кости", nil, {"Убейте 5 воинов-скелетов и 5 магов-скелетов по просьбе командира Алтеи Чернодрев."}},
[26687] = {"Подкрепления из Терамора", nil, {"Отправляйтесь на остров Терамор в Пылевые топи и поговорите с капитаном Гарраном Ваймсом."}},
[26688] = {"Воргены в лесу", nil, {"Убейте 7 воргенов из стаи Ночной Погибели в Светлой роще."}},
[26689] = {"Гниющий сад", nil, {"Убейте 10 тенеткачей Ночной Погибели в Гниющем саду."}},
[26690] = {"Ярые и Нечистые", nil, {"Убейте 8 Ярых Клыков и 8 Нечистых Воргенов из стаи Ночной Погибели в Погибели Роланда."}},
[26691] = {"Воргены в лесу", nil, {"Отнесите письмо Калора Джонатану Карвину."}},
[26692] = {"Истребление Темношкуров", nil, {"Убейте 20 любых гноллов из стаи Темношкуров."}},
[26693] = {"Темная башня", nil, {"Раздобудьте ключ от башни Илгалара."}},
[26694] = {"Великий волхв Доан", nil, {"Убейте великого волхва Доана."}},
[26695] = {"Приготовиться к взлету!", nil, {"Добудьте ручные гранаты Наркка в здании портовой инспекции в Пиратской Бухте."}},
[26696] = {"Зов Калимдора", nil, {"Отправляйтесь на корабле из Пиратской Бухты в Кабестан, где вам следует поговорить с Газлоу."}},
[26697] = {"Последнее путешествие Крушащей Волны", nil, {"Поговорите с Кебоком в Пиратской Бухте и попросите его посадить вас на смелого ветрокрыла. Затем при помощи гранат прикончите 85 матросов Крушащей Волны и потопите 6 боевых лодок Крушащей Волны."}},
[26698] = {"В поисках Морского Рога", nil, {"Поговорите с командиром флота Морским Рогом в Пиратской Бухте."}},
[26699] = {"Борьба с Крушащей Волной", nil, {"Убейте 10 представителей Крушащей Волны в Пиратской Бухте."}},
[26700] = {"Госпожа не-Удача", nil, {"Убейте 8 корсаров из шайки Кровавого Паруса в Пиратской Бухте."}},
[26701] = {"Полет в Гиблотопь", nil, {"Обратитесь к Брагоку в Кабестане, чтобы перелететь в Гиблотопь. Затем явитесь к Крогу."}},
[26702] = {"Полет в Терамор", nil, {"Обратитесь к Брагоку в Кабестане, чтобы перелететь в Терамор. Затем явитесь к Калии Гастингс."}},
[26703] = {"Конец Кровавому Парусу!", nil, {"Поговорите с Кебоком в Пиратской Бухте, получите ветрокрыла, а потом уничтожьте командира флота Фираллона."}},
