[26910] = {"Запечатанное письмо", nil, {"Прочитайте запечатанное письмо и поговорите с Эшли Бланк в долине Североземья."}},
[26911] = {"История Мизраэль", nil, {"Вернитесь к Заруку в Павший Молот на Нагорье Арати."}},
[26912] = {"Выпущенная принцесса", nil, {"Призовите к кристаллам Мизраэль и победите ее."}},
[26913] = {"Устремляясь в бой", nil, {"Поговорите с Лланом Бишером и обучитесь у него приему \"Рывок\", затем опробуйте этот прием на тренировочном манекене, расположенном к югу от входа в аббатство Североземья."}},
[26914] = {"Жертвенный огонь", nil, {"Поговорите с Друзиллой ла Салле и обучитесь у нее заклинанию \"Жертвенный огонь\", затем 5 раз опробуйте это заклинание на тренировочном манекене, расположенном к югу от входа в аббатство Североземья."}},
[26915] = {"Глубочайший порез", nil, {"Поговорите с Джориком Керриданом и обучитесь у него приему \"Потрошение\", затем разыщите тренировочный манекен, расположенный к югу от входа в Аббатство Североземья, и 3 раза опробуйте на нем этот прием."}},
[26916] = {"Постижение тайной магии", nil, {"Поговорите с Кельденом Бременом и обучитесь у него заклинанию \"Чародейские стрелы\", затем 2 раза опробуйте это заклинание на тренировочном манекене, расположенном к югу от входа в аббатство Североземья."}},
[26917] = {"Путь охотника", nil, {"Поговорите с Эшли Бланк и обучитесь у нее приему \"Верный выстрел\", затем разыщите тренировочный манекен, расположенный к югу от входа в аббатство Североземья, и сделайте по нему 5 \"Верных выстрелов\"."}},
[26918] = {"Сила Света", nil, {"Поговорите с братом Самуэлем и обучитесь у него заклинаниям \"Печать праведности\" и \"Правосудие света\". Наложите на себя \"Печать праведности\", а затем опробуйте \"Правосудие света\" на тренировочном манекене, расположенном к югу от входа в аббатство Североземья."}},
[26919] = {"Исцеление раненых", nil, {"Поговорите со жрицей Анеттой и обучитесь у нее заклинанию \"Быстрое исцеление\", затем 5 раз опробуйте это заклинание на раненых новобранцах в аббатстве Североземья."}},
[26920] = {"Битва за Андорал", nil, {"Отчитайтесь перед Кольтирой Ткачом Смерти в Андорале в Западных Чумных землях."}},
[26921] = {"Сначала Плеть... Потом Альянс", nil, {"Уничтожьте 15 иссохших воинов Плети в Андорале."}},
[26922] = {"Бесконечный поток", nil, {"Подорвите 5 костяных берсерков и уничтожьте костяного стража Плети ручными гранатами Джерла."}},
[26923] = {"Боевые машины", nil, {"Уничтожьте 6 пустобрюхих громадин в Андорале."}},
[26924] = {"Некроректор", nil, {"Убейте в Андорале темного магистра Гандлинга."}},
[26925] = {"Араж Призыватель", nil, {"Убейте Аража Призывателя. С костяными берсерками справляйтесь с помощью ручных гранат Джерла."}},
[26926] = {"Сиюминутная победа", nil, {"Доложитесь верховному палачу Деррингтону в Бастионе в Тирисфальских лесах."}},
[26927] = {"Откуда ни возьмись – мурлоки!", nil, {"Поговорите с Каннари Пивной Бочкой."}},
[26928] = {"Запахло новыми идеями", nil, {"Добудьте 7 ароматических желез мурлоков из племени Синежабрых."}},
[26929] = {"Партия кроколисков", nil, {"Соберите 6 челюстей кроколисков."}},
[26930] = {"После ордена", nil, {"Разведайте лагерь Алого ордена, расположенный к востоку от поля Джанис в Западных Чумных землях."}},
[26931] = {"Лисы и гончие", nil, {"Убейте 4 альянсовых охотников на лис, 4 бульмастифов и 4 белохвостых лисиц в Чумных землях."}},
[26932] = {"Конец канюкам", nil, {"Убейте 8 озерных канюков."}},
[26933] = {"Мечи и орала", nil, {"Убейте 9 работников Альянса на поле Джанис."}},
[26934] = {"Скрытая болезнь", nil, {"Убейте больных черных медведей в Западных Чумных землях, а затем сорвите 20 пучков чумной поросли, на которой они лежали."}},
[26935] = {"Лесопилка Северного Кряжа", nil, {"Поговорите с Натаниэлем Дамахом на лесопилке Северного Кряжа в Западных Чумных землях."}},
[26936] = {"Поехали!", nil, nil},
[26937] = {"Когда смерть еще не конец", nil, {"Убейте 8 чернорабочих Альянса на поле Джанис, чтобы ваша порабощенная валь'кира смогла обратить их в Отрекшихся."}},
[26938] = {"Боевые учения", nil, {"С помощью способностей вашей порабощенной валь'киры подчините 5 солдат-Отрекшихся на Поле Джанис. Затем уничтожьте пустобрюхое поганище в хлеву Фелстоунов."}},
[26939] = {"Команда \"Г\"", nil, {"Уничтожьте Липкую муть."}},
[26940] = {"Чародейские стрелы", nil, {"Обучитесь заклинанию \"Чародейские стрелы\" у Рианды. Найдите тренировочный манекен и дважды испытайте на нем Чародейские стрелы."}},
[26941] = {"Команда \"Г\"", nil, {"Ликвидируйте электрошокер-6000."}},
[26942] = {"Команда \"Г\"", nil, {"Убейте Анжинера Термоштепселя."}},
[26943] = {"Дом, милый гном", nil, {"Поговорите с Мерд-Доком."}},
[26944] = {"Тайны Гномрегана", nil, {"Поговорите с Мерд-Доком."}},
[26945] = {"Новые приемы", nil, {"Обучитесь у Алайссии рывку. Найдите поблизости тренировочный манекен и попрактикуйтесь на нем."}},
[26946] = {"Преимущество разбойника", nil, {"Обучитесь Потрошению у Фрагуна Шепота Тени. Найдите тренировочный манекен к юго-востоку от Альдрассила и проведите на нем прием потрошения 3 раза."}},
[26947] = {"Обучение лесного жителя", nil, {"Обучитесь верному выстрелу у Аянны Вечной Странницы. Найдите тренировочный манекен к юго-востоку от Альдрассила и произведите верный выстрел 5 раз."}},
[26948] = {"Омоложение", nil, {"Обучитесь \"Омоложению\" у Марданта Крепкого Дуба и с помощью этого заклинания помогите искалеченному часовому в Альдрассиле."}},
[26949] = {"Исцеление раненых", nil, {"Обучитесь \"Быстрому исцелению\" у Шанды. Используйте \"Быстрое исцеление\" 5 раз, чтобы вылечить искалеченных часовых в Альдрассиле."}},
[26950] = {"Темная сторона света", nil, {"Убейте дознавателя Вишаса и волшебника крови Талноса."}},
[26951] = {"Зачистка островов Э.", nil, {"Соберите 8 распыленных масок Тики."}},
[26952] = {"Участок Целителей Земли", nil, {"Отчитайтесь перед агентом ШРУ Каартишем на Участке Целителей Земли, что в Западных Чумных землях."}},
[26953] = {"Друид Зен'Кики", nil, {"Убейте вместе с Зен'Кики 20 зараженных животных. Вы можете охотиться на неповоротливых чумных медведей, зараженных волков, воронковых пауков Ядовитого Тумана и чумных воронковых пауков.\n\nЕсли вы с Зен'Кики потеряетесь, возвращайтесь на Участок Целителей Земли."}},
[26954] = {"Разный подход", nil, {"Отведите Зен'Кики на вершину холма к востоку от Участка Целителей Земли, затем заставьте его призвать и уничтожить 7 больных ястребов."}},
[26955] = {"Зен'Кики и служители культа", nil, {"Отведите Зен'Кики к Заставе Коварнессы в Западных Чумных землях, а затем выпустите из клеток 4 пойманных чумных медведей и дайте Зен'Кики их вылечить."}},
[26956] = {"Прополка", nil, {"Избавьтесь от 6 испорченных кукурузных стеблей, 4 гнилых яблока и 3 пораженных болезнью тыкв на Ферме Далсона в Западных Чумных землях."}},
[26957] = {"Долгая дорога домой", nil, {"Встретьтесь с Дель Гарроном на Участке Целителей Земли в Западных Чумных землях."}},
[26958] = {"Первый урок", nil, {"Обучитесь приему \"Рывок\" у Коура. Разыщите тренировочный манекен на северо-западной стороне места крушения и отработайте на нем \"Рывок\"."}},
[26959] = {"Ослабить защиту", nil, {"Убейте псаря Локси и чародея Доана."}},
[26960] = {"Мой сын, принц", nil, {"Познакомьтесь с принцем Андуином в крепости Штормграда. После этого поговорите с королем Ринном."}},
[26961] = {"Сбор идолов", nil, {"Принесите 8 резных каменных идолов Магмару Рудоколу на раскопки Сталекрута."}},
[26962] = {"Атака на Оружейную", nil, {"Убейте Ирода."}},
[26963] = {"Твердая рука, меткий выстрел", nil, {"Обучитесь приему \"Верный выстрел\" у Кейлней. Разыщите тренировочный манекен на северо-западной стороне места крушения и сделайте по нему 5 \"Верных выстрелов\"."}},
[26964] = {"Приказ вождя: на Серебряный бор!", nil, {"Явитесь к главному палачу Мортию в Ставку Отрекшихся в Серебряном бору."}},
[26965] = {"Вождь идет!", nil, {"Дождитесь прибытия вождя Гарроша Адского Крика."}},
[26966] = {"Сила света", nil, {"Обучитесь заклинаниям \"Печать праведности\" и \"Правосудие\" у Аурелона. Наложите на себя \"Печать праведности\", разыщите тренировочный манекен на северо-западной стороне места крушения и испытайте на нем \"Правосудие света\"."}},
[26967] = {"Битва за Монастырь Алого ордена", nil, {"Убейте командира Алого ордена Могрейна и верховного инквизитора Вайтмейн."}},
[26968] = {"Чародейские стрелы", nil, {"Обучитесь заклинанию \"Чародейские стрелы\" у Валаату. Разыщите тренировочный манекен на северо-западной стороне места крушения и 2 раза испытайте на нем это заклинание."}},
[26969] = {"Стихийный удар", nil, {"Обучитесь \"Стихийному удару\" у Фирманваара. Разыщите тренировочный манекен на северо-западной стороне места крушения и 3 раза испытайте на нем этот прием."}},
[26970] = {"Помощь раненым", nil, {"Обучитесь \"Быстрому исцелению\" у Зальдууна. 5 раз примените \"Быстрое исцеление\" к раненым дренеям у места крушения."}},
