[28390] = {"Глоп, сын Глопа", nil, {"Поговорите с целительницей земли Норсалой, а затем убейте грибоманта Глопа."}},
[28391] = {"Беспокойный род", nil, {"Победите Эозеру."}},
[28392] = {"Племя Древобрюхов", nil, {"Поговорите с Нафиеном у входа в Крепость Древобрюхов."}},
[28393] = {"Опасный союз", nil, {"Убейте 6 огнестражей-разрушителей и завладейте планами нападения генерала Кузни Гнева в Западном гарнизоне, что в Глубинах Черной горы."}},
[28394] = {"Создания повелителя големов", nil, {"Добудьте 5 стихийных модулей, убив големов на фабрике в Глубинах Черной горы, и найдите чертежи стихийных големов."}},
[28395] = {"Перья для Нафиена", nil, {"Требования:"}},
[28396] = {"Перья для Гразла", nil, {"Требования:"}},
