[28444] = {"Присмиревшие демоны", nil, {"Соберите тела 18 поверженных демонов огня."}},
[28445] = {"Аппетитная туша ворга", nil, {"Воспользуйтесь свежевателем воргов, чтобы добыть из поверженных огромных пепельных воргов вырезку из ворга в количестве 40 штук."}},
[28446] = {"Кровавый поход", nil, {"Убейте 25 черных драконоидов или черных драконид."}},
[28447] = {"Драконий авангард", nil, {"Ликвидируйте Минифа, укротительницу драконов Акуми, Терромата Опаленного и Тугнара Кровобрюха."}},
[28448] = {"Сделка с драконом", nil, {"Доставьте спасенных дракончиков Акрайду к Скользкой скале, что в Пылающих степях."}},
