[29316] = {"Азы профессии", nil, {"Помогите обитателям Террасы Ремесленников и Террасы Торговцев смолоть содержимое корзинок в рисовую муку."}},
[29317] = {"Выловить голову", nil, {"Арман Кромвель хочет, чтобы вы выловили отрубленную голову поганища в Подгороде и отнесли ее опытному аптекарю Фаранеллу."}},
[29318] = {"Ребрышки для часовых", nil, {"Приготовьте сырые ребрышки в печи или на огне, а затем накормите ими 5 дарнасских часовых."}},
[29319] = {"Опасные головастики", nil, {"Выловите 8 гигантских плотоядных головастиков для Армана Кромвеля из Подгорода."}},
[29320] = {"Любишь щуку?", nil, {"Накопайте трупных червей во внутреннем дворе Руин Лордерона и используйте их как наживку для щуки-трупоеда, которую можно выловить в озере Ясноводном к северо-востоку от Подгорода."}},
[29321] = {"Накопать моллюсков", nil, {"Соберите 10 моллюсков Тельдрассила на берегу вблизи деревни Рут'терран."}},
[29322] = {"Пора слизней", nil, {"Выловите 10 извивающихся плесневых слизней и отнесите их Арману Кромвелю в Подгород."}},
[29323] = {"Запасаемся", nil, {"Выудите 5 озерных сигов в Дарнасе."}},
[29324] = {"Подвеска сестры", nil, {"Найдите подвеску Аквинны в форме полумесяца внутри гигантской зубатки. Эту рыбу можно выловить повсеместно в Дарнасе."}},
[29325] = {"Скользкая закуска", nil, {"Наберите 6 осьминожков на мелководье вблизи деревни Рут'теран."}},
[29326] = {"Встреча у Нордрассила", nil, {"Поговорите с Траллом под Древом Жизни на горе Хиджал и поприсутствуйте на церемонии Служителей Земли."}},
[29327] = {"Власть стихий: сомнение", nil, {"Поговорите с Циклонасом, чтобы пройти к Траллу. Убивайте духов ветра, которые мучают его, до тех пор, пока он не будет освобожден."}},
[29328] = {"Власть стихий: желание", nil, {"Убивайте волны стремлений и вихри желаний у Обрыва над бездной, пока Тралл не освободится."}},
[29329] = {"Власть стихий: терпение", nil, {"Сообщите Аггре, когда будете готовы отправиться на помощь Траллу. Затем уничтожьте стихии терпения и краеугольные камни терпения, удерживающие пленника."}},
[29330] = {"Власть стихий: ярость", nil, {"Убивайте элементалей возле воздушного тотема Аггры, расположенного в западной части плато, до тех пор, пока он не активируется. Затем проделайте то же самое с водным, земным и огненным тотемами Аггры."}},
[29331] = {"Власть стихий: обет", nil, {"Поговорите с Траллом в Нордрассиле на горе Хиджал."}},
[29332] = {"Вот так лилии!", nil, {"Соберите 10 лилий сточных вод и отнесите растения Эвнике Барч в Подгород."}},
[29333] = {"Улитки прытки", nil, {"Соберите 8 пресноводных улиток и отнесите их Эвнике Барч в Подгород."}},
[29334] = {"Всем тараканам тараканы", nil, {"Соберите 20 жирных тараканов и отнесите их шеф-повару Одри в Подгород."}},
[29335] = {"Стремительные ветра", nil, {"Поговорите с Циклонасом на Утесе Южного Ветра в Ульдуме."}},
[29336] = {"В глубины вод", nil, {"Поговорите с Гидриасом у Обрыва над бездной, что в Бездонных глубинах в Вайш'ире."}},
[29337] = {"В толщу земли", nil, {"Поговорите с Теразан у Трона Теразан в Подземье.\n\nАггра может телепортировать вас к Трону Теразан с Обрыва над бездной."}},
[29338] = {"Жестокий огонь", nil, {"Поговорите с Аггрой на Незыблемом утесе в Подземье, чтобы попасть в Огненные Просторы."}},
[29339] = {"Награда за самоотверженность", nil, nil},
[29340] = {"Награда за самоотверженность", nil, nil},
[29341] = {"Награда за самоотверженность", nil, nil},
[29342] = {"Зимняя рыбалка", nil, {"Поймайте 5 арктических гольцов в озере Хельмово Ложе, что в Дун Мороге."}},
[29343] = {"Легендарная рыба", nil, {"Поймайте молодого сталезуба в Заброшенном гроте Стальгорна и выставьте его на обозрение над пустой табличкой в Библиотеке."}},
[29344] = {"Рыба для Трескунчика", nil, {"Поймайте 3 слепые ледоспинки в Заброшенном гроте и накормите ими Трескунчика."}},
[29345] = {"Хищники в пруду", nil, {"Ках Вестник Туманов из Громового Утеса просит вас выловить 8 азшарских змееголовов из пруда Громового Утеса."}},
[29346] = {"Дело в кольце", nil, {"Отправляйтесь на рыбалку на Пруды Видений в Громовом Утесе. Когда поймаете слепоглазку, выпотрошите ее и поищите внутри кольцо."}},
[29347] = {"На живца", nil, {"Намажьтесь наживкой Мрачнура, а затем прыгайте в озеро Хельмово Ложе, что в Дун Мороге."}},
[29348] = {"Заселить водоем", nil, {"Поймайте в озере Каменного Быка 2 страстные блестящие рыбки и 2 любвеобильных илистых луцианов и отнесите их Каху Вестнику Туманов."}},
[29349] = {"Деликатесные раки", nil, {"Ках Вестник Туманов из Громового Утеса просит вас поймать 10 раков из озера Каменного Быка в Мулгоре."}},
[29350] = {"Гномский блесн-о-матик", nil, {"Возьмите 5 блесен, взрыватель и 3 крысоловки и с помощью чертежей соберите гномский блесн-о-матик."}},
[29351] = {"Забота о стражниках", nil, {"Раздайте стальгорнский паек 6 стражникам Стальгорна."}},
[29352] = {"Нужны цыплята", nil, {"Поймайте 6 дунморогских цыплят у входа в Стальгорн."}},
[29353] = {"Рулета с потрошками много не бывает", nil, {"Купите мешок бараньих потрохов, 5 пакетиков пряных трав и 4 мешка толокна."}},
[29354] = {"Блестящие камешки", nil, {"Соберите 20 блестящих камешков у подножия центрального уступа для Нахари Заоблачной Охотницы."}},
[29355] = {"Хлеба с пряностями много не бывает", nil, {"Приготовьте сами или купите 10 хлебов с пряностями."}},
[29356] = {"Доставка бочонка", nil, {"Заберите бочонок стальгорнского светлого эля Драгана, который стоит у пивоварни в Караносе, и доставьте его в Стальгорн."}},
[29357] = {"Хлеба с пряностями хоть отбавляй", nil, {"Приготовьте сами или купите 10 хлебов с пряностями."}},
[29358] = {"Недостаток орехов", nil, {"Соберите 30 кедровых орешков, подержав над огнем мулгорские кедровые шишки, затем отнесите их Аске Вестнице Туманов в Громовой Утес."}},
[29359] = {"Любимая рыба", nil, {"Отправляйтесь в деревню Рут'теран и поймайте 8 калдорайских сельдей."}},
[29360] = {"Как насчет того, чтобы добавить мух?", nil, {"Соберите 25 жирных мух с \"мяса\", лежащего рядом с мясными фургонами. Затем вернитесь к Эвнике Барч в Подгород."}},
[29361] = {"Канавный монстр!", nil, {"Воспользуйтесь набором для ловли на живца, чтобы приманить канавного монстра в Руинах Лордерона. Затем скормите ему жирных жаб."}},
[29362] = {"\"Волшебные\" грибочки", nil, {"Соберите 6 \"волшебных\" грибочков у Прудов Видений в Громовом Утесе. Отнесите их Аске Вестнице Туманов."}},
[29363] = {"Мулгорский хлеб с пряностями", nil, {"Возьмите обычный хлеб с пряностями, мулгорские пряности и приготовьте мулгорский хлеб с пряностями. Обычный хлеб с пряностями вы можете либо купить, либо испечь самостоятельно. Отнесите готовый хлеб Аске Вестнице Туманов в Громовой Утес."}},
[29364] = {"Перемолка зерна", nil, {"Помогите жителям Громового Утеса перемолоть зерно в муку."}},
[29365] = {"Самые лучшие продукты", nil, {"Обойдите торговцев Громового Утеса и принесите Аске Вестнице Туманов самые лучшие мясо, рыбу, овощи и травы."}},
[29371] = {"Время терять", nil, {"Отправляйтесь в Руины Лордерона и облейте Плетеного человека гасящим веществом."}},
[29372] = {"Переполох в захолустье", nil, {"Спасите шерифа."}},
[29373] = {"Переполох в захолустье", nil, {"Спасите шерифа."}},
[29374] = {"Сбросить бомбы-вонючки", nil, {"Поговорите с Криной Фэнлоу, затем садитесь на метлу и бросайте бомбы-вонючки на Штормград."}},
[29375] = {"Уборка в Подгороде", nil, {"Воспользуйтесь чародейским нейтрализатором и очистите Подгород от бомб-вонючек."}},
[29376] = {"Время созидать", nil, {"Воспользуйтесь большим костром, чтобы поджечь Плетеного человека. Потом воспользуйтесь пеплом, который появится на земле возле Плетеного человека."}},
[29377] = {"Время разрушать", nil, {"Отправляйтесь в Штормград и облейте Плетеного человека гасящим веществом."}},
[29382] = {"Спасибо, конечно, но…", nil, {"Если вам правда не нравится ваш подарок, вы можете обменять его на золото, поговорив с одним из торговцев пастбищ Дымного леса.\n\nОтменить сделку будет нельзя."}},
[29383] = {"Спасибо, конечно, но…", nil, {"Если вам правда не нравится ваш подарок, вы можете обменять его на золото, поговорив с одним из торговцев пастбищ Дымного леса.\n\nОтменить сделку будет нельзя."}},
