[29386] = {"Воззвание к героям: гора Хиджал!", nil, {"Поговорите с кенарийской посланницей Нефритовой Луной, чтобы попасть на Лунную поляну."}},
[29392] = {"Пропавшие фамильные ценности", nil, {"Осмотрите каюту на корабле наемного курьера, чтобы понять, что могло его задержать."}},
[29400] = {"Время праздника", nil, {"Поговорите с зовущей Тьму Янкой в Руинах Лордерона у входа в Подгород."}},
[29401] = {"Улетевшие шарики", nil, {"Джага просит вас найти в Оргриммаре 5 унесенных ветром воздушных шариков."}},
[29412] = {"Улетевшие шарики", nil, {"Вин просит вас найти в Штормграде 5 унесенных ветром шариков."}},
[29415] = {"Пропавшие фамильные ценности", nil, {"Осмотрите каюту на корабле наемного курьера, чтобы понять, что могло его задержать. Корабль находится в порту, в восточной части Оргриммара."}},
[29430] = {"Друг в беде", nil, {"Поговорите с Ансоном Гастингсом в таверне \"Позолоченная роза\" в Торговом квартале Штормграда."}},
[29431] = {"Друг в беде", nil, {"Поговорите с Эдгаром Гудвином в \"Сломанном клыке\" в Оргриммаре."}},
[29433] = {"Испытай свою силу", nil, {"Керри Хикс хочет, чтобы вы добыли 250 жутких трофеев. Вы можете получать трофеи, убивая существ, за победу над которыми полагается опыт, если у вас при себе есть справочник ярмарки Новолуния."}},
[29434] = {"Командир танка", nil, {"Управляя паровым танком, поразите 30 целей за отведенное время."}},
[29436] = {"Человек-пушка", nil, {"Наберите 5 очков, приземляясь рядом с мишенью для пушки."}},
[29437] = {"Падшая стражница", nil, {"Сразитесь с Алисрой в Руинах Лар'донира на горе Хиджал и убейте ее."}},
[29438] = {"В яблочко!", nil, {"Выбейте 25 очков за 60 секунд."}},
[29439] = {"Зов шамана", nil, {"Поговорите с Траллом южнее Древа Жизни, что на горе Хиджал."}},
[29440] = {"Зов шамана", nil, {"Поговорите с Траллом южнее Древа Жизни, что на горе Хиджал."}},
[29443] = {"Интересный кристалл", nil, {"Отнесите зачарованный кристалл профессору Таддеусу Палео на ярмарку Новолуния, проходящую на Острове Новолуния."}},
[29445] = {"Загадочный гримуар", nil, {"Отнесите загадочный гримуар Воржею на ярмарку Новолуния, проходящую на Острове Новолуния."}},
[29455] = {"Цель: черепаха", nil, {"Набросьте 3 кольца на черепаху ярмарки Новолуния."}},
[29463] = {"Гноллобой", nil, {"Наберите 30 очков, колошматя гноллов."}},
[29475] = {"Гоблинское инженерное дело", nil, {"Принесите 20 больших железных бомб, 20 шашек твердого динамита и 5 взрывоопасных овец учителю инженерного дела в любой столице."}},
[29477] = {"Гномское инженерное дело", nil, {"Принесите 6 мифриловых трубок, точный прицел и 2 усовершенствованных манекена-мишени учителю инженерного дела в любой столице."}},
[29481] = {"Мастер эликсиров", nil, {"Принесите несколько разных настоев учителю алхимии в любой столице."}},
[29482] = {"Мастер трансмутации", nil, {"Принесите необходимые материалы учителю алхимии в любой столице."}},
[29507] = {"Развлечения для самых маленьких", nil, {"Принесите 15 фрагментов окаменелостей профессору Таддеусу Палео на ярмарку Новолуния, проходящую на Острове Новолуния."}},
[29512] = {"Поставить работников на ноги", nil, {"Воспользуйтесь бинтами ярмарки Новолуния, чтобы вылечить 4 раненых работников."}},
[29527] = {"Взятие укреплений", nil, {"Убейте начальника стражи Гарголмара, Вазрудена Глашатая и дракона Вазрудена, Назана. Принесите руку Гарголмара и голову Назана каменному стражу Сток'тону."}},
[29528] = {"Взятие укреплений", nil, {"Убейте начальника стражи Гарголмара, Вазрудена Глашатая и дракона Вазрудена, Назана. Принесите руку Гарголмара и голову Назана главному разведчику Чадвику."}},
[29529] = {"Демоны в цитадели", nil, {"Убейте Омора Неодолимого и принесите его копыто главному разведчику Чадвику."}},
[29530] = {"Демоны в цитадели", nil, {"Убейте Омора Неодолимого и принесите его копыто каменному стражу Сток'тону."}},
[29535] = {"Пустить кровь", nil, {"Достаньте для Каса'реса 10 фиалов крови орков и жезл вливания Скверны."}},
[29536] = {"В сердце ярости", nil, {"Детально изучите Кузню Крови и доложите о результатах Каса'ресу."}},
[29537] = {"Найти просвет", nil, {"Убейте Кели'дана Разрушителя и вернитесь к Каса'ресу."}},
[29538] = {"Пустить кровь", nil, {"Достаньте для Пушкаренка 10 фиалов крови орков и жезл вливания Скверны."}},
[29539] = {"В сердце ярости", nil, {"Детально изучите Кузню Крови и доложите о результатах Пушкаренку."}},
[29540] = {"Разрушитель", nil, {"Убейте Кели'дана Разрушителя и вернитесь к Пушкаренку."}},
[29542] = {"Захват крепости", nil, {"Поговорите с каменным стражем Сток'тоном в Бастионах Адского Пламени."}},
[29543] = {"Захват крепости", nil, {"Поговорите с главным разведчиком Чадвиком в Бастионах Адского Пламени."}},
[29563] = {"Пропавшие без вести", nil, {"Выясните, что случилось с натуралистом Кусом и культиватором Зеленопалом, после чего возвращайтесь к дозорной Джанг."}},
[29564] = {"Преданный брат", nil, {"Убейте Менну Предателя и возвращайтесь к Нахууду."}},
[29565] = {"Суть дела", nil, {"Принесите дозорной Джанг Коготь захватчика и Глаз повелителя рабов."}},
[29566] = {"Наведение справок", nil, {"Поговорите с дозорной Джанг в Узилище."}},
[29567] = {"Охота на Охотницу", nil, {"Принесите Тшу мозг Черной Охотницы."}},
[29568] = {"Необходимое зло", nil, {"Убейте Топеглада, после чего возвращайтесь к натуралисту Кусу."}},
[29570] = {"Спасение экспедиции", nil, {"Выясните, что случилось с землепрядом Гневвсом и призывателем ветров Когтем, после чего возвращайтесь к дозорной Джанг."}},
[29571] = {"Защита природы", nil, {"Поговорите с дозорной Джанг в Нижетопи."}},
[29573] = {"Прежде всего – безопасность!", nil, {"Убейте 10 эфириалов – осквернителей гробниц, 5 эфириалов-колдунов, 5 ловчих нексуса и 5 эфириалов-чароплетов, а затем вернитесь к работнику Морфалиусу."}},
[29574] = {"Интригующие образцы", nil, {"Принесите сущность Пандемония и сердце Таварока Мамди по кличке \"Олог\"."}},
[29575] = {"Жестокая вещь – конкуренция", nil, {"Принесите обмотки Шаффара работнику Морфалиусу."}},
[29582] = {"Безумная ночь Грека", nil, {"Поговорите с работником Морфалиусом в гробницах Маны в Аукиндоне."}},
[29583] = {"Сумасшедший Акерт", nil, {"Поговорите с работником Морфалиусом в гробницах Маны в Аукиндоне."}},
[29590] = {"Страж Мертвых", nil, {"Убейте Ширрака Стража Мертвых, затем возвращайтесь к духу дренея."}},
[29591] = {"Разъяренные духи", nil, {"Убейте 5 разгневанных скелетов, 5 разъяренных скелетов и 3 аукенайских некроманта, после чего возвращайтесь к истязаемому жрецу душ в Аукенайские гробницы."}},
[29593] = {"Удар по уязвимой точке", nil, {"Украдите 3 связки припасов Адского Пламени, а затем вернитесь к каменному стражу Сток'тону в Бастионы Адского Пламени."}},
[29594] = {"Удар по уязвимой точке", nil, {"Украдите 3 связки припасов Адского Пламени, а затем вернитесь к главному разведчику Чадвику в Бастионы Адского Пламени."}},
[29595] = {"Все будет в порядке", nil, {"Проникните в Аукенайские гробницы и поговорите с духом дренея по просьбе Великого Отца Алдримуса, который находится снаружи Аукенайских гробниц Аукиндона."}},
[29596] = {"Конец экзарху", nil, {"Убейте экзарха Маладаара и вернитесь к духу дренея в Аукенайские гробницы."}},
[29598] = {"План Тареты", nil, {"Проберитесь в крепость Дарнхольд и заложите пять зажигательных бомб возле бочек внутри каждого из бараков на выселках. Используйте упаковку зажигательных бомб, полученных от Эрозиона.\n\nПосле того как подожжете бараки, разыщите Тралла в подземной темнице."}},
[29599] = {"Побег из Дарнхольда", nil, {"Когда вы будете готовы идти, сообщите об этом Траллу. Помогите ему бежать из крепости Дарнхольд, освободить Тарету и исполнить свое предназначение.\n\nПосле этого возвращайтесь к Эрозиону в Старый Хилсбрад."}},
[29605] = {"Брат на брата", nil, {"Освободите из плена Лакку. После этого вернитесь к Исфару, который находится в Сетеккских залах."}},
[29606] = {"Наследство Терокка", nil, {"Принесите маску Терокка и перо Терокка Исфару, который находится в Сетеккских залах."}},
[29607] = {"Глаза желания", nil, {"Делец Вийяд из Сетеккских залов хочет, чтобы вы принесли ему 3 кобальтовых глаза."}},
[29608] = {"Союзники в Даларане", nil, {"Прилетите на грифоне в Даларан и поговорите с Изирами Попутный Ветер в таверне \"Благодарность за отвагу\"."}},
[29609] = {"Друзья в Даларане", nil, {"Летите в Даларан и поговорите с Удой Зверюгой в трактире \"Грязное животное\"."}},
[29613] = {"Бесславный конец", nil, {"Убейте полководца Калитреша и возвращайтесь к дозорной Джанг в Паровое подземелье."}},
[29614] = {"Сдерживание заразы", nil, {"Убейте 3 властителя болот, 6 лепрогномов из резервуара Кривого Клыка, а также достаньте облученный механизм Анжинера Паропуска, после чего возвращайтесь к натуралисту Кусу в Паровое подземелье."}},
[29615] = {"Призыватель ветра Коготь и водокрад", nil, {"Убейте гидроманта Теспию и возвращайтесь к призывателю ветра Когтю в Паровое подземелье."}},
[29616] = {"Штурм Парового подземелья", nil, {"Поговорите с дозорной Джанг в Паровом подземелье."}},
[29640] = {"Найти шпиона То'гуна!", nil, {"Найдите шпиона То'гуна в Темном лабиринте в Аукиндоне."}},
[29641] = {"Инструменты души", nil, {"Украдите 5 инструментов душ и принесите их шпиону Грик'те в Темный лабиринт Аукиндона."}},
[29642] = {"В Аукиндоне неспокойно", nil, {"Отправляйтесь к боевому командиру Мафууну в Темный Лабиринт Аукиндона в Лесу Террокар."}},
