[4903] = {"Befehl des Kriegsherrn", {"Tötet Hochlord Omokk, Kriegsmeister Voone und Oberanführer Wyrmthalak. Findet die wichtigen Blackrockdokumente. Kehrt zum Kriegsherrn Goretooth nach Kargath zurück, sobald Ihr diese Mission erledigt habt."}},
[4904] = {"Endlich frei", {"Geleitet Lakota Windsong vom Düsterwolkengipfel hinab und sprecht dann im Freiwindposten mit Thalia Amberhide."}},
[4906] = {"Weitere Verderbnis", {"Eridan Bluewind im Teufelswald möchte, dass Ihr Xavaric sowie 8 Jadefeuerhöllenrufer, 8 Jadefeuerverräter und 8 Jadefeuerschwindler tötet. Kehrt zu Eridan zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[4907] = {"Tinkee Steamboil", {"Sprecht mit Tinkee Steamboil."}},
[4921] = {"In der Schlacht vermisst", {"Sucht Mankriks Frau und kehrt dann zu ihm nach Crossroads zurück."}},
[4941] = {"Eitriggs Weisheit", {"Sprecht mit Etrigg in Orgrimmar. Wenn Ihr die Angelegenheit mit Etrigg besprochen habt, bittet Thrall um Rat.","","Ihr erinnert Euch, dass Ihr Etrigg in Thralls Gemächern gesehen habt."}},
[4961] = {"Die Säuberung der Kugel von Orahil", {"Tötet den Dämon der Kugel und sprecht dann mit Tabetha."}},
[4962] = {"Splitter eines Teufelshundes", {"Nehmt den Felhas-Rubin und benutzt ihn bei einem der Teufelshunde von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Voidrender im Brachland den eingekerkerten Teufelshundgeist und den Felhas-Rubin."}},
[4963] = {"Splitter einer Höllenbestie", {"Nehmt den Infus-Smaragd und benutzt ihn bei einer der Höllenbestien von Desolace. Wenn Ihr Erfolg hattet, bringt Menara Voidrender im Brachland den eingekerkerten Höllenbestiengeist und den Infus-Smaragd."}},
[4964] = {"Die fertig gestellte Kugel von Dar'Orahil", {"Wartet, bis Menara Voidrender die Kugel von Dar'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr."}},
[4965] = {"Wissen der Kugel von Orahil", {"Sprecht mit Menara Voidrender im Brachland."}},
[4966] = {"Kanati Greycloud schützen", {"Beschützt Kanati Greycloud vor den Angriffen der Zentauren."}},
[4967] = {"Wissen der Kugel von Orahil", {"Sprecht mit Menara Voidrender im Brachland."}},
