[4971] = {"Eine Frage der Zeit", {"Benutzt den Temporalversetzer in der Nähe eines der Silos von Andorhal, um die temporalen Parasiten aufzuspüren.","","Tötet 15 temporale Parasiten und bringt dann den Temporalversetzer zu Chromie im Gasthaus Andorhal in den Westlichen Pestländern."}},
[4972] = {"Tempus fugit", {"Sucht 5 Andorhal-Uhren, die in Schließkassetten überall im Schutt der Stadt zu finden sind. Kehrt mit ihnen zu Chromie ins Gasthaus Andorhal in den Westlichen Pestländern zurück."}},
[4973] = {"Tempus fugit", {"Sucht 5 Andorhal-Uhren in den Ruinen der Stadt und bringt sie Chromie im Gasthaus Andorhal in den Westlichen Pestländern."}},
[4974] = {"Für die Horde!", {"Begebt Euch zur Blackrockspitze und tötet den Kriegshäuptling Rend Blackhand. Nehmt seinen Kopf und kehrt nach Orgrimmar zurück."}},
[4975] = {"Die fertig gestellte Kugel von Noh'Orahil", {"Wartet, bis Menara Voidrender die Kugel von Noh'Orahil fertig gestellt hat, und sprecht dann noch einmal mit ihr."}},
[4976] = {"Ablieferung der gesäuberten Kugel", {"Bringt die gesäuberte Höllenkugel zu Menara Voidrender ins Brachland."}},
[4981] = {"Agentin Bijou", {"Begebt Euch zur Blackrockspitze und findet heraus, was aus Bijou geworden ist."}},
[4982] = {"Bijous Habseligkeiten", {"Sucht Bijous Habseligkeiten und bringt sie ihr. Ihr erinnert Euch daran, dass sie erwähnte, ihre Sachen auf der untersten Ebene der Stadt versteckt zu haben."}},
[4983] = {"Bijous Aufklärungsbericht", {"Bringt Bijous Aufklärungsbericht zu Großmeister Lexlort nach Kargath."}},
[4984] = {"Die Tierwelt leidet auch", {"Tötet 8 kranke Wölfe und kehrt dann zu Mulgris Deepriver auf dem trostlosen Feld in den Westlichen Pestländern zurück."}},
[4985] = {"Die Tierwelt leidet auch", {"Tötet 8 kranke Grizzlys und kehrt dann zu Mulgris Deepriver auf dem trostlosen Feld in den Westlichen Pestländern zurück."}},
[4986] = {"Glyphenverzierter Eichenast", {"Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Darnassus bringt. Bittet einen der Druiden dort um Hilfe."}},
[4987] = {"Glyphenverzierter Eichenast", {"Nach der Magie zu urteilen, mit der der glyphenverzierte Eichenast erfüllt ist, hatte der Taurendruide wohl gewollt, dass Ihr den Ast zum Zirkel des Cenarius nach Thunder Bluff bringt. Bittet einen der Druiden dort um Hilfe."}},
[5001] = {"Bijous Habseligkeiten", {"Sucht Bijous Habseligkeiten und bringt sie ihr. Viel Glück!"}},
[5002] = {"Nachricht an Maxwell", {"Begebt Euch in die brennende Steppe und leitet Bijous Informationen an Marshal Maxwell weiter."}},
[5021] = {"Besser spät als nie", {"Das wilde Gebrabbel der Geisterfrau deutete darauf hin, dass sie ein Paket ausliefern musste. Sie behauptete, dass es dort war, wo auch ihr Pferd war. Darüber, wo das Pferd jetzt ist oder wohin das Paket ausgeliefert werden muss, sagt der Geist nichts."}},
[5022] = {"Besser spät als nie", {"Wendet Euch an die königlichen Disponenten von Stormwind, um etwas über den Aufenthaltsort von Emma Felstone herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter im Rathaus."}},
[5023] = {"Besser spät als nie", {"Wendet Euch an die königlichen Vorarbeiter von Undercity, um etwas über den Aufenthaltsort von Jeremiah Felstone herauszufinden. Normalerweise findet sich ein Volkszählungsbeamter in der Nähe der Gilden- und Wappenrock-Registrierung."}},
