[5150] = {"Dadanga ist hungrig!", nil},
[5151] = {"Hyperkondensator-Dingsda", {"Erschlagt die Bestie und bringt den Hyperkondensator sowie den Pantherkäfigschlüssel zu Wizlo Bearingshiner zurück."}},
