[5153] = {"Ein seltsamer Historiker", {"Bringt Josephs Ehering zu Chromie."}},
[5154] = {"Die Annalen von Darrowshire", {"Bringt die 'Annalen von Darrowshire' zu Chromie in Andorhal."}},
[5155] = {"Streitmacht von Jaedenar", {"Betretet Jaedenar und erschlagt 4 Hunde von Jaedenar, 4 Gardisten von Jaedenar, 6 Adepten von Jaedenar und 6 Kultisten von Jaedenar, bevor Ihr zu Greta Mosshoof im Teufelswald zurückkehrt."}},
[5156] = {"Die Verderbnis überprüfen", {"Sucht und zerstört 2 entropische Bestien und 2 entropische Schrecken, während Ihr das Shatter Scar-Tal erforscht. Nachdem Ihr überprüft habt, ob die Krater immer noch mit verderbtem Fel-Wasser gefüllt sind, kehrt zu Taronn Redfeather im Teufelswald zurück."}},
[5157] = {"Verderbtes Wasser sammeln", {"Bringt die leere Feldflasche zum Mondbrunnen im Zentrum von Jaedenar und füllt sie, bevor Ihr zu Greta Mosshoof im Teufelswald zurückkehrt."}},
[5158] = {"Spirituelle Hilfe suchen", {"Bringt das verderbte Mondbrunnenwasser in das Brachland zu Islen Waterseer."}},
[5159] = {"Gesäubertes Wasser wird zum Teufelswald gebracht", {"Bringt das geläuterte Mondbrunnenwasser zu Greta Mosshoof im Teufelswald."}},
[5160] = {"Die oberste Beschützerin", {"Begebt Euch nach Winterspring und sucht Haleh. Gebt ihr Awbees Schuppe."}},
[5161] = {"Der Zorn des blauen Drachenschwarms", {"Sprecht mit Haleh, wenn Ihr weitermachen wollt."}},
[5162] = {"Der Zorn des blauen Drachenschwarms", {"Sprecht mit Jeziba in den Pestländern. Er wohnt in Andorhal."}},
[5163] = {"Sind wir endlich da, Yeti?", {"Nehmt Umis mechanischen Yeti und erschreckt ihre Freunde damit:","","Legacki in Everlook (Winterspring)","","Sprinkle in Gadgetzan (Tanaris)","","Quixxil in Marshals Zuflucht (Un'Goro Krater)","","Wenn Ihr damit fertig seid, bringt den mechanischen Yeti zu Umi zurück."}},
[5164] = {"Verzeichnis der Abtrünnigen", {"Lest das 'Verzeichnis der Abtrünnigen'."}},
[5165] = {"Übergießen der Flammen des Schutzes", {"Übergießt die Flammen der vier Kohlenpfannen des Schutzes in der Schattenfeste im Teufelswald mit dem geläuterten Mondbrunnenwasser."}},
[5166] = {"Brustplatte des Chromatischen Drachenschwarms", {"Um die Brustplatte des chromatischen Drachenschwarms zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem 'Bildhauer' bringen:","","1 chromatischer Knochenpanzer.","","10 glänzende chromatische Schuppen.","","10 Einheiten Blut von Helden.","","10 ausgefranste Monstrositätenstickereien."}},
[5167] = {"Beinplatten des chromatischen Widersetzers", {"Um die Beinplatten des chromatischen Widersetzers zu schmieden, müsst Ihr die folgenden Gegenstände zu Jeziba dem 'Bildhauer' bringen:","","1 chromatischer Knochenpanzer.","","10 glänzende chromatische Schuppen.","","10 Einheiten Blut von Helden.","","5 Häute des Schattens."}},
[5168] = {"Helden von Darrowshire", {"Bringt Davils Buchband und Redpaths Schild zu Carlin Redpath."}},
[5181] = {"Bösewichte von Darrowshire", {"Bringt den Schädel von Horgus und das zertrümmerte Schwert von Marduk zu Carlin Redpath in der Kapelle des hoffnungsvollen Lichts."}},
[5201] = {"Eindringlinge der Winterfelle", {"Rivern Frostwind möchte, dass Ihr 5 Schamanen der Winterfelle und 5 Ursa der Winterfelle erschlagt."}},
[5202] = {"Ein merkwürdiger roter Schlüssel", {"Durchsucht Jaedenar, um herauszufinden, wofür der blutrote Schlüssel verwendet wird."}},
[5203] = {"Rettung aus Jaedenar", {"Bringt Arko'narin dorthin, wo Lightforge und ihre Ausrüstung aufbewahrt werden, und beschützt sie dann bei eurer Flucht aus der Schattenfeste. Sucht danach Jessir Moonbow im Teufelswald auf und lasst sie wissen, was mit ihren Freunden geschehen ist."}},
[5204] = {"Vergeltung des Lichts", {"Kehrt nach Jaedenar zurück und tötet den Sukkubus Rakaiah, bevor Ihr die Überreste von Trey Lightforge sucht."}},
