[5245] = {"Die gequälten Seelen von Kel'Theril", {"Benutzt Jarons Hacke, um die vier Fragmente des Hochgeborenenrelikts zu suchen. Bringt sie zu Aurora Skycaller in den Östlichen Pestländern."}},
[5246] = {"Fragmente der Vergangenheit", {"Sucht die hochheiligen Schriften der Hochgeborenen im Tempel von Zin-Malor in Azshara und bringt sie zu Aurora Skycaller beim Nordpassturm in den östlichen Pestländern."}},
[5247] = {"Fragmente der Vergangenheit", {"Bringt einen verzauberten Thoriumbarren, 5 Kristallflicker und eine Phiole mit Düsterwasser zu Aurora Skycaller beim Nordpassturm in den östlichen Pestländern."}},
[5248] = {"Gequält von der Vergangenheit", {"Bringt den Kristall von Zin-Malor zum Kel'Therilsee in Winterspring und sucht einen Geist eines Hochgeborenen, der mit Euch sprechen will."}},
[5249] = {"Nach Winterspring!", {"Sucht Starfall in Winterspring und sprecht mit Wynd Nightchaser."}},
[5250] = {"Starfall", {"Sucht Starfall in Winterspring und sprecht mit Wynd Nightchaser."}},
[5251] = {"Der Archivar", {"Reist nach Stratholme und sucht Archivar Galford vom Scharlachroten Kreuzzug. Vernichtet ihn und verbrennt das Scharlachrote Archiv."}},
[5252] = {"Reuevolle Hochgeborene", {"Sprecht mit Wynd Nightchaser in Starfall und fragt nach dem richtigen Ort für den Kristall von Zin-Malor."}},
