[5261] = {"Eagan Peltskinner", {"Sprecht mit Eagan Peltskinner."}},
[5262] = {"Die Wahrheit zeigt sich mit Macht", {"Bringt den Kopf von Balnazzar zu Fürst Nicholas Zverenhoff in den Östlichen Pestländern."}},
[5263] = {"Übertroffen", {"Zieht nach Stratholme und vernichtet Baron Rivendare. Nehmt seinen Kopf und kehrt zu Fürst Nicholas Zverenhoff zurück."}},
[5264] = {"Lord Maxwell Tyrosus", {"Sprecht mit Lord Maxwell Tyrosus. Er befindet sich in der Kirche."}},
[5265] = {"Der Argentumtresor", {"Öffnet den Argentumtresor und nehmt Euch Eure Belohnung."}},
[5281] = {"Die ruhelosen Seelen", {"Sucht Egan. Ihr wisst nur, dass er zuletzt in der Nähe von Stratholme gesehen wurde."}},
[5282] = {"Die ruhelosen Seelen", {"Wendet Egans Blaster auf die geisterhaften und spektralen Bürger von Stratholme an. Wenn die ruhelosen Geister ihre geisterhaften Hüllen sprengen, wendet den Blaster erneut an - dann sind sie endlich frei!","","Befreit 15 ruhelose Seelen und kehrt zu Egan zurück."}},
[5283] = {"Die Kunst des Rüstungsschmiedes", {"Um Rüstungsschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Grumnus bringen: 4 verschnörkelte Mithrilhelme, 2 verschnörkelte Mithrilstiefel, 1 verschnörkelte Mithrilbrustplatte."}},
[5284] = {"Der Weg des Waffenschmieds...", {"Um Waffenschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Ironus bringen: 4 Mondstahlbreitschwerter, 4 massive Eisenäxte, 2 schwere Mithriläxte und 2 große schwarze Streitkolben."}},
[5301] = {"Die Kunst des Rüstungsschmiedes", {"Um Rüstungsschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Okothos bringen: 4 verschnörkelte Mithrilhelme, 2 verschnörkelte Mithrilstiefel, 1 verschnörkelte Mithrilbrustplatte."}},
[5302] = {"Der Weg des Waffenschmieds...", {"Um Waffenschmied zu werden, müsst Ihr die folgenden Gegenstände herstellen und zu Borgosh bringen: 4 Mondstahlbreitschwerter, 4 massive Eisenäxte, 2 schwere Mithriläxte und 2 große schwarze Streitkolben."}},
[5305] = {"Süße Beschaulichkeit", {"Begebt Euch nach Stratholme und tötet den purpurroten Hammerschmied. Nehmt die Schürze des purpurroten Hammerschmiedes und kehrt zu Lilith zurück."}},
[5306] = {"Schlangenstein der Schattenjägerin", {"Begebt Euch zur Blackrockspitze und erschlagt Schattenjägerin Vosh'gajin. Holt Vosh'gajins Schlangenstein und kehrt zu Kilram zurück."}},
[5307] = {"Verderbnis", {"Findet den Schwertschmied der schwarzen Wache in Stratholme und vernichtet ihn. Holt die Insignien der schwarzen Wache und kehrt zu Seril Scourgebane zurück."}},
[5321] = {"Der Schläfer ist erwacht", {"Begleitet Kerlonian Evershade zu Liladris Moonriver in Maestras Posten in Ashenvale."}},
[5341] = {"Das Familienvermögen der Barovs", {"Begebt Euch zur Scholomance und holt das Familienvermögen der Barovs zurück. Dieses Vermögen besteht aus vier Besitzurkunden: Es sind die Besitzurkunde für Caer Darrow, die Besitzurkunde für Brill, die Besitzurkunde für Tarrens Mühle und die Besitzurkunde für Southshore. Kehrt zu Alexi Barov zurück, sobald die Aufgabe erledigt ist."}},
[5342] = {"Der letzte Barov", {"Begebt Euch zum Chillwind-Lager - Territorium der Allianz - und ermordet Weldon Barov. Nehmt seinen Kopf und kehrt zu Alexi Barov zurück.","","Seid gewarnt, Weldon wird wahrscheinlich gut von Abschaum der Allianz geschützt."}},
[5343] = {"Das Familienvermögen der Barovs", {"Begebt Euch zur Scholomance und holt das Familienvermögen der Barovs zurück. Dieses Vermögen besteht aus vier Besitzurkunden: Es sind die Besitzurkunde für Caer Darrow, die Besitzurkunde für Brill, die Besitzurkunde für Tarrens Mühle und die Besitzurkunde für Southshore. Kehrt zu Weldon Barov zurück, sobald die Aufgabe erledigt ist."}},
[5344] = {"Der letzte Barov", {"Begebt Euch zum Bollwerk - Territorium der Horde - und ermordet Alexi Barov. Nehmt seinen Kopf und kehrt zu Weldon Barov zurück.","","Seid gewarnt, Alexi wird wahrscheinlich gut durch Abschaum der Horde geschützt."}},
