[6129] = {"Heilung der Kranken", {"Benutzt die heilende Tiersalbe bei 10 kränklichen Gazellen im nördlichen Teil des Brachlandes. Das müsste sie heilen. Kränkliche Gazellen wurden überall nördlich der Ost-West-Straße durch Crossroads gesehen."}},
[6130] = {"Macht über Gift", {"Kehrt zu Turak Runetotem auf der Anhöhe der Ältesten in Thunder Bluff zurück."}},
[6132] = {"Holt mich hier raus!", {"Eskortiert Melizza Brimbuzzle und sprecht anschließend mit Hornizz Brimbuzzle beim Kodofriedhdof Desolace."}},
[6133] = {"Das Ersuchen des Waldläuferlords", {"Begebt Euch zur nördlichsten Grenze der Östlichen Pestländer und bringt die Quel'Thalas-Registratur zurück. Der Gegenstand befindet sich irgendwo in der Quel'Lithien-Lodge.","","Außerdem müsst Ihr 8 Waldläufer, 8 Pfadschreiter und 8 Waldarbeiter töten."}},
[6134] = {"Geisterplasmasuche", {"Bringt 8 Einheiten Geisterplasma und die Kiste mit den Geistermagneten zu Hornizz Brimbuzzle in Desolace."}},
[6135] = {"Dämmerschwinge, oh, wie ich Euch hasse...", {"Sucht Dämmerschwinge und tötet ihn. Nehmt ein Büschel vom Fell Dämmerschwinges und bringt es zu Nathanos Blightcaller.","","Man sagt, dass Dämmerschwinge nördlich von hier durch das Land streift."}},
[6136] = {"Der Korpulente", {"Sucht in den nördlichen Wüsten der Östlichen Pestländer nach Blutschlinger und vernichtet ihn. Kehrt zu Nathanos Blightcaller zurück, sobald Ihr diese Mission erledigt habt."}},
[6141] = {"Bruder Anton", {"Sprecht mit Bruder Anton in Desolace."}},
[6142] = {"Muschelköder", {"Sucht 10 Teile Fleisch der weichschaligen Muschel und bringt sie zu Mai'Lahii im Dorf Shadowprey."}},
[6143] = {"Andere Fische zu grillen", {"Tötet 7 Myrmidonen der Zackenkämme, 7 Naga der Zackenkämme und 5 Zauberhexerinnen der Zackenkämme, dann sprecht in Desolace mit Drulzegar in Shadowprey."}},
[6144] = {"Der Befehl", {"Reist in die Undercity und sprecht mit Varimathras."}},
[6145] = {"Der Purpurrote Kurier", {"Kehrt in die östlichen Pestländer zurück und spürt den Purpurroten Kurier auf. Tötet den Kurier und schnappt Euch den Befehl des obersten Kreuzzüglers.","","Bringt den Befehl zu Nathanos Blightcaller und wartet auf weitere Anweisungen."}},
[6146] = {"Nathanos' List", {"Reist nach Tyrs Hand, südöstlich der Siedlung Marris. Dort angekommen beißt in den faulen Apfel. In der Verkleidung des Scharlachroten Kreuzzugs überbringt Ihr Lord Valdelmar den Befehl des obersten Kreuzzüglers."}},
[6147] = {"Kehrt zu Nathanos zurück.", {"Kehrt mit der neuen Information zu Nathanos Blightcaller zurück."}},
[6148] = {"Das Scharlachrote Orakel Demetria", {"Der Scharlachrote Kreuzzug ist unterwegs. Irgendwo auf der Straße von Tyrs Hand nach Stratholme findet Ihr das Orakel Demetria und ihr Gefolge. Jagt sie und tötet sie. Kehrt zu Nathanos Blightcaller zurück, wenn Ihr erfolgreich wart."}},
[6161] = {"Nehmt Rackmores Schatz!", {"Sucht Rackmores Silberschlüssel. Sucht Rackmores Goldschlüssel. Sucht und öffnet Rackmores Truhe."}},
[6162] = {"Eines Ehemanns letzter Kampf", {"Tötet Oberanführer Ror und bringt seine Klaue zu Dreka'Sur beim Blutgiftposten im Teufelswald."}},
[6163] = {"Ramstein", {"Reist nach Stratholme und tötet Ramstein den Würger. Bringt seinen Kopf als Souvenir zu Nathanos."}},
[6164] = {"Augustus' Rezeptbuch", {"Bringt Augustus' Quittungsbuch zu Augustus dem Meschuggenen im Schreckenstal."}},
[6181] = {"Eine eilige Nachricht", {"Bringt Lewis' Notiz zu Thor dem Greifenmeister."}},
[6182] = {"Der Erste und Letzte", {"Sprecht mit Meister Mathias Shaw in der Altstadt von Stormwind. Er wohnt in der Kaserne."}},
[6183] = {"Ehret die Toten", {"Sprecht wieder mit Mathias Shaw, wenn Ihr seine Aufgabe annehmen wollt."}},
[6184] = {"Flint Shadowmore", {"Reist zur Chillwindspitze in den Westlichen Pestländern und trefft Euren Kontaktmann Flint Shadowmore."}},
[6185] = {"Die östlichen Pestländer", {"Sucht in den östlichen Pestländern nach Hinweisen auf den 'Blightcaller' und die vermissten SI:7-Agenten. Wenn Ihr SI:7-Insignien findet, bringt sie zu Flint Shadowmore an der Chillwindspitze."}},
[6186] = {"Der Blightcaller kommt", {"Kehrt nach Stormwind zurück und informiert Hochlord Bolvar Fordragon über das Schicksal von Nathanos Marris."}},
[6187] = {"Ordnung muss wieder hergestellt werden", {"Stellt eine Armee auf und reist zu den östlichen Pestländern. Beginnt einen gnadenlosen Angriff auf Nathanos Blightcaller und jeden Abschaum der Horde, der ihn beschützen mag.","","Bewahrt einen klaren Kopf, <Name>. Die Horde wird den Waldläuferlord mit dem Leben verteidigen."}},
[6261] = {"Dungar Longdrink", {"Bringt Osrics Kiste zu Dungar Longdrink dem Greifenmeister."}},
[6281] = {"Weiter nach Stormwind", {"Bucht einen Greifenritt bei Greifenmeister Thor, dann bringt Lewis' Notiz zu Osric Strang, im Laden Begrenzte Immunität, in Stormwind."}},
[6282] = {"Harpyien drohen", {"Maggran Earthbinder in Sonnenfels möchte, dass Ihr 7 Harpyien der Blutfurien, 7 Wegelagerinnen der Blutfurien, 7 Töterinnen der Blutfurien und 7 Wildfedern der Blutfurien erschlagt."}},
[6283] = {"Blutfuriengeschlecht", {"Maggran in Sonnenfels möchte, dass Ihr die Zerfetzerin der Blutfurien beseitigt und ihre sterblichen Überreste als Beweis für Eure Tat mitbringt."}},
[6284] = {"Arachnophobie", {"Tötet Besseleth und bringt Besseleths Fangzahn zu Maggran in Sonnenfels."}},
[6285] = {"Zurück zu Lewis", {"Bucht einen Greifenritt zur Späherkuppe bei Greifenmeister Dungar Longdrink, dann bringt Osrics Kiste zu Lewis an der Späherkuppe."}},
[6301] = {"Zyklus der Wiedergeburt", {"Tammra Windfield in Sonnenfels möchte, dass Ihr 10 Gaeasamenkörner sammelt."}},
[6321] = {"Nachschub für das Grabmal", {"Bringt Podrigs Liste zu Karos Razok."}},
[6322] = {"Michael Garrett", {"Bringt Gordons Kiste zu Michael Garret."}},
[6323] = {"Flug nach Undercity", {"Bucht einen Fledermausritt zur Undercity bei Fledermausmeister Karos Razok und bringt dann Podrigs Anforderungsliste zu Gordon Wendham in Undercity."}},
[6324] = {"Rückkehr zu Podrig", {"Bucht einen Fledermausritt zum Grabmal bei Fledermausmeister Michael Garret und bringt dann Gordons Kiste zu Todeswache Podrig beim Grabmal."}},
[6341] = {"Die Arten von Teldrassil", {"Bringt Nessas Sammlung zu Vesprystus."}},
[6342] = {"Flug nach Auberdine", {"Bucht bei Hippogryphenmeister Vesprystus einen Hippogryphen nach Auberdine und bringt dann Nessas Sammlung zu Laird."}},
[6343] = {"Rückkehr zu Nessa", {"Bringt Lairds Antwort zu Nessa Shadowsong."}},
[6344] = {"Nessa Shadowsong", {"Sprecht mit Nessa Shadowsong."}},
[6361] = {"Ein Bündel Bälge", {"Bringt das Bündel Bälge zu Windreitermeister Devrak in Crossroads."}},
[6362] = {"Flug nach Thunder Bluff", {"Bucht einen Windreiter nach Thunder Bluff bei Windreitermeister Devrak und bringt dann das Bündel Bälge zu Ahanu in Thunder Bluff."}},
[6363] = {"Tal der Windreitermeister", {"Bringt Ahanus Lederwaren zu Tal in Thunder Bluff."}},
[6364] = {"Rückkehr zu Jahan", {"Bucht einen Windreiter nach Crossroads bei Windreitermeister Tal und bringt dann Ahanus Lederwaren zu Jahan Hawkwing."}},
[6365] = {"Fleisch für Orgrimmar", {"Bringt Zarghs Fleisch zu Devrak in Crossroads."}},
[6381] = {"Neues Leben", {"Pflanzt 10 Gaeasamenkörner in Gaeaerdhügel und sprecht dann mit Tammra in Sonnenfels."}},
