[6384] = {"Flug nach Orgrimmar", {"Bucht einen Windreiter nach Orgrimmar bei Windreitermeister Devrak und bringt dann Zarghs Fleisch zu Gryshka in Orgrimmar."}},
[6385] = {"Doras der Windreitermeister", {"Bringt Gryshkas Brief zu Doras in Orgrimmar."}},
[6386] = {"Kehrt nach Crossroads zurück", {"Bucht einen Windreiter nach Crossroads bei Windreitermeister Doras und bringt dann Gryshkas Brief zu Zargh in Crossroads."}},
