[6389] = {"Eine Plage komme über Euch", {"Befreit die Pestländer-Termiten im Sägewerk von Northridge, dann kehrt mit dem Fass Pestländer-Termiten zu Nathaniel Dumah im Chillwind-Posten in den westlichen Pestländern zurück."}},
[6390] = {"Eine Plage komme über Euch", {"Befreit die Pestländer-Termiten im Sägewerk von Northridge, dann kehrt mit dem Fass Pestländer-Termiten zu Mickey Levine im Bollwerk in Tirisfal zurück."}},
[6391] = {"Flug nach Ironforge", {"Bucht einen Greifen nach Ironforge von Greifenmeister Thorgrum Borrelson, dann bringt Brocks Liste zu Golnir Bouldertoe in Ironforge."}},
[6392] = {"Rückkehr zu Brock", {"Bucht einen Greifen nach Thelsamar bei Greifenmeister Gryth Thurden, dann bringt die Ehrenhacken zu Brock Stoneseeker in Thelsamar."}},
[6393] = {"Krieg der Elementare", {"Bringt 10 Pyrophore zu Tsunaman in Sonnenfels."}},
[6394] = {"Thazz'rils Hacke", {"Bringt Thazz'rils Hacke zu Großknecht Thazz'ril."}},
[6395] = {"Marlas letzter Wunsch", {"Bringt Samuel Fipps' sterbliche Überreste zu Marlas Grab und kehrt dann zu Novize Elreth zurück."}},
[6401] = {"Kaya lebt", {"Teilt Tammra Windfield, die in Sonnenfels lebt, die gute Nachricht mit."}},
[6402] = {"Treffen in Stormwind", {"Reist nach Stormwind und begebt Euch zu den Stadttoren. Sprecht mit Knappe Rowe, damit er Marshal Windsor über Eure Ankunft informiert."}},
[6403] = {"Die große Maskerade", {"Folgt Reginald Windsor durch Stormwind. Schützt ihn vor Gefahren!"}},
[6421] = {"Steinschlagklamm", {"Erkundet die Tiefen der Höhle in der Steinschlagklamm und bringt Mor'rogal in Sonnenfels 10 Resonitkristalle, damit er sie untersuchen kann."}},
[6441] = {"Satyrhörner", {"Sammelt für Pixel im Splintertreeposten 16 Satyrhörner."}},
[6442] = {"Die Naga am Zoramstrand", {"Bringt Marukai am Zoramstrand 20 Köpfe von Rächerflossen."}},
[6461] = {"Blutfresser", {"Xen'zilla am Malaka'jin möchte, dass Ihr 10 Kluftmooskrabbler und 7 Kluftmoosgiftspucker tötet."}},
[6462] = {"Troll-Glücksbringer", {"Bringt Mitsuwa im Zoram'gar-Außenposten 8 Troll-Glücksbringer."}},
[6481] = {"Der Irdene erhebt sich", {"Öffnet das Resonitfässlein mit dem verzauberten Resonitkristall und tötet Goggeroc. Kehrt mit der Vollzugsmeldung und dem verzauberten Resonitkristall zu Mor'rogal zurück."}},
[6482] = {"Freiheit für Ruul", {"Begleitet Ruul und sprecht dann mit Yama Snowhoof im Splintertreeposten."}},
[6501] = {"Das Großdrachenauge", {"Ihr müsst die Welt nach jemandem absuchen, der fähig ist, die Macht des Fragments des Großdrachenauges wieder herzustellen. Die einzige Information, die Ihr über ein solches Wesen besitzt ist, dass es existiert."}},
[6502] = {"Drachenfeueramulett", {"Ihr müsst das Blut des schwarzen Großdrachen-Helden von General Drakkisath bekommen. Ihr findet Drakkisath in seinem Thronsaal hinter den Hallen des Aufstiegs auf der Blackrockspitze."}},
[6503] = {"Die Kundschafter von Ashenvale", {"Tötet 9 Kundschafter von Ashenvale und kehrt dann zu Kuray'bin im Splintertreeaußenposten zurück."}},
[6504] = {"Die verlorenen Seiten", {"Findet die 12 fehlenden Seiten der Schredderanleitung und fügt sie zu den Kapiteln 1, 2 und 3 zusammen."}},
[6521] = {"Eine unheilige Allianz", {"Bringt den Kopf von Botschafter Malcin zu Varimathras nach Undercity."}},
[6522] = {"Eine unheilige Allianz", {"Bringt die kleine Rolle zu Varimathras nach Undercity."}},
[6523] = {"Schützt Kaya", {"Begleitet Kaya Flathoof zum Camp Aparaje und kehrt dann zu Makaba Flathoof am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6541] = {"Meldung bei Kadrak", {"Meldet Euch im Wachturm im Norden des Brachlands bei Kadrak."}},
[6542] = {"Meldung bei Kadrak", {"Meldet Euch im Wachturm im Norden des Brachlands bei Kadrak."}},
[6543] = {"Die Warsongberichte", {"Bringt die Warsongberichte dem Warsongspäher, dem Warsongläufer und der Warsongvorhut. Meldet die Neuigkeiten, die Ihr von ihnen erfahrt, Kadrak im nördlichen Wachturm des Brachlands."}},
[6544] = {"Toreks Angriff", {"Eskortiert Torek bei seinem Angriff auf den Außenposten der Silverwing und Duriel Moonfire. Erstattet dann Ertog Ragetusk im Splintertreeposten Bericht."}},
[6545] = {"Warsongläufer-Update", nil},
[6546] = {"Warsongvorhut-Update", nil},
[6547] = {"Warsongspäher-Update", nil},
[6548] = {"Rächt mein Dorf", {"Tötet 8 Grobiane der Grimmtotem und 6 Söldner der Grimmtotem. Kehrt dann zu Makaba Flathoof am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6561] = {"Blackfathom-Schurkerei", {"Bringt den Kopf des Twilight-Lords Kelris zu Bashana Runetotem in Thunder Bluff."}},
[6562] = {"Ärger in der Tiefe", {"Sprecht in Ashenvale mit Je'neu Sancrea."}},
[6563] = {"Die Essenz von Aku'mai", {"Bringt 20 Saphire von Aku'mai zu Je'neu Sancrea nach Ashenvale."}},
[6564] = {"Treue zu den Alten Göttern", {"Bringt die durchfeuchtete Notiz zu Je'neu Sancrea nach Ashenvale."}},
[6565] = {"Treue zu den Alten Göttern", {"Tötet Lorgus Jett in der Blackfathom-Tiefe und kehrt dann zu Je'neu Sancrea nach Ashenvale zurück."}},
[6566] = {"Was der Wind erzählt", {"Hört Thrall zu."}},
[6567] = {"Der Champion der Horde", {"Sucht Rexxar auf. Der Kriegshäuptling hat Euch erklärt, wo Ihr ihn finden könnt. Sucht die Wege von Desolace zwischen dem Steinkrallengebirge und Feralas ab."}},
[6568] = {"Nachricht von Rexxar", {"Überbringt Myranda der Vettel in den westlichen Pestländern Rexxars Nachricht."}},
[6569] = {"Oculus-Illusionen", {"Reist zur Blackrockspitze und sammelt 20 schwarze Drachenbrutaugen. Kehrt zu Myranda der Vettel zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[6570] = {"Aschenschwinge", {"Reist zum Drachensumpf in den Marschen von Dustwallow und begebt Euch in Aschenschwinges Bau. Sobald Ihr darin seid, tragt das Amulett der drachischen Subversion und sprecht mit Aschenschwinge."}},
[6571] = {"Warsongnachschub", {"Tragt den Warsongnachschub zusammen: Warsongsägeblätter, Holzfäller-Seil, Warsongöl und die Warsongaxtlieferung für Locke Okarr im Splintertreeposten."}},
[6581] = {"Warsongsägeblätter", nil},
[6582] = {"Die Prüfung der Schädel, Scryer", {"Ihr müsst Scryer, den Champion der blauen Großdrachen, finden und ihn töten. Schlagt seinem Kadaver den Schädel ab und bringt ihn zu Aschenschwinge.","","Ihr wisst, dass Scryer in Winterspring zu finden ist."}},
[6583] = {"Die Prüfung der Schädel, Somnus", {"Vernichtet den Drachenchampion des grünen Drachenschwarms, Somnus. Nehmt seinen Schädel und bringt ihn zu Aschenschwinge zurück."}},
[6584] = {"Die Prüfung der Schädel, Chronalis", {"Chronalis, ein Kind Nozdormus, bewacht die Höhlen der Zeit in der Tanariswüste. Vernichtet ihn und bringt seinen Schädel zu Aschenschwinge."}},
[6585] = {"Die Prüfung der Schädel, Axtroz", {"Begebt Euch nach Grim Batol und spürt Axtroz auf, den Drachenchampion des Roten Drachenschwarms. Vernichtet ihn und nehmt seinen Schädel. Bringt den Schädel zu Aschenschwinge zurück."}},
[6601] = {"Aufstieg...", {"Mir scheint, als sei die Charade jetzt vorüber. Ihr wisst, dass das Amulett der drachischen Unterwanderung, das Myranda die Vettel für Euch erschuf, auf der Blackrockspitze nicht funktioniert. Vielleicht solltet Ihr Rexxar aufsuchen und ihm Eure Notlage erklären. Zeigt ihm das glanzlose Drachenfeueramulett. Er wird hoffentlich wissen, was als Nächstes zu tun ist."}},
[6602] = {"Blut des schwarzen Großdrachen-Helden", {"Begebt Euch zur Blackrockspitze und tötet General Drakkisath. Sammelt sein Blut und bringt es zu Rexxar."}},
[6603] = {"Probleme in Winterspring", {"Findet Donova Snowden bei den Thermalquellen in Winterspring."}},
[6604] = {"Wütende Wildekin", {"Sprecht mit Jaron Stoneshaper in Starfall."}},
[6605] = {"Eine seltsame Person", {"Sprecht im Teufelswald mit Winna Hazzard."}},
[6606] = {"Ein bisschen Glück", {"Sprecht in Everlook mit Hexendoktorin Mau'ari."}},
[6607] = {"Nat Pagle, Extremangler", {"Nat Pagle möchte, dass Ihr folgende Fische fangt:","","Nebelschilf-Mahi Mahi vom Nebelschilfstrand in den Sümpfen des Elends.","","Einen Sar'theris-Barsch vom Sar'theris-Strand in Desolace.","","Feralas-Ahi aus dem Verdantis in Feralas.","","Blauwimpel von der ungezähmten Küste im Schlingendorntal.","","Kehrt zu Nat Pagle zurück, wenn Ihr alle an Land gezogen habt!"}},
[6608] = {"Ihr zu gut", {"Ihr nehmt an, dass Lumak Euch zu erklären versucht, dass er nicht mehr tun kann, um Eure Angelfertigkeit zu verbessern. Ihr solltet seinen Freund aufsuchen, diesen Nat Pagle, einen Menschen, der auf einer der Inseln südlich von Theramore (jedenfalls glaubt Ihr, dass Lumak das gemeint hat) auf Fischfang geht."}},
[6609] = {"Ich hab nix mehr!", {"Reist nach Kalimdor zur Küstenstadt Theramore. Dort müsst Ihr nach einer Insel suchen, die südlich der Hauptstadt liegt. Nat Pagle erwartet Euch!"}},
[6610] = {"Muschelette Surprise", {"Dirge Quikcleave möchte, dass Ihr folgende Dinge besorgt:","","12 Rieseneier. Ihr findet sie bei den Rocs, die Tanaris durchstreifen, oder anderen Großvogelarten.","","10 Stück scharfes Muschelfleisch. Die findet Ihr natürlich in Muscheln!","","20 Stück Alteraclochkäse. Begebt Euch zu einer Käserei!","","Sammelt all diese Gegenstände und kehrt dann zu Dirge zurück."}},
[6611] = {"Ab mit Euch nach Gadgetzan!", {"Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Quikcleave über den Ausbau Eurer Kochkarriere."}},
[6612] = {"Ich kenne einen Typen...", {"Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Quikcleave über den Ausbau Eurer Kochkarriere."}},
[6621] = {"König der Fäulnisklauen", {"Platziert Karangs Banner auf dem Totemhügel der Fäulnisklauen. Die Furbolgs dürfen das Banner nicht zerstören. Besiegt Häuptling Murgut und bringt Murguts Totem zu Karang Amakkar in Zoram'gar."}},
[6622] = {"Triage", {"Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.","","Denkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt."}},
[6623] = {"Traumachirurg der Horde", {"Reist nach Hammerfall im Arathihochland und sprecht mit Doktor Gregory Victor darüber, dass Ihr auch ein Traumachirurg der Horde werden wollt."}},
[6624] = {"Triage", {"Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.","","Denkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt."}},
[6625] = {"Traumachirurg der Allianz", {"Reist nach Theramore in den Marschen von Dustwallow und sprecht mit Doktor Gustaf van Howzen darüber, dass Ihr auch ein Traumachirurg der Allianz werden wollt."}},
[6626] = {"Ein Hort des Bösen", {"Tötet 8 Schlachtwachen von Razorfen, 8 Dornenwirker von Razorfen und 8 Kultistinnen der Totenköpfe und kehrt dann zu Myriam Moonsinger nahe dem Eingang zu den Hügeln von Razorfen zurück."}},
[6627] = {"Test der Lehre", {"Beantwortet Braug Dimspirits Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch im Steinkrallengebirge sein, wenn Ihr bereit seid."}},
[6628] = {"Test der Lehre", {"Beantwortet Parqual Fintallas' Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch in Undercity sein, wenn Ihr bereit seid."}},
[6629] = {"Tötet Grundig Finsterwolke", {"Tötet Grundig Finsterwolke und 6 Schläger der Grimmtotem und kehrt dann zu Makaba Flathoof am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6641] = {"Vorsha die Peitscherin", {"Beschützt Muglash, wenn er zu der Kohlenpfanne reist. Helft ihm, indem Ihr die Kohlenpfanne löscht, und beschützt ihn dann, während Ihr gegen die Naga kämpft.","","Wenn Ihr erfolgreich gewesen seid, kehrt zum Zoram'garposten zurück und informiert den Warsongläufer über den Tod von Vorsha der Peitscherin."}},
