[6662] = {"Mein Bruder Nipsy...", {"Bringt den Karton mit eigenartigem Fleisch zu Nipsy im Tiefenbahndepot von Stormwind."}},
[6681] = {"Das Anwesen, Ravenholdt", {"Bringt das Ravenholdt-Siegel zu Fahrad im Ravenholdt-Anwesen, das versteckt in den Hügeln von Hillsbrad liegt.","","Unterwegs wird man Eure Schlauheit testen. Seid darauf gefasst."}},
