[7507] = {"Nostros Kompendium", {"Bringt Nostros Kompendium des Drachentötens zurück in das Athenaeum."}},
[7508] = {"Das Schmieden von Quel'Serrar", {"Bringt dem Hüter des Wissens Lydros, die stumpfe und glanzlose Elfenklinge."}},
[7509] = {"Das Schmieden von Quel'Serrar", {"Bringt Onyxia dazu, ihren Feueratem auf die noch unerhitzte Klinge zu benutzen. Sobald dies geschehen ist, hebt die erhitzte Klinge wieder auf. Doch seid gewarnt, die erhitzte Klinge wird nicht auf ewig erhitzt bleiben - behaltet also die Zeit im Auge.","","Zuletzt müsst Ihr das Biest töten und die uralte erhitzte Klinge in ihren Leib rammen. Kehrt anschließend zu mir zurück und Quel'Serrar wird Euer sein."}},
[7541] = {"Dienst an der Horde", nil},
[7562] = {"Mor'zul Bloodbringer", {"Sprecht mit Mor'zul Bloodbringer in der brennenden Steppe."}},
[7563] = {"Kochendes Blut", {"Mor'zul Bloodbringer in der brennenden Steppe möchte, dass Ihr ihm 30 Flaschen mit dem Blut der tobenden Bestien bringt."}},
[7564] = {"Garzeeki Wildeyes", {"Bringt den Behälter mit Blut zu Gorzeeki Wildeyes in der brennenden Steppe."}},
[7581] = {"Das Fundament des Gefängnisses", {"Reist nach Düsterbruch in Feralas und holt Euch das Blut von 15 Satyrn der Wildhufe, die im Wucherborkenviertel ansässig sind. Kehrt anschließend zu Daio in der faulenden Narbe zurück."}},
[7582] = {"Die Gefängnishülle", {"Reist in die flüsternde Schlucht in Winterspring und besorgt Euch 5 Tränen bei den Dämonen von Hederine, die in der Schlucht leben. Kehrt zu Daio in der faulenden Narbe zurück, sobald Ihr diese Aufgabe vollständig erledigt habt."}},
[7583] = {"Unterdrückung", {"Wagt Euch in die faulende Narbe heraus und macht einen Kommandant der Verdammniswache ausfindig.","","Wendet das leuchtende Kristallgefängnis auf den Kommandanten der Verdammniswache an. Bereitet Euch auf eine heftige Serie von Attacken vor, da der Dämon alles gegen seine Gefangennahme unternehmen wird.","","Solltet Ihr erfolgreich sein, bringt die gefangene Verdammniswache zu Daio in der faulenden Narbe."}},
[7601] = {"Nibys Anweisungen", {"Sprecht mit Impsy im Teufelswald."}},
[7602] = {"Reine Teufelsessenz", {"Impsy im Teufelswald möchte, dass Ihr ihm drei reine Teufelsessenzen aus drei bestimmten Gebieten besorgt.","","Die Satyrn der Legashi von Azshara bergen die Teufelsessenz ihrer Region. Die Legionäre von Jaedenar in Jaedenar bergen die Teufelsessenz ihrer Region. Die Wachposten der Teufelswache in den verwüsteten Landen bergen die Teufelsessenz ihrer Region.","","Findet die Teufelsessenzen und bringt sie zu Impsy im Teufelswald."}},
[7603] = {"Kroshius Höllenbestienkern", {"Sucht im Shattered Scar-Tal im Teufelswald nach den Überresten von Kroshius. Habt Ihr sie gefunden, so verwendet das Teufelsfeuer in deren Nähe und wartet dann auf Kroshius' Rückkehr. Tötet die Höllenbestie und entnehmt ihrem Körper den Höllenbestienkern.","","Bringt Kroshius' Höllenbestienkern zu Niby dem Allmächtigen im Teufelswald."}},
[7604] = {"Ein verbindlicher Vertrag", {"Bringt den Vertrag der Thoriumbruderschaft zu Lokhtos Darkbargainer, wenn Ihr die Pläne für das Sulfuron erhalten möchtet."}},
[7621] = {"Eine Warnung", nil},
[7622] = {"Die Waage von Licht und Schatten", {"Rettet 50 Arbeiter bevor 15 getötet wurden. Sprecht mit Eris Havenfire, falls Ihr diesen Auftrag erfolgreich zu Ende bringen solltet.","","Konsultiert den Todesposten, um zu sehen wie viele Arbeiter bereits getötet wurden."}},
[7623] = {"Lord Banehollow", {"Erwerbt schattenhaftige Tränke von Gorzeeki in der brennenden Steppe.","","Benutzt die Tränke um durch Jaedenar zu reisen und sprecht mit Lord Banehollow."}},
[7624] = {"Ulathek der Verräter", {"Stellt Euch Ulathek und bringt das Herz des Verräters zu Lord Banehollow in Jaedenar."}},
[7625] = {"Xorothianischer Sternenstaub", {"Erwerbt xorothianischen Sternenstaub von Ur'dan. Bringt es anschließend Gorzeeki in der brennenden Steppe."}},
[7626] = {"Die Glocke von Dethmoora", {"Bringt 10 Elixiere der Schattenmacht zu Gorzeeki Wildeyes in die brennende Steppe."}},
[7627] = {"Das Rad des Schwarzen Marsches", {"Bringt 6 große glänzende Splitter und 25 Stücke Dunkeleisenerz zu Gorzeeki Wildeyes in die brennende Steppe."}},
[7628] = {"Die Altarkerze des Jüngsten Gerichts", {"Bringt 35 Schuppen eines schwarzen Drachen zu Gorzeeki Wildeyes in der brennenden Steppe."}},
[7629] = {"Wichtellieferung", {"Bringt den Wichtel im Gefäß in das Alchimielabor in der Scholomance. Bringt nach der Herstellung des Pergaments, dass Gefäß zurück zu Gorzeeki Wildeyes."}},
[7630] = {"Arkanit", {"Bringt 3 Arkanitbarren zu Gorzeeki in der brennenden Steppe."}},
[7631] = {"Schreckensross von Xoroth", {"Lest Morzuls Anweisungen. Beschwört ein xorothianisches Schreckensross, besiegt es und bindet seinen Geist an Euch."}},
[7632] = {"Das uralte Blatt", {"Findet den Besitzer des uralten, versteinerten Blatts. Viel Glück, <Name>; die Welt ist groß."}},
[7633] = {"Eine Einführung", nil},
[7634] = {"Uraltes in Sehnen eingewickeltes Laminablatt", {"Hastat der Uralte hat Euch um die Beschaffung einer Sehne eines ausgewachsenen, blauen Drachens gebeten. Solltet Ihr diese Sehne finden, kehrt zu Hastat im Teufelswald zurück."}},
[7635] = {"Die passende Sehne", {"Stoma der Uralte hat Euch um die Beschaffung einer Sehne eines ausgewachsenen, schwarzen Drachens gebeten. Solltet Ihr diese Sehne finden, kehrt zu Stoma im Teufelswald zurück."}},
[7636] = {"Holzstab der Uralten", {"Ihr müsst die vier dämonischen Verderber ausfindig machen und zerstören:","","Simone die Verführerin.","","Klinfran der Wahnsinnige.","","Solenor der Schlächter.","","Artorius der Verhängnisvolle.","","Vernichtet diese Kreaturen und kehrt zu Vartrus dem Uralten im Teufelswald zurück.","","Haltet Euch an die versteinerte Rinde in Eurem Inventar, um Hinweise zu ihren Aufenthaltsorten zu erhalten.","","Ihr MÜSST diese Aufgabe eigenständig erledigen."}},
[7637] = {"Ein Opfer darbieten", {"Reist nach Ironforge und besorgt Euch Hohepriester Rohans Weihrauchschwenker. Eine Spende von 150 Gold wird erforderlich sein, um es zu bekommen."}},
[7638] = {"Lord Grayson Shadowbreaker", {"Sprecht mit Lord Grayson Shadowbreaker im Kathedralenviertel von Stormwind."}},
[7639] = {"Lass Gerechtigkeit walten", {"Bringt den Weihrauchschwenker zu Lord Grayson Shadowbreaker in Stormwind zurück."}},
[7640] = {"Der Exorzismus des Schreckenstals", {"Vertreibt mit Hilfe des Weihrauchschwenkers die Geister im Schreckenstal. Vernichtet 25 Schreckenstalgeister und kehrt anschließend zu Lord Grayson Shadowbreaker im Kathedralenviertel von Stormwind zurück."}},
[7641] = {"Grimand Elmores Werk", {"Sprecht mit Grimand Elmore im Zwergendistrikt von Stormwind."}},
[7642] = {"Fünf Dinge", {"Bringt 40 Lagen Runenstoff, 6 Arkanitbarren, 10 Artha's Tränen, 5 Phiolen heiliges Wasser aus Stratholme und 150 Gold zu Grimand Elmore im Zwergendistrikt von Stormwind."}},
[7643] = {"Der uralte Pferdegeist", {"Beschafft spezielles Pferdefutter mit dem man ein Geisterpferd füttern kann. Meredith Carlson in Southshore scheint dafür eine recht gute Quelle zu sein.","","Reist anschließend nach Düsterbruch in Feralas und tötet Tendris Wucherborke. Dies wird den uralten Pferdegeist befreien. Füttert den Geist zur Beruhigung mit dem speziellen Pferdefutter. Gebt dem Pferdegeist das Arkanitpferdegeschirr, um es segnen zu lassen."}},
[7644] = {"Gesegnetes Arkanitpferdegeschirr", {"Kehrt mit dem gesegneten Arkanitpferdegeschirr zu Lord Grayson Shadowbreaker in das Kathedralenviertel von Stormwind zurück."}},
[7645] = {"Mit Mana angereichertes Pferdefutter", {"Treibt 20 angereicherte Manakekse - die ausschlaggebende Zutat für die Herstellung von mit Mana angereichertem Pferdefutter - für Merideth Carlson aus Southshore in dem Vorgebirge von Hillsbrad auf. Die Argentumdämmerung ist als alleiniger Händler dieser Kekse bekannt.","","Zusätzlich müsst Ihr ihre Gefühle noch mit 50 Goldstücken besänftigen."}},
[7646] = {"Das Orakel der Anrufung", {"Bringt Lord Grayson Shadowbreaker im Kathedralenviertel von Stormwind einen Azerothianischen Diamanten und einen makellosen schwarzen Diamanten."}},
[7647] = {"Gerechtigkeit und Erlösung", {"Benutzt das Orakel der Anrufung im Herzen des Kellergewölbes des großen Ossuariums, in Scholomance. Damit ruft Ihr die verfluchten Geister, über deren Schicksal Ihr richten müsst. Durch das besiegen der Geister wird der Todesritter Schattensichel beschworen. Besiegt ihn und holt Euch die verlorene Seele des gefallenen Streitrosses.","","Gebt die Seele des gefallenen Streitrosses und das gesegnete und verzauberte Pferdegeschirr Schattensichels gefallenem Streitross zurück."}},
[7648] = {"Grimands feinste Arbeit", {"Bringt das Arkanitpferdegeschirr zur Begutachtung zu Lord Grayson Shadowbreaker im Kathedralenviertel von Stormwind."}},
