[7701] = {"GESUCHT: Vorarbeiter Maltorius", {"Tief in der Schlackengrube des Kessels in der sengenden Schlucht werdet Ihr den verräterischen Dunkeleisenzwerg, Aufseher Maltorius, finden.","","Tötet den Aufseher Maltorius und nehmt seinen Kopf an Euch. Bringt diesen zum Ausguck Captain Lolo Longstriker in der sengenden Schlucht für eine Belohnung."}},
[7703] = {"Die offene Rechnung der Gordok", {"Findet die Stulpen der Gordokmacht und bringt sie zu Captain Kromcrush in Düsterbruch.","","Kromcrush zufolge sagen die \"Alte Zeit Geschichten\", dass Tortheldrin - ein \"gruseliger\" Elf, der sich selbst als Prinz bezeichnet- sie einem der Gordokkönige gestohlen hat."}},
[7704] = {"Schau dir die Größe an!", {"Bringt das Kissen von Zimmermädchen Kissenquetscher zu Evonice Sootsmoker in der sengenden Schlucht."}},
[7721] = {"Treibstoff fürs Schrumpfen", {"Sammelt 10 Wasserelementarkerne von den Meereselementaren und Meeresschauern entlang der Vergessenen Küste von Feralas und bringt sie zurück zu Zorbin Fandazzle in der Nähe des dortigen Docks."}},
[7722] = {"Was zum Fluxus?!", {"Irgendwo in der Schlackengrube des Kessels findet Ihr die geheimen Pläne des Feurigen Fluxus, die der Aufseher Maltorius stahl. Findet diese Pläne und bringt sie zurück zu Meisterschmied Burninate in der sengenden Schlucht."}},
[7723] = {"Verdammt seien diese fetten Finger", {"Hansel Heavyhands möchte, dass Ihr 20 Schwere Kriegsgolems tötet.","","Die Schweren Kriegsgolems wandern in der Gegend um den Kessel in der sengenden Schlucht herum. Sobald Ihr genug getötet habt, kehrt zu Hansel zurück."}},
[7724] = {"Feurige Bedrohung!", {"Hansel Heavyhands möchte, dass Ihr 20 große Lavaspinnen in der sengenden Schlucht tötet.","","Die großen Lavaspinnen findet Ihr auf den Plateaus und in den Lavabecken der segenden Schlucht. Sobald Ihr genug getötet habt, kehrt zu Hansel zurück."}},
[7725] = {"Und wieder unter den geschrumpften Riesen", {"Benutzt Zorbins Super-Schrumpfer, um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt 10 Schrumpf-Überreste, die auf den kleineren handlicheren Riesen liegen zu Zorbin Fandazzle am Dock der Vergessenen Küste in Feralas."}},
[7726] = {"Neuer Treibstoff fürs Schrumpfen", {"Sammelt 6 Wasserelementarkerne von den Meereselementaren und Meeresschauern entlang der Vergessenen Küste von Feralas und bringt sie zurück zu Zorbin Fandazzle in der Nähe des dortigen Docks."}},
[7727] = {"Incendosaurier? Was-auch-immer-Saurier?", {"Hansel Heavyhands möchte, dass Ihr 20 Incendosaurier tötet.","","Die Incendosaurier haben sich in den Lavabecken der Schlackengrube eingenistet. Ihr findet die Schlackengrube im Kessel der segenden Schlucht.","","Sobald Ihr genug Incendosaurier getötet habt, kehrt zu Hansel Heavyhands in der sengenden Schlucht zurück."}},
[7728] = {"GESTOHLEN: Schmiedewindform und ein Ausguck-Handfernrohr", {"Sucht die Schmiedewindform und das Ausguck-Handfernrohr. Bringt diese Gegenstände zu Zuchtmeister Scrange in der sengenden Schlucht.","","Die einzigen Informationen die Ihr habt sind: Die Schmiedewindform is ein SCHMIEDEwerkzeug welches von SCHMIEDEN verwendet wird und das Ausguck-Handfernrohr ist ein unverzichtbares Werkzeug für SPÄHPOSTEN."}},
[7729] = {"STELLE ZU VERGEBEN: Erledige die Konkurrenz", {"Tötet 15 Zuchtmeister der Dunkeleisenzwerge und 15 Sklavenhändler der Dunkeleisenzwerge. Kehrt zu Zuchtmeister Scrange zurück, sobald Ihr die Aufgabe erfüllt habt."}},
[7730] = {"Zukk'ash-Plage", {"Bringt Hadoken Swiftstrider im Camp Mojache in Feralas 20 Zukk'ashknochenpanzer."}},
[7731] = {"Zornstachel", {"Besiegt Zornstachel in der verwundenen Tiefe und bringt seine Drüsen zu Hadoken Swiftstrider im Camp Mojache in Feralas zurück."}},
[7732] = {"Bericht über die Zukk'ash", {"Bringt den Bericht über die Zukk'ash aus Camp Mojache zu Zilzibin Drumlore. Er wohnt in der Gasse von Orgrimmar."}},
