[7761] = {"Blackhands Befehl", {"Das war ja vielleicht mal ein dummer Orc. Es sieht so aus, als müsstet Ihr dieses Brandzeichen finden, um an das Mal von Drakkisath zu gelangen. Damit sollte sich die Befehlskugel aktivieren lassen.","","Dem Brief zufolge, wird das Brandzeichen von General Drakkisath bewacht. Vielleicht solltet Ihr diesem Hinweis nachgehen."}},
[7781] = {"Der Herrscher von Blackrock", {"Bringt Hochlord Bolvar Fordragon in Stormwind den Kopf von Nefarian."}},
[7782] = {"Der Herrscher von Blackrock", {"Reist in nach Stormwind in das Tal der Helden und sprecht mit Feldmarschall Afrasiabi."}},
[7783] = {"Der Herrscher von Blackrock", {"Bringt Thrall in Orgrimmar den Kopf von Nefarian."}},
[7784] = {"Der Herrscher von Blackrock", {"Sprecht mit Hochfürst Saurfang in Orgrimmar. Er wartet im Tal der Stärke auf Euch."}},
[7785] = {"Untersuchung des Gefäßes", {"Untersucht das Gefäß der Wiedergeburt und bringt es zu Hochlord Demitrian."}},
[7786] = {"Donneraan der Windsucher", {"Um Donneraan den Windsucher aus seiner Gefangenschaft zu befreien, müsst Ihr Hochlord Demitrian die rechten und linken Hälften der Fesseln des Windsuchers, 10 Elementiumbarren und die Essenz des Feuerfürsten bringen."}},
[7787] = {"Donnerzorn erwache!", {"Präsentiert Hochlord Demitrian die vom Wind berührte, schlummernde Klinge."}},
[7788] = {"Besiegt die Eindringlinge!", {"Betretet die Warsongschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Warsongschlucht und kehrt zur Schildwache Farsong am Hain der Silverwing zurück."}},
[7789] = {"Bezwingt die Eindringlinge der Silverwing", {"Betretet die Warsongschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Warsongschlucht und kehrt anschließend zu Captain Shatterskull im Mor'shan-Stützpunkt zurück."}},
