[4810] = {"Vuelve con Tinkee", {"Llévale el paquete de Felnok a Tinkee Vaporio en Las Estepas Ardientes."}},
[4811] = {"El cristal rojo", {"Viaja al este de Auberdine y busca un gran cristal rojo en la zona montañosa que hay al este de Costa Oscura. Vuelve a Auberdine e informa de lo que has encontrado a la centinela Glynda Nal'Shea."}},
[4812] = {"Agua en cascada", {"Llena el vial de agua vacío en la poza de la luna de Auberdine y luego examina el cristal rojo a lo largo de la pared montañosa oriental de Costa Oscura."}},
[4813] = {"Los fragmentos del interior", {"Vuelve a Auberdine e informa de lo que has averiguado a la centinela Glynda Nal'Shea."}},
[4821] = {"Un huevo extraño", {"Devuélvele el huevo extraño a Hagar Pezuña del Rayo, que está en el Poblado Viento Libre."}},
[4822] = {"¡Quiero un heladooooo!", {"Consigue helado de fresa para tu pupilo. Parece que el favorito del muchacho es el helado de Tigule y Foror."}},
[4841] = {"Apaciguar al centauro", {"Mata a 12 exploradores Galak, 10 retadores Galak y 6 cazavientos Galak, y después ve a ver de nuevo al vigiarrisco Cuerno Largo al Poblado Viento Libre."}},
[4842] = {"Fuentes extrañas", {"Sigue las indicaciones de Donova Cubilnevado y vuelve a informarle."}},
[4861] = {"Lechúcico iracundo", {"Localiza el campamento arrasado y los suministros de Jaron Tallapiedras en la parte sur de Cuna del Invierno."}},
[4862] = {"Bestia amaestrada", {"Viaja a la Cumbre de Roca Negra y reúne 15 huevos de araña de la cumbre para Kibler.","Yo diría que se pueden encontrar estos huevos cerca de arañas."}},
[4863] = {"Lechúcico salvaje iracundo", {"Encuentra el carro destrozado de Jaron Tallapiedras."}},
[4864] = {"Lechúcico iracundo", {"Recoge de la nieve los Suministros de Jaron y encuentra al lechúcico que robó el amuleto del que te habló Jaron.","Lleva los Suministros de Jaron y el Amuleto de plumas azules a Jaron Tallapiedras en el refugio de Cuna del Invierno."}},
[4865] = {"Venganza", {"Cuéntale a Motega Pirocabello lo que has descubierto."}},
[4866] = {"La leche de la madre", {"En el corazón de la Cumbre de Roca Negra encontrarás a Madre Telabrasada. Provócala para que te envenene. Lo más seguro es que tendrás que matarla también. Vuelve junto a John Andrajoso cuando estés envenenado para que pueda extraer el veneno de ti."}},
[4867] = {"Urok Aullasino", {"Lee el pergamino de Warosh. Llévale el mojo de Warosh a Warosh."}},
[4881] = {"Complot de asesinato", {"Avisa a Kanati Nubegris, que está en el Campamento del Tramo Blanco, acerca de la conspiración para acabar con su vida."}},
[4882] = {"Guardar un secreto", {"Lleva el Collar de plumas azules a Trull Fallarruina en el Puesto del Veneno."}},
[4883] = {"Guardar un secreto", {"Lleva el Collar de plumas azules a Nara Bravacrín."}},
[4901] = {"Guardianes del altar", {"Protege a Ranshalla mientras intenta reactivar el Altar de Elune. Informa sobre tus descubrimientos a Eralas Cieloámbar de Aldea Rut'theran."}},
[4902] = {"Lechúcico de Elune", {"Habla con el archidruida Fandral Corzocelada de Darnassus."}},
[4903] = {"La orden del Señor de la Guerra", {"Mata al alto señor Omokk, al maestro de guerra Voone y al señor supremo Vermiothalak. Recupera importantes documentos Roca Negra. Vuelve junto al señor de la guerra Dientegore en Kargath cuando hayas cumplido la misión."}},
[4904] = {"Al fin libre", {"Saca a Lakota Canzalvaje de Cumbre de la Nube Negra y después habla con Thalia Pielámbar, que está en el Poblado Viento Libre."}},
[4906] = {"Más corrupción", {"Eridan Vientoazul de Frondavil quiere que mates a 8 clamainfernos Fuego de Jade, 8 traidores Fuego de Jade, 8 timadores Fuego de Jade y a Xavaric. Vuelve con Eridan cuando lo hayas hecho."}},
[4907] = {"Tinkee Vaporino", {"Habla con Tinkee Vaporio."}},
[4921] = {"Perdida en la batalla", {"Encuentra a la esposa de Mankrik y llévala a El Cruce."}},
[4941] = {"La sabiduría de Eitrigg", {"Habla con Eitrigg en Orgrimmar. Cuando hayas tratado unos asuntos con Eitrigg, busca el consejo de Thrall.","Recuerdas haber visto a Eitrigg en la Cámara de Thrall."}},
[4961] = {"La purificación del orbe de Orahil", {"Mata al demonio del orbe y luego habla con Tabetha."}},
[4962] = {"Fragmento de can manáfago", {"Coge el rubí de Felhas y úsalo en uno de los canes manáfagos de Desolace. Cuando lo consigas, llévale el rubí de Felhas y el espíritu del can manáfago capturado a Menara Nadiz en Los Baldíos."}},
[4963] = {"Fragmento de infernal", {"Coge la esmeralda de Infus y úsala en uno de los infernales de Desolace. Cuando lo consigas, llévale la esmeralda de Infus y el espíritu del infernal capturado a Menara Nadiz en Los Baldíos."}},
[4964] = {"El orbe completo de Dar'Orahil", {"Espera a Menara Nadiz para completar el orbe de Dar'Orahil y vuelve entonces a hablar con ella."}},
[4965] = {"El conocimiento del orbe de Orahil", {"Habla con Menara Nadiz en Los Baldíos."}},
[4966] = {"Defensa de Kanati Nube Gris", {"Defiende a Kanati Nube Gris contra el ataque de los centauros."}},
[4967] = {"El conocimiento del orbe de Orahil", {"Habla con Menara Nadiz en Los Baldíos."}},
[4968] = {"El conocimiento del orbe de Orahil", {"Habla con Menara Nadiz en Los Baldíos."}},
[4969] = {"El conocimiento del Orbe de Orahil", {"Habla con Menara Nadiz en Los Baldíos."}},
[4970] = {"Provisiones Sable de hielo", {"Recoge 5 carnes dentoesquirla y 5 carnes venfresco para Rivern Ventogélido de Cuna del Invierno."}},
[4971] = {"Una cuestión de tiempo", {"Utiliza el desplazador temporal junto a uno de los silos de Andorhal y saca a la luz los parásitos temporales.","Mata a 15 parásitos temporales y devuélvele el desplazador temporal a Chromie en la posada de Andorhal, en las Tierras de la Peste del Oeste."}},
[4972] = {"Contar el tiempo", {"Localiza 5 relojes de Andorhal, los encontrarás en cajas fuertes entre las ruinas de la ciudad. Cuando los tengas regresa con Chromie, en la posada de Andorhal, en las Tierras de la Peste del Oeste."}},
[4973] = {"Contar el tiempo", {"Localiza 5 relojes de Andorhal entre las ruinas de la ciudad y llévaselos a Chromie, en la posada de Andorhal, en las Tierras de la Peste del Oeste."}},
[4974] = {"¡Por la Horda!", {"Ve a Cumbre de Roca Negra para matar al Jefe de Guerra Rend Puño Negro. Lleva su cabeza a Orgrimmar."}},
[4975] = {"El orbe completo de Noh'Orahil", {"Espera a Menara Nadiz para completar el orbe de Noh'Orahil y vuelve entonces a hablar con ella."}},
[4976] = {"Devolver el orbe purificado", {"Llévale el orbe infernal limpio a Menara Nadiz en Los Baldíos."}},
[4981] = {"La espía Bijou", {"Viaja hasta la Cumbre de Roca Negra y averigua qué le ha ocurrido a Bijou."}},
[4982] = {"Las pertenencias de Bijou", {"Encuentra las pertenencias de Bijou y devuélveselas. Recuerdas que ella mencionó haberlas ocultado en la planta baja de la ciudad.|n"}},
[4983] = {"El informe de reconocimiento de Bijou", {"Llévale el informe de reconocimiento de Bijou al maestro mayor Lexlort en Kargath."}},
[4984] = {"La naturaleza también sufre", {"Destruye 8 lobos malsanos y regresa con Mulgris Río Hondo en El Tormento, en las Tierras de la Peste del Oeste.|n|n"}},
[4985] = {"La naturaleza también sufre", {"Destruye 8 griseznos malsanos y regresa con Mulgris Riohondo en El Tormento, en las Tierras de la Peste del Oeste."}},
[4986] = {"Rama de roble con inscripciones", {"Según la magia con la que está encantada La rama de roble con inscripciones, el siguiente paso del druida tauren debía ser su entrega al Círculo Cenarion en Darnassus. Pide ayuda a uno de los druidas que encontrarás allí."}},
[4987] = {"Rama de roble con inscripciones", {"Según la magia con la que está encantada de La rama de roble con inscripciones, el siguiente paso del druida tauren debía ser su entrega al Círculo Cenarion de Cima del Trueno. Pide ayuda a uno de los druidas que encontrarás allí."}},
[5001] = {"Las pertenencias de Bijou", {"Encuentra las pertenencias de Bijou y devuélveselas. ¡Suerte!"}},
[5002] = {"Un mensaje para Maxwell", {"Viaja a Las Estepas Ardientes y dale la información sobre Bijou al alguacil Maxwell."}},
[5021] = {"Más vale tarde que nunca", {"Entre lamentos, la mujer fantasmal recordaba tener que entregar un paquete. Según ella estaba junto a su caballo. No puedes entender dónde está el caballo o dónde había que entregar el paquete."}},
[5022] = {"Más vale tarde que nunca", {"Consulta con los agentes reales de Ventormenta para averiguar el paradero de una tal Emma Piedra Mácula. Suele haber un oficial del censo en el Ayuntamiento."}},
[5023] = {"Más vale tarde que nunca", {"Consulta con los supervisores reales de Entrañas para averiguar el paradero de Jeremiah Piedra Mácula. Suele haber un oficial del censo junto a la oficina de registro de hermandades y tabardos."}},
[5041] = {"Suministros para El Cruce", {"Encuentra los cajones de suministros de El Cruce y devuélveselos a Thork en Los Baldíos."}},
