[5048] = {"El buen carácter de Emma", {"Encuentra a la Vieja Emma en Ventormenta y descubre si realmente es Emma Piedra Mácula. Si lo es, quizás quiera el paquete que Janice Piedra Mácula hizo para ella."}},
[5049] = {"La depre de Jeremiah", {"Encuentra a Jeremiah Payson en Entrañas y averigua si en realidad es Jeremiah Piedra Mácula. Si es así, quizás quiera el paquete que Janice Piedra Mácula preparó para él."}},
[5050] = {"Talismán de la buena suerte", {"Devuelve a Janice Piedra Mácula en las Tierras de la Peste del Oeste su Medio talismán de buena suerte."}},
[5051] = {"Dos mitades hacen una", {"Localiza la otra mitad del talismán de la suerte en algún lugar de la Granja de Piedra Mácula, en las Tierras de la Peste del Oeste, y recomponlo.","Dale el talismán de la suerte a Janice Piedra Mácula en la Granja de Piedra Mácula, en las Tierras de la Peste del Oeste."}},
[5052] = {"Fragmentos ensangrentados de Agamaggan", {"Llévale un fragmento ensangrentado a Quebrantadientes en el Campamento Taurajo en Los Baldíos.","Reúne más fragmentos ensangrentados si quieres que te bendiga con el poder de Agamaggan."}},
[5054] = {"Ursius de los dentoesquirla", {"Ventisca Pezumbría de Vista Eterna, en Cuna del Invierno, quiere que mates a Ursius."}},
[5055] = {"Brumeran de los Venfresco", {"Ventisca Pezumbría de Vista Eterna, en Cuna del Invierno, quiere que mates a Brumerán."}},
[5056] = {"Shy-Rotam", {"Ventisca Pezumbría de Vista Eterna, en Cuna del Invierno, quiere que acabes con Shy-Rotam."}},
