[5065] = {"Las tablillas perdidas de Mosh'aru", {"Llévale la tercera y cuarta tablillas de Mosh'aru al prospector Ferrobota, que está en Tanaris."}},
[5066] = {"Un llamamiento a las armas: las Tierras de la Peste", {"Busca al comandante Ashlam Puñovalor. Su campamento base está situado en el Campamento del Orvallo, al norte de las Montañas de Alterac."}},
[5067] = {"Leotardos de Arcana", {"Para crear los leotardos arcana deberás llevarle los siguientes objetos a Malyfous Martilloscuro: 1 cuero prístino de la Bestia; 5 costuras desgarradas de abominación; 5 lingotes de arcanita; 5 líquidos de embalsamar de Levescarcha.","También tendrás que pagar 50 piezas de oro para la elaboración del almete."}},
[5068] = {"Coraza Sed de Sangre", {"Para crear la coraza Sed de Sangre deberás llevarle los siguientes objetos a Malyfous Martilloscuro: 1 pellejo de la Bestia prístino; 10 costuras desgarradas de abominación; 5 barras de arcanita; 5 pieles de Sombra.","También tendrás que pagar 50 piezas de oro para la elaboración del almete."}},
[5081] = {"La misión de Maxwell", {"Viaja a la Cumbre de Roca Negra y destruye al maestro de guerra Voone, al alto señor Omokk y al Señor Supremo Vermiothalak. Vuelve junto al alguacil Maxwell cuando hayas acabado tu trabajo."}},
[5082] = {"La amenaza de los Nevada", {"Donova Cubilnevado de Cuna del Invierno quiere que mates 8 abrecaminos Nevada, 8 Vigilantes del cubil Nevada y 8 totémicos Nevada."}},
[5083] = {"Piroagua Nevada", {"Lleva el Frasco vacío de piroagua a Donova Cubilnevado de Cuna del Invierno."}},
[5084] = {"Sucumbir a la corrupción", {"Busca una pista en el campamento fúrbolg Muertobosque en el norte de Frondavil."}},
[5085] = {"Baba misteriosa", {"Informa sobre tus descubrimientos a Donova Cubilnevado."}},
[5086] = {"Horrores tóxicos", {"Recoge 3 gotitas horror tóxico para Donova Cubilnevado de Cuna del Invierno."}},
[5087] = {"Corredores Nevada", {"Encuentra a los corredores Nevada y detenlos antes de que entreguen su cargamento en Poblado Nevada."}},
[5088] = {"Arikara", {"Mata a Arikara. Llévale sus restos y el polvo de incendiaria a Motega Pirocabello al Campamento del Tramo Blanco como prueba de tus actos."}},
[5089] = {"Orden del general Drakkisath", {"Llévale la orden del general Drakkisath al alguacil Maxwell en Las Estepas Ardientes."}},
[5090] = {"Un llamamiento a las armas: las Tierras de la Peste", {"Busca al comandante Ashlam Puñovalor; su campamento base está en Campamento del Orvallo, al norte de las Montañas de Alterac."}},
[5091] = {"Un llamamiento a las armas: las Tierras de la Peste", {"Busca al comandante Ashlam Puñovalor. Su campamento base se encuentra en Campamento del Orvallo, al norte de las Montañas de Alterac."}},
[5092] = {"Despejando el camino", {"Mata 10 despellejadores esqueléticos y 10 necrófagos esclavizantes en la Colina de las Penas.","Vuelve con el comandante Ashlam Puñovalor en el Campamento del Orvallo, en las Tierras de la Peste del Oeste."}},
[5093] = {"Un llamamiento a las armas: las Tierras de la Peste", {"Busca al sumo ejecutor Derrington; su campamento base está en El Baluarte, al este de Claros de Tirisfal y Entrañas."}},
[5094] = {"Un llamamiento a las armas: las Tierras de la Peste", {"Busca al sumo ejecutor Derrington; su campamento base está en El Baluarte, al este de Claros de Tirisfal y Entrañas."}},
[5095] = {"Un llamamiento a las armas: las Tierras de la Peste", {"Busca al sumo ejecutor Derrington; su campamento base está en El Baluarte, al este de Claros de Tirisfal y Entrañas."}},
[5096] = {"Distracción Escarlata", {"Adéntrate en el campamento base de la Cruzada Escarlata entre el Campo de Piedramácula y el Llanto de Dalson y destruye su tienda de mando.","Coloca el estandarte de la Plaga en el campamento y vuelve con el sumo ejecutor Derrington en El Baluarte en las Tierras de la Peste del Oeste."}},
[5097] = {"Todas las torres", {"Con la Antorcha de aviso, marca las torres de Andorhal. Tendrás que estar en la entrada de la torre para poder marcarla.","Cuando estén marcadas las cuatro torres, devuelve la Antorcha de aviso al comandante Ashlam Puñovalor del Campamento del Orvallo, en las Tierras de la Peste del Oeste."}},
[5098] = {"Todas las atalayas", {"Con la antorcha de aviso, marca las atalayas de Andorhal. Tendrás que estar en la entrada de la atalaya para poder marcarla.","Cuando estén marcadas las cuatro atalayas, devuelve la antorcha de aviso al sumo ejecutor Derrington en El Baluarte, en las Tierras de la Peste del Oeste."}},
[5102] = {"Muerte al general Drakkisath", {"Viaja hasta la Cumbre de Roca Negra y mata al general Drakkisath. Vuelve junto al alguacil Maxwell cuando hayas acabado tu trabajo."}},
[5103] = {"Muerte abrasadora", {"Alguien en este mundo debe de saber qué hacer con estos guanteletes. ¡Suerte!"}},
[5121] = {"Alto Jefe Nevada", {"Donova Cubilnevado de Cuna del Invierno quiere que derrotes al Gran Jefe Nevada."}},
[5122] = {"El medallón de Fe", nil},
[5123] = {"La pieza final", {"Lleva el Diario mal escrito a Donova Cubilnevado de Cuna del Invierno."}},
[5124] = {"Guanteletes de placas ígneas", {"Lleva a Malyfous Martilloscuro 6 barras de torio encantado, 2 esencias de fuego y 4 rubíes estrellados. También tendrás que entregarle tus guanteletes de placas sin templar."}},
[5125] = {"La estimación de Aurius", nil},
[5126] = {"La historia de Lorax", {"Habla con Lorax. Escucha todo lo que tiene que decirte.|n"}},
[5127] = {"La forja demoníaca", {"Viaja a la Cumbre de Roca Negra y encuentra a Goraluk Yunquegrieta. Mátale y utiliza el Pico manchado de sangre sobre su cadáver. Cuando hayas absorbido su alma, el pico estará manchado de alma.","También debes encontrar el peto cubierto de runas sin forjar.","Lleva el pico manchado de alma y el peto cubierto de runas sin forjar a Lorax en Cuna del Invierno."}},
[5128] = {"Palabras del Gran Jefe", {"Lleva el registro escrito de forma rudimentaria a Kelek Guardacielo en el sur de Frondavil."}},
[5141] = {"Peletería dragontina", {"Consigue 2 corazas resistentes de escórpido, 2 guantes resistentes de escórpido, y 10 escamas de dragón desgastadas para Peter Galen, en Azshara.","Si completas esta misión tendrás acceso al arte de la peletería dracónica.","Si lo haces, también dejarás de tener acceso a la peletería elemental y tribal. Tienes que asegurarte de querer seguir este camino antes de completarla."}},
[5142] = {"La pequeña Pamela", {"Encuentra a Pamela Rutagrana."}},
[5143] = {"Peletería tribal", {"Lleva 1 jubón de cuero salvaje y 1 casco de cuero salvaje a Caryssia Cazaluna a Feralas.","Si completas esta misión, tendrás acceso a las artes de Peletería tribal.","Si lo haces, no podrás aprender peletería dragontina ni peletería elemental; asegúrate de que este es el camino que deseas seguir antes de completar la misión."}},
[5144] = {"Peletería elemental", {"Lleva 2 corazones de fuego, 2 globos de agua, 2 núcleos de tierra y 2 alientos de viento a Sarah Peletero a La Garganta de Fuego.","Al completar esta tarea aprenderás peletería elemental.","Si te dedicas a este arte, no podrás aprender peletería dragontina ni peletería tribal; piénsatelo bien antes de decidirte."}},
[5145] = {"Peletería dragontina", {"Consigue 2 corazas resistentes de escórpido, 2 guantes resistentes de escórpido, y 10 escamas de dragón desgastadas para Thorkaf Dragonojo, en las Tierras Inhóspitas.","Si completas esta misión tendrás acceso al arte de la peletería dracónica.","Si lo haces, también dejarás de tener acceso a la peletería elemental y tribal. Tienes que asegurarte de querer seguir este camino antes de completarla."}},
[5146] = {"Peletería elemental", {"Llévale 2 corazones de fuego, 2 globos de agua, 2 núcleos de tierra y 2 alientos de viento a Brumm Pezuña Invernal en las Tierras Altas de Arathi.","Cuando completes esta misión tendrás acceso a las artes de peletería elemental.","Si completas esta misión, no podrás aprender peletería dragontina ni peletería tribal; asegúrate de que este es el camino que deseas seguir antes de tomarlo."}},
[5147] = {"Se busca: Arnak Tótem Siniestro", {"Mata a Arnak Tótem Siniestro y llévale una prueba de tu hazaña a Vigiarisco Cuernolargo, que está en el Poblado Viento Libre."}},
[5148] = {"Peletería tribal", {"Lleva un jubón de cuero salvaje y un casco de cuero salvaje a Se'Jib en la Vega de Tuercespina.","Completar esta misión te proporcionará acceso a las artes de la peletería tribal.","Completar esta misión evitará que aprendas peletería dragontina y peletería elemental. Asegúrate que este es el camino que quieres seguir antes de hacerlo."}},
[5149] = {"La muñeca de Pamela", {"Encuentra la muñeca de Pamela."}},
[5150] = {"¡Dadanga tiene hambre!", nil},
[5151] = {"Hipercondensador", {"Acaba con la bestia y trae de regreso el hipercapacitor y la llave de la jaula a Wizlo Rodabrillante."}},
[5152] = {"Tía Marlene", {"Habla con Marlene. Está al sur de las Ruinas de Andorhal."}},
[5153] = {"Un extraño historiador", {"Lleva la alianza de Joseph a Chromie."}},
[5154] = {"Las Crónicas de Villa Darrow", {"Lleva el libro Crónicas de Villa Darrow a Cromi a Andorhal."}},
[5155] = {"Las fuerzas de Jaedenar", {"Entra en Jaedenar y mata a 4 canes Jaedenar, 4 soldados Jaedenar, 6 adeptos Jaedenar y 6 fieles Jaedenar antes de volver con Greta Pezuñamusgo a Frondavil."}},
[5156] = {"Investigación acerca de la corrupción", {"Busca y destruye a 2 bestias entrópicas y 2 horrores entrópicos a la vez que exploras la Cañada Gran Cicatriz. Cuando hayas comprobado si los cráteres del lugar aún contienen putraqua corrupta, vuelve con Taronn Plumarroja a Frondavil."}},
[5157] = {"Una muestra de agua corrupta", {"Lleva la cantimplora vacía a la poza de la luna que hay en el centro de Jaedenar y llénala antes de volver con Greta Pezuñamusgo a Frondavil."}},
[5158] = {"Buscando ayuda espiritual", {"Lleva el agua de poza de luna corrupta a Islen Vident'aqua a Los Baldíos."}},
[5159] = {"El agua purificada vuelve a Frondavil", {"Lleva el agua de poza de luna purificada a Greta Pezuñamusgo en Frondavil."}},
[5160] = {"El Protectorado de la matrona", {"Viaja hasta la Cuna del Invierno y encuentra a Haleh. Dale la escama de Awbee."}},
[5161] = {"La cólera del Vuelo Azul", {"Habla con Haleh si quieres continuar."}},
[5162] = {"La cólera del Vuelo Azul", {"Habla con Jeziba en las Tierras de la Peste. Vive en Andorhal."}},
[5163] = {"Yeti... ¿ya hemos llegado?", {"Coge el yeti mecánico de Umi y asusta con él a sus amigos:","Legacki en Vista Eterna (Cuna del Invierno)","Roxio en Gadgetzan (Tanaris)","Quixxil en el Refugio de Marshal (Cráter de Un'Goro)","Cuando hayas terminado, devuelve el yeti mecánico a Umi."}},
[5164] = {"Catálogo del Díscolo", {"Lee el Catálogo del Díscolo.|n"}},
[5165] = {"Sofocar las llamas de protección", {"Utiliza el agua de la Poza de la Luna purificada para apagar las llamas de los cuatro Blandones de Protección de la Guarida Sombría de Frondavil y vuelve con Greta Pezuñamusgo al Santuario Esmeralda."}},
[5166] = {"Coraza del Vuelo Cromático", {"Para forjar la coraza del Vuelo Cromático deberás llevarle a Jeziba el 'Escultor\" los siguientes objetos:","1 caparazón cromático.","10 escamas cromáticas luminosas.","10 sangres de héroes.","10 costuras desgarradas de abominación."}},
[5167] = {"Quijotes del Desafiante cromático", {"Para forjar los quijotes del desafiante cromático deberás llevarle a Jeziba el 'Escultor\" los siguientes objetos:","1 caparazón cromático.","10 escamas cromáticas brillantes.","10 sangres de héroes.","5 pieles de sombra."}},
[5168] = {"Los héroes de Villa Darrow", {"Lleva el Tratado de Davil y el Escudo de Rutagrana a Carlin Rutagrana."}},
[5181] = {"Los villanos de Villa Darrow", {"Lleva el cráneo de Horgus y la espada destrozada de Marduk a Carlin Rutagrana a la Capilla de la Esperanza de la Luz."}},
[5201] = {"Intrusión Nevada", {"Rivern Ventogélido quiere que mates 5 chamanes Nevada y 5 ursas Nevada."}},
[5202] = {"Una extraña llave roja", {"Recorre Jaedenar y descubre para qué se utiliza la llave roja de sangre."}},
[5203] = {"El rescate en Jaedenar", {"Escolta a Arko'narin hasta el lugar en el que están Forjaluz y su equipo y protégela en la huida de la Guarida Sombría. Después, busca a Jessir Arcolunar en Frondavil para contarle lo que les ha ocurrido a sus amigos."}},
[5204] = {"La reprensión de la Luz", {"Vuelve a Jaedenar y mata al súcubo Rakaiah antes de encontrar los Restos de Trey Forjaluz."}},
[5206] = {"Los merodeadores de Villa Dorrow", {"Lleva 5 calaveras resonantes y el cristal místico a Carlin Rutagrana a las Tierras de la Peste del Este."}},
[5210] = {"Carlin, el hermano de Joseph", {"Lleva las Crónicas ampliadas de Villa Darrow a Carlin Rutagrana a las Tierras de la Peste del Este."}},
[5211] = {"Los defensores de Villa Darrow", {"Libera a 15 espíritus de Villa Darrow y vuelve con Carlin Rutagrana."}},
[5212] = {"La carne no miente", {"Consigue 20 muestras de carne apestadas de Stratholme y llévaselas a Betina Bigglezink. Tienes la sospecha de que podrás obtener ese tipo de carne de cualquier criatura de Stratholme."}},
[5213] = {"El agente activo", {"Viaja a Stratholme y busca los zigurats. Encuentra información sobre la Plaga y llévasela a Betina Bigglezink.|n"}},
[5214] = {"El gran Ezra Grimm", {"Encuentra la Tienda de humo de Ezra Grimm en Stratholme y recupera una caja del tabaco de calidad de Grimm. Cuando lo hayas hecho, vuelve con Smokey LaRue."}},
[5215] = {"Las calderas de la Plaga", {"Habla con la suma sacerdotisa MacDonnell en el Alto del Orvallo, en las Tierras de la Peste del Oeste."}},
[5216] = {"Objetivo: Campo de Piedra Mácula", {"Ve al Campo de Piedra Mácula en las Tierras de la Peste del Oeste para localizar y derrotar a su señor de la caldera. Seguramente tendrá una llave para acceder a la caldera. Debes llevar contigo la Botella vacía del Campo de Piedra Mácula para obtener una muestra de los venenos utilizados en el interior de la caldera."}},
[5217] = {"El regreso al Campamento del Orvallo", {"Lleva la Botella rellena del Campo de Piedra Mácula a la suma sacerdotisa MacDonnell en el Campamento del Orvallo, en las Tierras de la Peste del Oeste."}},
