[5242] = {"El golpe final", {"Mata a Moora y Salia y lleva la cabeza del Señor de las Sombras Fel'dan a Greta Pezuñamusgo a Frondavil."}},
[5243] = {"Las casas de lo sagrado", {"Viaja al norte, a Stratholme. Registra los cajones de provisiones que hay por toda la ciudad y recupera 5 de agua bendita de Stratholme. Vuelve junto a Leonid Barthalomew el Venerado cuando hayas reunido suficiente líquido bendecido."}},
[5244] = {"Las ruinas de Kel'Theril", {"Habla con Jaron Tallapiedras en Aldea Estrella Fugaz."}},
[5245] = {"Espíritus condenados de Kel'Theril", {"Utiliza el pico de Jaron para encontrar los cuatro fragmentos de reliquia de los altonatos. Llévaselas a Aurora Llamacielo en las Tierras de la Peste del Este."}},
[5246] = {"Fragmentos del pasado", {"Encuentra las escrituras altonatas sagradas del templo de Zin-Malor en Azshara y tráelas para Aurora Llamacielo, en la Torre del Paso Norte en las Tierras de la Peste del Este."}},
[5247] = {"Fragmentos del pasado", {"Consigue una barra encantada de torio, 5 cristales de restauración y un vial de agua funesta para Aurora Llamacielo, en la Torre del Paso Norte, en las Tierras de la Peste del Este."}},
[5248] = {"Atormentados por el pasado", {"Lleva el Cristal de Zin-Malor a Lago Kel'Theril en Cuna del Invierno y encuentra un espíritu altonato que hable contigo."}},
[5249] = {"¡A la Cuna del Invierno!", {"Encuentra la Aldea Estrella Fugaz en Cuna del Invierno y habla con Wynd Cazanoche."}},
[5250] = {"Estrella Fugaz", {"Encuentra la Aldea Estrella Fugaz en Cuna del Invierno y habla con Wynd Cazanoche."}},
[5251] = {"El archivista", {"Viaja a Stratholme y encuentra al archivista Galford de La Cruzada Escarlata. Acaba con él y quema el Archivo Escarlata."}},
[5252] = {"Altonato arrepentido", {"Habla con Wynd Cazanoche en Aldea Estrella Fugaz y pregúntale por el lugar adecuado para el Cristal de Zin-Malor."}},
[5253] = {"El cristal de Zin-Malor", {"Entrega el Cristal de Zin-Malor al archidruida Fandral Corzocelada de Darnassus."}},
[5261] = {"Eagan Desollapieles", {"Habla con Eagan Desollapieles."}},
[5262] = {"La verdad cae del cielo", {"Lleva la cabeza de Balnazzar al Duque Nicholas Zverenhoff en las Tierras de la Peste del Este."}},
[5263] = {"Por encima y más allá", {"Viaja a Stratholme y acaba con el barón Osahendido. Coge su cabeza y vuelve con el duque Nicolas Zverenhoff."}},
[5264] = {"Lord Maxwell Neófitus", {"Habla con Lord Maxwell Neófitus. Está en el interior de la iglesia."}},
[5265] = {"El Bastión Argenta", {"Abre el Bastión Argenta y reclama tu recompensa."}},
[5281] = {"Espíritus inquietos", {"Encuentra a Egan. Solo sabes que fue visto por última vez en los alrededores de Stratholme."}},
[5282] = {"Espíritus inquietos", {"Utiliza el libertador de Egan en los ciudadanos espectrales y fantasmas de Stratholme. Cuando las almas se liberen de sus recipientes fantasmales, vuelve a usarlo y lograrás liberarlos para siempre.","Libera 15 almas y regresa a Egan."}},
[5283] = {"El arte del forjador de armaduras", {"Para convertirte en <forjador/forjadora> de armaduras tienes que fabricar lo siguiente y llevárselo a Grumnus: 4 cascos, 2 botas y 1 peto de mitril ornamentado."}},
[5284] = {"El arte del forjador de armas", {"Para convertirte en <armero/armera> tienes que fabricar lo siguiente y llevárselo a Ironus: 4 espadas de acero lunar, 4 hachas macizas de hierro, 2 hachas de mitril pesado y 2 grandes mazas negras."}},
[5301] = {"El arte del forjador de armaduras", {"Para convertirte en <forjador/forjadora> de armaduras tienes que fabricar lo siguiente y llevárselo a Okothos: 4 yelmos de mitril ornamentado, 2 pares de botas de mitril ornamentado y 1 coraza de mitril ornamentado."}},
[5302] = {"El arte del forjador de armas", {"Para convertirte en <forjador/forjadora> de armas tienes que fabricar lo siguiente y llevárselo a Borgosh: 4 espadas anchas de acero lunar, 4 hachas de hierro macizo, 2 hachas de mitril pesado y 2 mazas negras grandes.|n"}},
[5305] = {"Dulce serenidad", {"Viaja a Stratholme y mata al forjamartillos Carmesí. Consigue el delantal del forjamartillos Carmesí y regresa con Lilith.|n"}},
[5306] = {"Piedra Serpiente de la Cazadora de las Sombras", {"Viaja a la Cumbre de Roca Negra y mata a la cazadora de las Sombras Vosh'gajin. Recupera la piedra serpiente de Vosh'gajin y vuelve con Kilram."}},
[5307] = {"Corrupción", {"Encuentra al armero Guardia Negra en Stratholme y acaba con él. Recupera la Insignia de La Guardia Negra y regresa con Seril Finiquiplaga."}},
[5321] = {"El despertar", {"Escolta a Kerlonian Semprumbrío a la Atalaya de Maestra de Vallefresno."}},
[5341] = {"La fortuna de la familia Barov", {"Aventúrate a Scholomance y recupera la fortuna familiar de los Barov. La fortuna se compone de cuatro escrituras: Las escrituras de Castel Darrow, Las escrituras de Rémol, Las escrituras del Molino Tarren y Las escrituras de Costasur. Regresa con Alexi Barov cuando hayas terminado esta tarea."}},
[5342] = {"El último de los Barov", {"Viaja al Campamento del Orvallo, territorio de la Alianza, y asesina a Weldon Barov. Coge su cabeza y regresa con Alexi Barov.","Ten cuidado, Alexi seguramente estará muy protegido por la escoria de la Alianza."}},
[5343] = {"La fortuna de la familia Barov", {"Aventúrate a Scholomance y recupera la fortuna familiar de los Barov. La fortuna se compone de cuatro escrituras: Las escrituras de Castel Darrow, Las escrituras de Rémol, Las escrituras del Molino Tarren y Las escrituras de Costasur. Regresa con Weldon Barov cuando hayas terminado esta tarea."}},
[5344] = {"El último de los Barov", {"Viaja a El Baluarte, territorio de la Horda, y asesina a Alexi Barov. Coge su cabeza y regresa con Weldon Barov.","Ten cuidado, Alexi seguramente estará muy protegido por la escoria de la Horda."}},
[5361] = {"Árbol genealógico", {"Entrégale el informe de Vigiarisco Cuernolargo a Nataka Cuernolargo que se encuentra en el Campamento del Espíritu Errante de Desolace."}},
[5381] = {"La Mano de Iruxos", {"Extrae la caja de demonio de la Fortaleza del Hacha de Trueno y llévasela a Taiga Crin Sabia a la Aldea Cazasombras junto con el pico de demonio.|n"}},
[5382] = {"Doctor Theolen Krastinov, el Carnicero", {"Busca al doctor Theolen Krastinov en el interior de Scholomance. Acaba con él y quema los restos de Eva Sarkhoff y los restos de Lucien Sarkhoff. Cuando hayas terminado tu tarea regresa con Eva Sarkhoff.|n"}},
[5384] = {"Kirtonos el Heraldo", {"Vuelve a Scholomance con la Sangre de los inocentes. Busca el Porche y coloca la Sangre de los inocentes en el blandón. Kirtonos acudirá a devorar tu alma.","Lucha con valentía, ¡no cedas ni una pulgada! Destruye a Kirtonos y vuelve con Eva Sarkhoff."}},
[5385] = {"Restos de Trey Forjaluz", {"Lleva los Restos de Trey Forjaluz a Jessir Arcolunar a Frondavil."}},
[5386] = {"La pesca del día", {"Habla con Jinar'Zillen, en la Aldea Cazasombras, y luego lleva 2 panzasangres a Nataka Cuerno Largo."}},
