[5461] = {"El humano, Ras Levescarcha", {"Viaja a las Tierras Altas de Arathi, a las Ruinas de Stromgarde. En Stromgarde busca un Recuerdo de Evocación. Si lo encuentras llévaselo al magistrado Marduke."}},
[5462] = {"El moribundo, Ras Levescarcha", {"Viaja a Esperanza de la Luz en las Tierras de la Peste del Este y busca a Leonid Barthalomew el Venerado. Enséñale el Recuerdo de Evocación y cuéntale todo lo que has descubierto."}},
[5463] = {"El Obsequio de Menethil", {"Viaja a Stratholme y encuentra el Obsequio de Menethil. Coloca el Recuerdo de Evocación en suelo no consagrado."}},
[5464] = {"El Obsequio de Menethil", {"Lleva las Memorias del Vínculo del Alma a Leonid Barthalomew a las Tierras de la Peste del Este."}},
[5465] = {"Memorias del Vínculo del Alma", {"Vuelve con el magistrado Marduke a Castel Darrow. Muéstrale las Memorias del Vínculo del Alma."}},
[5466] = {"El Lich, Ras Levescarcha", {"Busca a Ras Levescarcha en Scholomance. Cuando lo hayas encontrado utiliza las Memorias del Vínculo del Alma sobre su rostro no-muerto. Si consiguieras convertirlo en mortal, derrótale y recupera la Cabeza humana de Ras Levescarcha. Lleva la cabeza al magistrado Marduke."}},
[5481] = {"La tarea de Gordo", {"Recoge 3 hierbas luminiscentes y entrégaselas al boticario de segunda Holland en el cementerio de Rémol."}},
[5482] = {"Apocalíseas", {"Recoge 10 apocalíseas y entrégaselas al boticario de segunda Holland."}},
[5501] = {"Colección de kodos", {"Lleva 10 huesos de kodo del Cementerio de Kodos a Bibbly F'utzbuckle a la Cabaña de Kormek."}},
[5502] = {"Alcaide de la Horda", {"Compra un expositor para espadas de gomaespuma a Blax Botemisil en La Calle Mayor de Orgrimmar. Después, habla con la supervisora de huérfanos Llantobatalla en La Calle Mayor para llevar a tu pupilo de vuelta al orfanato de Orgrimmar."}},
