[5526] = {"Fragmentos de gangrevid", {"Encuentra gangrevid en La Masacre y coge un fragmento. Es probable que solo puedas conseguirlo si derrotas a Alzzin el Formaferal. Usa el relicario de pureza para guardar el fragmento y llévaselo al rabino Saturna a Amparo de la Noche, en Claro de la Luna."}},
[5527] = {"Un relicario de pureza", {"Ve a Silithus a buscar un relicario de pureza en las ruinas de la Aldea del Viento del Sur. Llévaselo al rabino Saturna a Amparo de la Noche, en Claro de la Luna."}},
[5528] = {"La cata de los Gordok", nil},
[5529] = {"Crías de dragón apestadas", {"Mata a 20 crías de dragón apestadas y ve a ver a Betina Bigglezink a la Capilla de la Esperanza de la Luz."}},
[5531] = {"Betina Bigglezink", {"Lleva los huevos congelados a Betina Bigglezink, que está fuera."}},
[5533] = {"Scholomance", {"Habla con el alquimista Arbington en el Alto del Orvallo, en las Tierras de la Peste del Oeste."}},
[5534] = {"El equipamiento \"perdido\" de Kim'jael", {"Encuentra una runa que los nagas guardan en las Ruinas de Eldarath y llévasela a Kim'jael en Azshara."}},
[5535] = {"Agitación espiritual", {"Destruye 6 apariciones de Altonato y 6 exanimatos Altonato antes de volver junto a Loh'atu en Azshara."}},
[5536] = {"Una tierra llena de odio", {"Mata 6 sátiros Haldarr, 2 timadores Haldarr, 2 jurapenas Haldarr y vuelve junto a Loh'atu en la frontera de Vallefresno."}},
[5537] = {"Fragmentos esqueléticos", {"Lleva 15 fragmentos esqueléticos al alquimista Arbington, en el Alto del Orvallo, en las Tierras de la Peste del Oeste."}},
[5538] = {"Molde rima con... ¿oro?", {"Lleva los Fragmentos esqueléticos imbuidos y 15 piezas de oro a Krinkle Buenacero en Gadgetzan."}},
[5541] = {"Municiones para Estruendo", {"Lleva la munición de Estruendo a Hegnar Estruendo a Dun Morogh."}},
[5542] = {"Perros demoníacos", {"Mata 20 cachorros de can de la Peste, 5 canes de la Peste y 5 canes de la Peste demenciados. Cuando hayas terminado tu tarea regresa con Tirion Fordring."}},
[5543] = {"Cielos sangrientos", {"Mata 30 murcípestes y vuelve con Tirion Fordring."}},
