[6085] = {"Domesticar la bestia", {"Usa la fusta de domador para domar un oso zarpahelada. Practica un poco y devuélvele la fusta a Grif Corazón Salvaje en Kharanos."}},
[6086] = {"Entrenar a la bestia", {"Habla con Belia Tronagranito en Forjaz."}},
[6087] = {"Domesticar la bestia", {"Usa la fusta de domador para domar un acechador de pradera. Practica un poco y devuélvele la fusta a Yaw Trampa en el Poblado Pezuña de Sangre."}},
[6088] = {"Domesticar la bestia", {"Usa la fusta de domador para domar un aguilón. Practica un poco y devuélvele la fusta a Yaw Trampa en el Poblado Pezuña de Sangre."}},
[6089] = {"Entrenar a la bestia", {"Habla con Holt Astatrueno en el Alto de los Cazadores, en Cima del Trueno."}},
[6101] = {"Domesticar la bestia", {"Utiliza la fusta de domador para domar a un acechador sable de la noche. Practica tus habilidades y después devuélvele la fusta de domador a Dazalar, que está en Dolanaar."}},
[6102] = {"Domesticar la bestia", {"Utiliza la fusta de domador para domar a un estridador estrígido. Practica tus habilidades y después devuélvele la fusta de domador a Dazalar, que está en Dolanaar."}},
[6103] = {"Domesticar la bestia", {"Habla con Jocaste en el Enclave Cenarion de Darnassus."}},
[6121] = {"Nuevas lecciones", {"Viaja a Claro de la Luna, al poblado Amparo de la Noche, y habla con Dendrita Rafagaestelar."}},
[6122] = {"La fuente del envenenamiento", {"Recoge agua de Salto de Fonroca a la entrada de la cueva.","Entrégale la muestra llena de Salto de Fonroca a Alanndarian Arrullanoche; en Auberdine, en Costa Oscura."}},
[6123] = {"Reunir los ingredientes del remedio", {"Lleva 5 raíces de tierra y 12 hongos lunares a Alanndarian Arrullanoche a Auberdine, para que pueda preparar un bálsamo curativo."}},
[6124] = {"Curar la dolencia", {"Utiliza el bálsamo curativo para animales en los 10 ciervos enfermos que hay en Costa Oscura; el bálsamo debería curarlos. Se han avistado ciervos enfermos desde el sur del Río Fonroca hasta el norte de Auberdine y por toda la zona que lleva hasta el sur de Costa Oscura, donde empieza Vallefresno."}},
[6125] = {"Poder contra el veneno", {"Vuelve a Darnassus para hablar con Mathrengyl Garroso en el Enclave Cenarion."}},
[6126] = {"Nuevas lecciones", {"Viaja a Claro de la Luna, al poblado Amparo de la Noche, y habla con Dendrite Rafagaestelar."}},
[6127] = {"La fuente del envenenamiento", {"Recoge agua de Cima Calígine en el estanque de la cima.","Luego llévasela a Tonga Tótem Rúnico a El Cruce, en Los Baldíos."}},
[6128] = {"Reunir los ingredientes del remedio", {"Llévale 5 raíces de tierra y 5 cuernos de kodo a Tonga Runatótem en El Cruce para que pueda confeccionar un bálsamo curativo."}},
[6129] = {"Curar la dolencia", {"Usa el bálsamo animal curativo sobre las 10 gacelas enfermas que se encuentran en la parte norte de Los Baldíos; así se curarán. Hemos recibido informes según los cuales se han avistado gacelas enfermas por la carretera que va de este a oeste en El Cruce."}},
[6130] = {"Poder contra el veneno", {"Vuelve a Cima del Trueno para hablar con Turak Tótem Rúnico en Alto de los Ancestros."}},
[6132] = {"¡Sácame de aquí!", {"Escolta a Melizza Zumbala y luego ve a Desolace, al Cementerio de Kodos y habla con Hornizz Zumbala."}},
[6133] = {"La orden del Señor de exploradores", {"Viaja a la frontera norte de las Tierras de la Peste del Este y recupera el Registro de Quel'Thalas. Se encuentra en algún lugar del Refugio Quel'Lithien.","También tienes que matar a 8 exploradores, 8 zancaminos y 8 Leñantros."}},
[6134] = {"La caza de ectoplamas", {"Lleva 8 ectoplasmas y la caja de imanes fantasmales a Hornizz Zumbala a Desolace."}},
[6135] = {"Alatardecer, cómo te odio…", {"Encuentra a Alatardecer y mátalo. Recoge un parche de piel de Alatardecer de su cadáver y llévaselo a Nathanos Clamorinfecto.","Se dice que Alatardecer vaga por el campo, al norte de la Hacienda de Marris."}},
[6136] = {"El corpulento", {"Encuentra a Borelgore en las planicies del norte de las Tierras de la Peste del Este y destrúyelo. Vuelve junto a Nathanos Clamorinfecto cuando hayas cumplido la misión."}},
[6141] = {"Hermano Antón", {"Habla con el hermano Antón en Desolace."}},
[6142] = {"Almejas", {"Encuentra 10 piezas de carne de almeja blanda y llévaselas a Mai'Lahii a la Aldea Cazasombras."}},
[6143] = {"Otros pescados para freír", {"Mata a 7 mirmidones Filozante, 7 nagas Filozante y 5 hechiceras Filozante y luego ve a Cazasombras, en Desolace, a hablar con Drulzegar."}},
[6144] = {"La llamada del teniente", {"Ve a Entrañas y habla con Varimathras."}},
[6145] = {"El mensajero carmesí", {"Vuelve a las Tierras de la Peste del Este y encuentra al mensajero carmesí. Mata al mensajero y recupera la Orden del Gran Cruzado.","Lleva el documento a Nathanos Clamorinfecto y espera sus instrucciones."}},
[6146] = {"La estratagema de Nathanos", {"Viaja a Mano de Tyr, al sureste de la Hacienda de Marris. Una vez allí, da un mordisco a la manzana podrida. Entrega la Orden del Gran Cruzado al Señor Cruzado Valdemar con la protección de la forma de La Cruzada Escarlata."}},
[6147] = {"Regresa junto a Nathanos", {"Vuelve con Nathanos Clamañublo con la nueva información.|n"}},
[6148] = {"El Oráculo Escarlata, Demetria", {"La Cruzada Escarlata se está moviendo. En algún lugar, a lo largo del camino que va de Mano de Tyr a Stratholme, encontrarás al Oráculo, Demetria, con su escolta. Da caza a Demetria y mátala. Vuelve junto a Nathanos Clamorinfecto si tienes éxito."}},
[6161] = {"La búsqueda del tesoro de Masatormento", {"Encuentra la Rackmore's Silver Key. Encuentra la Rackmore's Golden Key. Encuentra y abre el cofre de Masatormento."}},
[6162] = {"La última batalla de un esposo", {"Mata al Señor Supremo Ror y lleva su zarpa a Dreka'Sur al Puesto del Veneno de Frondavil."}},
[6163] = {"Ramstein", {"Viaja a Stratholme y mata a Ramstein el Empachador. Coge su cabeza y llévasela como souvenir a Nathanos."}},
[6164] = {"Libro de entradas de Augustus", {"Lleva el libro de entradas de Augustus a Augustus el Agraciado a Valle del Terror."}},
[6181] = {"Mensajero a toda prisa", {"Lleva la Nota de Lewis a Thor, el maestro de grifos."}},
[6182] = {"El primero y el último", {"Habla con Mathias Shaw en el Casco Antiguo, en Ventormenta. Vive en los Cuarteles."}},
[6183] = {"Honrar a los muertos", {"Vuelve a hablar con Mathias Shaw si quieres aceptar esta misión."}},
[6184] = {"Flint Sombramás", {"Vete al Campamento del Orvallo en las Tierras de la Peste del Oeste y reúnete con tu contacto, Flint Sombramás."}},
[6185] = {"Las pestes del este", {"Explora las Tierras de la Peste del Este en busca de pistas sobre el \"Clamorinfecto\" y los agentes perdidos del IS:7. Si encuentras alguna insignia del IS:7 llévasela a Flint Sombramás en el Campamento del Orvallo."}},
[6186] = {"El regreso de Clamorinfecto", {"Regresa a Ventormenta e informa al alto señor Bolvar Fordragon sobre el destino de Nathanos Marris."}},
[6187] = {"Restaurar el orden", {"Reúne un ejército y viaja a las Tierras de la Peste del Este. Lanza un asalto contra Nathanos Clamorinfecto y cualquier miembro de la Horda que pueda intentar protegerle.","Tienes que permanecer alerta, <nombre>. Los de la Horda defenderán al Señor de exploradores con su vida."}},
[6261] = {"Dungar Tragolargo", {"Lleva el Cajón de Osric a Dungar Tragolargo, el maestro de grifos."}},
[6281] = {"Prosigue hasta Ventormenta", {"Cómprale al maestro de grifos Thor un vuelo en grifo y lleva la Nota de Lewis a Osric Strang, en la tienda Inmunidad Limitada, en el Casco Antiguo de Ventormenta."}},
[6282] = {"La ameneza de la arpías", {"Maggran Ligaterra en el Refugio Roca del Sol quiere que mates 7 arpías Furia Sangrienta, 7 emboscadores Furia Sangrienta, 7 cazadores Furia Sangrienta y 7 Plumapícaros Furia Sangrienta."}},
[6283] = {"El linaje Furia Sangrienta", {"Maggran, en el Refugio Roca del Sol, quiere que mates a la Destripadora Furia Sangrienta y le lleves sus restos como prueba de tu hazaña."}},
[6284] = {"Aracnofobia", {"Mata a Besseleth y lleva el colmillo de Besseleth a Maggran en el Refugio Roca del Sol."}},
[6285] = {"Regresa con Lewis", {"Compra un vuelo en grifo hasta la Colina del Centinela al maestro de grifos Dungar Tragolargo y lleva el Cajón de Osric a Lewis de la Colina del Centinela."}},
[6301] = {"El ciclo del renacimiento", {"Tammra Windfield, en el Refugio Roca dle Sol, quiere que le consigas 10 semillas de Gaia."}},
[6321] = {"Suministros para El Sepulcro", {"Lleva el pedido de Podrig a Karos Razok."}},
[6322] = {"Michael Garrett", {"Lleva la caja de Gordon a Michael Garrett."}},
[6323] = {"Vuela a Entrañas", {"Compra un viaje en murciélago a Entrañas al maestro de murciélagos Karos Razok. Después lleva el pedido de Podrig a Gordon Wendham en Entrañas."}},
[6324] = {"Vuelve con Podrig", {"Compra un viaje en murciélago a El Sepulcro al maestro de murciélagos Michael Garrett. Después lleva la caja de Gordon al guardia de la muerte Podrig en El Sepulcro."}},
[6341] = {"La prodigalidad de Teldrassil", {"Lleva la colección de Nessa a Vesprystus."}},
[6342] = {"Vuela a Auberdine", {"Consigue un hipogrifo del maestro de hipogrifos Vesprystus, viaja a Auberdine y lleva la colección de Nessa a Laird."}},
[6343] = {"Vuelve con Nessa", {"Lleva la respuesta de Laird a Nessa Cantosombrío."}},
[6344] = {"Nessa Cantosombrío", {"Habla con Nessa Cantosombrío."}},
[6361] = {"Un fardo de pieles", {"Llévale el fajo de pieles al maestro de jinetes del viento Devrak en El Cruce."}},
[6362] = {"Un viaje a Cima del Trueno", {"Cómprale un jinete del viento para Cima del Trueno al maestro de jinetes del viento Devrak y llévale entonces el fajo de pieles a Ahanu en Cima del Trueno."}},
[6363] = {"Tal, maestro de jinetes del viento", {"Lleva los artículos de piel de Ahanu a Tal en Cima del Trueno."}},
[6364] = {"Volver junto a Jahan", {"Cómprale un jinete del viento para El Cruce al maestro de jinetes del viento Tal y llévale entonces los artículos de piel de Ahanu a Jahan Alalcón."}},
[6365] = {"Entrega de carne a Orgrimmar", {"Llévale la carne de Zargh a Devrak en El Cruce."}},
[6381] = {"Nueva vida", {"Planta 10 semillas de Gaia en los Túmulos y lleva las semillas de Gaia encantadas restantes a Tammra en el Refugio Roca del Sol."}},
[6382] = {"La Caza de Vallefresno", {"Habla con Senani Truenozón en el Puesto del Hachazo, Vallefresno."}},
[6383] = {"La Caza de Vallefresno", nil},
[6384] = {"Un viaje a Orgrimmar", {"Cómprale un jinete del viento al maestro de jinetes del viento Devrak y llévale entonces la carne de Zargh a Gryshka en Orgrimmar."}},
[6385] = {"Doras, maestro de jinetes del viento", {"Llévale la carta de Gryshka a Doras en Orgrimmar."}},
[6386] = {"El regreso a El Cruce", {"Cómprale un jinete del viento para El Cruce al maestro de jinetes del viento Doras y llévale entonces la carta de Gryshka a Zargh a en El Cruce."}},
[6387] = {"Estudiantes de honor", {"Lleva la lista de Brock a Thorgrum Borrelson a Thelsamar."}},
[6388] = {"Gryth Thurden", {"Lleva los picos honorarios a Gryth Thurden en Forjaz."}},
[6389] = {"Una Peste en la Cruzada", {"Libera las termitas de las Tierras de la Peste en la Serrería Crestanorte y luego vuelve con el barril de termitas a las Tierras de la Peste del Oeste y busca a Nathaniel Dumah en el Campamento del Orvallo."}},
[6390] = {"Una Peste en la Cruzada", {"Libera las termitas de las Tierras de la Peste en el Aserradero Crestanorte y luego vuelve con la barrica de termitas de las Tierras de la Peste a Mickey Levine en El Baluarte en los Claros de Tirisfal."}},
[6391] = {"Vuela a Forjaz", {"Contrata un vuelo en grifo a Forjaz con el maestro de grifos Thorgrum Borrelson para llevar la lista de Brock a Golnir Dedorroca."}},
[6392] = {"Vuelve con Brock", {"Contrata un vuelo en grifo a Thelsamar con el maestro de grifos Gryth Thurden para llevar los picos honoríficos a Brock Buscapiedras."}},
[6393] = {"Guerra elemental", {"Lleva 10 incendritas a Tsunaman en el Refugio Roca del Sol."}},
[6394] = {"Pico de Thazz'ril", {"Lleva el pico de Thazz'ril al supervisor Thazz'ril."}},
[6395] = {"El último deseo de Marla", {"Lleva los restos de Samuel Fipps a la tumba de Marla y luego vuelve con la novicia Elreth."}},
[6401] = {"Kaya está viva", {"Da las buenas noticias a Tammra Campovento en el Refugio Roca del Sol."}},
[6402] = {"Tienes un cita en Ventormenta", {"Viaja hasta Ventormenta y aventúrate tras las puertas de la ciudad. Habla con el escudero Rowe para que haga saber al alguacil Windsor sobre tu llegada."}},
[6403] = {"La gran farsa", {"Sigue a Reginald Windsor por Ventormenta. ¡Protégele del peligro!"}},
[6421] = {"Barranco del Alud", {"Explora las profundidades de la cueva del Barranco del Alud y lleva 10 cristales de resonita Mor'rogal en el Refugio Roca del Sol para que los investigue."}},
[6441] = {"Los cuernos de sátiro", {"Reúne 16 cuernos de sátiro para Pixie en el Puesto del Hachazo."}},
[6442] = {"Naga en la Ensenada de Zoram", {"Llévale 20 cabezas de Colafuria a Marukai en La Ensenada de Zoram."}},
[6461] = {"Chupasangres", {"Xen'zilla, en Malaka'Jin, necesita que mates 10 trepadores musgondo y 7 escupevenenos musgondo."}},
[6462] = {"Los talismanes trols", {"Llévale 8 talismanes trols a Mitsuwa en la Avanzada de Zoram'gar."}},
[6481] = {"El despertar del terráneo", {"Abre el barril de resonita con el cristal de resonita encantado y, a continuación, mata a Goggeroc. Vuelve a ver a Mor'rogal para contarle lo ocurrido y entregarle el cristal de resonita encantado."}},
[6482] = {"Liberar a Ruul", {"Escolta a Ruul y después habla con Yama Pezuñablanca en el Puesto del Hachazo."}},
[6501] = {"El Ojo del dragón", {"Debes buscar por todo el mundo y encontrar un ser capaz de restaurar el poder del fragmento del Ojo del dragón. La única información que tienes sobre dicho ser, es que existe."}},
[6502] = {"Amuleto Pirodraco", {"Tienes que recuperar la sangre de dragón negro campeón, la tiene el general Drakkisath. Puedes encontrar a Drakkisath en su sala del trono, tras las Salas de la Ascensión, en la Cumbre de Roca Negra."}},
[6503] = {"Avanzada de Vallefresno", {"Mata a 9 avanzados de Vallefresno y vuelve junto a Kuray'bin en el Puesto del Hachazo."}},
[6504] = {"Las páginas perdidas", {"Encuentra las 12 páginas que faltan del manual de instrucciones de la trituradora y vuelve a componer con ellas los 3 primeros capítulos. Devuélvele las páginas a Gurda Cicafuria cerca del Puesto del Hachazo."}},
[6521] = {"Una alianza nefasta", {"Lleva la cabeza del embajador Malcin a Varimathras, que está en Entrañas."}},
[6522] = {"Una alianza nefasta", {"Lleva el pergamino a Varimathras, que está en Entrañas."}},
[6523] = {"Proteje a Kaya", {"Escolta a Kaya Patatleta hasta el Campamento Aparaje y ve a ver a Makaba Patatleta cerca del límite sureste de Espolón."}},
[6541] = {"Preséntate ante Kadrak", {"Preséntate ante Kadrak en la torre de vigilancia en los Baldíos del Norte."}},
[6542] = {"Preséntate ante Kadrak", {"Abre el fajo de informes. Lleva los informes Grito de Guerra al explorador Grito de Guerra, al corredor Grito de Guerra y al escolta Grito de Guerra. Lleva los últimos informes que ellos te den a Kadrak en la torre de vigilancia norte en los Baldíos."}},
[6543] = {"Los informes Grito de Guerra", {"Preséntate ante Kadrak en la torre de vigilancia en los Baldíos del Norte."}},
[6544] = {"El asalto de Torek", {"Escolta a Torek en su ataque sobre la Avanzada Ala de Plata contra Duriel Fuegolunar e informa después a Ertog Colmillorrabioso en el Puesto del Hachazo."}},
[6545] = {"Novedades del corredor Grito de Guerra", nil},
[6546] = {"Novedades del escolta Grito de Guerra", nil},
[6547] = {"Novedades del explorador Grito de Guerra", nil},
[6548] = {"Venga mi aldea", {"Mata 8 rufianes Tótem Siniestro y 6 mercenarios Tótem Siniestro. A continuación, ve a ver a Makaba Patatleta cerca del límite sureste de Espolón."}},
[6561] = {"La vileza de Brazanegra", {"Llévale la cabeza del Señor Crepuscular Kelris a Bashana Runatótem en Cima del Trueno."}},
[6562] = {"Problemas en las profundidades", {"Habla con Je'neu Sancrea en Vallefresno."}},
[6563] = {"La esencia de Aku'Mai", {"Llévale 20 zafiros de Aku'Mai a Je'neu Sancrea en Vallefresno."}},
[6564] = {"Lealtad a los dioses antiguos", {"Llévale la nota humedecida a Je'neu Sancrea en Vallefresno."}},
[6565] = {"Lealtad a los dioses antiguos", {"Mata a Lorgus Jett en las Cavernas de Brazanegra y vuelve junto a Je'neu Sancrea en Vallefresno."}},
[6566] = {"Lo que trae el viento", {"Escucha a Thrall."}},
[6567] = {"El Campeón de la Horda", {"Busca a Rexxar. El jefe de guerra te ha indicado dónde encontrarlo. Recorre los caminos de Desolace, entre la Sierra Espolón y Feralas."}},
[6568] = {"El testamento de Rexxar", {"Entrega el testamento de Rexxar a Myranda la Fada. Está en las Tierras de la Peste del Oeste."}},
[6569] = {"Ilusiones oculares", {"Viaja a la Cumbre de Roca Negra y recoge 20 ojos de dragauro negro. Cuando hayas terminado tu tarea regresa con Myranda la Fada."}},
[6570] = {"Brasaliza", {"Viaja a la Ciénaga de Fuego del Marjal Revolcafango y busca El Cubil de Brasaliza. Cuando estés dentro ponte el Amuleto de Subversión Dracónica y habla con Brasaliza.|n"}},
[6571] = {"Suministros Grito de Guerra", {"Reúne los suministros Grito de Guerra: las hojas de sierra Grito de Guerra, cabo de troncos, aceite de los Grito de Guerra y el envío de hachas de los Grito de Guerra para Locke Okarr en el Puesto del Hachazo."}},
[6581] = {"Las hojas de sierra Grito de Guerra", nil},
[6582] = {"La prueba de las calaveras, Arúspice", {"Debes encontrar a Arúspice, el campeón draco del Vuelo Azul y matarlo. Separa su calavera de su cuerpo y llévasela a Brasaliza.","Sabes que puedes encontrar a Arúspice en Cuna del Invierno."}},
[6583] = {"La prueba de las calaveras, Somnus", {"Destruye al campeón draco del Vuelo Verde, Somnus. Lleva su calavera a Brasaliza."}},
[6584] = {"La prueba de los cráneos, Chronalis", {"Chronalis, hijo de Nozdormu, custodia las Cavernas del Tiempo en el Desierto de Tanaris. Destrúyelo y lleva su cráneo a Brasaliza."}},
[6585] = {"La prueba de las calaveras, Axtroz", {"Viaja a Grim Batol y encuentra a Axtroz, el campeón draco del Vuelo Rojo. Destrúyelo y hazte con su calavera. Llévasela a Brasaliza."}},
[6601] = {"El ascenso", {"Diría que se acabaron las charadas. Ya sabes que el Amuleto de Subversión Dracónica que Myranda la Fada creó para ti no funcionará en el interior de la Cumbre de Roca Negra. Quizás deberías buscar a Rexxar y explicarle el apuro en el que te encuentras. Enséñale el amuleto pirodraco apagado. Con un poco de suerte, él sabrá que hay que hacer ahora."}},
[6602] = {"La sangre de dragón negro campeón", {"Viaja a la Cumbre de Roca Negra y mata al general Drakkisath. Recoge su sangre y llévasela a Rexxar."}},
[6603] = {"Problemas en Cuna del Invierno", {"Encuentra a Donova Cubilnevado cerca de las fuentes termales de Cuna del Invierno."}},
[6604] = {"Lechúcico iracundo", {"Habla con Jaron Tallapiedras en Aldea Estrella Fugaz."}},
[6605] = {"Algo extraño", {"Habla con Winna Hazzard en Frondavil."}},
[6606] = {"Un poco de suerte", {"Habla con la médico bruja Mau'ari en Vista Eterna."}},
[6607] = {"Nat Pagle, pescador del extremo", {"Nat Pagle quiere que pesques los siguientes peces:","Mahi mahi juncobruma de la Playa Juncobruma del Pantano de las Penas.","1 artillero Sar'theris de la Playa de Sar'theris de Desolace.","1 Ahi de Feralas del Río Verdantis de Feralas.","Latipinia azul de La Costa Salvaje de Vega de Tuercespina.","Cuando los tengas todos, vuelve a buscar a Nat Pagle."}},
[6608] = {"¡Qué bueno eres!", {"Parece que Lumak piensa que ya no puede enseñarte nada más sobre la pesca. Ve a buscar a su amigo, Nat Pagle, un humano pescador que vive en una isla al sur de Theramore (o eso parece que ha dicho)."}},
[6609] = {"¡Me has dejado seco!", {"Ve a Kalimdor, a la ciudad costera de Theramore. Busca una isla hacia el sur. ¡Nat Pagle te espera!"}},
[6610] = {"Sorpresa de almejates", {"Dirge Hojágil quiere que le consigas lo siguiente:","12 huevos gigantes. Pueden ser huevos de los rocs que deambulan por Tanaris o de cualquier otro pájaro de grandes dimensiones.","10 piezas de carne de almeja sabrosa. ¡Esta la podrás obtener de las almejas, por supuesto!","20 piezas de queso suizo de Alterac. ¡Podrás encontrarlo en cualquier quesería!","Consigue todo lo que Dirge necesita y llévaselo."}},
[6611] = {"Ve a Gadgetzan", {"Ve a Gadgetzan, en el Desierto de Tanaris, y habla con Dirge Hojágil sobre tus habilidades culinarias."}},
[6612] = {"Conozco a un tío…", {"Ve a Gadgetzan, en el Desierto de Tanaris, y habla con Dirge Hojágil sobre tus habilidades culinarias."}},
[6621] = {"El rey de los Corrupto", {"Coloca el estandarte de Karang en el túmulo del tótem corrupto. No permitas que los furbolgs destruyan el estandarte. Derrota al jefe Murgut y lleva el tótem de Murgut a Karang Amakkar en Zoram'gar."}},
[6622] = {"Orden de gravedad", {"Salva a pacientes asignando vendas identificativas para atender sus heridas por orden de gravedad. Debes salvar a 15 pacientes antes de que 6 mueran a consecuencia de sus heridas.","¡Ten presente el orden de prioridad! Los soldados con heridas graves mueren antes que los soldados con heridas de gravedad media. Los soldados heridos leves son los que más tardan en morir y solo hay que salvarlos DESPUÉS de identificar y atender a los soldados con heridas graves y medias."}},
[6623] = {"Dispensario de la Horda", {"Ve a Sentencia, en las Tierras Altas de Arathi, y habla con el doctor Gregory Victor sobre tu incorporación al Dispensario de la Horda.|n"}},
[6624] = {"Atenciones médicas", {"Salva a los pacientes utilizando vendas identificativas para ocuparte de sus heridas. Debes salvar a 15 pacientes antes de que 6 de ellos mueran a causa de sus heridas.","¡Recuerda el orden en el que debes atender a los pacientes! Los soldados heridos graves mueren antes, seguidos de los soldados malheridos. Los soldados heridos leves tardan más en morir y deben ser salvados DESPUÉS de haber salvado a los soldados heridos graves y a los soldados malheridos."}},
