[7462] = {"El tesoro de los Shen'dralar", {"Regresa a El Athenaeum y encuentra el tesoro de Shen'dralar. ¡Reclama tu recompensa!"}},
[7463] = {"El refresco arcano", {"Viaja al Barrio Alabeo de La Masacre y mata al elemental de agua, Hidromilecio. Lleva la esencia de Hidromilecio al Tradicionalista Lydros que está en El Athenaeum."}},
[7481] = {"Leyendas élficas", {"Busca a Telmius Buscasueños en La Masacre. Vuelve al Campamento Mojache e informa al Sabio Korolusk de cualquier cosa que encuentres."}},
[7482] = {"Leyendas élficas", {"Busca a Telmius Buscasueños en La Masacre. Vuelve al Bastión Plumaluna a informar a la erudita Runaespina de lo que hayas encontrado."}},
[7483] = {"El Tratado de Prontitud", {"Lleva 1 Tratado de Prontitud, 1 diamante negro prístino, 2 grandes fragmentos brillantes y 2 unidades de sangre de héroes al guardián del Conocimiento Lydros a La Masacre para conseguir un arcano de prontitud."}},
[7484] = {"El Tratado de Concentración", {"Lleva 1 Tratado de Concentración, 1 diamante negro prístino, 4 grandes fragmentos brillantes y 2 pieles de sombra al guardián del Conocimiento Lydros a La Masacre para conseguir un arcano de concentración."}},
[7485] = {"El tratado sobre protección", {"Lleva 1 tratado sobre protección, 1 diamante negro prístino, 2 fragmentos luminosos grandes y 1 costura desgarrada de abominación al Tradicionalista Lydros a La Masacre para conseguir un arcanum de protección."}},
[7486] = {"Una recompensa de héroe", {"Recoge tu recompensa en el arca de Hydraxis."}},
[7488] = {"La Membrana de Lethtendris", {"Lleva la Membrana de Lethtendris a Latronicus Lanzaluna al Bastión Plumaluna de Feralas."}},
[7489] = {"La Membrana de Lethtendris", {"Lleva la Membrana de Lethtendris a Talo Pezuñahendida al Campamento Mojache de Feralas."}},
[7490] = {"Victoria para la Horda", {"Llévale la cabeza de Onyxia a Thrall en Orgrimmar."}},
[7491] = {"Para que todos lo vean", {"Busca al señor supremo Runthak en el Valle de la Fuerza."}},
[7492] = {"Campamento Mojache", {"Ve al Campamento Mojache de Feralas y habla con Talo Pezuñahendida."}},
[7493] = {"El viaje acaba de empezar", nil},
