[7495] = {"Victoria para la Alianza", {"Lleva la cabeza de Onyxia al Alto Señor Bolvar Fordragon, en Ventormenta."}},
[7496] = {"Celebrar los buenos momentos", {"Busca al mayor Mattingly en el Valle de los Héroes."}},
