[7701] = {"SE BUSCA: vigía Maltorius", {"El traidor Hierro Negro llamado vigía Maltorius está en La Fosa de la Escoria de La Caldera, en La Garganta de Fuego.","Mátalo y lleva su cabeza al capitán de vigía Lolo Zancalarga a La Garganta de Fuego para recibir tu recompensa."}},
[7703] = {"El aunto inacabado de Gordok", {"Encuentra el Guante del Poder de Gordok y llévaselo al capitán Kromcrush a La Masacre.","Según Kromcrush, una \"historia muy, muy vieja\" dice que Tortheldrin, un elfo \"asqueroso\" que se llamaba a sí mismo príncipe, robó el guantelete a uno de los reyes de Gordok."}},
[7704] = {"¡Mira el tamaño de esto!", {"Lleva la almohada de la ayudante de cámara Pilaprieta a Evonice Fumollín a La Garganta de Fuego."}},
[7721] = {"Combustible para el Ultra-reductor", {"Reúne 10 núcleos de elemental de agua de los elementales marinos y los rocíos marinos que hay en La Costa Olvidada de Feralas, y llévaselos a Zorbin Ventipnotizador, que está cerca del embarcadero."}},
[7722] = {"La receta del fluido ardiente", {"Busca la receta de fluido ardiente robada por el vigía Maltorius en la La Fosa de la Escoria de La Caldera. Llévasela al maestro herrero Cremación a La Garganta de Fuego."}},
[7723] = {"¡Malditos dedos gordos!", {"Hansel Manospesadas quiere que mates 20 golems de guerra pesados.","Suelen estar cerca de La Caldera en La Garganta de Fuego. Después ve a ver a Hansel."}},
[7724] = {"Amenaza abrasadora", {"Hansel Manospesadas quiere que mates 20 lavarañas mayores en La Garganta de Fuego. Las encontrarás en los estanques de lava.","Después ve a ver a Hansel Manospesadas."}},
[7725] = {"Encoge más gigantes", {"Utiliza el Ultra-reductor de Zorbin para encoger a cualquier gigante que encuentres en Feralas a un tamaño más manejable. Lleva 10 residuos de miniaturización encontrados en las versiones encogidas de los gigantes a Zorbin Ventipnotizador, al embarcadero de La Costa Olvidada de Feralas."}},
[7726] = {"Más combustible para el Ultra-reductor", {"Reúne 6 núcleos de elemental de agua de los elementales marinos y los rocíos marinos que hay en La Costa Olvidada de Feralas, y llévaselos a Zorbin Ventipnotizador, que está cerca del embarcadero."}},
[7727] = {"¿Incendosaurio? Loqueseasaurio más bien", {"Hansel Manospesadas quiere que mates 20 incendosaurios.","Suelen estar en los estanques de lava de La Fosa de la Escoria. Está en La Caldera de La Garganta de Fuego.","Después ve a ver a Hansel Manospesadas."}},
[7728] = {"OBJETOS ROBADOS: Tobera y espejo espía", {"Encuentra la tobera y el espejo espía y llévaselos al capataz Scrange a La Garganta de Fuego.","Lo único que sabes es que fueron robados por enanos Hierro Negro. La tobera es una herramienta de herrero y el espejo es un instrumento muy valioso para los vigías."}},
[7729] = {"OPORTUNIDAD LABORAL: Acabar con la competencia", {"Mata a 15 capataces y 15 esclavistas Hierro Negro y ve a ver al capataz Scrange a La Garganta de Fuego."}},
[7730] = {"Infestación de Zukk'ash", {"Lleva 20 caparazones de Zukk'ash a Hadoken Zancoveloz al Campamento Mojache de Feralas."}},
[7731] = {"Aguijolatigador", {"Derrota a Aguijolatigador en Las Entrañas Retorcidas y lleva sus glándulas a Hadoken Zancoveloz al Campamento Mojache de Feralas."}},
[7732] = {"El informe Zukk'ash", {"Entrega a Zilzibin Tambor Sabio el informe del campamento Mojache Zukk'ash. Reside en La Calle Mayor de Orgrimmar."}},
[7733] = {"Calidad mejorada", {"Lleva 10 pellejos gruesos de yeti Cicatriz Feral a Pratt McGrubben al Bastión Plumaluna."}},
[7734] = {"Calidad mejorada", {"Lleva 10 pellejos gruesos de yeti Cicatriz Feral a Jangdor Zancoveloz al Campamento Mojache de Feralas."}},
