[8534] = {"Informe de exploración de Colmen'Zora", {"Contacta con la exploradora Cenarion Azenel en Colmen'Zora y entrega el informe de exploración de Colmen'Zora a Astaorgullo Clamavientos en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas VI para poder completar la misión."}},
[8535] = {"Templario vetusto", {"Invoca y mata a un Templario vetusto y preséntate ante Bor Bravacrín en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas IV para poder completar la misión."}},
[8536] = {"Templario de tierra", {"Invoca y mata a un Templario de tierra y preséntate ante Bor Bravacrín en el Fuerte Cenarion. También debes llevar el Informe de tareas tácticas III para poder completar la misión."}},
