[8551] = {"El cofre del capitán", {"Lleva el cofre de Smotts a Hecklebury Smotts en la Bahía del Botín."}},
[8552] = {"La banda con monograma", {"Devuelve la banda con monograma a su propietario."}},
[8553] = {"El aflanje del capitán", {"Habla con Sprogger."}},
[8554] = {"Enfréntate a Negolash", {"Lleva 10 Alas de águila ratonera a la parrilla y 5 botellas de vino de vid de jungla al barco salvavidas del capitán Smotts.","Mata a Negolash y lleva el alfanje de Smotts al capitán Smotts en Tuercespina."}},
[8555] = {"Encomienda a los Vuelos", nil},
[8556] = {"El anillo con sello de fuerza implacable", {"Llévale 1 anillo de magistrado qiraji, 2 ídolos luminiscentes, 5 escarabajos de bronce y 5 escarabajos de marfil a Clamavientos Yessendra en Silithus. También debes alcanzar la reputación de honrado ante el Círculo Cenarion para completar esta misión."}},
[8557] = {"El mantón de fuerza implacable", {"Llévale 1 mantón marcial qiraji, 2 ídolos de ónice, 5 escarabajos de plata y 5 escarabajos de hueso a Keyl Patacerce en Silithus. También debes alcanzar la reputación de venerado ante el Círculo Cenarion para completar esta misión."}},
[8558] = {"La hoz de fuerza implacable", {"Llévale 1 empuñadura con púas qiraji, 2 ídolos de porcelana, 5 escarabajos de cristal y 5 escarabajos de piedra al alcaide Haro en Silithus. También debes alcanzar la reputación de exaltado ante el Círculo Cenarion para completar esta misión."}},
