[8778] = {"La Brigada de Forjaz necesita explosivos", {"Lleva 6 aceites de inmolación, 5 unidades de combustible de cohete goblin y 10 unidades de pólvora densa al arcanista Bocafuente cerca de Colmen'Zora en Silithus. También debes llevar el Informe de tareas logísticas IV para poder completar la misión."}},
[8779] = {"Materiales de visión", {"Lleva 1 fragmento grande brillante, 1 fragmento radiante grande y 1 esmeralda enorme a la Geóloga Trotuina en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas V para poder completar la misión."}},
[8780] = {"Refuerzos para armadura para el campo de batalla", {"Lleva 8 refuerzos para armadura bastos y 8 refuerzos para armadura pesados a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, cerca de Colmen'Zora, en Silithus. También debes llevar el Informe de tareas logísticas VII para poder completar la misión."}},
[8781] = {"Armas para el campo de batalla", {"Lleva 2 espadas anchas de acero lunar a Janela Martillotenaz en la Avanzada de la Brigada de Forjaz, en las afueras de Colmen'Zora. También debes llevar el Informe de tareas logísticas VI para poder completar la misión."}},
[8782] = {"Suministros para uniformes", {"Lleva 1 unidad de tela lunar, 2 madejas de paño rúnico y 1 seda de araña Tejehierro a Astaorgullo Clamavientos en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas VIII para poder completar la misión."}},
[8783] = {"Materiales encantados", {"Lleva 2 lingotes de torio encantado y 2 cueros encantados a Vargus en el Fuerte Cenarion, en Silithus. También debes llevar el Informe de tareas logísticas IX para poder completar la misión."}},
[8784] = {"Los secretos de los qiraji", {"Llévale el artefacto antiguo qiraji a los dragones ocultos cerca de la entrada del templo.|n"}},
[8785] = {"¡La Legión de Orgrimmar necesita mojo!", {"Lleva 6 unidades de mojo potente, 6 frascos de mojo grande y 8 aceites de inmolación a la sacerdotisa de las Sombras Shai cerca de Colmen'Regal, en Silithus. También debes llevar el Informe de tareas logísticas IV para poder completar la misión."}},
[8786] = {"Armas para el campo de batalla", {"Lleva 3 hachas de hierro macizo a Merok Zancada Larga en el campamento de la Legión de Orgrimmar, en las afueras de Colmen'Regal. También debes llevar el Informe de tareas logísticas VI para poder completar la misión."}},
[8787] = {"Complementos para armadura para el campo de batalla", {"Lleva 8 complementos bastos para armadura y 8 complementos pesados para armadura a Merok Zancolargo, cerca de Colmen'Regal. También debes llevar el Informe de tareas logísticas VII para poder completar la misión."}},
