[5441] = {"Peones vagos", {"Utiliza la Cachiporra del supervisor con los peones vagos que estén durmiendo. Despierta a 5 peones y devuélvele la cachiporra al supervisor Thazz'ril en el Valle de los Retos."}},
[5461] = {"El humano, Ras Murmuhielo", {"Viaja a las Tierras Altas de Arathi, a las Ruinas de Stromgarde. En Stromgarde busca un Libro de los Recuerdos. Si lo encuentras llévaselo al magistrado Marduke."}},
[5462] = {"El moribundo, Ras Murmuhielo", {"Viaja a Esperanza de la Luz en las Tierras de la Peste del Este y busca a Leonid Barthalomew el Venerado. Enséñale el Libro de los Recuerdos y cuéntale todo lo que has descubierto."}},
[5463] = {"El Obsequio de Menethil", {"Viaja a Stratholme y encuentra el Obsequio de Menethil. Coloca el Libro de los Recuerdos en suelo no consagrado."}},
[5464] = {"El Obsequio de Menethil", {"Lleva el Libro de Memorias del Alma a Leonid Barthalomew a las Tierras de la Peste del Este."}},
[5465] = {"El Libro de Memorias del Alma", {"Vuelve con el magistrado Marduke a Castel Darrow. Muéstrale el Libro de Memorias del Alma."}},
[5466] = {"El exánime, Ras Murmuhielo", {"Busca a Ras Murmuhielo en Scholomance. Cuando lo hayas encontrado utiliza el Libro de Memorias del Alma sobre su rostro no-muerto. Si consiguieras convertirlo en mortal, derrótale y recupera la Cabeza humana de Ras Murmuhielo. Lleva la cabeza al magistrado Marduke."}},
[5481] = {"La tarea de Gordo", {"Recoge 3 hierbas luminiscentes y entrégaselas al boticario auxiliar Holland en el cementerio de Rémol."}},
[5482] = {"Apocalíseas", {"Recoge 10 apocalíseas y entrégaselas al boticario auxiliar Holland."}},
