[6122] = {"La fuente del envenenamiento", {"Recoge agua de Salto de Fonroca a la entrada de la cueva.","","Entrégale la muestra llena de Salto de Fonroca a Alanndarian Arrullanoche; en Auberdine, en Costa Oscura."}},
[6123] = {"Reunir los ingredientes del remedio", {"Lleva 5 raíces de tierra y 12 hongos lunares a Alanndarian Arrullanoche a Auberdine, para que pueda preparar un bálsamo curativo."}},
[6124] = {"Curar la dolencia", {"Utiliza el bálsamo curativo para animales en los 10 ciervos enfermos que hay en Costa Oscura; el bálsamo debería curarlos. Se han avistado ciervos enfermos desde el sur del Río Fonroca hasta el norte de Auberdine y por toda la zona que lleva hasta el sur de Costa Oscura, donde empieza Vallefresno."}},
[6125] = {"Poder contra el veneno", {"Vuelve a Darnassus para hablar con Mathrengyl Garroso en el Enclave Cenarion."}},
[6126] = {"Nuevas lecciones", {"Viaja a Claro de la Luna, al poblado Amparo de la Noche, y habla con Dendrita Rafagaestelar."}},
[6127] = {"La fuente del envenenamiento", {"Usa el muestrario de Cima Calígine vacío para recoger una muestra de agua de uno de los estanques de la cima.","","Luego llévale el muestrario de Cima Calígine lleno a Tonga Tótem de Runa a El Cruce, en Los Baldíos."}},
[6128] = {"Reunir los ingredientes del remedio", {"Llévale 5 raíces de tierra y 5 cuernos de kodo a Tonga Tótem de Runa en El Cruce para que pueda confeccionar un bálsamo curativo."}},
[6129] = {"Curar la dolencia", {"Usa el bálsamo animal curativo sobre las 10 gacelas enfermas que se encuentran en la parte norte de Los Baldíos; así se curarán. Hemos recibido informes según los cuales se han avistado gacelas enfermas por la carretera que va de este a oeste en El Cruce."}},
[6130] = {"Poder contra el veneno", {"Vuelve a Cima del Trueno para hablar con Turak Tótem de Runa en Alto de los Ancestros."}},
[6132] = {"¡Sácame de aquí!", {"Escolta a Melizza Zumbala, y luego habla con Hornizz Zumbala en el Cementerio de Kodos en Desolace."}},
[6133] = {"La orden del Señor forestal", {"Viaja a la frontera norte de las Tierras de la Peste del Este y recupera el Registro de Quel'Thalas. Se encuentra en algún lugar del Refugio Quel'Lithien.","","También tienes que matar a 8 forestales, 8 zancaminos y 8 leñadores."}},
[6134] = {"La caza de ectoplasmas", {"Llévale 8 ectoplasmas y el cajón de imanes fantasmales a Hornizz Zumbala en Desolace."}},
[6135] = {"Alaocaso, cómo te odio...", {"Encuentra a Alaocaso y mátalo. Recoge un parche de pelaje de Alaocaso de su cadáver y llévaselo a Nathanos Clamañublo.","","Se dice que Alaocaso vaga por el campo, al norte de la Hacienda de Marris."}},
[6136] = {"El corpulento", {"Encuentra a Borelgore en las planicies del norte de las Tierras de la Peste del Este y destrúyelo. Vuelve junto a Nathanos Clamañublo cuando hayas cumplido la misión."}},
[6141] = {"Hermano Anton", {"Habla con el hermano Anton en Desolace."}},
[6142] = {"Almejas", {"Encuentra 10 piezas de carne de almeja blanda y llévaselas a Mai'Lahii a la Aldea Cazasombras."}},
[6143] = {"Otros pescados para freír", {"Mata a 7 mirmidones Filozante, 7 nagas Filozante y 5 hechiceras Filozante y luego ve a Cazasombras, en Desolace, a hablar con Drulzegar."}},
[6144] = {"La llamada del teniente", {"Viaja a Entrañas y habla con Varimathras."}},
[6145] = {"El mensajero carmesí", {"Vuelve a las Tierras de la Peste del Este y encuentra al mensajero carmesí. Mata al mensajero y recupera la Orden del Gran Cruzado.","","Lleva el documento a Nathanos Clamañublo y espera sus instrucciones."}},
[6146] = {"La estratagema de Nathanos", {"Viaja a Mano de Tyr, al sureste de la Hacienda de Marris. Una vez allí, da un mordisco a la manzana podrida. Entrega la Orden del Gran Cruzado al señor cruzado Valdemar con la protección de la forma de la Cruzada Escarlata."}},
[6147] = {"Regresa junto a Nathanos", {"Vuelve con Nathanos Clamañublo con la nueva información."}},
[6148] = {"El Oráculo Escarlata, Demetria", {"La Cruzada Escarlata se está moviendo. En algún lugar, a lo largo del camino que va de Mano de Tyr a Stratholme, encontrarás al Oráculo, Demetria, con su escolta. Da caza a Demetria y mátala. Vuelve junto a Nathanos Clamañublo si tienes éxito."}},
[6161] = {"La búsqueda del tesoro de Masatormento", {"Encuentra la llave de plata de Masatormento. Encuentra la llave de oro de Masatormento. Encuentra y abre el cofre de Masatormento."}},
[6162] = {"La última batalla de un esposo", {"Mata al señor supremo Ror y lleva su garra a Dreka'Sur al Puesto del Veneno de Frondavil."}},
[6163] = {"Ramstein", {"Viaja a Stratholme y mata a Ramstein el Empachador. Coge su cabeza y llévasela como souvenir a Nathanos."}},
