[6181] = {"Mensajero a toda prisa", {"Lleva la Nota de Lewis a Thor, el maestro de grifos."}},
[6182] = {"El primero y el último", {"Habla con Mathias Shaw en el Casco Antiguo, en Ventormenta. Vive en los Cuarteles."}},
[6183] = {"Honra a los muertos", {"Vuelve a hablar con Mathias Shaw si quieres aceptar esta misión."}},
[6184] = {"Flint Sombramás", {"Vete al Campamento del Orvallo en las Tierras de la Peste del Oeste y reúnete con tu contacto, Flint Sombramás."}},
[6185] = {"Las pestes del este", {"Explora las Tierras de la Peste del Este en busca de pistas sobre el \"Clamañublo\" y los agentes perdidos del IV:7. Si encuentras alguna insignia del IV:7 llévasela a Flint Sombramás en el Campamento del Orvallo."}},
[6186] = {"El regreso de Clamañublo", {"Regresa a Ventormenta e informa al alto señor Bolvar Fordragón sobre el destino de Nathanos Marris."}},
[6187] = {"Restaurar el orden", {"Reúne un ejército y viaja a las Tierras de la Peste del Este. Lanza un asalto contra Nathanos Clamañublo y cualquier miembro de la Horda que pueda intentar protegerle.","","Tienes que permanecer alerta, <nombre>. Los de la Horda defenderán al Señor forestal con su vida."}},
[6261] = {"Dungar Tragolargo", {"Lleva el cajón de Osric a Dungar Tragolargo, el maestro de grifos."}},
[6281] = {"Prosigue hasta Ventormenta", {"Cómprale al maestro de grifos Thor un vuelo en grifo y lleva la Nota de Lewis a Osric Strang, en la tienda Inmunidad Limitada, en el Casco Antiguo de Ventormenta."}},
[6282] = {"La amenaza de las arpías", {"Maggran Vinculador Terrestre, en el Refugio Roca del Sol, quiere que mates 7 arpías Furia Sangrienta, 7 emboscadoras Furia Sangrienta, 7 destripadoras Furia Sangrienta y 7 malaplumas Furia Sangrienta."}},
[6283] = {"El linaje Furia Sangrienta", {"Maggran, en el Refugio Roca del Sol, quiere que mates a la desgarradora Furia Sangrienta y le lleves sus restos como prueba de tu hazaña."}},
[6284] = {"Aracnofobia", {"Mata a Besseleth y lleva el colmillo de Besseleth a Maggran en el Refugio Roca del Sol."}},
[6285] = {"Regresa junto a Lewis", {"Compra un vuelo en grifo hasta la Colina del Centinela al maestro de grifos Dungar Tragolargo y lleva el cajón de Osric a Lewis de la Colina del Centinela."}},
[6301] = {"El ciclo del renacimiento", {"Tammra Campo de Viento, en el Refugio Roca del Sol, quiere que le consigas 10 semillas de Gaia."}},
[6321] = {"Suministros para El Sepulcro", {"Lleva la orden de Podrig a Karos Razok."}},
[6322] = {"Michael Garrett", {"Lleva el cajón de Gordok a Michael Garrett."}},
[6323] = {"Vuela a Entrañas", {"Compra un viaje en murciélago al maestro de murciélagos Karos Razok y lleva la orden de Podrig a Gordon Wendham en Entrañas."}},
[6324] = {"Regresa con Podrig", {"Cómprale un viaje en murciélago hasta el Sepulcro al maestro de murciélagos Michael Garrett y lleva el cajón de Gordon al Guardia de la Muerte Podrig en el Sepulcro."}},
[6341] = {"Las presas de Teldrassil", {"Lleva la colección de Nessa a Vesprystus."}},
[6342] = {"Vuelo a Auberdine", {"Compra un vuelo en hipogrifo hasta Auberdine al maestro de hipogrifos Vesprystus y lleva la colección de Nessa a Laird."}},
