[6388] = {"Gryth Thurden", {"Lleva los picos honorarios a Gryth Thurden en Forjaz."}},
[6389] = {"Una Peste en la Cruzada", {"Libera las termitas de las Tierras de la Peste en el Aserradero Crestanorte y luego vuelve con la barrica de termitas de las Tierras de la Peste a Nathaniel Dumah en el Campamento del Orvallo en las Tierras de la Peste del Oeste."}},
[6390] = {"Una Peste en la Cruzada", {"Libera las termitas de las Tierras de la Peste en el Aserradero Crestanorte y luego vuelve con la barrica de termitas de las Tierras de la Peste a Mickey Levine en El Baluarte en los Claros de Tirisfal."}},
[6391] = {"Vuela a Forjaz", {"Contrata un vuelo en grifo a Forjaz con el maestro de grifos Thorgrum Borrelson y llévale la lista de Brock a Golnir Dedorroca, en Forjaz."}},
[6392] = {"Regresa con Brock", {"Contrata un vuelo en grifo a Thelsamar con el maestro de grifos Gryth Thurden para llevarle los picos honorarios a Brock Buscapiedras en Thelsamar."}},
[6393] = {"Guerra elemental", {"Lleva 10 incendritas a Tsunaman en el Refugio Roca del Sol."}},
[6394] = {"Pico de Thazz'ril", {"Lleva el pico de Thazz'ril al supervisor Thazz'ril."}},
[6395] = {"El último deseo de Marla", {"Lleva los restos de Samuel Fipps a la tumba de Marla y luego vuelve con la novicia Elreth."}},
[6401] = {"Kaya está viva", {"Da las buenas noticias a Tammra Campo de Viento en el Refugio Roca del Sol."}},
[6402] = {"Tienes una cita en Ventormenta", {"Viaja hasta la Ciudad de Ventormenta y aventúrate tras las puertas de la ciudad. Habla con el escudero Rowe para que haga saber al mariscal Windsor sobre tu llegada."}},
[6403] = {"La gran farsa", {"Sigue a Reginald Windsor por Ventormenta. ¡Protégele del peligro!"}},
[6421] = {"Barranco del Alud", {"Explora las profundidades de la cueva del Barranco del Alud y lleva 10 cristales de resonita al Refugio Roca del Sol para que Mor'rogal las estudie."}},
[6441] = {"Los cuernos de sátiro", {"Reúne 16 cuernos de sátiro para Pixie en el Puesto del Hachazo."}},
[6442] = {"Nagas en la Ensenada de Zoram", {"Llévale 20 cabezas de Colafuria a Marukai en La Ensenada de Zoram."}},
[6461] = {"Chupasangres", {"Xen'zilla, en Malaka'Jin, necesita que mates a 10 trepadores Musgondo y a 7 escupevenenos Musgondo."}},
[6462] = {"El talismán trol", {"Llévale 8 talismanes trols a Mitsuwa en la Avanzada de Zoram'gar."}},
[6481] = {"El despertar del terráneo", {"Abre el barril de resonita con el cristal de resonita encantado y, a continuación, mata a Goggeroc. Vuelve a ver a Mor'rogal para contarle lo ocurrido y entregarle el cristal de resonita encantado."}},
[6482] = {"Liberar a Ruul", {"Escolta a Ruul y después habla con Yama Pezuña Nevada en el Puesto del Hachazo."}},
[6501] = {"El Ojo del Dragón", {"Debes buscar por todo el mundo y encontrar un ser capaz de restaurar el poder del fragmento del Ojo del Dragón. La única información que tienes sobre dicho ser es que existe."}},
[6502] = {"Amuleto Pirodraco", {"Tienes que recuperar la sangre de dragón Negro Campeón, la tiene el general Drakkisath. Puedes encontrar a Drakkisath en su sala del trono, tras las Salas de la Ascensión, en la Cumbre de Roca Negra."}},
[6503] = {"Avanzada de Vallefresno", {"Mata a 9 avanzadas de Vallefresno y regresa con Kuray'bin en Puesto del Hachazo."}},
[6504] = {"Las páginas perdidas", {"Encuentra las 12 páginas que faltan del manual de instrucciones de trituradora y vuelve a componer con ellas los 3 primeros capítulos. Devuélvele las páginas a Gurda Cicafuria cerca del Puesto del Hachazo."}},
[6521] = {"Una alianza impía", {"Lleva la cabeza del embajador Malcin a Varimathras en Entrañas."}},
[6522] = {"Una alianza impía", {"Lleva el pergamino pequeño a Varimathras en Entrañas."}},
[6523] = {"Protege a Kaya", {"Escolta a Kaya Pezuña Plana hasta el Campamento Aparaje y ve a ver a Makaba Pezuña Plana cerca del límite sureste de Espolón."}},
[6541] = {"Preséntate ante Kadrak", {"Preséntate ante Kadrak en la torre de vigilancia en Los Baldíos del Norte."}},
[6542] = {"Preséntate ante Kadrak", {"Preséntate ante Kadrak en la torre de vigilancia en Los Baldíos del Norte."}},
[6543] = {"Los informes Grito de Guerra", {"Abre el fardo de informes. Lleva los informes Grito de Guerra al explorador Grito de Guerra, al corredor Grito de Guerra y al escolta Grito de Guerra. Preséntate ante Kadrak en la torre de vigilancia norte en los Baldíos con la información que te hayan dado."}},
[6544] = {"El asalto de Torek", {"Escolta a Torek en su ataque sobre la Avanzada Ala de Plata contra Duriel Fuegolunar e informa después a Ertog Colmillorrabioso en el Puesto del Hachazo."}},
