[6582] = {"La prueba de los cráneos, Arúspice", {"Debes encontrar a Arúspice, el Campeón draco del Vuelo Azul y matarlo. Separa su cráneo de su cuerpo y llévaselo a Brasaliza.","","Sabes que puedes encontrar a Arúspice en Cuna del Invierno."}},
[6583] = {"La prueba de los cráneos, Somnus", {"Destruye al Campeón del Vuelo Verde, Somnus. Lleva su cráneo a Brasaliza."}},
[6584] = {"La prueba de los cráneos, Chronalis", {"Chronalis, hijo de Nozdormu, custodia las Cavernas del Tiempo en el Desierto de Tanaris. Destrúyelo y lleva su cráneo a Brasaliza."}},
[6585] = {"La prueba de los cráneos, Axtroz", {"Viaja a Grim Batol y encuentra a Axtroz, el Campeón del Vuelo Rojo. Destrúyelo y hazte con su cráneo. Llévaselo a Brasaliza."}},
[6601] = {"El ascenso", {"Parece que la farsa se acabó. Sabes que el Amuleto de Subversión Dracónica que Myranda la Fada creó para ti no funcionará en la Cumbre de Roca Negra. Quizá debas buscar a Rexxar y explicarle tu situación. Muéstrale el Amuleto Pirodraco apagado. Con suerte, él sabrá qué hacer."}},
