[7241] = {"En defensa de Lobo Gélido", {"Dirígete al Valle de Alterac en las Montañas de Alterac. Encuentra al maestro de guerra Laggrond y habla con él para empezar tu carrera como soldado Lobo Gélido; lo encontrarás en la entrada del túnel. El Valle de Alterac se sitúa al norte de Molino Tarren en la falda de las Montañas de Alterac."}},
[7261] = {"El imperativo soberano", {"Dirígete al Valle de Alterac en las Laderas de Trabalomas. Delante de la entrada del túnel, encuentra al teniente Haggerdin y habla con él.","","¡Por la gloria de Barbabronce!"}},
[7281] = {"Amor fraternal", {"Viaja hasta el territorio de los Pico Tormenta y mata al comandante Karl Filis. Vuelve con el comandante Luis Philips cuando hayas completado la tarea."}},
[7282] = {"Amor fraternal", {"Viaja hasta el territorio de los Lobo Gélido y mata al comandante Luis Philips Vuelve con el comandante Karl Filis cuando hayas completado la tarea."}},
[7301] = {"Señores del Cielo caídos", {"Viaja hasta el territorio de los Lobo Gélido y busca al Comandante del aire Slidore, al comandante del aire Vipore y al comandante del aire Ichman. Vuelve con el comandante Duffy cuando hayas completado la misión."}},
[7302] = {"Señores del Cielo caídos", {"Averigua qué fue del comandante del aire Guse, el Comandante del aire Jeztor y el comandante del aire Mulverick. Vuelve con el comandante Mulfort cuando hayas completado la misión."}},
[7321] = {"Sopa de tortuga reconfortante", {"Llévale 10 piezas de carne de tortuga y algunas especias calmantes al Christoph Jeffcoat en Molino Tarren."}},
[7341] = {"Un trato justo", nil},
[7342] = {"Las flechas no valen para nada", nil},
[7361] = {"Favor entre los Lanza Negra", {"Se te asignado la tarea de matar a los jugadores elfos de la noche en el Valle de Alterac.","","Mata a un elfo de la noche y llévale su cabeza decapitada a Najak Malditor en el Bastión Lobo Gélido."}},
[7362] = {"Aliado de los Tauren", {"Se te asignado la tarea de matar a los jugadores gnomos en el Valle de Alterac.","","Mata a un gnomo y llévale un mechón de la víctima a Ravak Tótem Siniestro en el Bastión Lobo Gélido."}},
