[7364] = {"La recompensa de Gnomeregan", {"Se te asignado la tarea de matar a los jugadores adversarios tauren en el Valle de Alterac.","","Mata a un tauren y vuelve con Dirk Estafa en Dun'Baldar con una pezuña tauren."}},
[7365] = {"Réquiem de Corzocelada", {"Se te asignado la tarea de matar a los jugadores trol en el Valle de Alterac.","","Mata a un trol y vuelve con Athramanis en Dun'Baldar con el mojo de un trol Lanza Negra."}},
[7366] = {"La misericordia del arzobispo", {"Se te asignado la tarea de matar a los jugadores renegados en el Valle de Alterac.","","Mata a un renegado y vuelve con el comandante Karl Filis (que deambula entre la Serrería y la Mina) con el corazón de tu víctima."}},
[7367] = {"Acabar con la amenaza", {"Encuentra al experto en explosivos Lobo Gélido y mátalo. Vuelve con el experto en explosivos Pico Tormenta cuando hayas acabado este trabajo."}},
[7368] = {"Acabar con la amenaza", {"Encuentra al experto en explosivos Pico Tormenta y mátalo. Vuelve con el experto en explosivos Lobo Gélido cuando hayas acabado este trabajo."}},
[7381] = {"El regreso de Korrak", {"Si Korrak el Sanguinario vuelve por la Tierra de Disputa, encuéntralo y acaba con él.","","Llévale la calavera de Korrak al maestro de guerra Laggrond en las Laderas de Trabalomas."}},
[7382] = {"Korrak el Eterno", {"Si Korrak el Sanguinario vuelve por la Tierra de Disputa, encuéntralo y acaba con él.","","Llévale la calavera de Korrak al teniente Haggerdin en las Laderas de Trabalomas."}},
[7383] = {"La corona de la Tierra", {"Llena la ampolla de amatista y devuélvesela a Corithras Furia Lunar en Dolanaar."}},
