[7441] = {"Pusillín y el ancestro Azj'Tordin", {"Viaja a La Masacre y encuentra al diablillo Pusillín. Convence a Pusillín de que te dé el libro de Conjuros de Azj'Tordin, por cualquier medio.","","Si consigues hacerte con el libro de Conjuros, vuelve al Pabellón de Lariss de Feralas y busca a Azj'Tordin."}},
[7461] = {"Locura interior", {"Debes destruir a los guardianes que rodean las 5 torres que controlan la Prisión de Immol'thar. Una vez desactivadas las torres, el campo de fuerza que rodea a Immol'thar se disipará.","","Entra en la Prisión de Immol'thar y erradica al demonio que hace guardia en su interior. Por último, haz frente al príncipe Tortheldrin en El Athenaeum.","","Si consigues completar esta misión, ve al patio a buscar a la ancestro Shen'dralar."}},
[7462] = {"El tesoro de los Shen'dralar", {"Vuelve a El Athenaeum y encuentra el tesoro de los Shen'dralar. ¡Reclama tu recompensa!"}},
[7463] = {"El refresco arcano", {"Viaja al Barrio Alabeo de La Masacre y mata al elemental de agua, Hidromilecio. Lleva la esencia de Hidromilecio al Tradicionalista Lydros que está en El Athenaeum."}},
[7481] = {"Leyendas élficas", {"Busca a Telmius Buscasueños en La Masacre. Vuelve al Campamento Mojache e informa al Sabio Korolusk de cualquier cosa que encuentres."}},
[7482] = {"Leyendas élficas", {"Busca a Telmius Buscasueños en La Masacre. Vuelve al Bastión Plumaluna a informar a la erudita Runaespina de lo que hayas encontrado."}},
[7483] = {"El tratado sobre rapidez", {"Lleva 1 tratado sobre rapidez, 1 diamante negro prístino, 2 fragmentos luminosos grandes y 2 sangres de héroes al Tradicionalista Lydros a La Masacre para conseguir un arcanum de rapidez."}},
[7484] = {"El tratado sobre enfoque", {"Lleva 1 tratado sobre enfoque, 1 diamante negro prístino, 4 fragmentos luminosos grandes y 2 pieles de Sombra al Tradicionalista Lydros a La Masacre para conseguir un arcanum de enfoque."}},
[7485] = {"El tratado sobre protección", {"Lleva 1 tratado sobre protección, 1 diamante negro prístino, 2 fragmentos luminosos grandes y 1 costura desgarrada de abominación al Tradicionalista Lydros a La Masacre para conseguir un arcanum de protección."}},
[7486] = {"Una recompensa de héroe", {"Recoge tu recompensa en el arca de Hydraxis."}},
[7487] = {"Armonización con el Núcleo", {"Acércate al portal de entrada del Núcleo de Magma en las Profundidades de Roca Negra y recoge un trozo del Núcleo. Llévaselo a Lothos Levantagrietas a la Montaña Roca Negra."}},
[7488] = {"La membrana de Lethtendris", {"Lleva la Membrana de Lethtendris a Latronicus Lanzaluna al Bastión Plumaluna de Feralas."}},
[7489] = {"La membrana de Lethtendris", {"Lleva la Membrana de Lethtendris a Talo Pezuñahendida al Campamento Mojache de Feralas."}},
